UPDATE locales_npc_text SET Text0_0_loc4='在这片沙漠中生活可是很艰辛!在这种热度之下我口渴得要命。' WHERE entry=1493;
UPDATE locales_npc_text SET Text0_0_loc4='救命!$B$B如果你能帮我离开这里，我就帮你炸开通向祖尔法拉克酋长所在地的那扇门!' WHERE entry=1511;
