UPDATE locales_npc_text SET Text0_0_loc4='嗯……你需要学的东西有点超出我的能耐了。去找那边的塞拉多斯谈谈。我会的东西全是他教的!' WHERE entry=5417;
UPDATE locales_npc_text SET Text0_0_loc4='很好，开始学习裁缝吧。' WHERE entry=5418;
