UPDATE locales_npc_text SET Text0_0_loc4='你不会失望的，旅行者。裁缝是一种充满乐趣的工作。',Text0_1_loc4='你不会失望的，旅行者。裁缝是一种充满乐趣的工作。' WHERE entry=5439;
