UPDATE locales_npc_text SET Text0_0_loc4='你可以从这个码头找到去塞拉摩的路。记住我的话，那些地方对从联盟去的人们是非常不安全的。一定要小心谨慎。' WHERE entry=5517;
UPDATE locales_npc_text SET Text0_0_loc4='你好。' WHERE entry=5519;
