UPDATE locales_npc_text SET Text0_0_loc4='燃烧军团紧紧逼迫着我们，但是我们顶住他们潮水般的进攻。阿克蒙德接近世界之树的时间越晚，我们离胜利就越近一步。' WHERE entry=9230;
