UPDATE locales_npc_text SET Text0_0_loc4='占领日境圣所只是我们作战计画的第一步。下一个目标就是军械库。$B$B我们目前的目标达成率为$3233w％。' WHERE entry=12255;
UPDATE locales_npc_text SET Text0_0_loc4='我们控制了军械库，但是我们在岛上的任务还没结束。' WHERE entry=12256;
