UPDATE locales_page_text SET text_loc2='J\'ai découvert un site incroyable !$B$BToute la zone pullule de traces d\'architecture antique.$B$BSi la journée n\'était pas aussi avancée, je fouillerais la chambre sur-le-champ !$B$BJe vais avoir du mal à dormir, ce soir…' WHERE entry=3710;
UPDATE locales_page_text SET text_loc2='Une belle journée bien productive !$B$BLa chambre est effectivement ancienne, et elle abrite un magnifique mécanisme qui semble ne plus fonctionner.$B$BJe vais regarder ça de plus près demain.$B$BCette découverte va sûrement obliger Schnottz à me remarquer !$B$BComment est-ce qu\'il pourrait me refuser une promotion après un tel résultat !' WHERE entry=3711;

