UPDATE db_script_string SET content_loc2='Merci encore pour votre aide. Puisse le vent se trouver toujours dans votre dos durant vos périples.' WHERE entry=2000000269; /* content: Thank you again for your aid. May the wind always be at your back during your travels. */
UPDATE db_script_string SET content_loc2='%s écoute la supplication |2 $n.' WHERE entry=2000000270; /* content: %s listens to $N\'s plea. */
UPDATE db_script_string SET content_loc2='%s crée sa toxine...' WHERE entry=2000000272; /* content: %s creates his toxin... */
UPDATE db_script_string SET content_loc2='%s remplit de toxine un récipient.' WHERE entry=2000000274; /* content: %s fills a container with the toxin. */
UPDATE db_script_string SET content_loc2='La toxine est prête, $n.' WHERE entry=2000000275; /* content: The toxin is ready, $N. */
UPDATE db_script_string SET content_loc2='OK, les gars. Installez ces os.' WHERE entry=2000000276; /* content: Okay, crew. Get to work on getting these bones installed. */
UPDATE db_script_string SET content_loc2='%s aspire une traînée de poussière de rêve par le nez...' WHERE entry=2000000277; /* content: %s snorts a speck of dream dust up his nose... */
UPDATE db_script_string SET content_loc2='%s est confus...' WHERE entry=2000000278; /* content: %s is dazed... */
UPDATE db_script_string SET content_loc2='Atchoum !' WHERE entry=2000000279; /* content: ACHOO! */
UPDATE db_script_string SET content_loc2='Ça rafraîchit. Je dois maintenant vous communiquer des informations...' WHERE entry=2000000282; /* content: That was refreshing. Now there\'s information that needs to be told... */
UPDATE db_script_string SET content_loc2='%s se tord de douleur.' WHERE entry=2000000284; /* content: %s writhes in pain. */
UPDATE db_script_string SET content_loc2='AAAAAAAAAAAAAAAARGH !' WHERE entry=2000000285; /* content: AAAAAAAAAAAAAAAARGH! */
UPDATE db_script_string SET content_loc2='Hum… Je me demande…' WHERE entry=2000000286; /* content: Hmm... I wonder... */
UPDATE db_script_string SET content_loc2='Bon, où est-ce ?' WHERE entry=2000000287; /* content: Now, where is it? */
UPDATE db_script_string SET content_loc2='Stupides mages… Ils passent leur temps à vider les réserves de composants et ne les remplacent jamais.' WHERE entry=2000000288; /* content: Stupid mages... always using the last of a reagent and never replacing it. */
UPDATE db_script_string SET content_loc2='Préparer le sérum ne prendra que quelques minutes, $n.' WHERE entry=2000000289; /* content: The serum will only take a few minutes to prepare, $N. */
UPDATE db_script_string SET content_loc2='Presque prêt...' WHERE entry=2000000290; /* content: Almost done... */
UPDATE db_script_string SET content_loc2='Le sérum est enfin terminé, $n. Approchez-vous à présent. Nous devons parler.' WHERE entry=2000000291; /* content: At last, the serum is complete, $N. Now, come closer. We must talk. */
UPDATE db_script_string SET content_loc2='Me détendre ? Tu sais ce que Kael\'thas fait à ceux qui échouent, Geezle ? Souffrances et douleurs éternelles... Faut PAS que tu te plantes, crétin.' WHERE entry=2000000292; /* content: Relax? Do you know what Kael\'thas does to those that fail him, Geezle? Eternal suffering and pain... Do NOT screw this up, fool. */
UPDATE db_script_string SET content_loc2='Nos éclaireurs à Brume-sang ont trouvé notre contact. Cet imbécile de Velen se trouvera bientôt sans défense... Assez longtemps pour nous permettre de frapper ! Maintenant, hors de ma vue avant que je te vaporise...' WHERE entry=2000000293; /* content: Our Bloodmyst scouts have located our contact. The fool, Velen, will soon leave himself open and defenseless -- long enough for us to strike! Now get out of my sight before I vaporize you... */
UPDATE db_script_string SET content_loc2='Libre, enfin libre ! Je dois retourner au bastion Chanteguerre sur-le-champ !' WHERE entry=2000000294; /* content: Freedom at last! I must return to Warsong Hold at once! */
UPDATE db_script_string SET content_loc2='J\'aurai toujours une dette envers vous, mon $gami:amie;.' WHERE entry=2000000295; /* content: I am forever indebted to you, friend. */
UPDATE db_script_string SET content_loc2='Merci, mon $gami:amie;.' WHERE entry=2000000296; /* content: Thank you, friend. */
UPDATE db_script_string SET content_loc2='Je vous remercie, $gétranger:étrangère;.' WHERE entry=2000000297; /* content: You have my thanks, stranger. */
UPDATE db_script_string SET content_loc2='Je me sens… différent.' WHERE entry=2000000299; /* content: I feel... different. */
UPDATE db_script_string SET content_loc2='Le varleu se rebiffe !' WHERE entry=2000000300; /* content: No more mister nice wolvar! */
UPDATE db_script_string SET content_loc2='Grifferage est libre ! Troll va mourir !' WHERE entry=2000000301; /* content: Rageclaw free! Troll die now! */
UPDATE db_script_string SET content_loc2='Spécimen métallique d\'origine étrangère détecté.' WHERE entry=2000000303; /* content: Metallic specimen of foreign origin detected. */
UPDATE db_script_string SET content_loc2='Spécimen identifié. Alliage de saronite et de fer.' WHERE entry=2000000305; /* content: Specimen identified as an alloy of saronite and iron. */
UPDATE db_script_string SET content_loc2='La saronite contenue dans cet échantillon est quasiment pure. On ne trouve pas ce genre de minerai naturellement à la surface d\'Azeroth.' WHERE entry=2000000307; /* content: The saronite in this sample is nearly pure. Ores like this do not occur naturally on the surface of Azeroth. */
UPDATE db_script_string SET content_loc2='Accès non autorisé ?!' WHERE entry=2000000309; /* content: Access denied?! */
UPDATE db_script_string SET content_loc2='Informations supplémentaires disponibles uniquement depuis les archives d\'Ulduar, par ordre du gardien Loken.' WHERE entry=2000000310; /* content: Further information can only be accessed from the archives within Ulduar, by order of Keeper Loken. */
UPDATE db_script_string SET content_loc2='En tout cas, une chose est sûre : vu sa taille, son épaisseur et ses fixations, cette armure devait servir à quelque chose d\'une stature gigantesque.' WHERE entry=2000000311; /* content: But one thing is for certain: this plate\'s size, thickness, and bolt holes point to a use as armor for a colossal structure. */
UPDATE db_script_string SET content_loc2='Directive terminée. Retour au mode de veille.' WHERE entry=2000000312; /* content: Directive completed. Returning to standby mode. */
UPDATE db_script_string SET content_loc2='Je… Je vous comprends, maintenant ! Bon, maintenant qu\'on peut enfin se parler, j\'ai quelques explications à vous donner.' WHERE entry=2000000313; /* content: I... I can understand you now! Well, now that we can talk to each other, you have some explaining to do! */
UPDATE db_script_string SET content_loc2='Est-ce que c\'est la Ligue des explorateurs qui vous a donné mon communicateur ?' WHERE entry=2000000314; /* content: How did you get my communicator from the Explorers\' League? */
UPDATE db_script_string SET content_loc2='Je vous remercie de nous avoir débarrassé des nains de fer. Mais pourquoi est-ce que la Horde s\'intéresse à moi ?' WHERE entry=2000000315; /* content: You have my thanks for dispatching the iron dwarves. But why would the Horde have an interest in me? */
UPDATE db_script_string SET content_loc2='Ça serait pas l\'éclaireur Vor\'takh, par hasard ? Même moi je connais sa réputation de traqueur impitoyable. Il veut m\'enlever, alors ?' WHERE entry=2000000316; /* content: That wouldn\'t be Scout Vor\'takh, would it? Even I know of his reputation for ruthlessness. He means to abduct me, then? */
UPDATE db_script_string SET content_loc2='Si vous avez vu l\'journal, alors vous connaissez mes découvertes. Les créations des titans sont en guerre les unes contre les autres !' WHERE entry=2000000317; /* content: If you\'ve seen the journal, then you know what I\'ve been discovering. The titans\' own creations are at war with each other! */
UPDATE db_script_string SET content_loc2='Loken et ses nains de fer ont chassé les terrestres d\'Ulduar et se sont emparés de la ville.' WHERE entry=2000000318; /* content: Loken and his iron dwarf minions have ousted the Earthen from Ulduar and taken over the city. */
UPDATE db_script_string SET content_loc2='Si on dépense notre temps et notre énergie à s\'faire la guerre, Loken va utiliser les ressources d\'Ulduar pour tous nous détruire, Horde et Alliance.' WHERE entry=2000000319; /* content: If we spend our time and strength fighting with each other, Loken will use Ulduar\'s resources to destroy both Horde and Alliance. */
UPDATE db_script_string SET content_loc2='Si la Ligue des explorateurs a envoyé du monde, je n\'en ai pas vu la moindre trace. J\'ai trouvé votre message enterré dans un camp envahi par les nains de fer.' WHERE entry=2000000321; /* content: If the Explorers\' League sent men, I didn\'t see any trace of them. I found your note buried in a camp overrun by iron dwarves. */
UPDATE db_script_string SET content_loc2='Un éclaireur a trouvé les restes de votre journal à Thor Modan. Depuis, nous sommes à votre recherche.' WHERE entry=2000000322; /* content: A scout found the remains of your journal in Thor Modan. We\'ve been looking for you ever since. */
UPDATE db_script_string SET content_loc2='Hum… Après avoir déposé ça, je crois que je vais aller faire un tour à cette fromagerie pour manger un morceau.' WHERE entry=2000000327; /* content: Hm...after dropping this off, I think I\'ll head to that cheese shop for a snack. */
UPDATE db_script_string SET content_loc2='Oui, m\'dame, les affaires marchent bien !' WHERE entry=2000000340; /* content: Yes ma\'am, business is brisk! */
UPDATE db_script_string SET content_loc2='Merci pour le fromage !' WHERE entry=2000000341; /* content: Thanks for the cheese! */
UPDATE db_script_string SET content_loc2='Je suis de retour !' WHERE entry=2000000343; /* content: I\'m back! */
UPDATE db_script_string SET content_loc2='AÏE AÏE AÏE !' WHERE entry=2000000344; /* content: YEEOOWCH! */
UPDATE db_script_string SET content_loc2='Je n\'ai peur de rien ! Viens donc !' WHERE entry=2000000347; /* content: I\'m not afraid of anything -- bring it on! */
UPDATE db_script_string SET content_loc2='Debout, les damnés, de la terre !' WHERE entry=2000000348; /* content: Arise from the earth, my brothers! */
UPDATE db_script_string SET content_loc2='Nos maîtres de fer ont une mission pour nous !' WHERE entry=2000000349; /* content: Our iron masters have a mission for us! */
UPDATE db_script_string SET content_loc2='Suivez-moi vers les montagnes, allons mettre à exécution le plan de nos maîtres !' WHERE entry=2000000350; /* content: Follow me into the mountains to carry out the plan of the masters! */
UPDATE db_script_string SET content_loc2='Analyse du fragment en cours.' WHERE entry=2000000357; /* content: Fragment analysis underway. */
UPDATE db_script_string SET content_loc2='Objet identifié : Val\'anyr, marteau des anciens rois.' WHERE entry=2000000358; /* content: Object identified: Val\'anyr, hammer of ancient kings. */
UPDATE db_script_string SET content_loc2='Créé par les titans eux-mêmes, Val\'anyr fut offert au premier roi terrestre, Urel Cœur-de-pierre. C\'est avec ce marteau qu\'il devait créer et donner vie au reste des siens.' WHERE entry=2000000359; /* content: Created by the titans themselves, Val\'anyr was given to the first earthen king, Urel Stoneheart. With the hammer he was to create and give life to the rest of his brethren. */
UPDATE db_script_string SET content_loc2='Val\'anyr fut brisé pendant la première guerre entre les terrestres et les nains de fer. Les restes de l\'arme étaient considérés comme perdus au cours du conflit.' WHERE entry=2000000360; /* content: Val\'anyr was shattered during the first war between the earthen and the iron dwarves. The weapon\'s remnants were believed lost in the conflict. */
UPDATE db_script_string SET content_loc2='Les puissants fluides acides théoriquement contenus dans l\'entité appelée Yogg-Saron devraient permettre la liquéfaction de la saronite. L\'immersion dans cette substance pourrait être suffisante pour ressouder un alliage créé par les titans.' WHERE entry=2000000362; /* content: Powerful acidic content theoretically found inside the being known as Yogg-Saron would account for the liquification of saronite. Submersion in this substance might be sufficient to rebind an alloy of titan origin. */
UPDATE db_script_string SET content_loc2='Début de l\'analyse d\'état de la gardienne Freya.' WHERE entry=2000000363; /* content: Commencing Watcher Freya status analysis. */
UPDATE db_script_string SET content_loc2='L\'analyse de l\'ancien Ecorcepierre révèle une défense sonique qui perturbe l\'utilisation de la magie.' WHERE entry=2000000366; /* content: Elder Stonebark analysis reveals a sonic defense that disrupts use of magic. */
UPDATE db_script_string SET content_loc2='Début de l\'analyse d\'état du gardien Hodir.' WHERE entry=2000000370; /* content: Commencing Watcher Hodir status analysis. */
UPDATE db_script_string SET content_loc2='Le cachet de Hodir semble être situé à l\'intérieur d\'une cache d\'artéfacts.' WHERE entry=2000000371; /* content: Hodir\'s sigil appears to be located inside a cache of artifacts. */
UPDATE db_script_string SET content_loc2='Grande instabilité d\'humeur et de comportement du gardien Hodir. Probabilité élevée de destruction de la cache en cas de combat prolongé.' WHERE entry=2000000372; /* content: Watcher Hodir\'s temperament and behavior highly unstable. Destruction of cache highly probable during prolonged combat. */
UPDATE db_script_string SET content_loc2='La sauvegarde de la cache est essentielle pour la récupération du cachet de Hodir.' WHERE entry=2000000374; /* content: Preservation of cache is essential to recovering Hodir\'s sigil. */
UPDATE db_script_string SET content_loc2='Début de l\'analyse d\'état du gardien Mimiron.' WHERE entry=2000000375; /* content: Commencing Watcher Mimiron status analysis. */
UPDATE db_script_string SET content_loc2='Pour récupérer le cachet de Mimiron, initiez la séquence d\'autodestruction et abattez Mimiron avant son terme.' WHERE entry=2000000378; /* content: To retrieve Mimiron\'s sigil, initiate self-destruct sequence and defeat Mimiron before its completion. */
UPDATE db_script_string SET content_loc2='Réinitialisation planétaire ? Parle plus clairement, engin à la noix !' WHERE entry=2000000379; /* content: Planetary re-origination? Speak plainly, ye blasted machine! */
UPDATE db_script_string SET content_loc2='Début de l\'analyse d\'état du gardien Thorim.' WHERE entry=2000000380; /* content: Commencing Watcher Thorim status analysis. */
UPDATE db_script_string SET content_loc2='Alors ça, c\'est c\'que j\'appelle une belle trouvaille !' WHERE entry=2000000384; /* content: Now, this is what we call a nice find! */
UPDATE db_script_string SET content_loc2='J\'vais t\'en donner, d\'l\'accréditation, maudite machine. Et la r\'voilà : la clé de Norgannon !' WHERE entry=2000000385; /* content: I\'ll show ye access, ye wretched machine. Here it is again: Norgannon\'s key! */
UPDATE db_script_string SET content_loc2='Ça r\'semble drôlement aux autres archives des titans qu\'mes gars ont trouvées... mais en plus vieux... Et puis plus grand.' WHERE entry=2000000386; /* content: Looks just like all the other titan archives me lads have found... only older... and bigger. */
UPDATE db_script_string SET content_loc2='Et alors ? Qui c\'est qu\'écoute le signal ? Qu\'est-ce qui va s\'passer ?' WHERE entry=2000000389; /* content: Well? Who\'s on the listening side of the signal? What\'s going to happen? */
UPDATE db_script_string SET content_loc2='Et alors, ce Algalon, y va chercher quoi pour son diagnostic ? C\'est quoi nos chances ?' WHERE entry=2000000391; /* content: Well, what\'s this Algalon going to be looking for with his diagnostics? What are our chances? */
UPDATE db_script_string SET content_loc2='Malédiction. On dirait bien qu\'ça va s\'régler à la baston.' WHERE entry=2000000392; /* content: Blast it, lads. Looks like we\'ve got a fight ahead of us. */
UPDATE db_script_string SET content_loc2='La décomposition de la planète et de ses organismes vivants en leurs éléments constitutifs : métaux, roches, gaz, suivie d\'une période de réarrangement de ces éléments selon le modèle planétaire d\'origine.' WHERE entry=2000000398; /* content: The decomposition of the planet and its living organisms into base elements: metals, rocks, gases. This is followed by a period of reconstitution of each element into the original planetary blueprint. */
UPDATE db_script_string SET content_loc2='Quatre-vingt-dix-neuf virgule quatre-vingt-dix-neuf pour cent.' WHERE entry=2000000401; /* content: Ninety-nine point nine nine percent. */
UPDATE db_script_string SET content_loc2='Changement d\'programme ! Déverrouille l\'entrée du planétarium céleste.' WHERE entry=2000000403; /* content: New orders! Unlock the entry to the Celestial Planetarium. */
UPDATE db_script_string SET content_loc2='Quat\' gardiens ! Des cachets ! Tu pourrais pas être un peu moins cryptique, satané tas d\'boulons !' WHERE entry=2000000404; /* content: Four watchers? Sigils? Could ye be a little less cryptic, confounded machine? */
UPDATE db_script_string SET content_loc2='Et comment qu\'on leur prend les cachets, alors ? On va les voir et on leur demande gentiment ?' WHERE entry=2000000405; /* content: How do we get the sigils from them, then? Just walk up to them and ask them nicely? */
UPDATE db_script_string SET content_loc2='Mais tu vas nous dire comment qu\'on peut « dé-compromettre » les cachets, pas vrai ?' WHERE entry=2000000406; /* content: You\'re going to tell us how to... "un-compromise" the sigils, aren\'t you? */
UPDATE db_script_string SET content_loc2='Ça fait des années que j\'rêve de partir en vadrouille dans les salles des titans. Mais j\'avais jamais pensé que j\'devrais affronter leurs créations.' WHERE entry=2000000407; /* content: I\'ve dreamed of roaming the halls of the titans for years. I never thought I\'d be pitted against their creations. */
UPDATE db_script_string SET content_loc2='Requête refusée. L\'accès requiert une vérification manuelle des cachets des quatre gardiens.' WHERE entry=2000000408; /* content: Request denied. Access requires manual verification of the four watchers\' sigils. */
UPDATE db_script_string SET content_loc2='Corruption découverte. Gardiens sous contrôle d\'une influence extérieure. Cachets compromis.' WHERE entry=2000000411; /* content: Corruption found. External influence gaining control over watchers. Sigils compromised. */
UPDATE db_script_string SET content_loc2='Une force suffisante peut déclencher un redémarrage des fonctions des gardiens et purger l\'influence extérieure.' WHERE entry=2000000412; /* content: Sufficient use of force would trigger a reset in watcher functions, removing the external influence. */
UPDATE db_script_string SET content_loc2='Pour les risques d\'endommager les cachets au cours de ce processus, je peux analyser l\'état de chaque gardien et vous fournir ces informations une fois les calculs terminés.' WHERE entry=2000000413; /* content: As for not endangering the sigils during this process, I can analyze each watcher\'s status and make that information available to you upon completion of calculations. */
UPDATE db_script_string SET content_loc2='Tu as raté un sacré combat, Dorian ! Un qui restera dans les annales !' WHERE entry=2000000414; /* content: What a fight you missed, Dorian! It was one for the ages! */
UPDATE db_script_string SET content_loc2='On était là, chevauchant vers la bataille à toute vitesse sur le dos de Lourdeau…' WHERE entry=2000000415; /* content: There we were, riding into battle at full speed on the back of Stampy... */
UPDATE db_script_string SET content_loc2='Tout secouait partout, dans tous les sens. J\'arrivais à peine à stabiliser mon fusil.' WHERE entry=2000000416; /* content: Everything was shaking all over the place. I could hardly steady my gun. */
UPDATE db_script_string SET content_loc2='Et c\'est là qu\'elle est apparue, l\'effroyable bête !' WHERE entry=2000000417; /* content: And then we saw her, the fearsome beast! */
UPDATE db_script_string SET content_loc2='Il a fallu toute la maîtrise |2 $n pour réussir à garder le contrôle de Lourdeau.' WHERE entry=2000000418; /* content: It took all of $N\'s skill to maintain control of Stampy. */
UPDATE db_script_string SET content_loc2='La bataille n\'était plus qu\'un grand nuage de sang, de plomb et de sueur. Les attaques de la mère des couvées pleuvaient de plus en plus vite !' WHERE entry=2000000419; /* content: The battle was a haze of blood, lead, and sweat. The broodmother\'s attacks came faster and faster! */
UPDATE db_script_string SET content_loc2='Mais, à la fin, $n et moi on a gagné grâce à notre ruse et à notre travail d\'équipe. Et maintenant, on a ce glorieux trophée comme témoignage de notre victoire.' WHERE entry=2000000420; /* content: But, in the end, $N and I prevailed through our wits and teamwork. And now we have this glorious trophy as a memento of our victory. */
UPDATE db_script_string SET content_loc2='Est-ce que c\'est une petite poussée de jalousie que j\'entends là, Dorian ?' WHERE entry=2000000421; /* content: Do I detect a bit of jealousy, Dorian? */
UPDATE db_script_string SET content_loc2='Ben, je sais pas. Moi j\'étais ici, je me suis occupé du feu et j\'ai discuté des avantages et inconvénients des divers types de rouages avec Zazoutille. C\'est pas mal épique, aussi, dans son genre.' WHERE entry=2000000422; /* content: Well, I don\'t know. I was here tending the fire and discussing the drawbacks of cogs with Zootfizzle. That\'s pretty epic, too, in its own way. */
UPDATE db_script_string SET content_loc2='%s commence un rite de création...' WHERE entry=2000000424; /* content: %s begins a rite of creation... */
UPDATE db_script_string SET content_loc2='%s crée un nouveau totem...' WHERE entry=2000000426; /* content: %s creates a new totem... */
UPDATE db_script_string SET content_loc2='Le totem est prêt, $n.' WHERE entry=2000000427; /* content: The totem is made, $N. */
UPDATE db_script_string SET content_loc2='%s transforme en totem de purification les objets que $n lui a donnés.' WHERE entry=2000000428; /* content: %s transforms the items $N gave him into a totem of cleansing... */
UPDATE db_script_string SET content_loc2='%s fixe le nord-ouest et laisse échapper un long hurlement.' WHERE entry=2000000430; /* content: %s stares to the northwest and lets out a long, drawn out howl. */
UPDATE db_script_string SET content_loc2='%s laisse échapper un puissant hurlement.' WHERE entry=2000000431; /* content: %s lets forth a mighty howl. */
UPDATE db_script_string SET content_loc2='Vous êtes en retard, $gmessager:messagère;. Non, pas d\'excuse. Nous n\'avons pas le temps, et cette mission est bien trop importante pour ça.' WHERE entry=2000000432; /* content: You\'re late, courier. No, I don\'t want any excuses; this errand is far too important. */
UPDATE db_script_string SET content_loc2='Voilà le recueil que notre représentant a rapporté du Repos du ver. Apportez-le à Myralion Solembrase immédiatement.' WHERE entry=2000000433; /* content: Here\'s the tome our representative brought from Wyrmrest. Get this to Myralion Sunblaze immediately. */
UPDATE db_script_string SET content_loc2='Attention à vous, $gmessager:messagère;. Je n\'ai pas besoin de vous rappeler la valeur de ce livre, ni le fait que le Concordat argenté ne reculera devant rien pour se l\'approprier.' WHERE entry=2000000434; /* content: Watch your back, courier. I needn\'t remind you of the value of the book, nor the fact that the Silver Covenant will stop at nothing to take it from us. */
UPDATE db_script_string SET content_loc2='Voilà le livre. Vous devez vous assurer qu\'il arrive entre les mains de Caladis Brillelance à la Couronne de glace avant que les Saccage-soleil ne se rendent compte de la situation.' WHERE entry=2000000435; /* content: Here is the book. You must ensure it gets to Caladis Brightspear in Icecrown before the Sunreavers realize what is happening. */
UPDATE db_script_string SET content_loc2='Ne traînez pas en route ! Les Saccage-soleil ne perdront pas une seconde quand ils sauront que nous avons les informations qu\'ils recherchent.' WHERE entry=2000000436; /* content: Travel quickly! The Sunreavers will not waste any time once they realize that we have the information they seek. */
UPDATE db_script_string SET content_loc2='Ah, je vois…' WHERE entry=2000000438; /* content: Ah, I see... */
UPDATE db_script_string SET content_loc2='Oui, c\'est très regrettable.' WHERE entry=2000000439; /* content: Yes, this is unfortunate. */
UPDATE db_script_string SET content_loc2='Voilà, j\'ai terminé.' WHERE entry=2000000444; /* content: There, it is completed. */
UPDATE db_script_string SET content_loc2='Les arakkoa se cachent… Ils sont partout !' WHERE entry=2000000447; /* content: The arakkoa are hidden... everywhere! */
UPDATE db_script_string SET content_loc2='Je me rends.' WHERE entry=2000000449; /* content: I yield to you. */
UPDATE db_script_string SET content_loc2='Préparez-vous !' WHERE entry=2000000450; /* content: Prepare yourself! */
UPDATE db_script_string SET content_loc2='Que le combat commence !' WHERE entry=2000000451; /* content: Let it begin! */
UPDATE db_script_string SET content_loc2='Vous avez toute ma gratitude !' WHERE entry=2000000454; /* content: You have my thanks! */
UPDATE db_script_string SET content_loc2='Ne manquez pas les numéros comiques de Perry Gatner, dans quelques minutes !' WHERE entry=2000000458; /* content: Check out the comedy stylings of Perry Gatner in just a few minutes! */
UPDATE db_script_string SET content_loc2='Si vous avez envie de rire, allez faire un tour à la taverne de la Fin du monde !' WHERE entry=2000000459; /* content: If you are ready to laugh, head over to the World\'s End Tavern! */
UPDATE db_script_string SET content_loc2='Sans plus attendre, ici à Shattrath, les Tauren Chieftains ! Ils jouent dans la taverne de la Fin du monde au prochain tour de la pendule !' WHERE entry=2000000460; /* content: Now, in Shattrath City: The Tauren Chieftains! Playing in the World\'s End Tavern at the top of the hour! */
UPDATE db_script_string SET content_loc2='Frénécœur va vous tuer si vous revenez. On veut plus vous voir !' WHERE entry=2000000462; /* content: Frenzyheart kill you if you come back. You no welcome here no more! */
UPDATE db_script_string SET content_loc2='Merci, $r ! Je suis sûr que mon papa vous donnera une très belle récompense ! Salut !' WHERE entry=2000000464; /* content: Thanks, $R! I\'m sure my dad will reward you greatly! Bye! */
UPDATE db_script_string SET content_loc2='C\'est la dernière fois que je me fais attraper ! Promis ! Salut !' WHERE entry=2000000465; /* content: This is the last time I get caught! I promise! Bye! */
UPDATE db_script_string SET content_loc2='%s sent la viande, s\'éveille de son sommeil et pousse un hurlement de rage !' WHERE entry=2000000466; /* content: %s smells the meat, awakens from his slumber, and roars in anger! */
UPDATE db_script_string SET content_loc2='Mais à quoi ils s\'attendent, à faire descendre les chauves-souris sous cet angle ? Nuques brisées et ragoût de chiroptère, c\'est tout ce qu\'ils en tireront.' WHERE entry=2000000467; /* content: What do they expect, making the bats come in at that angle? Broken necks and gamey bat stew, that\'s what they get. */
UPDATE db_script_string SET content_loc2='GROARRRRR !!' WHERE entry=2000000470; /* content: ROARRRRR!! */
UPDATE db_script_string SET content_loc2='SOMBRE-COMTÉ... J\'AI FAIM !!' WHERE entry=2000000471; /* content: DARKSHIRE...I HUNGER!! */
UPDATE db_script_string SET content_loc2='Attention, $c. Ces trolls ont tué leur propre dieu serpent !' WHERE entry=2000000473; /* content: Careful here, $c. These trolls killed their own snake god! */
UPDATE db_script_string SET content_loc2='À une prochaine fois.' WHERE entry=2000000474; /* content: See you around. */
UPDATE db_script_string SET content_loc2='D\'accord. Donc je retourne voir le sergent.' WHERE entry=2000000475; /* content: Right. I\'d better get back to the sergeant then. */
UPDATE db_script_string SET content_loc2='Merci, $c.' WHERE entry=2000000476; /* content: Thanks, $c. */
UPDATE db_script_string SET content_loc2='Merci, $r. Je vais maintenant vous débarrasser de cette misérable canaille.' WHERE entry=2000000477; /* content: Thank you, $r. I will take this miserable cur from you now. */
UPDATE db_script_string SET content_loc2='Raliq t\'apprendre leçon maintenant !' WHERE entry=2000000478; /* content: Raliq teach you lesson now! */
UPDATE db_script_string SET content_loc2='F\'woooroonn ! Coosh\'coosh rien payer ! Meurs !' WHERE entry=2000000479; /* content: F\'woooroonn! Coosh\'coosh pay nothing! Die! */
UPDATE db_script_string SET content_loc2='Je choisis la troisième option : VOUS TUER !' WHERE entry=2000000480; /* content: I choose the third option: KILLING YOU! */
UPDATE db_script_string SET content_loc2='Comme vous voulez…' WHERE entry=2000000481; /* content: As you wish... */
UPDATE db_script_string SET content_loc2='Au sol éventré fut dérobé le sacrement. Le bouclier des Asirites est perdu. Le tumulte en sera éternel.' WHERE entry=2000000486; /* content: From sundered ground the sacrament was stolen. The Shield of Aesirites is lost. Unrest will be eternal. */
UPDATE db_script_string SET content_loc2='Merci de m\'avoir libéré, $n ! Je vais partir pour Shattrath !' WHERE entry=2000000491; /* content: Thank you for freeing me, $N! I\'m going to make my way to Shattrath! */
UPDATE db_script_string SET content_loc2='Content de vous voir enfin arriver, $gmessager:messagère;. Krasus nous a confié un des seuls exemplaires restants de Lames anciennes forgées par les dragons.' WHERE entry=2000000492; /* content: It\'s good to finally see you, courier. Krasus has entrusted us with one of the few remaining copies of Ancient Dragonforged Blades. */
UPDATE db_script_string SET content_loc2='[Furbolg] Merci, $n !' WHERE entry=2000000493; /* content: [Furbolg] Thank you, $n! */
UPDATE db_script_string SET content_loc2='[Furbolg] La prophétie est vraie !' WHERE entry=2000000494; /* content: [Furbolg] The prophecy is true! */
UPDATE db_script_string SET content_loc2='[Furbolg] Ceux qui vivent encore au repaire des Calmepins vous accueilleront en héros !' WHERE entry=2000000495; /* content: [Furbolg] Those remaining at Stillpine Hold will welcome you as a hero! */
UPDATE db_script_string SET content_loc2='[Furbolg] Les furbolgs Calmepins n\'oublieront pas votre bravoure de sitôt !' WHERE entry=2000000496; /* content: [Furbolg] The Stillpine furbolgs will not soon forget your bravery! */
UPDATE db_script_string SET content_loc2='Je sentais qu\'une part de lui restait en vie ! Emprisonnée… Résistante… Arthas !' WHERE entry=2000000500; /* content: I sensed a part of him still alive! Trapped... struggling... Oh, Arthas! */
UPDATE db_script_string SET content_loc2='Peut-être… Peut-être qu\'un jour il se rappellera de ce qu\'il fut.' WHERE entry=2000000501; /* content: Perhaps - perhaps he might someday remember what he once was. */
UPDATE db_script_string SET content_loc2='Par la Lumière, puisse-t-il enfin reposer en paix, libéré de l\'étreinte glaciale de cette maudite lame.' WHERE entry=2000000502; /* content: By the Light, may he at last find rest, free from the icy grip of that terrible blade. */
UPDATE db_script_string SET content_loc2='Ainsi, c\'est fait.' WHERE entry=2000000503; /* content: So, it is done. */
UPDATE db_script_string SET content_loc2='Je n\'osais me fier à mes sens… Le roi-liche s\'était déjà trop souvent joué de moi.' WHERE entry=2000000504; /* content: I had not dared to trust my senses - too many times has the Lich King made me to be a fool. */
UPDATE db_script_string SET content_loc2='Enfin... Il a enfin payé pour les atrocités infligées à mon peuple.' WHERE entry=2000000505; /* content: Finally, he has been made to pay for the atrocities he imposed upon my people. */
UPDATE db_script_string SET content_loc2='Puisse Azeroth ne jamais oublier le prix terrible que nous avons dû payer pour notre faiblesse... pour notre orgueil.' WHERE entry=2000000506; /* content: May Azeroth never fail to remember the terrible price we paid for our weakness... for our pride. */
UPDATE db_script_string SET content_loc2='Mais ensuite, héros ? Qu\'est-ce qui attend ceux qui sont libérés de son emprise, mais toujours enchaînés à leurs dépouilles mortelles ?' WHERE entry=2000000507; /* content: But what now, hero? What of those freed from his grasp, but still shackled to their mortal coils? */
UPDATE db_script_string SET content_loc2='Je dois réfléchir à tout ça.' WHERE entry=2000000509; /* content: I have much to ponder. */
UPDATE db_script_string SET content_loc2='Arthas...' WHERE entry=2000000510; /* content: Arthas... */
UPDATE db_script_string SET content_loc2='Hélas, soldat d\'Azeroth, ce cadeau que vous me faites vaut plus que vous ne le pensez.' WHERE entry=2000000511; /* content: Alas, hero of Azeroth, you give me a greater gift than you know. */
UPDATE db_script_string SET content_loc2='J\'ai longtemps lutté pour pardonner au prince ses terribles transgressions.' WHERE entry=2000000512; /* content: Long have I struggled to forgive the prince for his terrible transgressions. */
UPDATE db_script_string SET content_loc2='Mon âme tourmentée par une insupportable angoisse, de noires pensées… qui m\'éloignaient de la Lumière.' WHERE entry=2000000513; /* content: My soul has been wracked with unbearable anxiety, dark thoughts... distancing me from the Light. */
UPDATE db_script_string SET content_loc2='Je revois encore la lueur de fierté présente dans ses yeux quand il se tenait devant moi, impatient de triompher des ennemis de la Lumière…' WHERE entry=2000000514; /* content: I recall clearly the gleam of pride in his eye as he stood before me, eager to defeat the enemies of the Light... */
UPDATE db_script_string SET content_loc2='Impatient de défendre son peuple, quel qu\'en soit le prix.' WHERE entry=2000000515; /* content: Eager to defend his people, no matter the cost. */
UPDATE db_script_string SET content_loc2='C\'est ce souvenir d\'Arthas que je choisis de garder dans mon cœur.' WHERE entry=2000000516; /* content: It is this memory of Arthas that I choose to keep in my heart. */
UPDATE db_script_string SET content_loc2='J\'ai une dette éternelle envers vous, mon $gami:amie;.' WHERE entry=2000000517; /* content: I shall always be in your debt, friend. */
UPDATE db_script_string SET content_loc2='Merci.' WHERE entry=2000000518; /* content: Thank you. */
UPDATE db_script_string SET content_loc2='Ho, petit...' WHERE entry=2000000519; /* content: Oh, lad... */
UPDATE db_script_string SET content_loc2='Elles me manquent, ces longues journées de Lordaeron...' WHERE entry=2000000520; /* content: How I miss those endless days in Lordaeron... */
UPDATE db_script_string SET content_loc2='… à croiser l\'fer avec une lame en bois...' WHERE entry=2000000521; /* content: ...sharpenin\' yer skill with this dull blade... */
UPDATE db_script_string SET content_loc2='… pour faire de toi une arme capable de supporter l\'poids d\'un grand destin.' WHERE entry=2000000522; /* content: ...forgin\' ya into a weapon meant to withstand the demands of a great destiny. */
UPDATE db_script_string SET content_loc2='Hé... Au moins t\'auras mis tout ça en pratique, pas vrai ?' WHERE entry=2000000523; /* content: Heh... Yeh sure put them skills ta use, didn\'t yeh, lad? */
UPDATE db_script_string SET content_loc2='Si seulement j\'avais réussi à t\'arrêter ce jour-là, comme tout aurait pu être différent.' WHERE entry=2000000524; /* content: If only I\'da been able ta stop yeh that day, how different things mighta been. */
UPDATE db_script_string SET content_loc2='Si seulement j\'avais jamais découvert cette maudite lame...' WHERE entry=2000000525; /* content: If only I\'da never discovered that accursed blade... */
UPDATE db_script_string SET content_loc2='Adieu, Arthas... Mon frère.' WHERE entry=2000000526; /* content: Farewell, Arthas... my brother. */
UPDATE db_script_string SET content_loc2='Darion, mon fils.' WHERE entry=2000000527; /* content: Darion, my son. */
UPDATE db_script_string SET content_loc2='Enfin, je peux te revoir de mes propres yeux.' WHERE entry=2000000528; /* content: At last, I am able to lay my eyes upon you again. */
UPDATE db_script_string SET content_loc2='Le roi-liche m\'a torturé sans relâche, Darion.' WHERE entry=2000000529; /* content: The Lich King tormented me without end, Darion. */
UPDATE db_script_string SET content_loc2='Sans répit, il a voulu me briser, me forcer à servir, me lier à sa lame…' WHERE entry=2000000530; /* content: Endlessly, he sought to break my will, to force me to serve him, to bind me to his blade... */
UPDATE db_script_string SET content_loc2='Au final, quand les évènements ont exigé toute son attention, il m\'a laissé.' WHERE entry=2000000531; /* content: Finally, when events demanded his full attention, he left me. */
UPDATE db_script_string SET content_loc2='Il y a un souvenir auquel je me suis accroché, Darion... Une pensée qui m\'a permis de ne jamais abandonner…' WHERE entry=2000000532; /* content: The one memory, I clung to, Darion... The one thought that kept me from giving in... */
UPDATE db_script_string SET content_loc2='C\'était ton sacrifice, mon fils, qui m\'a sauvé une seconde fois d\'une damnation éternelle.' WHERE entry=2000000533; /* content: It was your sacrifice, my son, that again saved me from eternal peril. */
UPDATE db_script_string SET content_loc2='Père...' WHERE entry=2000000534; /* content: Father... */
UPDATE db_script_string SET content_loc2='Père, je... je m\'inquiétais pour... pour ton… équilibre.' WHERE entry=2000000535; /* content: Father. I feared for your... your sanity. */
UPDATE db_script_string SET content_loc2='Père... Pour toi, je... je donnerais ma vie mille fois.' WHERE entry=2000000536; /* content: Father... For you, I would give my life a thousand times. */
UPDATE db_script_string SET content_loc2='%s renifle l\'élixir puis l\'avale goulûment !' WHERE entry=2000000537; /* content: %s sniffs the elixir then eagerly digs in! */
UPDATE db_script_string SET content_loc2='Et merci, $n. Sans votre aide, je n\'aurais peut-être jamais trouvé le chemin qui mène aux Réprouvés.' WHERE entry=2000000538; /* content: And thank you, $N.  Without your aid I may never have found my way to the Forsaken. */
UPDATE db_script_string SET content_loc2='Adieu, mon ami.' WHERE entry=2000000539; /* content: Farewell, my friend. */
UPDATE db_script_string SET content_loc2='Cela fait longtemps, Bethor, mon ami.' WHERE entry=2000000540; /* content: It has been a long time, Bethor, my friend. */
UPDATE db_script_string SET content_loc2='Quand nous en aurons le temps, il faudra que nous parlions longuement. Car nous avons beaucoup à nous dire.' WHERE entry=2000000541; /* content: When time permits, we must speak at length.  For we have much to discuss. */
UPDATE db_script_string SET content_loc2='%s convoque un messager…' WHERE entry=2000000542; /* content: %s summons a messenger... */
UPDATE db_script_string SET content_loc2='%s remet un message à l\'apothicaire Zamah.' WHERE entry=2000000544; /* content: %s gives message to Apothecary Zamah. */
UPDATE db_script_string SET content_loc2='Mais c\'est le seul à la taille de Brogg ! Gobelins construire trop petit !' WHERE entry=2000000546; /* content: But this the only building that fit Brogg! Goblins make buildings too small! */
UPDATE db_script_string SET content_loc2='Brogg, on en a déjà parlé. Tu ne peux pas rester dans ce bâtiment. Bien sûr, il est à ta taille, mais il n\'y a plus de place pour rien d\'autre !' WHERE entry=2000000547; /* content: I thought we talked about this, Brogg. You can\'t stay in this building. Sure, it can house you, but there\'s no room for anyone else! */
UPDATE db_script_string SET content_loc2='Oui, on a dit que tu pouvais rester, mais ce bâtiment est notre quartier général. C\'est là qu\'on garde tous nos manuels et tous nos plans !' WHERE entry=2000000548; /* content: Yes, we said you can stay here, but this building is our headquarters! All of our manuals and blueprints are stored here! */
UPDATE db_script_string SET content_loc2='Question de point de vue. Je pense que quelqu\'un a construit les ogres trop grands. Bon, je ne vais pas te convaincre de bouger, mais ne t\'imagine pas que c\'est définitif !' WHERE entry=2000000549; /* content: I\'m inclined to say they made ogres too big! I can see I\'m not going to convince you to move now, but don\'t think this is permanent! */
UPDATE db_script_string SET content_loc2='Vous dire Brogg pouvoir rester ici ! Vous promettre Brogg que vous aider à reprendre tertre clan Cognepierre !' WHERE entry=2000000550; /* content: You said Brogg could stay here! You promised Brogg you\'d help regain Stonemaul clan mound! */
UPDATE db_script_string SET content_loc2='Pendant la Fracture, Ameth\'Aran était à la merci de la terreur qui avait saisi le pays. L\'espoir de survie était faible.' WHERE entry=2000000551; /* content: In the throes of the Sundering, Ameth\'Aran was at the whim of the terror that gripped the land. There was little hope for survival. */
UPDATE db_script_string SET content_loc2='Athrikus est venu nous voir. Il nous a dit qu\'il pouvait nous sauver du mal. Il nous a jeté un sort pour nous protéger du mal.' WHERE entry=2000000552; /* content: Athrikus came to us. He told us that he could save us from harm. He cast a spell upon us to protect us from harm. */
UPDATE db_script_string SET content_loc2='Lorsque les tremblements cessèrent, ses véritables motifs devinrent apparents. Nous étions pris au piège et il drainait lentement nos pouvoirs.' WHERE entry=2000000553; /* content: When the shaking stopped, his true motives were revealed. We were trapped, and he was slowly draining our powers. */
UPDATE db_script_string SET content_loc2='Ils étaient des centaines emprisonnés par ce sort. Il n\'en reste plus que quelques-uns dans leurs prisons. Il nous parlera bien un jour.' WHERE entry=2000000554; /* content: There were hundreds imprisoned by his spell. Now only a few remain in their prisons. He would speak to us sometimes. */
UPDATE db_script_string SET content_loc2='Il avait peur que son pouvoir ne diminue et que bientôt même nous, les dernières de ses précieuses gemmes d\'âme, ne nous affaiblissions et mourions.' WHERE entry=2000000555; /* content: He worried that his power weakened, that soon even we last of his precious soulgems would fade and die. */
UPDATE db_script_string SET content_loc2='Hmmm… Où est mon hameçon ? Oh, le voilà.' WHERE entry=2000000557; /* content: Hmmm... Where is my fishing hook? Oh, there it is. */
UPDATE db_script_string SET content_loc2='On va voir si le poisson mord.' WHERE entry=2000000558; /* content: Let\'s see if the fish are biting. */
UPDATE db_script_string SET content_loc2='Hmmm… Je pense qu\'il faudra un meilleur appât. Je vais jeter un œil au chariot de mon père.' WHERE entry=2000000559; /* content: Hmmm... I think I need better bait. I\'ll check my father\'s wagon. */
UPDATE db_script_string SET content_loc2='Des bouts de huran ! Ça ira, je crois. Les poissons aiment la viande de huran.' WHERE entry=2000000560; /* content: Quillboar scraps! These should do the trick. Fish love Quillboar. */
UPDATE db_script_string SET content_loc2='J\'en ai un ! On va aller voir si les gardes ont faim. Allez, viens, Croc !' WHERE entry=2000000561; /* content: Caught one! I\'ll see if any of the guards are hungry. Come on Fang! */
UPDATE db_script_string SET content_loc2='Si vous avez faim, je peux vous pêcher quelque chose…' WHERE entry=2000000562; /* content: I caught a fish if you are hungry... */
UPDATE db_script_string SET content_loc2='Belle prise ! Tu fais honneur à la Horde, petit ! Merci beaucoup !' WHERE entry=2000000563; /* content: A nice catch it is at that! You bring honor to the Horde boy, We thank you! */
UPDATE db_script_string SET content_loc2='Voudriez-vous autre chose à manger, Monsieur ?' WHERE entry=2000000564; /* content: Would you like something more to eat sir? */
UPDATE db_script_string SET content_loc2='Garçon, encore du pain !' WHERE entry=2000000565; /* content: More bread boy! */
UPDATE db_script_string SET content_loc2='Tout de suite, monsieur.' WHERE entry=2000000566; /* content: Right away sir. */
UPDATE db_script_string SET content_loc2='Voici votre pain, $gmonsieur:madame;.' WHERE entry=2000000567; /* content: Here is your bread sir. */
UPDATE db_script_string SET content_loc2='Aaahh... Très bien. Dehors, maintenant !' WHERE entry=2000000568; /* content: Aaahh... Very good. Now scat! */
UPDATE db_script_string SET content_loc2='Comme vous voudrez, monsieur.' WHERE entry=2000000569; /* content: As you wish sir. */
UPDATE db_script_string SET content_loc2='Est-ce que je peux aller chercher plus de cailloux pour mon collier, Papa ?' WHERE entry=2000000570; /* content: Can I go collect more rocks for my necklace, Papa? */
UPDATE db_script_string SET content_loc2='Dabu ! Je ferai attention, papa !' WHERE entry=2000000572; /* content: Dabu! I will be careful Papa! */
UPDATE db_script_string SET content_loc2='Ooohh... Ce caillou est joli et tout coloré. Voyons si je peux en trouver d\'autres...' WHERE entry=2000000573; /* content: Ooohh... This rock is nice and colorful. Let\'s see if I can find more like it... */
UPDATE db_script_string SET content_loc2='Hmm... Ce caillou est joli, mais trop petit.' WHERE entry=2000000574; /* content: Hmm... That rock is nice, but too small. */
UPDATE db_script_string SET content_loc2='Je parie que mon ami Gruk voudrait ce caillou pour la lance qu\'il fabrique ! Il est très coupant... J\'espère qu\'il lui plaira.' WHERE entry=2000000576; /* content: I bet my friend Gruk would like this rock for the spear he is making! It\'s very sharp... I hope he likes it. */
UPDATE db_script_string SET content_loc2='Voilà... Ça devrait être assez pour faire un collier. C\'est sûrement l\'heure de rentrer à la maison, de toute façon.' WHERE entry=2000000577; /* content: There... That should be enough rocks to make a necklace. I should probably get home now anyway. */
UPDATE db_script_string SET content_loc2='La Croisade d\'argent vous invite à prendre la mesure de votre talent contre d\'autres héros d\'Azeroth au tournoi d\'Argent.' WHERE entry=2000000580; /* content: The Argent Crusade invites you to test your skills in honorable combat against other heroes of Azeroth at the Argent Tournament. */
UPDATE db_script_string SET content_loc2='Les champions des plus grandes nations du monde sont rassemblés sur le site du tournoi d\'Argent, au nord-est de la Couronne de glace, pour y affronter tous les défis.' WHERE entry=2000000581; /* content: The champions of the world\'s great nations have gathered at the Argent Tournament Grounds in northeastern Icecrown to face all challengers. */
UPDATE db_script_string SET content_loc2='Vous êtes en retard, surveillant.' WHERE entry=2000000582; /* content: You\'re late, overseer. */
UPDATE db_script_string SET content_loc2='Le destin n\'attendra pas. Vos artisans doivent accélérer la production des golems de guerre avant que le chef des géants de pierre et son armée n\'atteignent Ulduar.' WHERE entry=2000000583; /* content: Destiny will not wait. Your craftsmen must increase their production of the war golems before the stone giant leader and his army reach Ulduar. */
UPDATE db_script_string SET content_loc2='Dites à vos forgerunes de continuer à convertir le plus grand nombre de géants possible. Que ceux qui ne se soumettent pas soient détruits.' WHERE entry=2000000584; /* content: Tell your rune-smiths to continue converting what stone giants you can. Those that will not submit must be destroyed. */
UPDATE db_script_string SET content_loc2='Si les géants de pierre perturbent nos travaux à Ulduar, je vous en tiendrai, vous et votre thane, comme personnellement responsables. Il ne faut pas laisser les mortels venir aider les géants.' WHERE entry=2000000585; /* content: If the stone giants interfere with our work at Ulduar, I will hold you and your thane responsible. Mortals must not be allowed to come to the aid of the giants. */
UPDATE db_script_string SET content_loc2='Retournez à votre poste, surveillant. Assurez-vous que vos ouvriers saisissent pleinement l\'urgence de leur tâche.' WHERE entry=2000000586; /* content: Return to your duties, overseer. Be certain to impress upon your workers the urgency of their tasks. */
UPDATE db_script_string SET content_loc2='L\'oracle a donc décidé de vous aider, $gétranger:étrangère;. Très bien, je vais vous donner ce que je sais.' WHERE entry=2000000587; /* content: So the oracle has decided to aid you, outlander. Very well, I will offer you what i know. */
UPDATE db_script_string SET content_loc2='Votre compagnon recherche le patriarche cornéclat, Farunn, qui se tient à l\'écart de ses frères.' WHERE entry=2000000588; /* content: Your companion seeks the shardhorn patriarch Farunn, who holds himself aloof from his brothers. */
UPDATE db_script_string SET content_loc2='Retournez voir votre compagnon. Dites-lui d\'aller saisir ce qu\'il recherche auprès de Farunn.' WHERE entry=2000000590; /* content: Return to your companion. Tell him to go forth and claim his prize in Farunn. */
UPDATE db_script_string SET content_loc2='Quand ce sera fait, dites-lui de quitter cette terre en paix. Sa place n\'est pas ici.' WHERE entry=2000000591; /* content: Once he has done so, tell him to leave this land in peace. He does not belong here. */
UPDATE db_script_string SET content_loc2='[Furbolg] Soyez témoin des sauvages exactions de nos adversaires, $n. Ils volent nos jeunes... nos faibles... Emprisonnés ici, ils sont à la merci des stratagèmes des Bras-hirsutes. Libérez-les, $gPromis:Promise; !' WHERE entry=2000000592; /* content: [Furbolg] Bear witness to the savage acts of our adversaries, $N. They steal our young... our weak... Imprisoned here, they are left to the devices of the Bristlelimb. Set them free, Promised One! */
UPDATE db_script_string SET content_loc2='Je n\'ai pas encore découvert de quoi il s\'agit, mais les sources chaudes ont quelque chose d\'étrange…' WHERE entry=2000000593; /* content: I haven\'t really figured out what it is, but there\'s something strange about the hot springs... */
UPDATE db_script_string SET content_loc2='%s jette l\'épée dans le bassin.' WHERE entry=2000000594; /* content: %s tosses the sword into the shallow pool. */
UPDATE db_script_string SET content_loc2='J\'ai découvert que lorsque je jetais quelque chose dans l\'eau, l\'objet s\'améliorait incroyablement.' WHERE entry=2000000595; /* content: I\'ve found that when I throw something into this water, it has improved incredibly when I retrieve it. */
UPDATE db_script_string SET content_loc2='Eh bien, quoi que ce soit, ça fonctionne très bien !' WHERE entry=2000000596; /* content: Well, whatever it is, it works quite well! */
UPDATE db_script_string SET content_loc2='Je suis déterminée à en apprendre davantage… Alors, si vous avez encore besoin de mon aide, vous saurez où me trouver.' WHERE entry=2000000597; /* content: And I am determined to find out more... So if you need my help again, you know where I\'ll be. */
UPDATE db_script_string SET content_loc2='S\'il vous plaît, suivez-moi.' WHERE entry=2000000598; /* content: Please, follow me. */
UPDATE db_script_string SET content_loc2='Oh, ce sont des jacinthes d\'hiver ? Pour moi ?' WHERE entry=2000000599; /* content: Are those winter hyacinths? For me? */
UPDATE db_script_string SET content_loc2='On ne m\'avait pas apporté de fleurs ici depuis si longtemps.' WHERE entry=2000000600; /* content: It\'s been so long since a traveler has come here bearing flowers. */
UPDATE db_script_string SET content_loc2='Le lac est un endroit bien solitaire depuis quelques années. Les voyageurs n\'y viennent plus, et le mal en a envahi les eaux.' WHERE entry=2000000601; /* content: The lake has been too lonely these past years. The travelers stopped coming and evil came to these waters. */
UPDATE db_script_string SET content_loc2='Votre cadeau révèle une rare bonté, $gvoyageur:voyageuse;. Je vous en prie, prenez cette lame en gage de ma gratitude. Il y a bien longtemps, c\'est un autre voyageur qui l\'avait laissée ici, mais je n\'en ai pas l\'utilité.' WHERE entry=2000000602; /* content: Your gift is a rare kindness, traveler. Please take this blade with my gratitude. Long ago, another traveler left it here, but I have little use for it. */
UPDATE db_script_string SET content_loc2='Elle ne m\'a valu que malheur et peine. Peut-être trouverez-vous quelqu\'un capable d\'en contenir la puissance.' WHERE entry=2000000604; /* content: It has brought me naught but ill. Mayhap you can find someone who will contain its power. */
UPDATE db_script_string SET content_loc2='Misérable traître ! Sironas vous détruira, vous et tout votre peuple !' WHERE entry=2000000606; /* content: Treacherous whelp! Sironas will destroy you and your people! */
UPDATE db_script_string SET content_loc2='Mon frère, Sabot-tempête, était un guerrier bien plus puissant que moi. Alors que je m\'entraînais dans le but de diriger notre clan, lui poursuivait une quête bien plus ambitieuse.' WHERE entry=2000000607; /* content: My brother, Stormhoof, was a far greater warrior than I. While I trained to be the chieftain of our clan, he prepared for a larger quest. */
UPDATE db_script_string SET content_loc2='Comment pourrait-il être menacé ? Tout ce qu\'il voulait, c\'était nous donner une emprise sur les éléments qui nous mènent la vie si dure ici.' WHERE entry=2000000608; /* content: How could he be in danger? All he wanted was to give us power over the elements that made life harsh for us here. */
UPDATE db_script_string SET content_loc2='Il était à la recherche d\'une relique… Une corne, je crois. Il a quitté le village et entrepris un long voyage pour cette quête.' WHERE entry=2000000609; /* content: He sought an artifact... a horn, I think. He left the village on a long journey in search of it. */
UPDATE db_script_string SET content_loc2='Je sais qu\'un ennemi terrifiant est parti à sa poursuite après qu\'il s\'est emparé de cette corne, mais… Je ne me rappelle plus… Ce qui est arrivé ensuite.' WHERE entry=2000000610; /* content: I know that a terrible enemy pursued him after he won the horn, but I... I don\'t recall... what happened... next. */
UPDATE db_script_string SET content_loc2='C\'est comme si mes souvenirs étaient enveloppés de brume. Je ne me souviens même plus de ce qu\'il est advenu de mon frère. Est-ce ce que vous vouliez dire quand vous m\'expliquiez qu\'il est en danger ?' WHERE entry=2000000611; /* content: It is as though my memories are shrouded in mist. I cannot even recall what became of my brother. Is this how you mean that he is in danger? */
UPDATE db_script_string SET content_loc2='Les perturbations qui agitent cette tombe… elles doivent y être liées. Regardez autour de vous, $n. Les voyez-vous ?' WHERE entry=2000000612; /* content: The disturbances in the tomb, they must be involved. Look around you, $N. Do you see them? */
UPDATE db_script_string SET content_loc2='Oui, ce sont elles les responsables, mais je n\'ai pas le pouvoir de les arrêter. Pourriez-vous avertir les gens de Tunka\'lo, $gétranger:étrangère; ? Dites-leur que leur passé et leurs ancêtres sont en danger.' WHERE entry=2000000613; /* content: Yes, they are at fault, but I am powerless to stop them. Will you warn the people of Tunka\'lo, stranger, that their past and their ancestors are in danger? */
UPDATE db_script_string SET content_loc2='Le marteau de la connaissance reste silencieux. Ça n\'est pas de l\'époque de Sabot-tempête.' WHERE entry=2000000615; /* content: The Lorehammer is silent. This isn\'t from Stormhoof\'s time. */
UPDATE db_script_string SET content_loc2='Que la meilleure gagne !' WHERE entry=2000000617; /* content: May the best win! */
UPDATE db_script_string SET content_loc2='Vous ne me vaincrez pas !' WHERE entry=2000000618; /* content: You will not defeat me! */
UPDATE db_script_string SET content_loc2='Tu n\'es pas digne de Thorim !' WHERE entry=2000000619; /* content: You\'re not worthy of Thorim! */
UPDATE db_script_string SET content_loc2='Le sectateur bégaie.' WHERE entry=2000000621; /* content: The cultist practically sputters. */
UPDATE db_script_string SET content_loc2='Madame.' WHERE entry=2000000622; /* content: My lady. */
UPDATE db_script_string SET content_loc2='J\'écoute.' WHERE entry=2000000623; /* content: Report. */
UPDATE db_script_string SET content_loc2='Des nouvelles de Jotunheim. Les gardiens du sommeil pensent avoir trouvé un personnage de grande importance.' WHERE entry=2000000624; /* content: There is word from Jotunheim. The sleep-watchers there believe that they have found someone of significance. */
UPDATE db_script_string SET content_loc2='Décris-le, ce vrykul. À quoi est-ce qu\'il ressemble ?' WHERE entry=2000000625; /* content: Describe this vrykul. What does he look like? */
UPDATE db_script_string SET content_loc2='À quoi il ressemble, madame ? À un vrykul, je suppose. Ils ne me l\'ont pas montré. Depuis l\'incident du caveau des Ombres…' WHERE entry=2000000626; /* content: Look like, my lady? A vrykul, I suppose. They did not actually show him to me. Ever since The Shadow Vault.... */
UPDATE db_script_string SET content_loc2='Tes excuses ne m\'intéressent pas. Peut-être t\'ont-ils donné un nom ?' WHERE entry=2000000627; /* content: I am not interested in excuses. Perhaps they gave you a name? */
UPDATE db_script_string SET content_loc2='Un nom ? Ah oui, le nom ! Je crois que c\'était Iskalder.' WHERE entry=2000000628; /* content: A name? Oh, yes, the name! I believe it was Iskalder. */
UPDATE db_script_string SET content_loc2='Retourne à Jotunheim, et dis-leur de le garder endormi jusqu\'à mon arrivée. Je veux voir ce que vaut ce vrykul de mes propres yeux.' WHERE entry=2000000630; /* content: Return to Jotunheim and tell them to keep him asleep until I arrive. I will judge this vrykul with my own eyes. */
UPDATE db_script_string SET content_loc2='Tout de suite, madame.' WHERE entry=2000000631; /* content: Right away, my lady. */
UPDATE db_script_string SET content_loc2='Morae… c\'est toi ?' WHERE entry=2000000632; /* content: Morae... Is that you? */
UPDATE db_script_string SET content_loc2='Non, vous n\'êtes pas Morae, mais je sens que vous la connaissez. Lorsque vous la verrez, dites-lui que j\'ai survécu à l\'accident, mais que les infiltrateurs m\'ont achevé.' WHERE entry=2000000633; /* content: No... you\'re not Morae, but I sense that you have met. When you see her, tell her that I survived the crash, only to be done in by the infiltrators. */
UPDATE db_script_string SET content_loc2='Qu\'elle sache… que ma dernière pensée a été pour elle…' WHERE entry=2000000634; /* content: Let her know... my last thought was of her... */
UPDATE db_script_string SET content_loc2='Grek essayer !' WHERE entry=2000000635; /* content: Grek try! */
UPDATE db_script_string SET content_loc2='Cette huile pas bien pour Grek ! Vous penser que Grek mauviette en robe ?' WHERE entry=2000000636; /* content: This oil no good for Grek! What Grek look like to you, some weakling in robes? */
UPDATE db_script_string SET content_loc2='%s rit.' WHERE entry=2000000637; /* content: %s laughs. */
UPDATE db_script_string SET content_loc2='Cela vous effraye-t-il de savoir qu\'il existe des serviteurs si dévoués à la Légion qu\'ils sont prêts à demeurer d\'indéfectibles citoyens de vos civilisations ridicules jusqu\'au jour où ils seront mis à contribution ?' WHERE entry=2000000638; /* content: Does it frighten you to know that there are those that would serve the Legion with such devotion as to remain unwavering citizens of your pointless civilizations until called upon? */
UPDATE db_script_string SET content_loc2='Vivre dans la peur, mourir par la volonté de Kael\'thas... Cela revient au même.' WHERE entry=2000000639; /* content: Live in fear, die by the will of Kael\'thas... It\'s all the same. */
UPDATE db_script_string SET content_loc2='Et maintenant, je découpe !' WHERE entry=2000000640; /* content: And now, I cut you! */
UPDATE db_script_string SET content_loc2='Mourez !' WHERE entry=2000000641; /* content: DIE! */
UPDATE db_script_string SET content_loc2='%s ramasse le drapeau naga.' WHERE entry=2000000642; /* content: %s picks up the naga flag. */
UPDATE db_script_string SET content_loc2='Nous ne connaîtrons pas l\'échec !' WHERE entry=2000000643; /* content: We will not fail! */
UPDATE db_script_string SET content_loc2='%s dépose la caisse de pièces sur le sol.' WHERE entry=2000000644; /* content: %s places the crate of parts on the ground. */
UPDATE db_script_string SET content_loc2='%s saisit une pièce et la glisse dans sa poche...' WHERE entry=2000000645; /* content: %s grabs a part and puts it in his pocket... */
UPDATE db_script_string SET content_loc2='Voilà, ça devrait suffire...' WHERE entry=2000000646; /* content: There, that should do it... */
UPDATE db_script_string SET content_loc2='Anaya...? Mes yeux me jouent-ils des tours ? Est-ce vraiment vous ?' WHERE entry=2000000647; /* content: Anaya...? Do my eyes deceive me? Is it really you? */
UPDATE db_script_string SET content_loc2='Les temps ont été cruels envers vous et moi, mon amour, mais je peux vous l\'assurer, et nous sommes enfin réunis.' WHERE entry=2000000648; /* content: The ages have been cruel to you and I, my love, but be assured, it is, and at long last we are reunited. */
UPDATE db_script_string SET content_loc2='Faut-il que le destin soit si cruel qu\'il ne nous permette que cela après une séparation de mille ans...' WHERE entry=2000000649; /* content: That the fates should be so cruel as to permit us only this after a thousand years apart... */
UPDATE db_script_string SET content_loc2='Me haïssez-vous, mon amour ? Car j\'ai été forcé de détruire votre enveloppe vivante afin que votre esprit soit libéré d\'une triste servitude.' WHERE entry=2000000650; /* content: Do you hate me, my love? That I was forced to destroy your living form, that your spirit be released from unhappy bondage. */
UPDATE db_script_string SET content_loc2='Ne laissez pas votre cœur se troubler, bien-aimé. Vous m\'avez libérée de l\'esclavage et pour cette raison, je vous aime d\'autant plus.' WHERE entry=2000000651; /* content: Let it not trouble your heart, beloved. You have freed me from slavery, and for that I love you all the more. */
UPDATE db_script_string SET content_loc2='Malheureusement, même cela doit être abrégé... Les liens qui me rattachent à ce monde faiblissent et m\'éloignent...' WHERE entry=2000000652; /* content: Sadly, even this must be cut short... The ties that bind me to this world weaken, and pull me away... */
UPDATE db_script_string SET content_loc2='Non ! Anaya... Anaya ! Ne me quittez pas ! Je vous en prie...' WHERE entry=2000000653; /* content: No! Anaya... Anaya! Don\'t leave me! Please... */
UPDATE db_script_string SET content_loc2='Adieu, Cerellean, jusqu\'à ce que nous soyons à nouveau réunis...' WHERE entry=2000000654; /* content: Farewell, Cerellean, until we are joined once again... */
UPDATE db_script_string SET content_loc2='Comment, mon amour ? Comment trouverai-je la force de faire face à ce monde vieillissant sans vous avoir à mes côtés...' WHERE entry=2000000656; /* content: How, my love? How will I find the strength to face the ages of the world without you by my side... */
UPDATE db_script_string SET content_loc2='Les chutes de la Bondissante longent les montagnes à l\'est et au nord.' WHERE entry=2000000658; /* content: Cliffspring Falls lies along the mountains to the east and north. */
UPDATE db_script_string SET content_loc2='Ce sont mes actes, frère ! Bientôt, vous aurez définitivement cessé de me nuire !' WHERE entry=2000000659; /* content: The deeds are mine, brother! Soon you shall be out of my way for good! */
UPDATE db_script_string SET content_loc2='TROLL M\'A MIS EN COLÈRE !' WHERE entry=2000000660; /* content: TROLL MAKE ME ANGRY! */
UPDATE db_script_string SET content_loc2='Alors là, c\'est la guerre !' WHERE entry=2000000661; /* content: This mean war! */
UPDATE db_script_string SET content_loc2='Je suis le héraut de Mécazod. Vous allez être dé-maudits !' WHERE entry=2000000663; /* content: I am the herald of Mechazod. You will be decursed! */
UPDATE db_script_string SET content_loc2='Vos bricolages ne vont plus durer. Quand j\'en aurai terminé avec vous, Mécazod vous guérira de cette malédiction.' WHERE entry=2000000664; /* content: Your meddling is at an end. Mechazod will relieve your curse once I am done with you. */
UPDATE db_script_string SET content_loc2='ED-210 en ligne !' WHERE entry=2000000665; /* content: ED-210 online! */
UPDATE db_script_string SET content_loc2='Twonky !' WHERE entry=2000000666; /* content: Twonky! */
UPDATE db_script_string SET content_loc2='La victoire ou la mort ! Pour la Horde !' WHERE entry=2000000669; /* content: Victory or death! For the Horde! */
UPDATE db_script_string SET content_loc2='Que ma puissance vous baigne ! Buvez à ma grandeur ! Battez-vous pour la gloire de la Horde !' WHERE entry=2000000671; /* content: Be bathed in my power! Drink in my might! Battle for the glory of the Horde! */
UPDATE db_script_string SET content_loc2='Attendez, $n !!! J\'ai l\'impression que nous allons avoir besoin d\'aide ici…' WHERE entry=2000000673; /* content: Wait, $N!!! It looks like we\'re going to need some help here... */
UPDATE db_script_string SET content_loc2='%s se met à danser.' WHERE entry=2000000674; /* content: %s begins to dance. */
UPDATE db_script_string SET content_loc2='Avez-vous libéré Drull ? Je ne sais pas où il est détenu.' WHERE entry=2000000676; /* content: Have you freed Drull yet? I don\'t know where he\'s being held. */
UPDATE db_script_string SET content_loc2='Je ne ferai que vous ralentir, mais ce ne sont pas des humains au teint porcin qui m\'empêcheront de recouvrer ma liberté !' WHERE entry=2000000677; /* content: I will only slow you down, but no pink-skinned humans will keep me from freedom! */
UPDATE db_script_string SET content_loc2='Là-haut, sur la colline, $n.' WHERE entry=2000000679; /* content: Up here on the hill, $N. */
UPDATE db_script_string SET content_loc2='Laissez-moi vous aider.' WHERE entry=2000000680; /* content: Let me help you with those. */
UPDATE db_script_string SET content_loc2='Buvez ceci, vous irez mieux.' WHERE entry=2000000681; /* content: Drink this, it will help. */
UPDATE db_script_string SET content_loc2='Tout ira bien.' WHERE entry=2000000682; /* content: You\'re going to be just fine. */
UPDATE db_script_string SET content_loc2='Vous êtes morts, pas enterrés. Maintenant montrez-moi de quoi vous êtes capable !' WHERE entry=2000000686; /* content: You\'re dead, not buried. Now show me your best! */
UPDATE db_script_string SET content_loc2='D\'accord, pauvres sacs d\'os. Voyons ce que vous avez dans le ventre. Frappez votre adversaire et n\'arrêtez pas avant que je vous le dise.' WHERE entry=2000000687; /* content: Alright you sorry sacks of bones. Let\'s see what you\'ve got. Strike your opponent, and don\'t stop till I say so. */
UPDATE db_script_string SET content_loc2='J\'espère que les elfes de sang qui sont de l\'autre côté ne pensent pas que je les espionne !' WHERE entry=2000000688; /* content: I hope those blood elves across the way don\'t think that I\'m spying on them! */
UPDATE db_script_string SET content_loc2='Je t\'ai apporté un cadeau pour ta pendaison de crémaillère, Jarl.' WHERE entry=2000000692; /* content: I brought you a housewarming gift, Jarl. */
UPDATE db_script_string SET content_loc2='Allez, ouvre-le, je suis sûr que ça va te plaire.' WHERE entry=2000000693; /* content: Go on, open it. I think you\'ll like it. */
UPDATE db_script_string SET content_loc2='Jarl ?' WHERE entry=2000000694; /* content: Jarl? */
UPDATE db_script_string SET content_loc2='J\'en veux pas. Sauf si ça se mange.' WHERE entry=2000000695; /* content: Unless it\'s something I can eat, I don\'t want it. */
UPDATE db_script_string SET content_loc2='Jarl, qu\'est-ce qu\'il y a ? Tu n\'es plus toi-même depuis mon arrivée.' WHERE entry=2000000696; /* content: Jarl, you haven\'t been yourself ever since I arrived. What\'s going on? */
UPDATE db_script_string SET content_loc2='Y a rien. Tu veux m\'aider à préparer du brouet de cuisses de grenouilles ?' WHERE entry=2000000697; /* content: Nothing. Wanna help me whip up some frog leg stew? */
UPDATE db_script_string SET content_loc2='Jarl, je suis venu te rendre visite ! Et je te trouve dans une cabane vide, au milieu de nulle part, à bredouiller des trucs à propos de grenouilles ? Tu as un problème !' WHERE entry=2000000698; /* content: I came out here to visit you, Jarl! And I find you living in an empty cabin in the middle of nowhere, babbling about frogs? You\'re hopeless! */
UPDATE db_script_string SET content_loc2='Edward. Tyler. Préparez-vous à votre premier défi.' WHERE entry=2000000699; /* content: Edward. Tyler. Prepare for your first challenge. */
UPDATE db_script_string SET content_loc2='Lysta, faites venir les prisonniers.' WHERE entry=2000000700; /* content: Lysta, summon in the captives. */
UPDATE db_script_string SET content_loc2='Lysta, invoquez des prisonniers morts-vivants.' WHERE entry=2000000702; /* content: Lysta, summon in undead captives. */
UPDATE db_script_string SET content_loc2='Lysta, invoquez... l\'abomination !' WHERE entry=2000000704; /* content: Lysta, summon forth... the abomination! */
UPDATE db_script_string SET content_loc2='Bien joué, Edward et Tyler. Votre entraînement progresse au mieux. Nous mettrons encore votre courage à l\'épreuve très bientôt.' WHERE entry=2000000705; /* content: Well done Edward and Tyler. You are progressing along in your training quite nicely. We shall test your mettle again soon. */
UPDATE db_script_string SET content_loc2='Merci de m\'avoir sauvée, $n ! Mon père va être fou de joie !' WHERE entry=2000000706; /* content: Thank you for saving me, $N! My father will be delighted! */
UPDATE db_script_string SET content_loc2='Est-ce que c\'est VOUS qui avez interrompu mon dîner ? Alors, est-ce vous ?' WHERE entry=2000000707; /* content: Are YOU the one who interrupted my dinner? Well, are you? */
UPDATE db_script_string SET content_loc2='Vous êtes bien trop laide et squelettique pour être une femelle yéti ! Et maintenant, vous m\'énervez vraiment…' WHERE entry=2000000708; /* content: You\'re too ugly and scrawny to be a female yeti! And now you\'re making me mad... */
UPDATE db_script_string SET content_loc2='Merci encore, $n. Maintenant je vais attendre ici jusqu\'à ce qu\'il n\'y ait plus de danger.' WHERE entry=2000000709; /* content: Thank again, $n.  Now I\'ll just wait here until it\'s safe to leave. */
UPDATE db_script_string SET content_loc2='Le mélange raté se renverse et semble prendre vie !' WHERE entry=2000000710; /* content: The failed concoction spills on the floor and appears to come to life! */
UPDATE db_script_string SET content_loc2='Il est vivant !' WHERE entry=2000000711; /* content: It\'s alive! */
UPDATE db_script_string SET content_loc2='Urtrak tuer vous !' WHERE entry=2000000715; /* content: Urtrak kill you! */
UPDATE db_script_string SET content_loc2='La vengeance est nôtre ! Attaquez, mes frères !' WHERE entry=2000000716; /* content: Vengeance is ours!  Attack my brothers! */
UPDATE db_script_string SET content_loc2='Voyons si ces graines pousseront dans le sol que j\'ai préparé...' WHERE entry=2000000718; /* content: Let\'s see how these seeds grow in my prepared soil... */
UPDATE db_script_string SET content_loc2='J\'espère que cette feuille prendra racine...' WHERE entry=2000000722; /* content: I hope this frond takes root... */
UPDATE db_script_string SET content_loc2='%s commence à manipuler la Pierre de Relu au-dessus des deux fossiles.' WHERE entry=2000000723; /* content: %s begins to manipulate the Stone of Relu over the two fossils. */
UPDATE db_script_string SET content_loc2='Qui a invoqué Aman ?' WHERE entry=2000000724; /* content: Who hath summoned forth Aman? */
UPDATE db_script_string SET content_loc2='Ah, je vois que vous avez du mal avec les reliques du passé.' WHERE entry=2000000725; /* content: Ah, I see you toil with relics of the past. */
UPDATE db_script_string SET content_loc2='Prenez garde, même vos créateurs sont faillibles.' WHERE entry=2000000726; /* content: Be warned that even your creators are fallible. */
UPDATE db_script_string SET content_loc2='À fouiller trop loin dans votre passé, vous risquez de voir votre avenir prendre fin d\'une manière abrupte.' WHERE entry=2000000727; /* content: Digging too deep into your past might bring an abrupt end to your future. */
UPDATE db_script_string SET content_loc2='Comment se passe la surveillance des tonneaux ?' WHERE entry=2000000730; /* content: How goes the barrel watching...? */
UPDATE db_script_string SET content_loc2='%s revêt ses habits en fourrure de loup.' WHERE entry=2000000732; /* content: %s dons his wolf-fur vestments. */
UPDATE db_script_string SET content_loc2='%s hurle en réponse à l\'appel de Malukaz.' WHERE entry=2000000733; /* content: %s howls in response to Malukaz\'s call. */
UPDATE db_script_string SET content_loc2='%s manifeste son accord.' WHERE entry=2000000734; /* content: %s expresses its approval. */
UPDATE db_script_string SET content_loc2='Que notre lien avec vous ne soit plus jamais rompu, esprit.' WHERE entry=2000000735; /* content: May our bond with you never be broken again, spirit. */
UPDATE db_script_string SET content_loc2='Nous avons réussi ! Thrall serait fier de nous.' WHERE entry=2000000736; /* content: We have done it! Thrall would be proud. */
UPDATE db_script_string SET content_loc2='Qu\'est-ce que ça signifie, Mor\'ghor ?' WHERE entry=2000000737; /* content: What is the meaning of this, Mor\'ghor? */
UPDATE db_script_string SET content_loc2='Silence !' WHERE entry=2000000738; /* content: SILENCE! */
UPDATE db_script_string SET content_loc2='C\'est ÇA, votre héros ?' WHERE entry=2000000740; /* content: THIS is your hero? */
UPDATE db_script_string SET content_loc2='Imbécile, on vous a trompé.' WHERE entry=2000000741; /* content: You have been deceived, imbecile. */
UPDATE db_script_string SET content_loc2='Toute... cette... opération... EST COMPROMISE !' WHERE entry=2000000742; /* content: This... whole... operation... HAS BEEN COMPROMISED! */
UPDATE db_script_string SET content_loc2='J\'attends les morceaux de la carcasse de cet insecte à mon repaire dans l\'heure. Échouez, et vous subirez un sort bien pire que la mort.' WHERE entry=2000000743; /* content: I expect to see this insect\'s carcass in pieces in my lair within the hour. Fail and you will suffer a fate so much worse than death. */
UPDATE db_script_string SET content_loc2='Est-ce qu\'on peut traverser ce fichu machin en toute sécurité maintenant ?' WHERE entry=2000000747; /* content: Is that damned thing safe to go through yet? */
UPDATE db_script_string SET content_loc2='Pas vraiment.' WHERE entry=2000000748; /* content: Not really. */
UPDATE db_script_string SET content_loc2='Vous voulez bien arrêter de poser cette question ?' WHERE entry=2000000749; /* content: Will you stop asking that? */
UPDATE db_script_string SET content_loc2='Bien, ça a l\'air sûr. Entrez, ingénieur !' WHERE entry=2000000750; /* content: Alright, it looks safe. Get in there, engineer! */
UPDATE db_script_string SET content_loc2='Rien ne va plus !' WHERE entry=2000000751; /* content: Here goes nothing! */
UPDATE db_script_string SET content_loc2='Ça va laisser une marque…' WHERE entry=2000000752; /* content: This is gonna leave a mark... */
UPDATE db_script_string SET content_loc2='%s passe ses mains sur son corps pour vérifier que tout est intact.' WHERE entry=2000000753; /* content: %s runs his hands across his body to make sure everything is intact. */
UPDATE db_script_string SET content_loc2='J\'ai réussi ! J\'ai réussi !' WHERE entry=2000000754; /* content: I made it! I made it! */
UPDATE db_script_string SET content_loc2='%s secoue la tête d\'un air désapprobateur.' WHERE entry=2000000755; /* content: %s shakes his head in disapproval. */
UPDATE db_script_string SET content_loc2='Je ne l\'aimais pas trop de toute façon. Trop de paroles, pas assez d\'actions.' WHERE entry=2000000756; /* content: I didn\'t like him much anyway. Too much talk, not enough "do." */
UPDATE db_script_string SET content_loc2='Formidable, bande d\'incompétents ! Un autre de nos ingénieurs s\'est vaporisé !' WHERE entry=2000000757; /* content: Nice going you incompetent morons! Another one of our engineers vaporized! */
UPDATE db_script_string SET content_loc2='Vous ne nous empêcherez pas de révéler la lâcheté de Jaina !' WHERE entry=2000000758; /* content: You can\'t stop us from exposing the truth about Jaina\'s cowardice! */
UPDATE db_script_string SET content_loc2='Si vous croyez pouvoir nous arrêter, vous faites erreur, « $gmon ami:mon amie; ».' WHERE entry=2000000759; /* content: If you think you can stop us, you\'re mistaken, \'friend.\' */
UPDATE db_script_string SET content_loc2='Ces gens sauront que l\'amiral Portvaillant était un vrai héros !' WHERE entry=2000000760; /* content: These people will know Admiral Proudmoore for the true hero he was! */
UPDATE db_script_string SET content_loc2='Moyennant quelques corrections créatives, ils pourraient devenir très utiles…' WHERE entry=2000000761; /* content: With just a little creative editing, these are much more useful... */
UPDATE db_script_string SET content_loc2='Ce serait vraiment dommage de laisser perdre ces pamphlets, n\'est-ce pas ?' WHERE entry=2000000762; /* content: It would be a shame to let these leaflets go to waste, wouldn\'t it? */
UPDATE db_script_string SET content_loc2='… la création d\'un fonds consacré à l\'achat de gel pour les cheveux ? Ma coiffure me convient, merci bien !' WHERE entry=2000000763; /* content: ...to create a fund for the purchase of hair gel? I like my hair the way it is, thanks! */
UPDATE db_script_string SET content_loc2='… en tant que déserteur, vous aurez le plaisir de participer à des activités comme la nage attaché et la toilette des chiens ? Mais c\'est ridicule !' WHERE entry=2000000764; /* content: ...as a deserter, you\'ll enjoy activities like tethered swimming and dog grooming? How ridiculous! */
UPDATE db_script_string SET content_loc2='Je ne risque pas de vous rejoindre. Mais je garde votre machin, il me sera très utile aux toilettes…' WHERE entry=2000000765; /* content: I\'d never join anything like this. Better keep this, though. It\'ll come in handy in the privy... */
UPDATE db_script_string SET content_loc2='… l\'objectif des déserteurs est une Theramore où tous les citoyens seraient libres de jongler à toute heure du jour et de la nuit ? Il n\'y a pas une coquille quelque part ?' WHERE entry=2000000766; /* content: ...the deserters seek a Theramore where citizens will be free to juggle at all hours of the day? What is this nonsense? */
UPDATE db_script_string SET content_loc2='Je n\'avais rien lu d\'aussi drôle depuis des mois. Merci, vous avez ensoleillé ma journée !' WHERE entry=2000000767; /* content: That\'s the funniest thing I\'ve read in a long time. You\'ve just made my day! */
UPDATE db_script_string SET content_loc2='Non mais… c\'est une blague ?' WHERE entry=2000000768; /* content: This... this is a joke, right? */
UPDATE db_script_string SET content_loc2='Les esprits vous protègent, $r !' WHERE entry=2000000770; /* content: Spirits watch over you, $r! */
UPDATE db_script_string SET content_loc2='Merci mille fois, héros !' WHERE entry=2000000771; /* content: Many Thanks, hero! */
UPDATE db_script_string SET content_loc2='Merci de m\'avoir sauvé. Mais les autres sont encore prisonniers sur les étages supérieurs !' WHERE entry=2000000774; /* content: Thank you for rescuing me, but the others are still held on the tiers above! */
UPDATE db_script_string SET content_loc2='Les nains de fer sont cinglés ! Ils n\'arrêtent pas de parler des ordres de leur « maître Loken », mais si je comprends bien, ils ne sont que quelques-uns à recevoir des instructions de sa part.' WHERE entry=2000000775; /* content: These irons are crazy! They keep talking about serving their \'master Loken\' but from what I can tell, only a few receive orders from him. */
UPDATE db_script_string SET content_loc2='Oh merci ! J\'avais peur qu\'ils ne m\'obligent à servir dans leur guerre contre les géants de pierre !' WHERE entry=2000000776; /* content: Thank goodness! I was worried they were going to press me into service in their war against the stone giants! */
UPDATE db_script_string SET content_loc2='Osez-vous défier le drottinn Hrothgar, roi des mers ?' WHERE entry=2000000777; /* content: You dare to challenge Drottinn Hrothgar, king of the seas? */
UPDATE db_script_string SET content_loc2='Donnez une bonne leçon aux sectateurs, $n. Personne ne peut chercher des noises au croisé Kul et s\'en tirer comme ça !' WHERE entry=2000000778; /* content: Teach those cultists a lesson, $N. No one messes with Crusader Kul and gets away with it! */
UPDATE db_script_string SET content_loc2='Louée soit la Lumière ! Je ne referai jamais quelque chose d\'aussi idiot !' WHERE entry=2000000779; /* content: Thank the Light! I\'ll never do anything this foolish again! */
UPDATE db_script_string SET content_loc2='Libérons le reste des aspirants, puis rentrons au camp. Après ça, c\'est un tonneau de bière entier qu\'il va me falloir !' WHERE entry=2000000780; /* content: Let\'s rescue the rest of those aspirants and get back to camp. I\'m going to need a whole keg of ale after this! */
UPDATE db_script_string SET content_loc2='Quelques secondes de plus, et ils m\'attachaient sur un de ces autels pour me siphonner mon âme. Sortons d\'ici !' WHERE entry=2000000781; /* content: They were seconds from strapping me to one of those altars and sucking my soul out. Let\'s get out of here! */
UPDATE db_script_string SET content_loc2='Merci, $c.' WHERE entry=2000000782; /* content: Thank you, $C. */
UPDATE db_script_string SET content_loc2='Ils tuent les prisonniers ! Vite, aidez les autres.' WHERE entry=2000000783; /* content: They\'re killing the prisoners! Hurry, help the others. */
UPDATE db_script_string SET content_loc2='Je savais que je n\'aurais pas dû venir au tournoi !' WHERE entry=2000000784; /* content: I knew I shouldn\'t have come to the tournament! */
UPDATE db_script_string SET content_loc2='J\'ai cru que je ne m\'en sortirais jamais vivante.' WHERE entry=2000000785; /* content: I thought I\'d never make it out of here alive. */
UPDATE db_script_string SET content_loc2='Imbécile |2 $c, jamais vous n\'arrêterez les kvaldirs !' WHERE entry=2000000786; /* content: Foolish $c, you cannot stop the Kvaldir! */
UPDATE db_script_string SET content_loc2='Quittez ce lieu.' WHERE entry=2000000792; /* content: Leave this place. */
UPDATE db_script_string SET content_loc2='Vous n\'empêcherez jamais le seigneur du Givre de pénétrer dans ce monde, $gmortel:mortelle;. La puissance du Chasse-marée écrasera Ragnaros une fois pour toutes, et fera de cette terre un paradis glacé !' WHERE entry=2000000797; /* content: You will not stop the Frost Lord from entering this world, mortal. The Tidehunter\'s might will crush that of Ragnaros once and for all, leaving your land a frozen paradise! */
UPDATE db_script_string SET content_loc2='Merci, $r. Je vais me joindre à votre combat contre les Forge-foudre.' WHERE entry=2000000798; /* content: Thank you, $r. I will join your struggle against the stormforged. */
UPDATE db_script_string SET content_loc2='Erreur de compilation. Unité défectueuse. Directive : désactivation de l\'unité.' WHERE entry=2000000799; /* content: Does not compute. Unit malfunctioning. Directive: shut down. */
UPDATE db_script_string SET content_loc2='Encore libre ? La mission du gardien Mimir attend toujours.' WHERE entry=2000000800; /* content: Free again? Keeper Mimir\'s work awaits. */
UPDATE db_script_string SET content_loc2='Nouvelle directive : aider à vaincre les nains de fer.' WHERE entry=2000000801; /* content: New directive: assist in the defeat of the iron dwarves. */
UPDATE db_script_string SET content_loc2='Imbécile ! Vous vouliez dérober le sang de Zelemar ? Vous allez le payer… avec le vôtre !' WHERE entry=2000000802; /* content: Insolent fool! You thought to steal Zelemar\'s blood? You shall pay with your own! */
UPDATE db_script_string SET content_loc2='Sortez-le de là !' WHERE entry=2000000804; /* content: Get him out of there! */
UPDATE db_script_string SET content_loc2='Peut-être Ograbisi utilisera-t-il votre tête comme petit chapeau quand j\'en aurai fini avec vous.' WHERE entry=2000000805; /* content: Perhaps Ograbisi will use your head as a tiny little hat when I\'m through with you. */
UPDATE db_script_string SET content_loc2='Là d\'où je viens, on dit merci aux gens qui ouvrent la porte d\'un détenu !' WHERE entry=2000000806; /* content: Where I come from, you get shanked for opening another inmate\'s cell door! */
UPDATE db_script_string SET content_loc2='J\'en ai assez de vos pleurnicheries incessantes, capitaine. Vous êtes capitaine de quoi, au fait ? Franchement, quel culot !' WHERE entry=2000000808; /* content: I\'ve had just about enough of your incessant whining, captain. What are you even a captain of? I mean, The nerve! */
UPDATE db_script_string SET content_loc2='%s hausse les épaules.' WHERE entry=2000000810; /* content: %s shrugs. */
UPDATE db_script_string SET content_loc2='D\'accord, retour à la planche à dessin.' WHERE entry=2000000811; /* content: Oh well, back to the drawing board. */
UPDATE db_script_string SET content_loc2='J\'espère qu\'il verra la Lumière et qu\'il consacrera son énergie à la guérison de Quel\'Thalas, qui fut si belle.' WHERE entry=2000000816; /* content: I can only hope that he will see the Light and instead turn his energies to restoring once-beautiful Quel\'Thalas. */
UPDATE db_script_string SET content_loc2='Ouf ! Mon chapeau porte-bonheur est là. Je l\'ai cherché partout.' WHERE entry=2000000819; /* content: Phew! There\'s my lucky hat. I\'ve been looking for it everywhere. */
UPDATE db_script_string SET content_loc2='Je ne sais pas ce qui m\'a pris de sortir sans mon épée. Je l\'aurais emportée si je l\'avais vue ici…' WHERE entry=2000000820; /* content: I don\'t know what I was thinking, going out without my sword. I would\'ve put it on if I\'d seen it here... */
UPDATE db_script_string SET content_loc2='Il n\'est pas concevable que je sorte sans ma cape. J\'espère qu\'elle est ici…' WHERE entry=2000000821; /* content: I can\'t possibly go out without my cloak. I hope it\'s in here... */
UPDATE db_script_string SET content_loc2='La voilà ! J\'aurais pourtant juré qu\'elle n\'y était pas la dernière fois que j\'ai regardé…' WHERE entry=2000000822; /* content: There it is! I could\'ve sworn it wasn\'t here last time I checked... */
UPDATE db_script_string SET content_loc2='Je n\'arrive pas à m\'endormir si on ne me raconte pas une belle histoire. Maintenant, je suis sûre de bien me reposer.' WHERE entry=2000000823; /* content: I can\'t sleep without a good bedtime story. Now I\'m certain to rest well. */
UPDATE db_script_string SET content_loc2='Quel est ce bruit ? On aurait dit un mugissement.' WHERE entry=2000000824; /* content: What was that noise? It sounded like a roar. */
UPDATE db_script_string SET content_loc2='Griffe-sinistre ?! Doucement mon ami... Où est ton maître Volcor ?' WHERE entry=2000000826; /* content: Grimclaw?! Easy there, my friend... where is your master Volcor? */
UPDATE db_script_string SET content_loc2='Doucement, doucement mon ami. Un moment...' WHERE entry=2000000828; /* content: Whoa, whoa there, my friend. One moment... */
UPDATE db_script_string SET content_loc2='Là... Ça devrait vous faire du bien. Maintenant, racontez-moi ce qui s\'est passé, Griffe-sinistre.' WHERE entry=2000000830; /* content: There... that should help. Now, tell me what\'s happened, Grimclaw. */
UPDATE db_script_string SET content_loc2='Je comprends, mon ami. Je trouverai quelqu\'un pour venir en aide à votre maître. Retournez auprès de lui maintenant, ou tout du moins, restez à proximité.' WHERE entry=2000000832; /* content: I understand, my friend. I shall find someone to help your master. Go back to him now, or at least stay close. */
UPDATE db_script_string SET content_loc2='Si vous avez le temps, vous pourriez donner un coup de main à Griffe-sinistre et son maître Volcor. Si cela vous intéresse, faites-le-moi savoir...' WHERE entry=2000000834; /* content: If you have the time, Grimclaw and his master Volcor could use your help. If you\'re interested, speak with me further... */
UPDATE db_script_string SET content_loc2='Excusez-moi un moment, je commence mes expériences…' WHERE entry=2000000835; /* content: Please excuse me while I begin my tests... */
UPDATE db_script_string SET content_loc2='Intéressant…' WHERE entry=2000000836; /* content: Interesting... */
UPDATE db_script_string SET content_loc2='Mais qu\'est-ce que c\'est ?' WHERE entry=2000000837; /* content: But what is this? */
UPDATE db_script_string SET content_loc2='Oh là là !' WHERE entry=2000000838; /* content: Oh, my! */
UPDATE db_script_string SET content_loc2='Si mon sang ne s\'était figé il y a bien longtemps, il serait en train de bouillir de rage…' WHERE entry=2000000839; /* content: If my blood hadn\'t clotted long ago, it would be boiling with rage right now... */
UPDATE db_script_string SET content_loc2='Salutations, Gezhe ! Dix-sept commandes supplémentaires ont été émises dans le secteur Hara\'samid. Le marché de la veldarite lourde s\'est quasiment effondré, comme vous l\'aviez prédit.' WHERE entry=2000000842; /* content: Hail, Gezhe!  Seventeen more orders have been placed in the Hara\'samid Sector.  The markets of heavy veldarite have all but collapsed, as you predicted. */
UPDATE db_script_string SET content_loc2='Les exportations d\'Heraazi sont en augmentation, maître. Nous avons d\'ores et déjà soudoyé les officiers taxateurs, et les marchandises de contrebande commenceront à arriver dans quelques jours.' WHERE entry=2000000843; /* content: Heraazi exports are on the rise, sir.  We\'ve already bribed the appropriate taxation officers and contraband will begin to arrive within days. */
UPDATE db_script_string SET content_loc2='Cela n\'a rien de très surprenant. Revenez quand vous aurez de vraies nouvelles, merci.' WHERE entry=2000000844; /* content: Nothing surprising there.  Come back with some real news, will you? */
UPDATE db_script_string SET content_loc2='Comme prévu. Très bien, disposez.' WHERE entry=2000000845; /* content: As expected.  Very well, dismissed. */
UPDATE db_script_string SET content_loc2='Avec votre permission, maître…' WHERE entry=2000000846; /* content: By your leave, sir. */
UPDATE db_script_string SET content_loc2='Si vous êtes ici, cela signifie que vous êtes $gprêt:prête; à entamer une étude sur l\'invocation des cohortes démoniaques en vue de réaliser votre union. Nous commencerons par les créatures les plus humbles que vous serez en mesure d\'invoquer puis nous continuerons. Commençons.' WHERE entry=2000000847; /* content: If you\'re here, then it means you are prepared to begin the study of summoning demonic cohorts to do your bidding. We will start with the lowliest creatures you will be able to call and continue from there. Let us begin. */
UPDATE db_script_string SET content_loc2='La créature que vous pourrez invoquer sans aucune difficulté est le diablotin. Vous devriez déjà être en mesure de créer ce serviteur, mais afin de vous délivrer un enseignement complet je vais commencer par lui.' WHERE entry=2000000848; /* content: The easiest creature for you to summon is the imp. You should already be able to bring forth this minion but for completeness\' sake I will start with him. */
UPDATE db_script_string SET content_loc2='Cette ignoble petite créature est le diablotin. Il est petit et faible, et donc quasiment inutile en tant que bouclier de chair, et sa capacité à infliger des dégâts est au mieux médiocre. Il vaut mieux utiliser cette créature pour soutenir un groupe assez grand.' WHERE entry=2000000849; /* content: This foul little beast is the imp. It is small and weak, making it almost useless as a meatshield, and its damage output is mediocre at best. This creature is best used for support of a larger group. */
UPDATE db_script_string SET content_loc2='Maintenant que vous avez eu la chance d\'étudier un diablotin, passons au prochain serviteur que vous serez en mesure d\'invoquer, le marcheur du Vide.' WHERE entry=2000000850; /* content: Now that you have had a chance to study the imp, let us move on to the next minion you will be able to summon, the voidwalker. */
UPDATE db_script_string SET content_loc2='Cette entité démoniaque est connue sous le nom de "marcheur du Vide". Sa force et son endurance, qui ne sont pas à négliger, sont parfaitement adaptées à la défense. Envoyez-la attaquer vos ennemis, puis utilisez-la comme un bouclier pendant que vous employez vos sorts et vos techniques pour drainer la vie hors du corps de votre adversaire.' WHERE entry=2000000851; /* content: This demonic entity is known as the Voidwalker. Its strength and endurance are significant, making it ideal for defense. Send it to attack your enemy, then use it as a shield while you use your spells and abilities to drain away your opponent\'s life. */
UPDATE db_script_string SET content_loc2='Si vous n\'en avez jamais vu, c\'est un spectacle à voir. Une créature très impressionnante, à la fois sur le champ de bataille et en dehors. À présent, jetons un œil à celle que tous les étudiants mâles attendent. La succube.' WHERE entry=2000000852; /* content: If you\'ve never seen one, it is a sight to behold. A very impressive creature both on and off the field of battle. Next, let us take a look at what I am sure all you male students have been waiting for. The succubus. */
UPDATE db_script_string SET content_loc2='Voyons. Mis à part les diversions évidentes qu\'une telle servante occasionnera envers vos ennemis mâles, elle est également capable de causer des dommages d\'une ampleur impressionnante. Cependant, sa faible résistance la rend quasiment inutile en tant que bouclier.' WHERE entry=2000000853; /* content: All right now. Aside from the obvious distractions a minion like this will provide against your more masculine foes, she is also capable of dealing out impressive amounts of damage. However, her fragile endurance makes her almost useless as a shield. */
UPDATE db_script_string SET content_loc2='Étudiez assidûment, et un jour vous pourriez être capable d\'en invoquer une par vous-même. Mais pour le moment, passons au chasseur corrompu.' WHERE entry=2000000854; /* content: Study hard and you might one day be able to summon one on your own, but for now it\'s time to move on to the felhunter. */
UPDATE db_script_string SET content_loc2='Ce que vous voyez devant vous est un chasseur corrompu. Les talents naturels de cette créature lui permettent de neutraliser des sorts. Ce talent, ainsi que d\'autres capacités, la rendent sans égale face à un adversaires doté de pouvoirs magiques.' WHERE entry=2000000855; /* content: What you see before you is a felhunter. This creature\'s natural talents include spell lock and other abilities which make it unequalled when facing a magically attuned opponent. */
UPDATE db_script_string SET content_loc2='Lorsque vous êtes face à un lanceur de sorts quelconque, cette bête sauvage est votre meilleur ami. Maintenant, intéressons-nous à quelque chose d\'un peu différent. La créature suivante vous aidera à voyager et facilitera grandement vos futurs déplacements. Jetons un coup d\'œil sur un palefroi corrompu.' WHERE entry=2000000856; /* content: When facing a spellcaster of any kind, this feral beast will be your best friend. Now, let us take a look at something a bit different. This next creature will aid your travels and make your future journeys much easier. Let\'s take a look at a felsteed. */
UPDATE db_script_string SET content_loc2='Voilà. Votre leçon sur l\'invocation est terminée. Un nouveau cours est sur le point de commencer, alors si vous souhaitez réviser, n\'hésitez pas à rester.' WHERE entry=2000000858; /* content: There you have it. Our lesson on summoning has come to an end. A new class will begin shortly, so if you wish to brush up, feel free to stay around. */
UPDATE db_script_string SET content_loc2='Le prêtre s\'agenouille et prie silencieusement.' WHERE entry=2000000859; /* content: The priest kneels in silent prayer. */
UPDATE db_script_string SET content_loc2='Lumière, donne-moi la force.' WHERE entry=2000000860; /* content: Light, give me strength. */
UPDATE db_script_string SET content_loc2='Lumière, conduis les défunts de l\'illusion vers la vérité, de l\'ombre vers la Lumière et de la mort vers l\'immortalité.' WHERE entry=2000000861; /* content: Light, lead the fallen from untruth to truth, from darkness to Light and from death to immortality. */
UPDATE db_script_string SET content_loc2='Nooooon !!' WHERE entry=2000000865; /* content: NOOOOO!!! */
UPDATE db_script_string SET content_loc2='Mes amis… Je vous ai laissé tomber…' WHERE entry=2000000866; /* content: Friends... I\'ve failed you... */
UPDATE db_script_string SET content_loc2='Non, mon ami… c\'est nous… c\'est nous qui vous laissons tomber.' WHERE entry=2000000867; /* content: No my friend... we failed you... we failed you. */
UPDATE db_script_string SET content_loc2='Les dragons noirs se sont matérialisés d\'un coup, et nous ont attaqués avant que nous ne puissions réagir.' WHERE entry=2000000870; /* content: The Black Dragonkin materialized from thin air, and set upon us before we could react. */
UPDATE db_script_string SET content_loc2='Ils nous ont froidement massacrés, mais ce qui les intéressait vraiment, c\'était les œufs qui sont conservés dans le sanctum.' WHERE entry=2000000872; /* content: They slaughtered us with cold efficiency, but the true focus of their interest seemed to be the eggs kept here in the sanctum. */
UPDATE db_script_string SET content_loc2='Votre victoire sera de courte durée, intrus. Je suis le commandant Varos Arpentenuée. Mes drakes contrôlent les cieux et protègent ce passage, et je vais m\'assurer personnellement que l\'Oculus ne tombe pas entre vos mains !' WHERE entry=2000000877; /* content: Intruders, your victory will be short-lived. I am Commander Varos Cloudstrider. My drakes control the skies and protect this conduit. I will see to it personally that the Oculus does not fall into your hands! */
UPDATE db_script_string SET content_loc2='Ceux qui se repaissent d\'âmes perdues connaissent toujours la faim.' WHERE entry=2000000880; /* content: They who dine on lost souls know only hunger. */
UPDATE db_script_string SET content_loc2='Plongez votre regard dans le vide. Voilà la perpétuité de leur errance.' WHERE entry=2000000881; /* content: Gaze into the void. It is the perpetuity in which they dwell. */
UPDATE db_script_string SET content_loc2='Ils boiront votre peur, car c\'est le sang de la vie.' WHERE entry=2000000882; /* content: They drink your fear: it is the blood of life. */
UPDATE db_script_string SET content_loc2='Ils sont le murmure du vent glacé.' WHERE entry=2000000883; /* content: They are the whisper on the shivering wind. */
UPDATE db_script_string SET content_loc2='Ils ne meurent pas, car ils ne vivent pas. Ils sont hors du cycle.' WHERE entry=2000000884; /* content: They do not die; they do not live. They are outside the cycle. */
UPDATE db_script_string SET content_loc2='Là où un seul tombe, ils seront nombreux à prendre sa place.' WHERE entry=2000000885; /* content: Where one falls, many shall take its place. */
UPDATE db_script_string SET content_loc2='Nous nous retrouverons… Un jour.' WHERE entry=2000000888; /* content: We will meet again... Someday. */
UPDATE db_script_string SET content_loc2='La mort incarnée est à nouveau lâchée sur le monde ! Teron Fielsang est de retour…' WHERE entry=2000000889; /* content: Death incarnate has been released upon the world once more! Teron Gorefiend has returned... */
UPDATE db_script_string SET content_loc2='Cachez-vous ! Vite, avant qu\'ils ne vous voient ! Reculez !' WHERE entry=2000000892; /* content: Hide! Hide before they see you! Back up! */
UPDATE db_script_string SET content_loc2='L\'heure de frapper est venue. Supprimez Icarius.' WHERE entry=2000000893; /* content: The time to strike is at hand. Terminate Icarius. */
UPDATE db_script_string SET content_loc2='Halte !' WHERE entry=2000000894; /* content: Halt! */
UPDATE db_script_string SET content_loc2='Qu\'y a-t-il, seigneur ?' WHERE entry=2000000895; /* content: What is it, my lord? */
UPDATE db_script_string SET content_loc2='Zarath, il faut que vous retourniez au Temple noir sur-le-champ ! Il… Il semble que j\'aie égaré temporairement les ordres du seigneur Illidan. Faites vite !' WHERE entry=2000000896; /* content: Zarath you must return to the Black Temple at once! I... I seem to have misplaced Lord Illidan\'s orders. Quickly! */
UPDATE db_script_string SET content_loc2='Seigneur, vous ne vous attendiez sûrement pas à ce que je vous laisse sans protection. Le seigneur Illidan aurait ma peau s\'il vous arrivait quoi que ce soit.' WHERE entry=2000000897; /* content: My lord, surely you do not expect me to leave you unattended. Lord Illidan would have my head if anything were to happen to you. */
UPDATE db_script_string SET content_loc2='Zarath, je suis parfaitement capable de remonter la route jusqu\'à la halte de l\'Éclipse tout seul. Si nous n\'assurons pas la livraison de la missive, ce sont nos peaux à nous deux qu\'aura le seigneur Illidan ! Vous pouvez disposez !' WHERE entry=2000000898; /* content: Zarath, I am perfectly capable of making it up this road to Eclipse Point. If we do not deliver the missive, Lord Illidan will have both of our heads! You are dismissed! */
UPDATE db_script_string SET content_loc2='À vos ordres, monseigneur.' WHERE entry=2000000899; /* content: As you wish, my lord. */
UPDATE db_script_string SET content_loc2='Ah, un joli petit fagot de chardons sanglants… Sans doute abandonné par l\'un des sales drogués de la halte de l\'Éclipse.' WHERE entry=2000000900; /* content: Ah, sweet, sweet bloodthistle... Probably left behind by one of those filthy addicts at Eclipse Point. */
UPDATE db_script_string SET content_loc2='Leur malheur fait vraiment mon bonheur…' WHERE entry=2000000901; /* content: Their loss is most definitely my gain... */
UPDATE db_script_string SET content_loc2='%s place le fagot de chardons sanglants dans sa robe.' WHERE entry=2000000902; /* content: %s puts the bundle of bloodthistle in his robe. */
UPDATE db_script_string SET content_loc2='Reculez, $n. Les esprits pourraient s\'en prendre à vous.' WHERE entry=2000000903; /* content: Stand back, $N. The spirits may lash out. */
UPDATE db_script_string SET content_loc2='Pourquoi nous avez-vous capturés ?' WHERE entry=2000000904; /* content: Why have you captured us? */
UPDATE db_script_string SET content_loc2='Qu\'espérez-vous en retirer ?' WHERE entry=2000000905; /* content: What is it that you hope to gain from this? */
UPDATE db_script_string SET content_loc2='%s s\'incline.' WHERE entry=2000000906; /* content: %s bows. */
UPDATE db_script_string SET content_loc2='Nous voulons vous aider, esprit. Nous faisons appel à votre sagesse.' WHERE entry=2000000907; /* content: We wish to help you, spirit. We seek your wisdom. */
UPDATE db_script_string SET content_loc2='Sagesse ? C\'est votre peuple qui a éveillé l\'épouvantable colère de Cyrukh.' WHERE entry=2000000908; /* content: Wisdom? It was your people that caused the insufferable wrath of Cyrukh. */
UPDATE db_script_string SET content_loc2='Pourquoi devrions-nous vous aider ? Le monde est en ruine. Anéanti.' WHERE entry=2000000909; /* content: Why should we help you? The world is ruined. Destroyed. */
UPDATE db_script_string SET content_loc2='%s acquiesce.' WHERE entry=2000000910; /* content: %s nods. */
UPDATE db_script_string SET content_loc2='Vous dites vrai, esprit. C\'est mon peuple qui vous a infligé cela. Permettez-nous de nous racheter.' WHERE entry=2000000911; /* content: You are right, spirit. It was my people that did this to you. Allow us to make it right. */
UPDATE db_script_string SET content_loc2='Dites-nous, je vous prie : qui est Cyrukh, ou qu\'est-ce ?' WHERE entry=2000000912; /* content: Please tell us this: Who or what is Cyrukh? */
UPDATE db_script_string SET content_loc2='Idiot ! Cyrukh le seigneur du Feu ! Quand le lien fut rompu, Cyrukh s\'éveilla. Ce sont les larmes de Cyrukh qui couvrent ce monde de roche en fusion.' WHERE entry=2000000913; /* content: Fool! Cyrukh the fire lord! When the tie was severed, Cyrukh awoke. It is the tears of Cyrukh that fill this land with molten rock. */
UPDATE db_script_string SET content_loc2='Le… Le seigneur du Feu ? Un seigneur élémentaire fut exhumé ? Mais bien sûr… Gul\'dan, tu n\'étais qu\'un satané imbécile !' WHERE entry=2000000914; /* content: The... The fire lord? An elemental lord was unearthed? Of course... Gul\'dan you were a damned fool! */
UPDATE db_script_string SET content_loc2='Il est devenu fou. Il a plongé dans la démence pendant la rupture. Il n\'y a pas de répit pour les éléments.' WHERE entry=2000000915; /* content: He is insane now. Driven mad during the shattering. There is no rest for the elements. */
UPDATE db_script_string SET content_loc2='Il doit y avoir un moyen.' WHERE entry=2000000916; /* content: There must be a way. */
UPDATE db_script_string SET content_loc2='Il y en a un, oui. Détruire Cyrukh. Vaincre un seigneur élémentaire.' WHERE entry=2000000917; /* content: Yes, a way. Destroy Cyrukh. Destroy an elemental lord. */
UPDATE db_script_string SET content_loc2='Ayez foi en mes compagnons et moi, esprit. Dites-nous comment faire apparaître le seigneur du Feu. Mes alliés le détruiront.' WHERE entry=2000000918; /* content: Do not doubt my comrades and I, spirit. Tell us how to conjure the fire lord. My allies will destroy him. */
UPDATE db_script_string SET content_loc2='Vous devez dire les mots…' WHERE entry=2000000919; /* content: You must utter the words... */
UPDATE db_script_string SET content_loc2='Oui… Oui… les mots. Les mots de damnation. Les mots prononcés par le ténébreux. Votre maître.' WHERE entry=2000000920; /* content: Yes... Yes... the words. The words of damnation. The words spoken by the dark one. Your master. */
UPDATE db_script_string SET content_loc2='Oui… Gul\'dan…' WHERE entry=2000000921; /* content: Yes... Gul\'dan... */
UPDATE db_script_string SET content_loc2='Nous découvrirons ces mots. Nous invoquerons votre maître et nous le détruirons.' WHERE entry=2000000922; /* content: We will find these words. We will summon your master and we will destroy him. */
UPDATE db_script_string SET content_loc2='Des paroles. Encore, toujours des paroles. Maintenant, relâchez-nous, mortel.' WHERE entry=2000000923; /* content: Talk. More talk... Now release us, mortal. */
UPDATE db_script_string SET content_loc2='Et maintenant, retrouvons les mots oubliés !' WHERE entry=2000000924; /* content: Now to recover the lost words! */
UPDATE db_script_string SET content_loc2='Je vais commencer à ne plus demander gentiment, homme-poisson.' WHERE entry=2000000925; /* content: I\'m through asking nicely, fish-man. */
UPDATE db_script_string SET content_loc2='Où est la Formule de damnation ? Je sais qu\'une des trois parties se trouve dans ce village.' WHERE entry=2000000926; /* content: Where is the Cipher of Damnation? I know a third of it is in this village. */
UPDATE db_script_string SET content_loc2='Qu\'est-sssce que... vous voulez... peau-lissse ?' WHERE entry=2000000927; /* content: Whhaaat... are you sssspeaking of, ssssoft-sskin? */
UPDATE db_script_string SET content_loc2='Les nagas… n\'ont pas… ssscette formule…' WHERE entry=2000000928; /* content: The naga do nottt... have thissss... cipherrr... */
UPDATE db_script_string SET content_loc2='Non… Bien sûr que non… Où avais-la tête ? Pourquoi auriez-vous un fragment de la Formule de damnation ?' WHERE entry=2000000929; /* content: No... Of course not. What was I thinking? Why would you have a third of the Cipher of Damnation? */
UPDATE db_script_string SET content_loc2='Je me sens tellement bête. Peut-être que je devrais vous détacher et vous laisser partir, non ?' WHERE entry=2000000930; /* content: I feel so stupid. Maybe I should unchain you and set you free? */
UPDATE db_script_string SET content_loc2='Ssssi… Sssssi… Laissssez-moi…' WHERE entry=2000000931; /* content: Yesss... Yesss... Ssset me freee... */
UPDATE db_script_string SET content_loc2='EST-CE QUE J\'AI VRAIMENT L\'AIR SI BÊTE QUE ÇA, NAGA ? MAINTENANT TU VAS SOUFFRIR !' WHERE entry=2000000932; /* content: DO I HONESTLY LOOK THAT STUPID TO YOU, NAGA? NOW YOU FEEL PAIN! */
UPDATE db_script_string SET content_loc2='%s crache sur le naga glissentaille.' WHERE entry=2000000933; /* content: %s spits on the Coilskar naga. */
UPDATE db_script_string SET content_loc2='DEBOUT, SALE TAS DE FUMIER ! DEBOUT AVANT QUE JE T\'ACHÈVE !' WHERE entry=2000000934; /* content: GET UP YOU ROTTEN PIECE OF TRASH! GET UP BEFORE I END YOU! */
UPDATE db_script_string SET content_loc2='%s reprend conscience.' WHERE entry=2000000935; /* content: %s regains consciousness. */
UPDATE db_script_string SET content_loc2='Cesssez de frapper le commandant glissssentaille, orc ! Je vais parler ! La forrrmule… Elle est dissssimulée dans un coffre à la halte de Glisssentaille… Les nagas conssservent la clé…' WHERE entry=2000000937; /* content: Do not beat the Coilsssskaarrr commander anymore, orc! I will tell you! The cipherrr... It is hidden in one of the chests at Coilssskarrr Point... The naga there hold the keysss... */
UPDATE db_script_string SET content_loc2='GLISSSSENTAILLE !' WHERE entry=2000000938; /* content: COILSSSKARRR! */
UPDATE db_script_string SET content_loc2='Que voulez-vous de moi, chaman ?' WHERE entry=2000000939; /* content: What is it that you want from me, shaman? */
UPDATE db_script_string SET content_loc2='N\'avez-vous donc pas d\'yeux pour voir ? N\'avez-vous pas vu les démons qui sont à notre porte ? Je n\'ai pas assez de soldats pour en détacher ailleurs !' WHERE entry=2000000940; /* content: Do you not have eyes? Did you not see the demons on our very doorstep? I do not have the soldiers to spare! */
UPDATE db_script_string SET content_loc2='Priez vos dieux, chaman. Peut-être vous viendront-ils en aide. La Horde, elle, ne le peut pas…' WHERE entry=2000000941; /* content: Pray to your gods, shaman. Perhaps they will come to your aid. The Horde, however, cannot... */
UPDATE db_script_string SET content_loc2='Tout est prêt, porteguerre.' WHERE entry=2000000942; /* content: Everything is in readiness, warbringer. */
UPDATE db_script_string SET content_loc2='Le seigneur funeste Kazzak sera content. Il faut augmenter la cadence de vos attaques. Détruisez les bastions orcs et nains au plus vite.' WHERE entry=2000000943; /* content: Doom Lord Kazzak will be pleased. You are to increase the pace of your attacks. Destroy the orcish and dwarven strongholds with all haste. */
UPDATE db_script_string SET content_loc2='Porteguerre, cela impliquera de faire donner tous les infernaux du bastion. Cela pourrait nous rendre vulnérables à une contre-attaque.' WHERE entry=2000000944; /* content: Warbringer, that will require the use of all the hold\'s infernals. It may leave us vulnerable to a counterattack. */
UPDATE db_script_string SET content_loc2='Ne vous inquiétez pas. J\'ai augmenté le rythme de production à la Forgemort. Vous aurez tous les infernaux qu\'il vous faudra pour exécuter vos ordres. N\'échouez pas, Jovaan.' WHERE entry=2000000945; /* content: Don\'t worry about that. I\'ve increased production at the Deathforge. You\'ll have all the infernals you need to carry out your orders. Don\'t fail, Jovaan. */
UPDATE db_script_string SET content_loc2='Il sera fait selon vos ordres, porteguerre. Une dernière question, si je puis…' WHERE entry=2000000946; /* content: It shall be as you say, warbringer. One last question, if I may... */
UPDATE db_script_string SET content_loc2='Oui ?' WHERE entry=2000000947; /* content: Yes? */
UPDATE db_script_string SET content_loc2='Y a quoi dans la caisse ?' WHERE entry=2000000948; /* content: What\'s in the crate? */
UPDATE db_script_string SET content_loc2='La caisse ? Je n\'ai pas envoyé de caisse, Jovaan. N\'avez-vous rien de plus important à faire ? Allez vous en occuper !' WHERE entry=2000000949; /* content: Crate? I didn\'t send you a crate, Jovaan. Don\'t you have more important things to worry about? Go see to them! */
UPDATE db_script_string SET content_loc2='Si vous voulez la clé, il va falloir m\'attraper !' WHERE entry=2000000950; /* content: If you want the key, you\'ll have to catch me! */
UPDATE db_script_string SET content_loc2='Poursuivez-moi si vous l\'osez ! Je m\'enfuis sans hésiter !' WHERE entry=2000000951; /* content: Chase me if you dare! I run without a care! */
UPDATE db_script_string SET content_loc2='Pourquoi voudriez-vous me faire souffrir ? Venez. L\'amitié peut nous réunir !' WHERE entry=2000000952; /* content: Why would you ever want to harm me!? Come. Friends we can be! */
UPDATE db_script_string SET content_loc2='MEURS ? Vous faites pleurer Pusillin !' WHERE entry=2000000953; /* content: DIE?! You make Pusillin cry! */
UPDATE db_script_string SET content_loc2='Dites bonjour à mes petits amis !' WHERE entry=2000000954; /* content: Say hello to my little friends! */
UPDATE db_script_string SET content_loc2='Voilà !' WHERE entry=2000000956; /* content: Ok, here I go! */
UPDATE db_script_string SET content_loc2='Allons-y !' WHERE entry=2000000961; /* content: Let\'s move forward! */
UPDATE db_script_string SET content_loc2='La pierre de glace a fondu !' WHERE entry=2000000962; /* content: The Ice Stone has melted! */
UPDATE db_script_string SET content_loc2='Ahune, ta puissance ne grandira plus !' WHERE entry=2000000963; /* content: Ahune, your strength grows no more! */
UPDATE db_script_string SET content_loc2='Tu ne règneras pas de ta main de glace !' WHERE entry=2000000964; /* content: Your frozen reign will not come to pass! */
UPDATE db_script_string SET content_loc2='Sa mort est proche ! La deuxième partie de la Formule de damnation est à nous. Je…' WHERE entry=2000000965; /* content: It is dying! The second part of the Cipher of Damnation is ours. I... */
UPDATE db_script_string SET content_loc2='Je disparais… Retournez voir Ar\'tor… Ret… our… voi…' WHERE entry=2000000966; /* content: I am fading... Return to Ar\'tor... Ret... rn... to... */
UPDATE db_script_string SET content_loc2='Une voix profonde, à vous glacer les os, retentit en provenance de %s…' WHERE entry=2000000967; /* content: A deep, bone chilling voice echoes from the %s... */
UPDATE db_script_string SET content_loc2='Silence ! La rupture est sur le point d\'avoir lieu !' WHERE entry=2000000970; /* content: Be silent! The shattering is soon to come! */
UPDATE db_script_string SET content_loc2='Soyez les témoins de la puissance implacable de notre sombre maître !' WHERE entry=2000000971; /* content: Bear witness to the undeniable power of our dark master! */
UPDATE db_script_string SET content_loc2='Grâce à ce don, je vais raser cette terre et la reconstruire !' WHERE entry=2000000972; /* content: With his gift, I shall raze this land and reform it! */
UPDATE db_script_string SET content_loc2='Regardez ! Voyez la terre se rompre devant nous ! Voyez l\'énergie affluer ! Elle nourrira nos armées…' WHERE entry=2000000973; /* content: Watch! See the ground shatter before us! Watch as the energy flows! It will feed our armies... */
UPDATE db_script_string SET content_loc2='Jamais la puissance ne nous manquera ! Notre avenir est assuré ! Soyez éblouis par ma gloire !' WHERE entry=2000000974; /* content: We will never be without power! I have secured our future! Bask in my glory! */
UPDATE db_script_string SET content_loc2='C\'est… maintenant… fait…' WHERE entry=2000000975; /* content: It... is... done... */
UPDATE db_script_string SET content_loc2='Déchirez cette terre, Gul\'dan ! Rompez le lien !' WHERE entry=2000000976; /* content: Tear the land asunder, Gul\'dan! Sever the tie! */
UPDATE db_script_string SET content_loc2='Ne faites pas ça, Gul\'dan ! Les éléments ne nous le pardonneront jamais !' WHERE entry=2000000977; /* content: Do not do this, Gul\'dan! The elements will never forgive us! */
UPDATE db_script_string SET content_loc2='Ils nous ont abandonnés ! Ne l\'oubliez jamais !' WHERE entry=2000000979; /* content: They have abandoned us! Do not forget! */
UPDATE db_script_string SET content_loc2='Nous prendrons à la terre ce qu\'elle refuse de nous donner !' WHERE entry=2000000980; /* content: We will take from the land if it refuses to give! */
UPDATE db_script_string SET content_loc2='Stop ! Arrêtez, il le faut !' WHERE entry=2000000981; /* content: Stop! You must stop! */
UPDATE db_script_string SET content_loc2='Vous nous avez damnés… nous sommes perdus.' WHERE entry=2000000982; /* content: You have damned us all... We are lost. */
UPDATE db_script_string SET content_loc2='À vos ordres !' WHERE entry=2000000983; /* content: By your command! */
UPDATE db_script_string SET content_loc2='Attendez ici, $n. Le robot espion va faire sortir Lescovar dès que possible. Préparez-vous ! N\'attaquez qu\'après avoir entendu leur conversation.' WHERE entry=2000000984; /* content: Wait here, $N. Spybot will make Lescovar come out as soon as possible. Be ready! Attack only after you\'ve overheard their conversation. */
UPDATE db_script_string SET content_loc2='Bonjour, j\'aimerais parler au seigneur Lescovar.' WHERE entry=2000000985; /* content: Good day to you both. I would speak to Lord Lescovar. */
UPDATE db_script_string SET content_loc2='Bien sûr, il vous attend dans la bibliothèque.' WHERE entry=2000000986; /* content: Of course. He awaits you in the library. */
UPDATE db_script_string SET content_loc2='Merci. Que la Lumière vous guide.' WHERE entry=2000000987; /* content: Thank you. The Light be with you both. */
UPDATE db_script_string SET content_loc2='Votre invité est arrivé et vous attend.' WHERE entry=2000000988; /* content: Milord, your guest has arrived. He awaits your presence. */
UPDATE db_script_string SET content_loc2='Ah, merci. Je vous laisserai à la bibliothèque pendant que je m\'occupe de cette petite affaire.' WHERE entry=2000000989; /* content: Ah, thank you kindly. I will leave you to the library while I tend to this small matter. */
UPDATE db_script_string SET content_loc2='C\'est l\'heure de ma méditation, laissez-moi.' WHERE entry=2000000991; /* content: It\'s time for my meditation, leave me. */
UPDATE db_script_string SET content_loc2='Oui, chef !' WHERE entry=2000000992; /* content: Yes, sir! */
UPDATE db_script_string SET content_loc2='%s attend que les gardes ne soient plus en vue.' WHERE entry=2000000993; /* content: %s waits for the guards to be out of sight. */
UPDATE db_script_string SET content_loc2='Vous voilà. Quelles sont les nouvelles de la marche de l\'Ouest ?' WHERE entry=2000000994; /* content: There you are. What news from Westfall? */
UPDATE db_script_string SET content_loc2='VanCleef vous fait savoir que les plans sont en cours. Mais il a entendu des rumeurs au sujet d\'un espion.' WHERE entry=2000000995; /* content: VanCleef sends word that the plans are underway. But he\'s heard rumors about someone snooping about. */
UPDATE db_script_string SET content_loc2='Hum, c\'est peut-être ce fouineur de Shaw. Je vais voir ce que je peux découvrir. Je reprendrai bientôt contact avec vous.' WHERE entry=2000000996; /* content: Hmm, it could be that meddler Shaw. I will see what I can discover. Be off with you. I\'ll contact you again soon. */
UPDATE db_script_string SET content_loc2='Très bien. Je vais rentrer, alors.' WHERE entry=2000000997; /* content: Very well. I will return then. */
UPDATE db_script_string SET content_loc2='C\'est ça ! C\'est ce que vous attendiez ! TUEZ-LES !' WHERE entry=2000000998; /* content: That\'s it! That\'s what you were waiting for! KILL THEM! */
UPDATE db_script_string SET content_loc2='Le roi devait arriver en secret par bateau. Mais le navire n\'est jamais parvenu à destination et le roi a disparu.' WHERE entry=2000001001; /* content: The king was to travel here by ship, in secret. But the ship never arrived, and King Wrynn disappeared. */
UPDATE db_script_string SET content_loc2='Pendant ce temps, le roi est parvenu à échapper à ses ravisseurs, et il est rentré à Hurlevent pour régler son compte à Onyxia.' WHERE entry=2000001003; /* content: Meanwhile, the king managed to escape his captors, and returned to Stormwind to deal with Onyxia. */
UPDATE db_script_string SET content_loc2='Merci, $n.' WHERE entry=2000001008; /* content: Thank you, $N. */
UPDATE db_script_string SET content_loc2='Je devrais peut-être vous expliquer...' WHERE entry=2000001014; /* content: Perhaps I should explain... */
UPDATE db_script_string SET content_loc2='Maintenant !' WHERE entry=2000001021; /* content: Go! */
UPDATE db_script_string SET content_loc2='Pitoyable être mortel, Onyxia n\'a à se justifier devant personne !' WHERE entry=2000001026; /* content: Pitiful mortal, Onyxia answers to no one! */
UPDATE db_script_string SET content_loc2='Votre misérable défi n\'est pas passé inaperçu. Je vais prendre plaisir à jouer avec vous avant de vous tuer.' WHERE entry=2000001027; /* content: Your pathetic challenge has not gone unnoticed. I shall enjoy toying with you before you die. */
UPDATE db_script_string SET content_loc2='Merci ! Je ne sais pas ce que ces brutes m\'auraient fait.' WHERE entry=2000001028; /* content: Thank you! There\'s no telling what those brutes would\'ve done to me. */
UPDATE db_script_string SET content_loc2='Nous pourrons nous faufiler sans qu\'ils nous voient. S\'il est possible d\'abattre l\'assemblage qui garde la porte, nous devrions pouvoir passer.' WHERE entry=2000001029; /* content: We can sneak past them. As long as we can take down that construct in front of the gate, we should be able to get inside. */
UPDATE db_script_string SET content_loc2='La nuish est pleinesh de chants joyeux…' WHERE entry=2000001031; /* content: Shcourged trooolls roashting.... */
UPDATE db_script_string SET content_loc2='Voilà ! Ch\'est tout che que j\'ai pour le momensh… Z\'en pensez quoi ?' WHERE entry=2000001034; /* content: That\'s it! Far as I got... Whatd\'ya think? */
UPDATE db_script_string SET content_loc2='Ça ne va pas du tout, $n. Dans l\'histoire que m\'a racontée la dompteuse des vents Barah, Sabot-tempête était un héros, pas un méchant.' WHERE entry=2000001042; /* content: This isn\'t right at all, $N. In the tale Wind Tamer Barah told me, Stormhoof was a hero, not a villain. */
UPDATE db_script_string SET content_loc2='Le Vent du Nord est un ancien ennemi des taunkas, un dirigeant des éléments, et la force même qui saigne la vie de ces peuples anciens.' WHERE entry=2000001043; /* content: The North Wind is the ancient enemy of the taunka, a ruler among the elements, and the very force which bleeds the life from these ancient people. */
UPDATE db_script_string SET content_loc2='Ils préféreraient mourir plutôt que se soumettre au Vent du Nord. Quelque chose ne va pas.' WHERE entry=2000001044; /* content: They would rather die than bow before the North Wind. Something is amiss here. */
UPDATE db_script_string SET content_loc2='Le marteau de la connaissance…' WHERE entry=2000001045; /* content: The Lorehammer... */
UPDATE db_script_string SET content_loc2='Ceci confirme nos soupçons. Les fils du Temps ont été coupés et retissés en suivant un modèle dénaturé. Mais comment ?' WHERE entry=2000001046; /* content: It confirms our suspicions. The threads of time have been severed and rewoven in the wrong pattern. But, how? */
UPDATE db_script_string SET content_loc2='RAH ! Oh non, ça m\'a tout engluée ! Ça colle ! MES CHEVEUUUX !' WHERE entry=2000001048; /* content: ACK! Oh no, I webbed myself! It\'s sticky. MY HAIR! */
UPDATE db_script_string SET content_loc2='C\'est juste…' WHERE entry=2000001061; /* content: It\'s just... */
UPDATE db_script_string SET content_loc2='Ils rangent leurs outils dans des boîtes qui ressemblent à celle-ci. Vous les trouverez près des pavillons.' WHERE entry=2000001065; /* content: They keep their tools in boxes that look like this. Look for them near the pavilions. */
UPDATE db_script_string SET content_loc2='Je ne vais graver que les runes qui, à mon avis, vous donneront le contrôle d\'un géant.' WHERE entry=2000001066; /* content: I\'m only going to carve the runes I think will give you command over a giant. */
UPDATE db_script_string SET content_loc2='Voilà. La rune de commandement. Enfin j\'espère. Loin d\'être aussi funeste que sa cousine des nains de fer, mais efficace.' WHERE entry=2000001068; /* content: There we go, the Rune of Command... hopefully. Not nearly as vicious as its Iron Dwarf cousin, but functional. */
UPDATE db_script_string SET content_loc2='Hourra !' WHERE entry=2000001090; /* content: Hooray! */
UPDATE db_script_string SET content_loc2='GLOIRE À L\'ALLIANCE !' WHERE entry=2000001094; /* content: GLORY TO THE ALLIANCE! */
UPDATE db_script_string SET content_loc2='Nous faufiler ? Vous nous prenez pour des mulots, ou quoi ?' WHERE entry=2000001100; /* content: Sneak?! What do you think we are, marmots? */
UPDATE db_script_string SET content_loc2='D\'accord. Si c\'est nos alliés qui doivent mettre les mains dans le cambouis, alors on va les laisser décider comment qu\'ils veulent s\'y prendre.' WHERE entry=2000001102; /* content: Fine. If our allies are going to be the ones getting their hands dirty, we\'ll leave it to them to decide how to proceed. */
UPDATE db_script_string SET content_loc2='Mais… Mais qu\'avez-vous fait, $n ? Brann ! BRAAAANNNN !' WHERE entry=2000001103; /* content: What... What did you just do, $n?! Brann! Braaaaannn! */
UPDATE db_script_string SET content_loc2='Brann ! $n vient d\'activer le système de défense orbitale ! Si on ne sort pas vite d\'ici, on va se faire carboniser !' WHERE entry=2000001104; /* content: Brann! $n just activated the orbital defense system! If we don\'t get out of here soon, we\'re going to be toast! */
UPDATE db_script_string SET content_loc2='Mages du Kirin Tor, au signal de Brann, levez le bouclier ! Défendez la plateforme et nos alliés de vos vies ! Pour Dalaran !' WHERE entry=2000001108; /* content: Mages of the Kirin Tor, on Brann\'s Command, release the shield! Defend this platform and our allies with your lives! For Dalaran! */
UPDATE db_script_string SET content_loc2='Nos alliés sont prêts. Levez le bouclier et faites place !' WHERE entry=2000001109; /* content: Our allies are ready. Bring down the shield and make way! */
UPDATE db_script_string SET content_loc2='On a réussi, les gars ! On est arrivés avant Algalon. On va p\'têt\' pouvoir bidouiller l\'système pour traficoter son analyse et -' WHERE entry=2000001111; /* content: We did it, lads! We got here before Algalon\'s arrival. Maybe we can rig the systems to interfere with his analysis-- */
UPDATE db_script_string SET content_loc2='J\'vais retourner à l\'Archivum et voir si j\'peux pas brouiller son signal. Ça devrait permettre de gagner du temps pendant qu\'vous vous occupez d\'lui.' WHERE entry=2000001112; /* content: I\'ll head back to the Archivum and see if I can jam his signal. I might be able to buy us some time while you take care of him. */
UPDATE db_script_string SET content_loc2='La température est de 122 degrés Kraklenheit.' WHERE entry=2000001113; /* content: The temperature is 122 degrees Kraklenheit. */
UPDATE db_script_string SET content_loc2='La température est de 9280 degrés Kraklenheit ! Il fait CHAUD !' WHERE entry=2000001114; /* content: The temperature is 9280 degrees Kraklenheit! That\'s HOT! */
UPDATE db_script_string SET content_loc2='DING ! 428000 degrés Kraklenheit, exactement ! Enfin, en gros. Presque. Pas trop loin, quoi...' WHERE entry=2000001116; /* content: DING! 428,000 degrees Kraklenheit, exactly! Well, approximately. Almost. Somewhere around there... */
UPDATE db_script_string SET content_loc2='Hé ! Arrêtez de fouiner dans ma cave ! Ouste !' WHERE entry=2000001119; /* content: Hey! Stop rooting around in my cellar! Clear out! */
UPDATE db_script_string SET content_loc2='Qu\'est-ce que vous fichiez dans ma cave ? Il y a pénurie de nourriture, alors la dernière chose dont j\'ai besoin, c\'est des étrangers qui viennent fouiner dans mes provisions ! Allez ouste !' WHERE entry=2000001120; /* content: What were you doing in my cellar? There\'s a food scare going on, and the last thing I need is strangers rummaging around in my goods! Shoo! */
UPDATE db_script_string SET content_loc2='Toute cette agitation, ça me semble légèrement paranoïaque, non ?' WHERE entry=2000001121; /* content: This whole situation seems a bit paranoid, don\'t you think? */
UPDATE db_script_string SET content_loc2='Les ordres, c\'est les ordres. Si le prince dit de sauter…' WHERE entry=2000001122; /* content: Orders are orders. If the Prince says jump... */
UPDATE db_script_string SET content_loc2='Ces ordres sont bizarres, personne ne peut le nier. De la nourriture suspecte ? Avec cette description, on devrait coffrer Belfast !' WHERE entry=2000001123; /* content: It\'s a strange order, you can\'t deny. Suspicious food? Under that definition, you should arrest Belfast! */
UPDATE db_script_string SET content_loc2='HÉ, J\'AI ENTENDU ! Plus de bière pour toi ! Plus une goutte !' WHERE entry=2000001124; /* content: I HEARD THAT! No more ale for you! Not a drop! */
UPDATE db_script_string SET content_loc2='Ça suffit, Michael. Les affaires vont déjà assez mal avec la pénurie actuelle. Chaque pièce de cuivre compte.' WHERE entry=2000001125; /* content: Enough, Michael. Business is hurting enough with this scare as it is. We can use every copper. */
UPDATE db_script_string SET content_loc2='Les soldats font un travail important. La santé des gens est le plus important, Mal, surtout si tu veux que nos clients restent vivants pour continuer à acheter.' WHERE entry=2000001126; /* content: The soldiers are doing important work. The safety of the people is more important, Mal, if you\'re interested in your customers living to spend another day. */
UPDATE db_script_string SET content_loc2='%s acquiesce à contrecœur.' WHERE entry=2000001127; /* content: %s grudgingly nods. */
UPDATE db_script_string SET content_loc2='Là, je peux pas lutter.' WHERE entry=2000001128; /* content: I can\'t argue with that. */
UPDATE db_script_string SET content_loc2='Ne vous en faites pas trop. Quand j\'ai terminé mon service, on n\'avait pas encore trouvé le moindre grain contaminé ici.' WHERE entry=2000001129; /* content: Don\'t worry too much. By the time I went off duty, we hadn\'t found a scrap of befouled grain here. */
UPDATE db_script_string SET content_loc2='Que la Lumière en soit remerciée.' WHERE entry=2000001130; /* content: Thank the Light for that. */
UPDATE db_script_string SET content_loc2='%s acquiesce.' WHERE entry=2000001131; /* content: %s nods. */
UPDATE db_script_string SET content_loc2='Hé, mais, rebonjour !' WHERE entry=2000001132; /* content: Why, hello again! */
UPDATE db_script_string SET content_loc2='Bon, assez travaillé pour l\'instant. Il est grand temps d\'aller se rafraîchir et bavarder un peu à l\'auberge.' WHERE entry=2000001133; /* content: Ok, enough work for now. Time for refreshments and a little conversation in the inn. */
UPDATE db_script_string SET content_loc2='Attendez, qu\'est-ce que c\'est que cette odeur ?' WHERE entry=2000001134; /* content: Wait, what\'s that smell? */
UPDATE db_script_string SET content_loc2='Ça peut pas être moi, j\'ai pris un bain y a trois jours !' WHERE entry=2000001135; /* content: Can\'t be me, I took a bath 3 days ago! */
UPDATE db_script_string SET content_loc2='Oh, pas loin. C\'est juste les grains, là.' WHERE entry=2000001136; /* content: Oh, close call. It\'s just the grain here. */
UPDATE db_script_string SET content_loc2='Euh, une seconde. Le grain, c\'est pas censé puer comme ça ! Je ferais mieux de trouver un garde.' WHERE entry=2000001137; /* content: Wait a second. Grain isn\'t supposed to smell like THAT! I better go find a guard. */
UPDATE db_script_string SET content_loc2='Ça ne vous dérange pas que je jette un œil à vos marchandises, voir si personne n\'y a touché, hein ?' WHERE entry=2000001138; /* content: You don\'t mind me checking out your merchandise for signs of tampering, do you? */
UPDATE db_script_string SET content_loc2='Non, sergent.' WHERE entry=2000001139; /* content: No, Sergeant. */
UPDATE db_script_string SET content_loc2='Attendez, qu\'est-ce que c\'est que ça ? Vous m\'avez caché des choses, Perelli !' WHERE entry=2000001140; /* content: Wait, what is this? You\'ve been holding out on me, Perelli! */
UPDATE db_script_string SET content_loc2='De quoi est-ce que vous parlez, sergent ?' WHERE entry=2000001141; /* content: What are you talking about, Sergeant! */
UPDATE db_script_string SET content_loc2='Je confisque ces sacs de grain suspects, Perelli. Nous étions à la recherche de nourriture trafiquée, et il serait dans votre intérêt de rester bien tranquille pendant que le prince Arthas y jette un œil.' WHERE entry=2000001142; /* content: I\'m confiscating this suspicious grain, Perelli. We were looking for signs of tampered food, and it would be in your best interest to stay put while Prince Arthas checks this out. */
UPDATE db_script_string SET content_loc2='Croyez-moi, je suis innocent !' WHERE entry=2000001143; /* content: You have to believe me, I\'m innocent! */
UPDATE db_script_string SET content_loc2='Nous verrons, Perelli. Nous verrons.' WHERE entry=2000001144; /* content: We\'ll see about that, Perelli. We\'ll see about that. */
UPDATE db_script_string SET content_loc2='Martha, je n\'ai plus de farine pour le pain. Est-ce qu\'il ne te resterait pas des grains du dernier arrivage ?' WHERE entry=2000001145; /* content: Martha, I\'m out of flour for bread. You wouldn\'t happen to have any grain from that recent shipment, would you? */
UPDATE db_script_string SET content_loc2='Oh, bonjour Jenna. Oui, bien sûr, tu peux prendre un peu de grain. Sers-toi.' WHERE entry=2000001146; /* content: Oh hello, Jena. Of course you can borrow some grain. Help yourself. */
UPDATE db_script_string SET content_loc2='Merci, Martha ! À charge de revanche !' WHERE entry=2000001147; /* content: Thanks, Martha! I owe you one. */
UPDATE db_script_string SET content_loc2='Martha, il y a quelque chose de pas normal dans ce grain ! C\'est ce que recherchaient les soldats du prince Arthas. Je vais essayer d\'en trouver un.' WHERE entry=2000001148; /* content: Martha, something\'s wrong with this grain! Some of the Prince\'s soldiers were looking for this. I\'m going to go look for one. */
UPDATE db_script_string SET content_loc2='Oh, grands dieux.' WHERE entry=2000001149; /* content: Oh, dear. */
UPDATE db_script_string SET content_loc2='Oh, diable.' WHERE entry=2000001150; /* content: Oh, my. */
UPDATE db_script_string SET content_loc2='On dirait qu\'il va y avoir de l\'orage, Barbouille…' WHERE entry=2000001151; /* content: Looks like a storm\'s coming in, Scruffy... */
UPDATE db_script_string SET content_loc2='%s se met à grogner…' WHERE entry=2000001152; /* content: %s begins to growl... */
UPDATE db_script_string SET content_loc2='Qu\'est-ce qu\'il y a, mon grand ?' WHERE entry=2000001153; /* content: What\'s wrong, pal? */
UPDATE db_script_string SET content_loc2='Qu\'est-ce que tu as trouvé, garçon ?' WHERE entry=2000001154; /* content: What did you find, boy? */
UPDATE db_script_string SET content_loc2='Oh, c\'est pas bon, ça, Barbouille. Reste-là, garde la maison, il faut que je trouve un soldat.' WHERE entry=2000001155; /* content: This is no good, Scruffy. Stay here and guard the house, I need to go find a soldier. */
UPDATE db_script_string SET content_loc2='Cette cargaison de grain ne m\'apporte que des ennuis !' WHERE entry=2000001156; /* content: This grain shipment has been nothing but trouble! */
UPDATE db_script_string SET content_loc2='Ma récompense pour le temps gagné va y passer !' WHERE entry=2000001157; /* content: I\'m going to lose my on time bonus because of this! */
UPDATE db_script_string SET content_loc2='Je n\'aurais même pas dû être là aujourd\'hui !' WHERE entry=2000001158; /* content: I wasn\'t even supposed to be here today! */
UPDATE db_script_string SET content_loc2='Bon, ben je suppose qu\'il faut que je remonte tout dans le wagon.' WHERE entry=2000001159; /* content: Well, guess I should load everything back into the cart. */
UPDATE db_script_string SET content_loc2='Oh, mais allez ! Mon chariot est cassé, mon cheval a perdu un fer, et maintenant c\'est la cargaison qui pourrit !' WHERE entry=2000001160; /* content: Oh, come on! My cart broke, my horse lost a shoe, and now the cargo goes bad! */
UPDATE db_script_string SET content_loc2='Vous m\'avez libéré ! Les vents parlent à nouveau à mon peuple et lui apportent leur force. Soyez-en $gremercié, étranger:remerciée, étrangère;.' WHERE entry=2000001200; /* content: You\'ve freed me!  The winds speak to my people once again and grant us their strength.  I thank you, stranger. */
UPDATE db_script_string SET content_loc2='Parfait. Tant que vous ne vous y frayez pas un chemin à grands coups d\'épée, ils n\'y verront que du feu.' WHERE entry=2000001214; /* content: Perfect.  As long as you don\'t go hacking and slashing your way through, they\'ll never know the difference. */
UPDATE db_script_string SET content_loc2='Il veut que je fasse QUOI ?' WHERE entry=2000001215; /* content: He wants me to do WHAT? */
UPDATE db_script_string SET content_loc2='Ils n\'abandonneront pas avant de l\'avoir trouvé. On ne peut pas laisser faire ça, $n, et Brann le sait bien.' WHERE entry=2000001228; /* content: They\'re not going to give up until they find him. We can\'t allow that to happen, $N, and Brann knows it. */
UPDATE db_script_string SET content_loc2='Cette note comporte des instructions un peu cryptiques. Brann devait avoir une sorte de plan de secours, mais il n\'arrivera pas à échapper à la capture éternellement…' WHERE entry=2000001229; /* content: There are some rather cryptic instructions in this note. Brann must\'ve had some kind of backup plan, but he can\'t keep evading capture forever... */
UPDATE db_script_string SET content_loc2='C\'est à vous de le trouver avant les nains de fer !' WHERE entry=2000001230; /* content: It\'s up to you to find him before the irons do! */
UPDATE db_script_string SET content_loc2='Merci.' WHERE entry=2000001231; /* content: Hey, thanks. */
