UPDATE `locales_npc_text` SET `Text0_0_loc3`='Magier? Sie sind im Magieviertel, im Zentralgebäude. Hätte gedacht, Ihr wärt klug genug um soweit mitzudenken.',`Text0_1_loc3`='Am Äußeren Ring des Magieviertels werdet Ihr ein großes Gebäude finden, zu dem Treppen führen. Dort verstecken sich die Magier.',`Text1_0_loc3`='Am Äußeren Ring des Magieviertels werdet Ihr ein großes Gebäude finden, zu dem Treppen führen. Dort verstecken sich die Magier.',`Text1_1_loc3`='Am Äußeren Ring des Magieviertels werdet Ihr ein großes Gebäude finden, zu dem Treppen führen. Dort verstecken sich die Magier.' WHERE `entry`=14917;
