UPDATE db_script_string SET content_loc3='Hmm, sorgt Euch nicht um mich!' WHERE entry=2000000166; /* content: Hmm, don\'t mind if I do! */
UPDATE db_script_string SET content_loc3='Sie sagen er kann sich manchmal in einen Raben verwandeln.' WHERE entry=2000000167; /* content: They say he can turn into a raven sometimes. */
UPDATE db_script_string SET content_loc3='%s starrt nach Nordwesten und lässt ein langes, herausgezogenes Heulen von sich.' WHERE entry=2000000168; /* content: And then the rabbit just bit his head off... I swear. */
UPDATE db_script_string SET content_loc3='%s lässt ein mächtiges Heulen von sich.' WHERE entry=2000000169; /* content: I swear, people have actualy seen them. Pandaren really do exist!! */
UPDATE db_script_string SET content_loc3='%s beginnt einen Ritus der Entstehung.' WHERE entry=2000000170; /* content: You know why Orcs eyes glow red? It\'s because they drink blood! */
UPDATE db_script_string SET content_loc3='Ich habe das Totem geschaffen. Ihr, $N, müsst es gut reinigen.' WHERE entry=2000000171; /* content: I dont think there\'s any fish in these canals. */
UPDATE db_script_string SET content_loc3='%s erschafft ein neues Totem...' WHERE entry=2000000172; /* content: And that\'s how Lother killed thirty six orcs with his bare hands! */
UPDATE db_script_string SET content_loc3='Das Totem ist fertig, $N' WHERE entry=2000000173; /* content: Eww... that\'s not a fish! */
UPDATE db_script_string SET content_loc3='%s verwandelt die Gegenstände, welche $N ihm gab, in ein Totem der Reinigung' WHERE entry=2000000174; /* content: I got worm guts on my shoes. */
UPDATE db_script_string SET content_loc3='Das Totem wird erschaffen. Es kann nun gut gereinigt werden.' WHERE entry=2000000175; /* content: I think i see something. */
UPDATE db_script_string SET content_loc3='Danke, $N. Und nun, um den Test durchzuführen...' WHERE entry=2000000176; /* content: I hope that was a fish! */
UPDATE db_script_string SET content_loc3='Ah, ich verstehe...' WHERE entry=2000000177; /* content: Worm goes on the hook, hook goes in the water, Fish is in the water, our fish. */
UPDATE db_script_string SET content_loc3='Ja, das ist ungünstig.' WHERE entry=2000000178; /* content: Can you imagine? */
UPDATE db_script_string SET content_loc3='$N, ich kann bestätigen, dass dieses Blut vom Orcklan der Knochenmalmer stammt, der unter dämonischem Einfluss steht. Ich fürchte aber, mit meiner momentanen Ausrüstung kann ich dieses Geheimnis nicht näher ergründen...' WHERE entry=2000000179; /* content: Really? */
UPDATE db_script_string SET content_loc3='Sprecht mit Schwadronskommandant Brack, er wird Euch einen unserer Flügeldrachenzerstörer geben. Und, viel Glück!' WHERE entry=2000000180; /* content: Wow. */
UPDATE db_script_string SET content_loc3='Vielen Dank, $N! Es sieht ganz so aus, als kämen sie wunderbar zurecht.' WHERE entry=2000000181; /* content: My father says that\'s just a story. */
UPDATE db_script_string SET content_loc3='Ich habe eine Idee. Lasst uns diesen Splitter in die Erde stecken und schauen, was passiert.' WHERE entry=2000000182; /* content: That\'s neat. */
UPDATE db_script_string SET content_loc3='Das Böse, das der Kristall ausstrahlt, hat einen der Kolosse angelockt. Vernichtet ihn, schnell!' WHERE entry=2000000183; /* content: I\'ll never stop. Never... */
UPDATE db_script_string SET content_loc3='Dort wird es vollendet.' WHERE entry=2000000184; /* content: Hello, Charys. Here my list, can you find me all that, and especially the last ingredient? */
UPDATE db_script_string SET content_loc3='Samia, wisst Ihr wer Kolphis Dunkelschuppe ist?' WHERE entry=2000000185; /* content: Of course, Paige. But be nice. */
UPDATE db_script_string SET content_loc3='Ja, er ist der Stellvertreter des Wyrmkults am Pechschwingenzikel im Westen.' WHERE entry=2000000186; /* content: Thank you, Charys. Let us go Paige, my angel. */
UPDATE db_script_string SET content_loc3='Die Arakkoa liegen im Verborgenem... überall!' WHERE entry=2000000187; /* content: When we arrive ? */
UPDATE db_script_string SET content_loc3='Ruht Euch nun aus, $GFreund:Freundin;. Ihr braucht Eure Kräfte.' WHERE entry=2000000188; /* content: Where we going ? */
UPDATE db_script_string SET content_loc3='Ich hätte nie gedacht, dass es Manaöl werden würde! Dafür herrscht genug Bedarf! Außer von Grek natürlich.' WHERE entry=2000000189; /* content: Why we going by there ? */
UPDATE db_script_string SET content_loc3='Jackpot!' WHERE entry=2000000190; /* content: Billy says that formerly Fizzle was a large wizard. But it was transformed into rabbit because of a spell which went wrong. */
UPDATE db_script_string SET content_loc3='%s riecht das Fleisch, erwacht aus seinem Schlaf und brüllt vor Wut!' WHERE entry=2000000191; /* content: I want to see the Tower of the mages */
UPDATE db_script_string SET content_loc3='Warum müssen wir immer den gleichen Weg gehen?' WHERE entry=2000000192; /* content: Why do we always go the same way? */
UPDATE db_script_string SET content_loc3='Armer Dorius. Wenn ich diese Dunkeleisenzwerge in die Finger bekomme, hat ihr letztes Stündlein geschlagen!' WHERE entry=2000000194; /* content: Poor Dorius. If I ever get my hands on those Dark Irons, so help me... */
UPDATE db_script_string SET content_loc3='Wie Ihr befehlt...' WHERE entry=2000000195; /* content: It\'s getting away! */
UPDATE db_script_string SET content_loc3='Raliq wird\'s Euch jetzt zeigen!' WHERE entry=2000000196; /* content: I know you are there, rougue, Leave my home or join the others at the bottom of the world tree. */
UPDATE db_script_string SET content_loc3='F\'woooroonn! Kusch\'kusch bezahlt nichts! Sterbt!' WHERE entry=2000000197; /* content: Filth! Filth everywhere! The forests must be Cleansed */
UPDATE db_script_string SET content_loc3='Ich entscheide mich für die dritte Option: EUCH TÖTEN!' WHERE entry=2000000198; /* content: Come one, come all! Welcome to the Darkmoon Faire! Do you crave adventure? Do you seek exotic and mysterious treasures? Then look no further! You, my friend, have come to the right place! Dive right in and take part in all that the Faire has to offer! We\'ll be at this location all week, so be sure to tell your friends and loved ones! */
UPDATE db_script_string SET content_loc3='Mammi? Kann ich Frizzles streicheln?' WHERE entry=2000000200; /* content: Mommy? Can I pet Fizzles? */
UPDATE db_script_string SET content_loc3='Ducken!' WHERE entry=2000000201; /* content: Duck! */
UPDATE db_script_string SET content_loc3='Athrikus ist zu uns gekommen. Er sagte, dass er uns vor Bösem bewahren könne. Er hat einen Schutzzauber auf uns gewirkt, der uns vor dem Bösen beschützen soll.' WHERE entry=2000000202; /* content: Don\'t go close to the lake! It\'s haunted! */
UPDATE db_script_string SET content_loc3='Als die Erschütterungen endeten, kamen seine wahren Motive ans Licht. Wir waren gefangen und er entzog uns langsam unsere Macht.' WHERE entry=2000000203; /* content: I will do my best to take care of the children, Grandmother. */
UPDATE db_script_string SET content_loc3='Hunderte wurden durch seinen Zauber gefangen gehalten. Jetzt sind nur noch einige wenige in ihren Gefängnissen. Von Zeit zu Zeit redet er mit uns.' WHERE entry=2000000204; /* content: Well hand it overand let\'s see what you got! */
UPDATE db_script_string SET content_loc3='Er fürchtete, seine Macht würde schwinden und dass bald selbst wir, die letzten seiner kostbaren Seelenedelsteine, verblassen und sterben würden.' WHERE entry=2000000205; /* content: Great Gazlowe! */
UPDATE db_script_string SET content_loc3='Sein Leutnant, Ilkrud Magskluve, besitzt einen Folianten, der Hinweise auf den Verbleib der letzten Seelensteine geben könnte.' WHERE entry=2000000206; /* content: I have another reading from the nether. */
UPDATE db_script_string SET content_loc3='%s riecht das Elixier und gräbt eifrig weiter!' WHERE entry=2000000207; /* content: Right. We just busted our tails to haul all of this stuff up here, and you want us to work more? */
UPDATE db_script_string SET content_loc3='Es ist eine lange Zeit gewesen, Bethor, mein Freund' WHERE entry=2000000208; /* content: The wind speaks of our enemies\' movements, Ashyen. The Naga will attack again. */
UPDATE db_script_string SET content_loc3='Wenn wir Zeit haben, müssen wir ausfürhlicher reden. Wir haben viel zu besprechen.' WHERE entry=2000000209; /* content: Very well. When that decision is made, I will be ready. Let us hope it won\'t be too late. */
UPDATE db_script_string SET content_loc3='Und danke, $N. Ohne Eure Hilfe hätte ich nie den Weg zu den Verlassenen gefunden.' WHERE entry=2000000210; /* content: I\'m up to looking into that bounty after this shift. */
UPDATE db_script_string SET content_loc3='Lebt wohl, mein Freund' WHERE entry=2000000211; /* content: You want to go do the bounty off of this wanted poster after this shift is over? */
UPDATE db_script_string SET content_loc3='%s beschwört einen Boten...' WHERE entry=2000000212; /* content: Be the first on your block to own the incredible Electrostatic Maculation Blaster! */
UPDATE db_script_string SET content_loc3='Geht mein Diener. Sendet die Worte der Steinkrallen zu Apothekerin Zamah.' WHERE entry=2000000213; /* content: Derived from cutting edge Shattered Hand Offensive military technology, it blasts away stubborn stains with ease! */
UPDATE db_script_string SET content_loc3='%s übergibt Apothekerin Zamah eine Nachricht.' WHERE entry=2000000214; /* content: Wine stains, grass stains, even pet stains are no match for the Electrostatic Maculation Blaster! */
UPDATE db_script_string SET content_loc3='%s lächelt.' WHERE entry=2000000215; /* content: Don\'t wait! Order now and receive my Ultra-Kinetic Waffle Iron at no additional charge! */
UPDATE db_script_string SET content_loc3='Es wird langsam, Waffelfritte, es wird... Hast du etwa keine Produktpräsentation zu machen, oder was?' WHERE entry=2000000216; /* content: It\'s coming along, Wafflefry. Haven\'t you got a product demonstration to do or something? */
UPDATE db_script_string SET content_loc3='Ich werde wohl Jagd auf diesen Getriebenen machen, jetzt, da eine Belohnung auf seinen Kopf aussteht.' WHERE entry=2000000220; /* content: I might just have to go hunting for that Wretched beast now that there\'s a bounty on his head. */
UPDATE db_script_string SET content_loc3='Hey, kann mir jemand helfen dies hier zu fangen!' WHERE entry=2000000231; /* content: Hey, someone help me catch this thing! */
UPDATE db_script_string SET content_loc3='Wenn Ihr zu lange wartet, verbleibt nicht mehr viel von Eurer Zukunft für meine Voraussagen!' WHERE entry=2000000240; /* content: The longer you wait, the less future you have for me to foretell. */
UPDATE db_script_string SET content_loc3='Nehmt eine Kerze des Heranlockens aus dieser Kiste, $n.' WHERE entry=2000000244; /* content: Take a Candle of Beckoning from this crate, $N. */
UPDATE db_script_string SET content_loc3='M... m... mir... geht... es... nicht... gut...' WHERE entry=2000000245; /* content: I. . .I. . .don\'t. . .feel. . .right. . . */
UPDATE db_script_string SET content_loc3='Mein Verstand... mein Fleisch... ich... verwese...!' WHERE entry=2000000246; /* content: My mind. . .my flesh. . .I\'m. . .rotting. . . .! */
UPDATE db_script_string SET content_loc3='Der Meister hat viel riskiert, um an Informationen über den Verbleib der Exodar zu kommen. Deinetwegen wäre beinahe die gesamte Unternehmung geplatzt, einschließlich der Identität unseres Spions im innersten Kreis des Feindes.' WHERE entry=2000000248; /* content: The Master has gone to great lengths to secure information about the whereabouts of the Exodar. You could have blown the entire operation, including the cover of our spy on the inside. */
UPDATE db_script_string SET content_loc3='Onu betrachtet das Pergament aus dem Folianten der Schattenhammerkultisten...' WHERE entry=2000000249; /* content: Onu studies the parchment from the Twilight Tome... */
UPDATE db_script_string SET content_loc3='Onu wird von freigesetzter Magie getroffen' WHERE entry=2000000250; /* content: Onu is struck by unleashed magic! */
UPDATE db_script_string SET content_loc3='Hmmm...' WHERE entry=2000000251; /* content: Hmm... */
UPDATE db_script_string SET content_loc3='Ich dachte wir haben über dies gesprochen, Brogg. Du kannst in diesem Gebäude nicht bleiben. Näturlich, du kannst darin wohnen, aber es ist kein Platz für irgendjemand Anderen!' WHERE entry=2000000252; /* content: Terenthis begins to cast a spell as you take the cloak from him. */
UPDATE db_script_string SET content_loc3='Ja, wir haben gesagt, dass Ihr hier bleiben könnt, aber dieses Gebäude ist unser Hauptquartier! All unsere Unterlagen und Pläne werden hier aufbewahrt!' WHERE entry=2000000253; /* content: Go now, $N. Find Volcor... and remember, the magics protecting you will fail if you interact with anyone, save for talking. */
UPDATE db_script_string SET content_loc3='Diesen Seelenstein zu zerbrechen ist eine ziemlich einfache Aufgabe!' WHERE entry=2000000255; /* content: A simple enough task to break this soulstone! */
UPDATE db_script_string SET content_loc3='Leuchtender Seelenstein springt auf!' WHERE entry=2000000256; /* content: Glowing Soulgem bursts open! */
UPDATE db_script_string SET content_loc3='Dann müssen wir Athrikus Narassin beseitigen. Sagt mir, Geist, wo kann ich die anderen Seelensteine finden, um seine Magie rückgängig zu machen?' WHERE entry=2000000257; /* content: Then we must put an end to Athrikus Narassin. Tell me, spirit, where might we find the other soulgems, to undo his magic? */
UPDATE db_script_string SET content_loc3='Ich sollte jetzt sofort beginnen, $n.' WHERE entry=2000000259; /* content: I shall get started right away, $N. */
UPDATE db_script_string SET content_loc3='So... wo sind Nadel und Faden? Ah! Da sind sie ja...' WHERE entry=2000000260; /* content: Now... where was my thread and needle. Ah! There it is... */
UPDATE db_script_string SET content_loc3='Beeilt Euch, $n. Benutzt das Portal!' WHERE entry=2000000261; /* content: Make haste, $N. Use the portal! */
UPDATE db_script_string SET content_loc3='Na gut, sehen wir uns das Ding da mal an...' WHERE entry=2000000262; /* content: Ok, let\'s take a look at this thing... */
UPDATE db_script_string SET content_loc3='Oh, verdammt...' WHERE entry=2000000263; /* content: Oh, darn... */
UPDATE db_script_string SET content_loc3='Die Geister des Steinkrallengebirges wüten noch immer, $N.' WHERE entry=2000000264; /* content: The spirits of Stonetalon still rage, $N. */
UPDATE db_script_string SET content_loc3='Ich fürchte, wir werden sie niemals beschwichtigen können...' WHERE entry=2000000265; /* content: I fear we may never soothe them... */
UPDATE db_script_string SET content_loc3='... Uups.' WHERE entry=2000000267; /* content: ... oops. */
UPDATE db_script_string SET content_loc3='He, $N! Vergesst nicht, eine Kiste, ein Handbuch und einen Leitstecken mitzunehmen!' WHERE entry=2000000268; /* content: Hey $N! Don\'t forget to grab a crate, a manual and a command stick! */
UPDATE db_script_string SET content_loc3='Habt abermals Dank für Eure Hilfe. Möge Euch bei Euren Reisen der Wind immer im Rücken stehen.' WHERE entry=2000000269; /* content: Thank you again for your aid. May the wind always be at your back during your travels. */
UPDATE db_script_string SET content_loc3='%s hört sich die Bitte von $N an.' WHERE entry=2000000270; /* content: %s listens to $N\'s plea. */
UPDATE db_script_string SET content_loc3='Ihr habt gut daran getan zu kommen. Meine Weisheit gehört Euch.' WHERE entry=2000000271; /* content: You were right to come. My wisdom will be yours. */
UPDATE db_script_string SET content_loc3='%s erschafft sein Toxin...' WHERE entry=2000000272; /* content: %s creates his toxin... */
UPDATE db_script_string SET content_loc3='Da wären wir, $N. Das Toxin steht bereit.' WHERE entry=2000000273; /* content: There we are, $N. The toxin is ready. */
UPDATE db_script_string SET content_loc3='%s füllt einen Behälter mit dem Toxin.' WHERE entry=2000000274; /* content: %s fills a container with the toxin. */
UPDATE db_script_string SET content_loc3='Das Toxin ist fertig, $N.' WHERE entry=2000000275; /* content: The toxin is ready, $N. */
UPDATE db_script_string SET content_loc3='Okay, Leute. Macht euch an die Arbeit und bringt diese Knochen an.' WHERE entry=2000000276; /* content: Okay, crew. Get to work on getting these bones installed. */
UPDATE db_script_string SET content_loc3='%s saugt etwas Traumstaub in die Nase...' WHERE entry=2000000277; /* content: %s snorts a speck of dream dust up his nose... */
UPDATE db_script_string SET content_loc3='%s ist benommen...' WHERE entry=2000000278; /* content: %s is dazed... */
UPDATE db_script_string SET content_loc3='Hatschi!' WHERE entry=2000000279; /* content: ACHOO! */
UPDATE db_script_string SET content_loc3='Unglaublich! $N, dieser Traumstaub ist aber ein Teufelszeug!' WHERE entry=2000000280; /* content: Wow! $N, that dream dust is powerful stuff! */
UPDATE db_script_string SET content_loc3='%s trinkt einen großen Schluck Bier.' WHERE entry=2000000281; /* content: %s takes big swig of ale. */
UPDATE db_script_string SET content_loc3='Das war erfrischend. Jetzt zu den Informationen, die ich loswerden muss...' WHERE entry=2000000282; /* content: That was refreshing. Now there\'s information that needs to be told... */
UPDATE db_script_string SET content_loc3='Ich denke, dass die Verlassenen die Verbündeten der horde in die Irre führen... wartet... ich fühle mich so... benommen...' WHERE entry=2000000283; /* content: I believe the Forsaken are misleading the allies of the Horde... wait... I feel so... dizzy... */
UPDATE db_script_string SET content_loc3='%s windet sich vor Schmerzen.' WHERE entry=2000000284; /* content: %s writhes in pain. */
UPDATE db_script_string SET content_loc3='AAAAAAAARGH!' WHERE entry=2000000285; /* content: AAAAAAAAAAAAAAAARGH! */
UPDATE db_script_string SET content_loc3='Hmm... ich frage mich...' WHERE entry=2000000286; /* content: Hmm... I wonder... */
UPDATE db_script_string SET content_loc3='Nun, wo ist es?' WHERE entry=2000000287; /* content: Now, where is it? */
UPDATE db_script_string SET content_loc3='Dumme Magier... verbrauchen alle Reagenzien und besorgen nie Ersatz.' WHERE entry=2000000288; /* content: Stupid mages... always using the last of a reagent and never replacing it. */
UPDATE db_script_string SET content_loc3='Ich brauche nur wenige Minuten, um das Serum zuzubereiten, $n.' WHERE entry=2000000289; /* content: The serum will only take a few minutes to prepare, $N. */
UPDATE db_script_string SET content_loc3='Fast fertig...' WHERE entry=2000000290; /* content: Almost done... */
UPDATE db_script_string SET content_loc3='Endlich ist das Serum vollendet, $N. Kommt jetzt näher. Wir müssen reden.' WHERE entry=2000000291; /* content: At last, the serum is complete, $N. Now, come closer. We must talk. */
UPDATE db_script_string SET content_loc3='Ruhig bleiben? Hast du auch nur eine Ahnung davon, was Kael\'thas mit denjenigen macht, die ihn enttäuschen, Geezle? Endloses Leiden und Schmerzen... es darf NICHTS schief gehen, Trottel.' WHERE entry=2000000292; /* content: Relax? Do you know what Kael\'thas does to those that fail him, Geezle? Eternal suffering and pain... Do NOT screw this up, fool. */
UPDATE db_script_string SET content_loc3='Unsere Späher auf der Blutmythosinsel haben unseren Kontakt ausfindig gemacht. Dieser Narr, Velen, wird schon bald einen Moment nicht Acht geben und uns dadurch schutzlos ausgeliefert sein. Lange genug, damit wir zuschlagen können! Und nun geh mir aus den Augen, bevor ich dich töte...' WHERE entry=2000000293; /* content: Our Bloodmyst scouts have located our contact. The fool, Velen, will soon leave himself open and defenseless -- long enough for us to strike! Now get out of my sight before I vaporize you... */
UPDATE db_script_string SET content_loc3='Endlich frei! Ich muss sogleich zur Kriegshymnenfeste zurückkehren!' WHERE entry=2000000294; /* content: Freedom at last! I must return to Warsong Hold at once! */
UPDATE db_script_string SET content_loc3='Ich stehe auf ewig in Eurer Schuld, Freund.' WHERE entry=2000000295; /* content: I am forever indebted to you, friend. */
UPDATE db_script_string SET content_loc3='Danke vielmals.' WHERE entry=2000000296; /* content: Thank you, friend. */
UPDATE db_script_string SET content_loc3='Mein Dank sei mit Euch, $GFremder:Fremde;.' WHERE entry=2000000297; /* content: You have my thanks, stranger. */
UPDATE db_script_string SET content_loc3='Ich fühle mich... anders.' WHERE entry=2000000299; /* content: I feel... different. */
UPDATE db_script_string SET content_loc3='Nix mehr Herr Nett-zu-Wolvar!' WHERE entry=2000000300; /* content: No more mister nice wolvar! */
UPDATE db_script_string SET content_loc3='Zornklaue frei! Troll jetzt sterben!' WHERE entry=2000000301; /* content: Rageclaw free! Troll die now! */
UPDATE db_script_string SET content_loc3='Metallische Probe fremden Ursprungs ermittelt.' WHERE entry=2000000303; /* content: Metallic specimen of foreign origin detected. */
UPDATE db_script_string SET content_loc3='Exemplar identifiziert als Legierung aus Saronit und Eisen.' WHERE entry=2000000305; /* content: Specimen identified as an alloy of saronite and iron. */
UPDATE db_script_string SET content_loc3='Das Saronit in dieser Probe ist beinahe rein. Erze wie diese kommen nicht natürlich auf der Oberfläche Azeroths vor.' WHERE entry=2000000307; /* content: The saronite in this sample is nearly pure. Ores like this do not occur naturally on the surface of Azeroth. */
UPDATE db_script_string SET content_loc3='Zugriff verweigert?!' WHERE entry=2000000309; /* content: Access denied?! */
UPDATE db_script_string SET content_loc3='Auf weitere Informationen kann auf Befehl des Hüters Loken nur in den Archiven innerhalb Ulduars zugegriffen werden.' WHERE entry=2000000310; /* content: Further information can only be accessed from the archives within Ulduar, by order of Keeper Loken. */
UPDATE db_script_string SET content_loc3='Aber eines ist gewiss: die Größe und Dicke dieser Platte sowie die Löcher für die Bolzen weisen auf ihre Verwendung als Rüstung für eine riesige Statue hin.' WHERE entry=2000000311; /* content: But one thing is for certain: this plate\'s size, thickness, and bolt holes point to a use as armor for a colossal structure. */
UPDATE db_script_string SET content_loc3='Anfrage ausgeführt. Kehre in Bereitschaftsmodus zurück.' WHERE entry=2000000312; /* content: Directive completed. Returning to standby mode. */
UPDATE db_script_string SET content_loc3='Ich... ich kann Euch plötzlich verstehen! Tja, nun, da wir miteinander reden können, habt Ihr mir einiges zu erklären!' WHERE entry=2000000313; /* content: I... I can understand you now! Well, now that we can talk to each other, you have some explaining to do! */
UPDATE db_script_string SET content_loc3='Wie habt Ihr meinen Kommunikator von der Forscherliga bekommen?' WHERE entry=2000000314; /* content: How did you get my communicator from the Explorers\' League? */
UPDATE db_script_string SET content_loc3='Ihr habt meinen Dank dafür, dass Ihr die Eisenzwerge beseitigt habt. Aber warum sollte die Horde ein Interesse an mir haben?' WHERE entry=2000000315; /* content: You have my thanks for dispatching the iron dwarves. But why would the Horde have an interest in me? */
UPDATE db_script_string SET content_loc3='Das ist doch wohl nicht Späher Vor\'takh, oder? Selbst ich habe von seinem skrupellosen Ruf gehört. Er will mich also entführen?' WHERE entry=2000000316; /* content: That wouldn\'t be Scout Vor\'takh, would it? Even I know of his reputation for ruthlessness. He means to abduct me, then? */
UPDATE db_script_string SET content_loc3='Wenn Ihr das Tagebuch gesehen habt, dann wisst Ihr ja, was ich herausgefunden habe. Der Titanen eigene Schöpfungen führen Krieg gegeneinander!' WHERE entry=2000000317; /* content: If you\'ve seen the journal, then you know what I\'ve been discovering. The titans\' own creations are at war with each other! */
UPDATE db_script_string SET content_loc3='Loken und seine Lakaien, die Eisenzwerge, haben die Irdenen aus Ulduar vertrieben und die Stadt übernommen.' WHERE entry=2000000318; /* content: Loken and his iron dwarf minions have ousted the Earthen from Ulduar and taken over the city. */
UPDATE db_script_string SET content_loc3='Wenn wir unsere Zeit und Energie damit verschwenden, dass wir gegeneinander kämpfen, wird Loken Ulduars Ressourcen nutzen, um sowohl die Horde als auch die Allianz zu zerstören.' WHERE entry=2000000319; /* content: If we spend our time and strength fighting with each other, Loken will use Ulduar\'s resources to destroy both Horde and Alliance. */
UPDATE db_script_string SET content_loc3='Wenn die Forscherliga Männer gesandt hat, dann habe ich keine Spur von ihnen gesehen. Ich habe Eure Nachricht in einem von den Eisenzwergen überrannten Lager gefunden.' WHERE entry=2000000321; /* content: If the Explorers\' League sent men, I didn\'t see any trace of them. I found your note buried in a camp overrun by iron dwarves. */
UPDATE db_script_string SET content_loc3='Ein Späher hat die Überbleibsel Eures Tagebuchs in Thor Modan gefunden. Seither haben wir nach Euch gesucht.' WHERE entry=2000000322; /* content: A scout found the remains of your journal in Thor Modan. We\'ve been looking for you ever since. */
UPDATE db_script_string SET content_loc3='Corbett, mein Teurer. Harlan benötigt so schnell wir können eine Ladung gestrickter Hemden und Hosen.' WHERE entry=2000000323; /* content: Corbett, dear. Harlan needs a load of knitted shirts and pants as soon as we can manage. */
UPDATE db_script_string SET content_loc3='Corbett, seid Ihr da? Harlan benötigt noch eine Ladung Strickwaren. Könnt Ihr sie ihm bringen?' WHERE entry=2000000324; /* content: Corbett, you there? Harlan needs another load of knitted goods. Can you take it to him? */
UPDATE db_script_string SET content_loc3='Die Geschäfte im Basar scheinen ja gut zu laufen. Ich werde ihm sofort Nachschub bringen!' WHERE entry=2000000326; /* content: Business must be good down at the bazaar. I\'ll get him resupplied right away! */
UPDATE db_script_string SET content_loc3='Hm... wenn ich das hier losgeworden bin, werde ich vielleicht in dieses Käsegeschäft gehen und mir dort einen kleinen Imbiss genehmigen.' WHERE entry=2000000327; /* content: Hm...after dropping this off, I think I\'ll head to that cheese shop for a snack. */
UPDATE db_script_string SET content_loc3='Ich müsste doch ein paar Münzen bei diesem Geschäft verdient haben. Vielleicht kaufe ich mir ein Mittagessen...' WHERE entry=2000000328; /* content: I should have a few extra coins from this sale. Maybe I\'ll buy myself some lunch... */
UPDATE db_script_string SET content_loc3='He, Harlan. Hier ist eine Ladung Strickstoff für Euch.' WHERE entry=2000000329; /* content: Hey Harlan. Here\'s a load of knitted cloth for you. */
UPDATE db_script_string SET content_loc3='Uff! Hier ist eine Ladung an Vorräten, Harlan. Euer Geschäft scheint ja gut zu laufen!' WHERE entry=2000000330; /* content: Oomph! Here\'s a load of supplies. It must be selling fast! */
UPDATE db_script_string SET content_loc3='Ah ja, und pünktlich geliefert. Es ist wie immer ein Vergnügen, mit Euch Geschäfte zu machen, Corbett.' WHERE entry=2000000331; /* content: Ah yes, and promptly delivered. As always, it\'s a pleasure doing business with you, Corbett. */
UPDATE db_script_string SET content_loc3='Ah, das weiß ich sehr zu schätzen, Corbett. Wir werden alles sofort in die Regale räumen.' WHERE entry=2000000332; /* content: Ahh, much appreciated, Corbett. We\'ll get these on the racks immediately. */
UPDATE db_script_string SET content_loc3='Dann will ich mal wieder. Macht\'s gut, Harlan.' WHERE entry=2000000334; /* content: Well, I am off than. Take care, Harlan! */
UPDATE db_script_string SET content_loc3='Hallo, Triasklan! Eine Kugel von Eurem geräucherten Mozzarella, bitte!' WHERE entry=2000000336; /* content: Hullo, Trias clan! A ball of your smoked mozzarella, if you please! */
UPDATE db_script_string SET content_loc3='Guten Tag, Elling! Hallo Elaine! Kann ich einen Laib Blaukäse bekommen?' WHERE entry=2000000337; /* content: Good day. Elling! Hullo Elaine! Let me have a wheel of blue cheese, eh? */
UPDATE db_script_string SET content_loc3='Guten Tag, Corbett. Hier ist Euer Käse. Heute Morgen ganz frisch gemacht! Und wie läuft Euer Laden?' WHERE entry=2000000338; /* content: Good day, Corbett. Here\'s your cheese, fresh made this morning! And how are things at your shop? */
UPDATE db_script_string SET content_loc3='Hallo, Corbett! Bitte sehr! Sehe ich es richtig, dass Euer Tuchladen gut läuft...?' WHERE entry=2000000339; /* content: Hi Corbett! Here, you go! I trust business is faring well at your clothier shop...? */
UPDATE db_script_string SET content_loc3='Ja, gnädige Frau, das Geschäft läuft gut!' WHERE entry=2000000340; /* content: Yes ma\'am, business is brisk! */
UPDATE db_script_string SET content_loc3='Danke für den Käse!' WHERE entry=2000000341; /* content: Thanks for the cheese! */
UPDATE db_script_string SET content_loc3='Ich bin zurück!' WHERE entry=2000000343; /* content: I\'m back! */
UPDATE db_script_string SET content_loc3='JIIAUTSCH!' WHERE entry=2000000344; /* content: YEEOOWCH! */
UPDATE db_script_string SET content_loc3='Ich habe vor nichts Angst... immer her damit!' WHERE entry=2000000347; /* content: I\'m not afraid of anything -- bring it on! */
UPDATE db_script_string SET content_loc3='Erhebt Euch, meine Brüder!' WHERE entry=2000000348; /* content: Arise from the earth, my brothers! */
UPDATE db_script_string SET content_loc3='Unsere Meister des Eisens haben eine Mission für uns!' WHERE entry=2000000349; /* content: Our iron masters have a mission for us! */
UPDATE db_script_string SET content_loc3='Kommt mit mir in die Berge, um den Plan der Meister auszuführen!' WHERE entry=2000000350; /* content: Follow me into the mountains to carry out the plan of the masters! */
UPDATE db_script_string SET content_loc3='Dank Euch, großer Geist. Danke!' WHERE entry=2000000355; /* content: Thank you great spirit. Thank you! */
UPDATE db_script_string SET content_loc3='Ma reth bromo zoln kilagrin dra ma zoern tu ko fraht ka tadrom Ma krin drinor zaln dirnor Ma krin kan aasrugel korsul' WHERE entry=2000000356; /* content: Ma reth bromo zoln kilagrin dra ma zoern tu ko fraht ka tadrom Ma krin drinor zaln dirnor Ma krin kan aasrugel korsul */
UPDATE db_script_string SET content_loc3='Analyse des Fragments läuft.' WHERE entry=2000000357; /* content: Fragment analysis underway. */
UPDATE db_script_string SET content_loc3='Objekt identifiziert: Val\'anyr, Hammer der Uralten Könige.' WHERE entry=2000000358; /* content: Object identified: Val\'anyr, hammer of ancient kings. */
UPDATE db_script_string SET content_loc3='Von den Titanen selbst erschaffen, wurde Val\'anyr Urel Steinherz, dem ersten König der Irdenen, überreicht. Mit dem Hammer sollte er seine Brüder erschaffen und ihnen Leben einhauchen.' WHERE entry=2000000359; /* content: Created by the titans themselves, Val\'anyr was given to the first earthen king, Urel Stoneheart. With the hammer he was to create and give life to the rest of his brethren. */
UPDATE db_script_string SET content_loc3='Im ersten Krieg zwischen den Irdenen und den Eisenzwergen wurde Val\'anyr zerstört. Es hieß, die Überreste der Waffe seien in dem Konflikt verlorengegangen.' WHERE entry=2000000360; /* content: Val\'anyr was shattered during the first war between the earthen and the iron dwarves. The weapon\'s remnants were believed lost in the conflict. */
UPDATE db_script_string SET content_loc3='Säuregehalt innerhalb des Wesens namens Yogg-Saron könnte theoretisch die Verflüssigung von Saronit erklären. Das Eintauchen in diese Substanz könnte ausreichen, um die titanische Legierung wiederherzustellen.' WHERE entry=2000000362; /* content: Powerful acidic content theoretically found inside the being known as Yogg-Saron would account for the liquification of saronite. Submersion in this substance might be sufficient to rebind an alloy of titan origin. */
UPDATE db_script_string SET content_loc3='Beginne mit Statusanalyse von Wächterin Freya.' WHERE entry=2000000363; /* content: Commencing Watcher Freya status analysis. */
UPDATE db_script_string SET content_loc3='Analyse des Ältesten Steinrinde zeigt eine Schallverteidigung, die den Einsatz von Magie stört.' WHERE entry=2000000366; /* content: Elder Stonebark analysis reveals a sonic defense that disrupts use of magic. */
UPDATE db_script_string SET content_loc3='Beginne mit Statusanalyse von Wächter Hodir.' WHERE entry=2000000370; /* content: Commencing Watcher Hodir status analysis. */
UPDATE db_script_string SET content_loc3='Hodirs Siegel scheint sich in einem Artefaktschrein zu befinden.' WHERE entry=2000000371; /* content: Hodir\'s sigil appears to be located inside a cache of artifacts. */
UPDATE db_script_string SET content_loc3='Wesen und Verhalten Hodirs sehr unbeständig. Zerstörung des Schreins im Laufe eines langen Kampfes sehr wahrscheinlich.' WHERE entry=2000000372; /* content: Watcher Hodir\'s temperament and behavior highly unstable. Destruction of cache highly probable during prolonged combat. */
UPDATE db_script_string SET content_loc3='Die Erhaltung des Schreins ist zur Wiedererlangung von Hodirs Siegel essentiell.' WHERE entry=2000000374; /* content: Preservation of cache is essential to recovering Hodir\'s sigil. */
UPDATE db_script_string SET content_loc3='Beginne mit Statusanalyse von Wächter Mimiron.' WHERE entry=2000000375; /* content: Commencing Watcher Mimiron status analysis. */
UPDATE db_script_string SET content_loc3='Um Mimirons Siegel zu erlangen muss die Selbstzerstörungssequenz gestartet und Mimiron besiegt werden, bevor die Zeit abläuft.' WHERE entry=2000000378; /* content: To retrieve Mimiron\'s sigil, initiate self-destruct sequence and defeat Mimiron before its completion. */
UPDATE db_script_string SET content_loc3='Planetarische Neuerschaffung? Drück dich deutlich aus, du verfluchte Maschine!' WHERE entry=2000000379; /* content: Planetary re-origination? Speak plainly, ye blasted machine! */
UPDATE db_script_string SET content_loc3='Beginne mit Statusanalyse von Wächter Thorim.' WHERE entry=2000000380; /* content: Commencing Watcher Thorim status analysis. */
UPDATE db_script_string SET content_loc3='Na, das nenne ich mal einen netten Fund!' WHERE entry=2000000384; /* content: Now, this is what we call a nice find! */
UPDATE db_script_string SET content_loc3='Ich zeig\' dir Zugang, elende Maschine. Hier ist er noch mal: Norgannons Schlüssel!' WHERE entry=2000000385; /* content: I\'ll show ye access, ye wretched machine. Here it is again: Norgannon\'s key! */
UPDATE db_script_string SET content_loc3='Sieht genauso aus wie die anderen Titanenarchive, die wir gefunden haben... nur älter... und größer.' WHERE entry=2000000386; /* content: Looks just like all the other titan archives me lads have found... only older... and bigger. */
UPDATE db_script_string SET content_loc3='Und? Wer empfängt das Signal? Was passiert dann?' WHERE entry=2000000389; /* content: Well? Who\'s on the listening side of the signal? What\'s going to happen? */
UPDATE db_script_string SET content_loc3='Gut, wonach sucht dieser Algalon denn in seiner Diagnose? Wie stehen unsere Chancen?' WHERE entry=2000000391; /* content: Well, what\'s this Algalon going to be looking for with his diagnostics? What are our chances? */
UPDATE db_script_string SET content_loc3='Verdammte Axt! Sieht so aus, als stände uns ein Kampf bevor.' WHERE entry=2000000392; /* content: Blast it, lads. Looks like we\'ve got a fight ahead of us. */
UPDATE db_script_string SET content_loc3='Die Zersetzung des Planeten und seiner lebenden Organismen in Grundelemente: Metalle, Gesteine, Gase. Es folgt eine Zeit der Wiederherstellung jedes Elements nach dem Originalplan des Planeten.' WHERE entry=2000000398; /* content: The decomposition of the planet and its living organisms into base elements: metals, rocks, gases. This is followed by a period of reconstitution of each element into the original planetary blueprint. */
UPDATE db_script_string SET content_loc3='Neunundneunzig Komma neun neun Prozent.' WHERE entry=2000000401; /* content: Ninety-nine point nine nine percent. */
UPDATE db_script_string SET content_loc3='Neue Anweisungen! Öffne den Eingang zum Himmlischen Planetarium.' WHERE entry=2000000403; /* content: New orders! Unlock the entry to the Celestial Planetarium. */
UPDATE db_script_string SET content_loc3='Vier Wächter? Siegel? Etwas weniger kryptisch bitte, verdammte Maschine!' WHERE entry=2000000404; /* content: Four watchers? Sigils? Could ye be a little less cryptic, confounded machine? */
UPDATE db_script_string SET content_loc3='Wie kommen wir an die Siegel ran? Gehen wir einfach hin und fragen lieb?' WHERE entry=2000000405; /* content: How do we get the sigils from them, then? Just walk up to them and ask them nicely? */
UPDATE db_script_string SET content_loc3='Du wirst uns jetzt erklären, wie wir das rückgängig machen können, oder?' WHERE entry=2000000406; /* content: You\'re going to tell us how to... "un-compromise" the sigils, aren\'t you? */
UPDATE db_script_string SET content_loc3='Ich habe immer davon geträumt, die Hallen der Titanen zu erforschen. Ich hätte nie gedacht, gegen ihre Schöpfungen antreten zu müssen.' WHERE entry=2000000407; /* content: I\'ve dreamed of roaming the halls of the titans for years. I never thought I\'d be pitted against their creations. */
UPDATE db_script_string SET content_loc3='Anfrage abgelehnt. Zugang erfordert manuelle Verifizierung der vier Wächtersiegel.' WHERE entry=2000000408; /* content: Request denied. Access requires manual verification of the four watchers\' sigils. */
UPDATE db_script_string SET content_loc3='Korrumpierung entdeckt. Externer Einfluss gewinnt an Macht über Wächter. Siegel gefährdet.' WHERE entry=2000000411; /* content: Corruption found. External influence gaining control over watchers. Sigils compromised. */
UPDATE db_script_string SET content_loc3='Ausreichend Gewaltanwendung kann ein Zurücksetzen der Wächterfunktionen auslösen und externe Einflüsse entfernen.' WHERE entry=2000000412; /* content: Sufficient use of force would trigger a reset in watcher functions, removing the external influence. */
UPDATE db_script_string SET content_loc3='Um die Siegel während dieses Prozesses nicht zu gefährden, kann ich den Status jedes Wächters analysieren und Euch die Informationen nach Abschluss der Berechnung zur Verfügung stellen.' WHERE entry=2000000413; /* content: As for not endangering the sigils during this process, I can analyze each watcher\'s status and make that information available to you upon completion of calculations. */
UPDATE db_script_string SET content_loc3='Ihr habt einen großartigen Kampf verpasst, Dorian!' WHERE entry=2000000414; /* content: What a fight you missed, Dorian! It was one for the ages! */
UPDATE db_script_string SET content_loc3='Da waren wir also, auf Stampfis Rücken im Galopp auf dem Weg in die Schlacht...' WHERE entry=2000000415; /* content: There we were, riding into battle at full speed on the back of Stampy... */
UPDATE db_script_string SET content_loc3='Alles ist durchgerüttelt worden. Ich konnte kaum meine Waffe ruhig halten.' WHERE entry=2000000416; /* content: Everything was shaking all over the place. I could hardly steady my gun. */
UPDATE db_script_string SET content_loc3='Und dann haben wir es gesehen, das fürchterliche Biest!' WHERE entry=2000000417; /* content: And then we saw her, the fearsome beast! */
UPDATE db_script_string SET content_loc3='$n brauchte all $g seine:ihre; Kraft, um Stampfi zu halten.' WHERE entry=2000000418; /* content: It took all of $N\'s skill to maintain control of Stampy. */
UPDATE db_script_string SET content_loc3='Die Schlacht war ein Nebel aus Blut, Blei und Schweiß. Die Angriffe der Brutmutter kamen schneller und schneller!' WHERE entry=2000000419; /* content: The battle was a haze of blood, lead, and sweat. The broodmother\'s attacks came faster and faster! */
UPDATE db_script_string SET content_loc3='Aber am Ende haben $n und ich es geschafft, durch die Anwendung unseres Verstandes und durch Teamwork. Und jetzt haben wir diese herrliche Trophäe zum Andenken an unseren Sieg.' WHERE entry=2000000420; /* content: But, in the end, $N and I prevailed through our wits and teamwork. And now we have this glorious trophy as a memento of our victory. */
UPDATE db_script_string SET content_loc3='Entdecke ich da ein wenig Eifersucht, Dorian?' WHERE entry=2000000421; /* content: Do I detect a bit of jealousy, Dorian? */
UPDATE db_script_string SET content_loc3='Nun ja, ich weiß nicht. Ich war hier, habe mich um das Feuer gekümmert und habe mich mit Spleenix über die Schattenseiten von Zahnrädern unterhalten. Das ist schon ziemlich episch, auf seine eigene Art und Weise.' WHERE entry=2000000422; /* content: Well, I don\'t know. I was here tending the fire and discussing the drawbacks of cogs with Zootfizzle. That\'s pretty epic, too, in its own way. */
UPDATE db_script_string SET content_loc3='%s beginnt einen Ritus der Erschaffung...' WHERE entry=2000000424; /* content: %s begins a rite of creation... */
UPDATE db_script_string SET content_loc3='%s erschafft ein neues Totem...' WHERE entry=2000000426; /* content: %s creates a new totem... */
UPDATE db_script_string SET content_loc3='Das Totem ist fertig, $n.' WHERE entry=2000000427; /* content: The totem is made, $N. */
UPDATE db_script_string SET content_loc3='%s verwandelt die Gegenstände von $n in ein Totem der Läuterung...' WHERE entry=2000000428; /* content: %s transforms the items $N gave him into a totem of cleansing... */
UPDATE db_script_string SET content_loc3='%s starrt nach Nordwesten und lässt ein langes, herausgezogenes Heulen von sich.' WHERE entry=2000000430; /* content: %s stares to the northwest and lets out a long, drawn out howl. */
UPDATE db_script_string SET content_loc3='%s lässt ein mächtiges Heulen von sich.' WHERE entry=2000000431; /* content: %s lets forth a mighty howl. */
UPDATE db_script_string SET content_loc3='Ihr kommt spät, Kurier. Nein, ich will keine Ausreden hören; dieser Botengang ist viel zu wichtig.' WHERE entry=2000000432; /* content: You\'re late, courier. No, I don\'t want any excuses; this errand is far too important. */
UPDATE db_script_string SET content_loc3='Hier ist das Buch, das unser Vertreter aus Wyrmruh mitgebracht hat. Bringt es unverzüglich Myralion Sonnenfeuer.' WHERE entry=2000000433; /* content: Here\'s the tome our representative brought from Wyrmrest. Get this to Myralion Sunblaze immediately. */
UPDATE db_script_string SET content_loc3='Seid auf der Hut, Kurier. Ich brauche Euch ja nicht an den Wert des Buches zu erinnern oder daran, dass der Silberbund alles tun wird, um es uns abzunehmen.' WHERE entry=2000000434; /* content: Watch your back, courier. I needn\'t remind you of the value of the book, nor the fact that the Silver Covenant will stop at nothing to take it from us. */
UPDATE db_script_string SET content_loc3='Hier ist das Buch. Ihr müsst dafür sorgen, dass es zu Caladis Prunkspeer in Eiskrone kommt, bevor die Sonnenhäscher mitbekommen, was los ist.' WHERE entry=2000000435; /* content: Here is the book. You must ensure it gets to Caladis Brightspear in Icecrown before the Sunreavers realize what is happening. */
UPDATE db_script_string SET content_loc3='Beeilt Euch! Die Sonnenhäscher werden keine Zeit verlieren, wenn ihnen erst einmal klar wird, dass wir die Informationen haben, nach denen sie suchen.' WHERE entry=2000000436; /* content: Travel quickly! The Sunreavers will not waste any time once they realize that we have the information they seek. */
UPDATE db_script_string SET content_loc3='Ah, ich verstehe...' WHERE entry=2000000438; /* content: Ah, I see... */
UPDATE db_script_string SET content_loc3='Ja, das ist ungünstig.' WHERE entry=2000000439; /* content: Yes, this is unfortunate. */
UPDATE db_script_string SET content_loc3='So, es ist vollbracht.' WHERE entry=2000000444; /* content: There, it is completed. */
UPDATE db_script_string SET content_loc3='Die Arakkoa liegen im Verborgenem... überall!' WHERE entry=2000000447; /* content: The arakkoa are hidden... everywhere! */
UPDATE db_script_string SET content_loc3='Ich gebe mich geschlagen.' WHERE entry=2000000449; /* content: I yield to you. */
UPDATE db_script_string SET content_loc3='Macht Euch bereit!' WHERE entry=2000000450; /* content: Prepare yourself! */
UPDATE db_script_string SET content_loc3='Lasst uns anfangen!' WHERE entry=2000000451; /* content: Let it begin! */
UPDATE db_script_string SET content_loc3='Da... Dank Euch. Ich war mir sicher, dass ich sterben werde.' WHERE entry=2000000454; /* content: You have my thanks! */
UPDATE db_script_string SET content_loc3='Genießen sie die komödiantischen Einlagen von Hel\'gesh Neider in ein paar Minuten!' WHERE entry=2000000458; /* content: Check out the comedy stylings of Perry Gatner in just a few minutes! */
UPDATE db_script_string SET content_loc3='Wenn Ihr bereit für einen Lacher seid, geht in die Taverne Weltenend!' WHERE entry=2000000459; /* content: If you are ready to laugh, head over to the World\'s End Tavern! */
UPDATE db_script_string SET content_loc3='Jetzt in Shattrath: die Tauren Chieftains! Sie spielen zur vollen Stunde in der Taverne Weltenend.' WHERE entry=2000000460; /* content: Now, in Shattrath City: The Tauren Chieftains! Playing in the World\'s End Tavern at the top of the hour! */
UPDATE db_script_string SET content_loc3='Wildherz dich töten, wenn du kommen zurück. Du nicht mehr hier willkommen!' WHERE entry=2000000462; /* content: Frenzyheart kill you if you come back. You no welcome here no more! */
UPDATE db_script_string SET content_loc3='Danke, $R. Ich bin sicher, mein Vater wird Euch reich belohnen! Tschüss!' WHERE entry=2000000464; /* content: Thanks, $R! I\'m sure my dad will reward you greatly! Bye! */
UPDATE db_script_string SET content_loc3='Das ist das letzte Mal, dass ich mich fangen lasse! Versprochen! Tschüss!' WHERE entry=2000000465; /* content: This is the last time I get caught! I promise! Bye! */
UPDATE db_script_string SET content_loc3='%s riecht das Fleisch, wacht aus seinem Schlummer auf und brüllt in seiner Wut!' WHERE entry=2000000466; /* content: %s smells the meat, awakens from his slumber, and roars in anger! */
UPDATE db_script_string SET content_loc3='Was erwarten sie denn, was passiert, wenn sie die Fledermäuse zwingen, in so einem Winkel hereinzukommen? Genickbrüche und schlecht schmeckenden Fledermauseintopf, das ist, was sie kriegen!' WHERE entry=2000000467; /* content: What do they expect, making the bats come in at that angle? Broken necks and gamey bat stew, that\'s what they get. */
UPDATE db_script_string SET content_loc3='Aaarrrrr!!' WHERE entry=2000000470; /* content: ROARRRRR!! */
UPDATE db_script_string SET content_loc3='DUNKELHAIN... ICH HABE HUNGER!!!' WHERE entry=2000000471; /* content: DARKSHIRE...I HUNGER!! */
UPDATE db_script_string SET content_loc3='Euer Körper gehört mir! Unsere Schicksale sind für immer verflochten!' WHERE entry=2000000472; /* content: Beware, $n! Look to the west! */
UPDATE db_script_string SET content_loc3='Vorsicht, $C. Diese Trolle haben ihren eigenen Schlangengott getötet!' WHERE entry=2000000473; /* content: Careful here, $c. These trolls killed their own snake god! */
UPDATE db_script_string SET content_loc3='Wir sehen uns.' WHERE entry=2000000474; /* content: See you around. */
UPDATE db_script_string SET content_loc3='Richtig. Ich sollte also besser zum Unteroffizier zurückkehren.' WHERE entry=2000000475; /* content: Right. I\'d better get back to the sergeant then. */
UPDATE db_script_string SET content_loc3='Habt Dank, $C.' WHERE entry=2000000476; /* content: Thanks, $c. */
UPDATE db_script_string SET content_loc3='Danke, $R. Ich nehme Euch jetzt diese erbärmliche Töle ab.' WHERE entry=2000000477; /* content: Thank you, $r. I will take this miserable cur from you now. */
UPDATE db_script_string SET content_loc3='Raliq wird\'s Euch jetzt zeigen!' WHERE entry=2000000478; /* content: Raliq teach you lesson now! */
UPDATE db_script_string SET content_loc3='F\'woooroonn! Kusch\'kusch bezahlt nichts! Sterbt!' WHERE entry=2000000479; /* content: F\'woooroonn! Coosh\'coosh pay nothing! Die! */
UPDATE db_script_string SET content_loc3='Ich entscheide mich für die dritte Option: EUCH TÖTEN!' WHERE entry=2000000480; /* content: I choose the third option: KILLING YOU! */
UPDATE db_script_string SET content_loc3='Wie Ihr befehlt...' WHERE entry=2000000481; /* content: As you wish... */
UPDATE db_script_string SET content_loc3='Aus der zerschlissenen Erde wurde das Sakrament gestohlen. Der Schild von Aesirites ist verloren. Nun wird ewige Ruhelosigkeit herrschen.' WHERE entry=2000000486; /* content: From sundered ground the sacrament was stolen. The Shield of Aesirites is lost. Unrest will be eternal. */
UPDATE db_script_string SET content_loc3='Vielen Dank, dass Ihr mich befreit habt, $n! Ich werde es alleine nach Shattrath schaffen.' WHERE entry=2000000491; /* content: Thank you for freeing me, $N! I\'m going to make my way to Shattrath! */
UPDATE db_script_string SET content_loc3='Es ist gut, Euch endlich zu sehen, Kurier. Krasus hat uns eines der wenigen noch übrigen Exemplare der Uralten drachengeschmiedeten Klingen anvertraut.' WHERE entry=2000000492; /* content: It\'s good to finally see you, courier. Krasus has entrusted us with one of the few remaining copies of Ancient Dragonforged Blades. */
UPDATE db_script_string SET content_loc3='[Furbolg] Ich danke Euch, $n!' WHERE entry=2000000493; /* content: [Furbolg] Thank you, $n! */
UPDATE db_script_string SET content_loc3='[Furbolg] Die Prophezeiung ist wahr!' WHERE entry=2000000494; /* content: [Furbolg] The prophecy is true! */
UPDATE db_script_string SET content_loc3='[Furbolg] Die noch bei der Tannenruhfeste Verbliebenen werden Euch als Held willkommen heißen!' WHERE entry=2000000495; /* content: [Furbolg] Those remaining at Stillpine Hold will welcome you as a hero! */
UPDATE db_script_string SET content_loc3='[Furbolg] Die Furbolgs der Tannenruhfeste werden Eure Tapferkeit nicht so schnell vergessen!' WHERE entry=2000000496; /* content: [Furbolg] The Stillpine furbolgs will not soon forget your bravery! */
UPDATE db_script_string SET content_loc3='Ich spürte, dass ein Teil von ihm noch lebt! Gefangen... allein... Oh, Arthas!' WHERE entry=2000000500; /* content: I sensed a part of him still alive! Trapped... struggling... Oh, Arthas! */
UPDATE db_script_string SET content_loc3='Eines Tages... Eines Tages erinnert er sich vielleicht daran, was er einmal war.' WHERE entry=2000000501; /* content: Perhaps - perhaps he might someday remember what he once was. */
UPDATE db_script_string SET content_loc3='Möge das Licht ihm nun Frieden schenken, frei von der Umklammerung dieser schrecklichen Klinge.' WHERE entry=2000000502; /* content: By the Light, may he at last find rest, free from the icy grip of that terrible blade. */
UPDATE db_script_string SET content_loc3='Es ist also vollbracht.' WHERE entry=2000000503; /* content: So, it is done. */
UPDATE db_script_string SET content_loc3='Ich wagte es nicht, meinen Sinnen zu trauen - zu oft hat mich der Lichkönig zum Narren gehalten.' WHERE entry=2000000504; /* content: I had not dared to trust my senses - too many times has the Lich King made me to be a fool. */
UPDATE db_script_string SET content_loc3='Endlich ist er für die Gräuel an meinem Volk zur Rechenschaft gezogen worden.' WHERE entry=2000000505; /* content: Finally, he has been made to pay for the atrocities he imposed upon my people. */
UPDATE db_script_string SET content_loc3='Möge Azeroth nie den schrecklichen Preis vergessen, den wir für unsere Schwäche zahlen mussten... für unseren Stolz.' WHERE entry=2000000506; /* content: May Azeroth never fail to remember the terrible price we paid for our weakness... for our pride. */
UPDATE db_script_string SET content_loc3='Und nun? Was wird aus jenen, die befreit, doch noch immer an ihr irdisches Dasein gebunden sind?' WHERE entry=2000000507; /* content: But what now, hero? What of those freed from his grasp, but still shackled to their mortal coils? */
UPDATE db_script_string SET content_loc3='Ich muss über Vieles nachdenken.' WHERE entry=2000000509; /* content: I have much to ponder. */
UPDATE db_script_string SET content_loc3='Arthas...' WHERE entry=2000000510; /* content: Arthas... */
UPDATE db_script_string SET content_loc3='Ach, Held von Azeroth, Ihr macht mir ein größeres Geschenk als Euch bewusst ist.' WHERE entry=2000000511; /* content: Alas, hero of Azeroth, you give me a greater gift than you know. */
UPDATE db_script_string SET content_loc3='Lange kämpfte ich mit mir, dem Prinzen seine grauenhaften Sünden zu vergeben.' WHERE entry=2000000512; /* content: Long have I struggled to forgive the prince for his terrible transgressions. */
UPDATE db_script_string SET content_loc3='Meine Seele war zerfressen von quälender Sorge, dunklen Gedanken... die mich vom Licht entfernten.' WHERE entry=2000000513; /* content: My soul has been wracked with unbearable anxiety, dark thoughts... distancing me from the Light. */
UPDATE db_script_string SET content_loc3='Ich erinnere mich deutlich an den Stolz in seinen Augen, als er vor mir stand, begierig, die Feinde des Lichts zu vernichten...' WHERE entry=2000000514; /* content: I recall clearly the gleam of pride in his eye as he stood before me, eager to defeat the enemies of the Light... */
UPDATE db_script_string SET content_loc3='Gewillt, sein Volk zu verteidigen - koste es, was es wolle…' WHERE entry=2000000515; /* content: Eager to defend his people, no matter the cost. */
UPDATE db_script_string SET content_loc3='Es ist diese Erinnerung an Arthas, die ich in meinem Herzen bewahren will.' WHERE entry=2000000516; /* content: It is this memory of Arthas that I choose to keep in my heart. */
UPDATE db_script_string SET content_loc3='Ich werde immer in Eurer Schuld stehen.' WHERE entry=2000000517; /* content: I shall always be in your debt, friend. */
UPDATE db_script_string SET content_loc3='Ich danke Euch.' WHERE entry=2000000518; /* content: Thank you. */
UPDATE db_script_string SET content_loc3='Oh, Jungchen...' WHERE entry=2000000519; /* content: Oh, lad... */
UPDATE db_script_string SET content_loc3='Wie ich die endlosen Tage in Lordaeron vermisse...' WHERE entry=2000000520; /* content: How I miss those endless days in Lordaeron... */
UPDATE db_script_string SET content_loc3='...mit der stumpfen Klinge dein Können schärfend…' WHERE entry=2000000521; /* content: ...sharpenin\' yer skill with this dull blade... */
UPDATE db_script_string SET content_loc3='…dich zu einer Waffe schmiedend, die deiner großen Bestimmung würdig war.' WHERE entry=2000000522; /* content: ...forgin\' ya into a weapon meant to withstand the demands of a great destiny. */
UPDATE db_script_string SET content_loc3='Hm... Du hast dieses Können genutzt, nicht wahr, mein Junge?' WHERE entry=2000000523; /* content: Heh... Yeh sure put them skills ta use, didn\'t yeh, lad? */
UPDATE db_script_string SET content_loc3='Hätte ich dich an diesem Tag nur aufgehalten, wie anders hätte alles kommen können.' WHERE entry=2000000524; /* content: If only I\'da been able ta stop yeh that day, how different things mighta been. */
UPDATE db_script_string SET content_loc3='Hätte ich diese verfluchte Klinge nur niemals entdeckt...' WHERE entry=2000000525; /* content: If only I\'da never discovered that accursed blade... */
UPDATE db_script_string SET content_loc3='Lebt wohl, Arthas... mein Bruder.' WHERE entry=2000000526; /* content: Farewell, Arthas... my brother. */
UPDATE db_script_string SET content_loc3='Darion, mein Sohn.' WHERE entry=2000000527; /* content: Darion, my son. */
UPDATE db_script_string SET content_loc3='Endlich ist es mir gestattet, dich wiederzusehen.' WHERE entry=2000000528; /* content: At last, I am able to lay my eyes upon you again. */
UPDATE db_script_string SET content_loc3='Der Lichkönig hat mich endlosen Qualen ausgesetzt, Darion.' WHERE entry=2000000529; /* content: The Lich King tormented me without end, Darion. */
UPDATE db_script_string SET content_loc3='Ohne Unterlass suchte er meinen Willen zu brechen, auf dass ich ihm diene und mich an seine Klinge binde…' WHERE entry=2000000530; /* content: Endlessly, he sought to break my will, to force me to serve him, to bind me to his blade... */
UPDATE db_script_string SET content_loc3='Doch als seine Aufmerksamkeit anderorts verlangt wurde, ließ er von mir ab.' WHERE entry=2000000531; /* content: Finally, when events demanded his full attention, he left me. */
UPDATE db_script_string SET content_loc3='Das Einzige, an das ich mich festhielt... der Gedanke, der mich daran hinderte, aufzugeben...' WHERE entry=2000000532; /* content: The one memory, I clung to, Darion... The one thought that kept me from giving in... */
UPDATE db_script_string SET content_loc3='Es war dein Opfer, mein Sohn, das mich erneut vor ewiger Pein gerettet hat.' WHERE entry=2000000533; /* content: It was your sacrifice, my son, that again saved me from eternal peril. */
UPDATE db_script_string SET content_loc3='Vater...' WHERE entry=2000000534; /* content: Father... */
UPDATE db_script_string SET content_loc3='Vater. Ich - ich fürchtete um deinen Verstand.' WHERE entry=2000000535; /* content: Father. I feared for your... your sanity. */
UPDATE db_script_string SET content_loc3='Vater... für dich würde ich mein Leben tausendfach geben.' WHERE entry=2000000536; /* content: Father... For you, I would give my life a thousand times. */
UPDATE db_script_string SET content_loc3='%s riecht das Elixier und gräbt eifrig weiter!' WHERE entry=2000000537; /* content: %s sniffs the elixir then eagerly digs in! */
UPDATE db_script_string SET content_loc3='Und danke, $N. Ohne Eure Hilfe hätte ich nie den Weg zu den Verlassenen gefunden.' WHERE entry=2000000538; /* content: And thank you, $N.  Without your aid I may never have found my way to the Forsaken. */
UPDATE db_script_string SET content_loc3='Lebt wohl, mein Freund' WHERE entry=2000000539; /* content: Farewell, my friend. */
UPDATE db_script_string SET content_loc3='Es ist eine lange Zeit gewesen, Bethor, mein Freund' WHERE entry=2000000540; /* content: It has been a long time, Bethor, my friend. */
UPDATE db_script_string SET content_loc3='Wenn wir Zeit haben, müssen wir ausfürhlicher reden. Wir haben viel zu besprechen.' WHERE entry=2000000541; /* content: When time permits, we must speak at length.  For we have much to discuss. */
UPDATE db_script_string SET content_loc3='%s beschwört einen Boten...' WHERE entry=2000000542; /* content: %s summons a messenger... */
UPDATE db_script_string SET content_loc3='Geht mein Diener. Sendet die Worte der Steinkrallen zu Apothekerin Zamah.' WHERE entry=2000000543; /* content: Go, my servant. Send word of Stonetalon to Apothecary Zamah. */
UPDATE db_script_string SET content_loc3='%s übergibt Apothekerin Zamah eine Nachricht.' WHERE entry=2000000544; /* content: %s gives message to Apothecary Zamah. */
UPDATE db_script_string SET content_loc3='%s lächelt.' WHERE entry=2000000545; /* content: Apothecary Zamah smiles. */
UPDATE db_script_string SET content_loc3='Aber es ist das Einzige, in das Brogg hineinpasst! Ihr Goblins baut viel zu kleine Unterkünfte!' WHERE entry=2000000546; /* content: But this the only building that fit Brogg! Goblins make buildings too small! */
UPDATE db_script_string SET content_loc3='Wir haben schon oft genug darüber gesprochen, Brogg. Ihr könnt nicht in diesem Gebäude bleiben. Sicher, es bietet Euch genug Platz, doch es passt sonst niemand mehr hinein!' WHERE entry=2000000547; /* content: I thought we talked about this, Brogg. You can\'t stay in this building. Sure, it can house you, but there\'s no room for anyone else! */
UPDATE db_script_string SET content_loc3='Ja, natürlich! Aber dieses Gebäude ist unser Hauptquartier! Alle unsere Baupläne und Handbücher befinden sich da drinnen.' WHERE entry=2000000548; /* content: Yes, we said you can stay here, but this building is our headquarters! All of our manuals and blueprints are stored here! */
UPDATE db_script_string SET content_loc3='Ich widerspreche nur ungern, aber wenn Ihr mich fragt, seid ihr Oger einfach nur zu groß! Ich sehe schon, heute werde ich Euch wohl nicht zu einem Umzug bewegen können. Aber glaubt nicht, dass Ihr auf ewig hier wohnen könnt!' WHERE entry=2000000549; /* content: I\'m inclined to say they made ogres too big! I can see I\'m not going to convince you to move now, but don\'t think this is permanent! */
UPDATE db_script_string SET content_loc3='Ihr habt gesagt, dass Brogg hier bleiben kann! Ihr habt versprochen, dass Ihr Brogg bei der Rückeroberung des Horts der Steinbrecher helft!' WHERE entry=2000000550; /* content: You said Brogg could stay here! You promised Brogg you\'d help regain Stonemaul clan mound! */
UPDATE db_script_string SET content_loc3='In den Qualen der Teilung war Ameth\'Aran den Launen des Schreckens, der das Land ergriff, ausgeliefert. Es gab kaum Hoffnung auf Überleben.' WHERE entry=2000000551; /* content: In the throes of the Sundering, Ameth\'Aran was at the whim of the terror that gripped the land. There was little hope for survival. */
UPDATE db_script_string SET content_loc3='Athrikus ist zu uns gekommen. Er sagte, dass er uns vor Bösem bewahren könne. Er hat einen Schutzzauber auf uns gewirkt, der uns vor dem Bösen beschützen soll.' WHERE entry=2000000552; /* content: Athrikus came to us. He told us that he could save us from harm. He cast a spell upon us to protect us from harm. */
UPDATE db_script_string SET content_loc3='Als die Erschütterungen endeten, kamen seine wahren Motive ans Licht. Wir waren gefangen und er entzog uns langsam unsere Macht.' WHERE entry=2000000553; /* content: When the shaking stopped, his true motives were revealed. We were trapped, and he was slowly draining our powers. */
UPDATE db_script_string SET content_loc3='Hunderte wurden durch seinen Zauber gefangen gehalten. Jetzt sind nur noch einige wenige in ihren Gefängnissen. Von Zeit zu Zeit redet er mit uns.' WHERE entry=2000000554; /* content: There were hundreds imprisoned by his spell. Now only a few remain in their prisons. He would speak to us sometimes. */
UPDATE db_script_string SET content_loc3='Er fürchtete, seine Macht würde schwinden und dass bald selbst wir, die letzten seiner kostbaren Seelenedelsteine, verblassen und sterben würden.' WHERE entry=2000000555; /* content: He worried that his power weakened, that soon even we last of his precious soulgems would fade and die. */
UPDATE db_script_string SET content_loc3='Sein Leutnant, Ilkrud Magskluve, besitzt einen Folianten, der Hinweise auf den Verbleib der letzten Seelensteine geben könnte.' WHERE entry=2000000556; /* content: His lieutenant, Ilkurd Magthrull possesses a tome which might indicate the location of the remaining soulgems. */
UPDATE db_script_string SET content_loc3='Hmmm... wo ist mein Angelhaken? Oh, da ist er ja.' WHERE entry=2000000557; /* content: Hmmm... Where is my fishing hook? Oh, there it is. */
UPDATE db_script_string SET content_loc3='Mal sehen, ob die Fische anbeißen.' WHERE entry=2000000558; /* content: Let\'s see if the fish are biting. */
UPDATE db_script_string SET content_loc3='Hmmm... ich glaube ich brauche einen besseren Köder. Ich werde mal im Wagen meines Vaters nachsehen.' WHERE entry=2000000559; /* content: Hmmm... I think I need better bait. I\'ll check my father\'s wagon. */
UPDATE db_script_string SET content_loc3='Ich hätte nie gedacht, dass es Manaöl werden würde! Dafür herrscht genug Bedarf! Außer von Grek natürlich.' WHERE entry=2000000560; /* content: Quillboar scraps! These should do the trick. Fish love Quillboar. */
UPDATE db_script_string SET content_loc3='Jackpot!' WHERE entry=2000000561; /* content: Caught one! I\'ll see if any of the guards are hungry. Come on Fang! */
UPDATE db_script_string SET content_loc3='%s riecht das Fleisch, erwacht aus seinem Schlaf und brüllt vor Wut!' WHERE entry=2000000562; /* content: I caught a fish if you are hungry... */
UPDATE db_script_string SET content_loc3='AUUUU!' WHERE entry=2000000563; /* content: A nice catch it is at that! You bring honor to the Horde boy, We thank you! */
UPDATE db_script_string SET content_loc3='Darf es noch etwas mehr zu Essen sein?' WHERE entry=2000000564; /* content: Would you like something more to eat sir? */
UPDATE db_script_string SET content_loc3='Mehr Brot, Junge!' WHERE entry=2000000565; /* content: More bread boy! */
UPDATE db_script_string SET content_loc3='Sofort, Sire.' WHERE entry=2000000566; /* content: Right away sir. */
UPDATE db_script_string SET content_loc3='Hier ist Euer Brot, $gmein Herr:meine Dame;.' WHERE entry=2000000567; /* content: Here is your bread sir. */
UPDATE db_script_string SET content_loc3='Aaahh... sehr gut. Jetzt aber los!' WHERE entry=2000000568; /* content: Aaahh... Very good. Now scat! */
UPDATE db_script_string SET content_loc3='Wie Ihr wünscht, Sire.' WHERE entry=2000000569; /* content: As you wish sir. */
UPDATE db_script_string SET content_loc3='Kann ich noch mehr Steine für meine Halskette sammeln gehen, Papa?' WHERE entry=2000000570; /* content: Can I go collect more rocks for my necklace, Papa? */
UPDATE db_script_string SET content_loc3='Ja, du kannst, Okla. Treib dich aber nicht zu weit von zu Hause umher.' WHERE entry=2000000571; /* content: Yes you may, Okla. But do not stray too far from home. */
UPDATE db_script_string SET content_loc3='Dabu! Ich werde vorsichtig sein, Papa!' WHERE entry=2000000572; /* content: Dabu! I will be careful Papa! */
UPDATE db_script_string SET content_loc3='Ooohh... dieser Stein ist so schön und bunt. Vielleicht kann ich ja noch mehr davon finden...' WHERE entry=2000000573; /* content: Ooohh... This rock is nice and colorful. Let\'s see if I can find more like it... */
UPDATE db_script_string SET content_loc3='Hmm... dieser Stein ist schön, aber zu klein.' WHERE entry=2000000574; /* content: Hmm... That rock is nice, but too small. */
UPDATE db_script_string SET content_loc3='Das hier ist ein glänzender Stein, ich werde ihn in meinen Eimer legen. Ok, ich brauche nur noch ein paar mehr...' WHERE entry=2000000575; /* content: This is a shiney rock. I\'ll put it in my bucket. Ok, I just need a few more... */
UPDATE db_script_string SET content_loc3='Ich bin mir sicher, dass mein Freund Gruk diesen Stein gerne für den Speer hätte, an dem er gerade bastelt! Er ist sehr scharfkantig... ich hoffe er gefällt ihm.' WHERE entry=2000000576; /* content: I bet my friend Gruk would like this rock for the spear he is making! It\'s very sharp... I hope he likes it. */
UPDATE db_script_string SET content_loc3='Na also... diese Steine dürften für eine Halskette genügen. Ich sollte jetzt wohl sowieso nach Hause gehen.' WHERE entry=2000000577; /* content: There... That should be enough rocks to make a necklace. I should probably get home now anyway. */
UPDATE db_script_string SET content_loc3='Der Argentumkreuzzug lädt Euch ein, beim Argentumturnier Eure Fähigkeiten im ehrenhaften Kampf gegen andere Helden von Azeroth zu beweisen.' WHERE entry=2000000580; /* content: The Argent Crusade invites you to test your skills in honorable combat against other heroes of Azeroth at the Argent Tournament. */
UPDATE db_script_string SET content_loc3='Die Champions der größten Nationen der Welt haben sich auf dem Argentumturnierplatz im Nordosten Eiskrones versammelt, um allen Herausforderern entgegen zu treten.' WHERE entry=2000000581; /* content: The champions of the world\'s great nations have gathered at the Argent Tournament Grounds in northeastern Icecrown to face all challengers. */
UPDATE db_script_string SET content_loc3='Ihr seid spät dran, Vorarbeiter.' WHERE entry=2000000582; /* content: You\'re late, overseer. */
UPDATE db_script_string SET content_loc3='Das Schicksal wartet nicht. Eure Handwerker müssen die Produktion von Kriegsgolems erhöhen, bevor der Anführer der Steinriesen und seine Armee Ulduar erreichen.' WHERE entry=2000000583; /* content: Destiny will not wait. Your craftsmen must increase their production of the war golems before the stone giant leader and his army reach Ulduar. */
UPDATE db_script_string SET content_loc3='Sagt Euren Runenschmieden, dass sie damit weitermachen sollen, so viele Steinriesen umzuformen, wie sie können. Diejenigen, die sich nicht unterwerfen, müssen zerstört werden.' WHERE entry=2000000584; /* content: Tell your rune-smiths to continue converting what stone giants you can. Those that will not submit must be destroyed. */
UPDATE db_script_string SET content_loc3='Sollten die Steinriesen unsere Arbeiten in Ulduar beeinträchtigen, so werde ich Euch und Euren Than zur Rechenschaft ziehen. Sterblichen darf nicht erlaubt werden, den Riesen zu Hilfe zu kommen.' WHERE entry=2000000585; /* content: If the stone giants interfere with our work at Ulduar, I will hold you and your thane responsible. Mortals must not be allowed to come to the aid of the giants. */
UPDATE db_script_string SET content_loc3='Kehrt an Eure Arbeit zurück, Vorarbeiter. Stellt sicher, dass Euren Arbeitern die Dringlichkeit ihrer Aufgaben klar ist.' WHERE entry=2000000586; /* content: Return to your duties, overseer. Be certain to impress upon your workers the urgency of their tasks. */
UPDATE db_script_string SET content_loc3='Also hat das Orakel beschlossen, Euch zu helfen, $g Fremder : Fremde;. Nun gut, ich werde mein Wissen mit Euch teilen.' WHERE entry=2000000587; /* content: So the oracle has decided to aid you, outlander. Very well, I will offer you what i know. */
UPDATE db_script_string SET content_loc3='Euer Gefährte sucht den Splitterhornpatriarchen Farunn, der sich von seinen Brüdern fernhält.' WHERE entry=2000000588; /* content: Your companion seeks the shardhorn patriarch Farunn, who holds himself aloof from his brothers. */
UPDATE db_script_string SET content_loc3='Kehrt zu Eurem Gefährten zurück. Sagt ihm, dass er losgehen und in Farunn seinen Preis einfordern soll.' WHERE entry=2000000590; /* content: Return to your companion. Tell him to go forth and claim his prize in Farunn. */
UPDATE db_script_string SET content_loc3='Sobald er dies getan hat, sagt ihm, dass er dieses Land friedlich verlassen soll. Er gehört hier nicht her.' WHERE entry=2000000591; /* content: Once he has done so, tell him to leave this land in peace. He does not belong here. */
UPDATE db_script_string SET content_loc3='[Furbolg] Legt Zeugnis für die grausamen Taten unserer Gegner ab, $n. Sie rauben unsere Jüngsten... unsere Schwachen... Hier gefangen gehalten, den Sichelklauen ausgeliefert. Befreit sie, Erlöser!' WHERE entry=2000000592; /* content: [Furbolg] Bear witness to the savage acts of our adversaries, $N. They steal our young... our weak... Imprisoned here, they are left to the devices of the Bristlelimb. Set them free, Promised One! */
UPDATE db_script_string SET content_loc3='Es entkommt!' WHERE entry=2000000593; /* content: I haven\'t really figured out what it is, but there\'s something strange about the hot springs... */
UPDATE db_script_string SET content_loc3='Jeden Gegenstand, den ich verkaufe, habe ich selbst erlangt. Ihr habt meine persönliche Garantie auf Echtheit.' WHERE entry=2000000594; /* content: %s tosses the sword into the shallow pool. */
UPDATE db_script_string SET content_loc3='Sehnt Ihr Euch nicht auch die gute alte Zeit herbei? Ich habe da ein paar Freunde, die uns bei der Erfüllung dieses Wunsches behilflich sein können...' WHERE entry=2000000595; /* content: I\'ve found that when I throw something into this water, it has improved incredibly when I retrieve it. */
UPDATE db_script_string SET content_loc3='Lasst uns zum Gasthaus gehen, wenn Ihr Dienstschluss habt. Ich gebe eine Runde aus und wir können uns über die gute alte Zeit unterhalten.' WHERE entry=2000000596; /* content: Well, whatever it is, it works quite well! */
UPDATE db_script_string SET content_loc3='Kommt sprecht mit mir, und was einst verschwommen war soll kristallklar werden.' WHERE entry=2000000597; /* content: And I am determined to find out more... So if you need my help again, you know where I\'ll be. */
UPDATE db_script_string SET content_loc3='Gewinne! Holt Eure Gewinne direkt hier ab! Kommt her und löst Eure Gewinnlose des Dunkelmond-Jahrmarkts gegen wertvolle Gewinne ein!' WHERE entry=2000000598; /* content: Please, follow me. */
UPDATE db_script_string SET content_loc3='Sind das Winterhyazinthen? Für mich?' WHERE entry=2000000599; /* content: Are those winter hyacinths? For me? */
UPDATE db_script_string SET content_loc3='Es ist lange her, dass ein Reisender mit Blumen vorbeigekommen ist.' WHERE entry=2000000600; /* content: It\'s been so long since a traveler has come here bearing flowers. */
UPDATE db_script_string SET content_loc3='Es war die letzten Jahre viel zu einsam an diesem See. Die Reisenden haben aufgehört zu kommen, und stattdessen hat das Böse Besitz von diesen Wassern ergriffen.' WHERE entry=2000000601; /* content: The lake has been too lonely these past years. The travelers stopped coming and evil came to these waters. */
UPDATE db_script_string SET content_loc3='Euer Geschenk zeugt von rar gewordener Freundlichkeit. Bitte, nehmt diese Klinge als Zeichen meiner Dankbarkeit. Vor langer Zeit ließ ein anderer Reisender sie hier zurück, doch ich habe wenig Verwendung dafür.' WHERE entry=2000000602; /* content: Your gift is a rare kindness, traveler. Please take this blade with my gratitude. Long ago, another traveler left it here, but I have little use for it. */
UPDATE db_script_string SET content_loc3='Sie hat mir nichts als Übel gebracht. Vielleicht findet Ihr jemanden, der ihre Macht zu beherrschen weiß.' WHERE entry=2000000604; /* content: It has brought me naught but ill. Mayhap you can find someone who will contain its power. */
UPDATE db_script_string SET content_loc3='Verräterischer Wurm! Sironas wird Euch und Euer Volk vernichten!' WHERE entry=2000000606; /* content: Treacherous whelp! Sironas will destroy you and your people! */
UPDATE db_script_string SET content_loc3='Mein Bruder, Sturmhuf, war ein größerer Krieger, als ich es bin. Während ich dafür trainierte, der Häuptling unseres Klans zu werden, hat er sich auf eine weit größere Aufgabe vorbereitet.' WHERE entry=2000000607; /* content: My brother, Stormhoof, was a far greater warrior than I. While I trained to be the chieftain of our clan, he prepared for a larger quest. */
UPDATE db_script_string SET content_loc3='Wie könnte er sich in Gefahr befinden? Alles, was er wollte, war, uns Macht über die Elemente zu geben, die uns das Leben hier so schwer gemacht haben.' WHERE entry=2000000608; /* content: How could he be in danger? All he wanted was to give us power over the elements that made life harsh for us here. */
UPDATE db_script_string SET content_loc3='Er hat nach einem Artefakt gesucht... ein Horn, glaube ich. Er hat das Dorf für eine lange Suche danach verlassen.' WHERE entry=2000000609; /* content: He sought an artifact... a horn, I think. He left the village on a long journey in search of it. */
UPDATE db_script_string SET content_loc3='Ich weiß, dass ihn ein schrecklicher Gegner gejagt hat, nachdem er das Horn erhalten hatte, aber ich... Ich kann mich nicht erinnern... was als Nächstes... geschehen ist.' WHERE entry=2000000610; /* content: I know that a terrible enemy pursued him after he won the horn, but I... I don\'t recall... what happened... next. */
UPDATE db_script_string SET content_loc3='Es ist, als wären meine Erinnerungen von Nebel umgeben. Ich kann mich noch nicht mal erinnern, was aus meinem Bruder geworden ist. Meint Ihr darum, er sei in Gefahr?' WHERE entry=2000000611; /* content: It is as though my memories are shrouded in mist. I cannot even recall what became of my brother. Is this how you mean that he is in danger? */
UPDATE db_script_string SET content_loc3='Die Unruhen in der Gruft müssen etwas damit zu tun haben. Schaut Euch um, $n. Seht Ihr sie?' WHERE entry=2000000612; /* content: The disturbances in the tomb, they must be involved. Look around you, $N. Do you see them? */
UPDATE db_script_string SET content_loc3='Ja, sie sind im Unrecht, aber ich bin machtlos, sie aufzuhalten. Werdet Ihr die Leute von Tunka\'lo warnen, Fremder, dass ihre Vergangenheit und ihre Vorfahren in Gefahr sind?' WHERE entry=2000000613; /* content: Yes, they are at fault, but I am powerless to stop them. Will you warn the people of Tunka\'lo, stranger, that their past and their ancestors are in danger? */
UPDATE db_script_string SET content_loc3='Der Lehrenhammer bleibt stumm. Das stammt nicht aus Sturmhufs Zeit.' WHERE entry=2000000615; /* content: The Lorehammer is silent. This isn\'t from Stormhoof\'s time. */
UPDATE db_script_string SET content_loc3='Möge $gder:die; Bessere gewinnen!' WHERE entry=2000000617; /* content: May the best win! */
UPDATE db_script_string SET content_loc3='Ihr werdet mich nicht bezwingen!' WHERE entry=2000000618; /* content: You will not defeat me! */
UPDATE db_script_string SET content_loc3='Ihr seid Thorims nicht würdig!' WHERE entry=2000000619; /* content: You\'re not worthy of Thorim! */
UPDATE db_script_string SET content_loc3='Aus dem Kultisten sprudelt es förmlich heraus.' WHERE entry=2000000621; /* content: The cultist practically sputters. */
UPDATE db_script_string SET content_loc3='Mylady.' WHERE entry=2000000622; /* content: My lady. */
UPDATE db_script_string SET content_loc3='Berichtet.' WHERE entry=2000000623; /* content: Report. */
UPDATE db_script_string SET content_loc3='Nachrichten aus Jotunheim. Die dortigen Schlafwächter glauben, jemanden gefunden zu haben, der wichtig sein könnte.' WHERE entry=2000000624; /* content: There is word from Jotunheim. The sleep-watchers there believe that they have found someone of significance. */
UPDATE db_script_string SET content_loc3='Beschreibt diesen Vrykul. Wie sieht er aus?' WHERE entry=2000000625; /* content: Describe this vrykul. What does he look like? */
UPDATE db_script_string SET content_loc3='Aussehen, Mylady? Wie ein Vrykul, nehme ich an. Sie haben ihn mir nicht wirklich gezeigt. Seit der Sache mit dem Schattengewölbe...' WHERE entry=2000000626; /* content: Look like, my lady? A vrykul, I suppose. They did not actually show him to me. Ever since The Shadow Vault.... */
UPDATE db_script_string SET content_loc3='Ausreden interessieren mich nicht. Vielleicht haben sie Euch einen Namen gegeben?' WHERE entry=2000000627; /* content: I am not interested in excuses. Perhaps they gave you a name? */
UPDATE db_script_string SET content_loc3='Ein Name? Oh, ja, der Name! Ich glaube, es war Iskalder!' WHERE entry=2000000628; /* content: A name? Oh, yes, the name! I believe it was Iskalder. */
UPDATE db_script_string SET content_loc3='Kehrt nach Jotunheim zurück und sagt ihnen, sie sollen ihn weiterschlafen lassen, bis ich komme. Ich werde mir diesen Vrykul mit eigenen Augen ansehen.' WHERE entry=2000000630; /* content: Return to Jotunheim and tell them to keep him asleep until I arrive. I will judge this vrykul with my own eyes. */
UPDATE db_script_string SET content_loc3='Auf der Stelle, Mylady.' WHERE entry=2000000631; /* content: Right away, my lady. */
UPDATE db_script_string SET content_loc3='Morae... Bist du das?' WHERE entry=2000000632; /* content: Morae... Is that you? */
UPDATE db_script_string SET content_loc3='Nein... Ihr seid nicht Morae, aber ich spüre, dass Ihr Euch begegnet seid. Wenn Ihr sie seht, sagt ihr, dass ich den Absturz überlebt habe, nur um wenig später durch die Verräter den Tod zu finden.' WHERE entry=2000000633; /* content: No... you\'re not Morae, but I sense that you have met. When you see her, tell her that I survived the crash, only to be done in by the infiltrators. */
UPDATE db_script_string SET content_loc3='Sagt ihr... dass meine letzten Gedanken einzig ihr galten...' WHERE entry=2000000634; /* content: Let her know... my last thought was of her... */
UPDATE db_script_string SET content_loc3='Grek probieren!' WHERE entry=2000000635; /* content: Grek try! */
UPDATE db_script_string SET content_loc3='Dieses Öl nicht gut für Grek! Sieht Grek aus wie Ihr, Schwächling in Robe?' WHERE entry=2000000636; /* content: This oil no good for Grek! What Grek look like to you, some weakling in robes? */
UPDATE db_script_string SET content_loc3='%s lacht.' WHERE entry=2000000637; /* content: %s laughs. */
UPDATE db_script_string SET content_loc3='Macht Euch das Wissen Angst, dass jene, die der Legion so inbrünstig dienen, sich bis zu dem Moment ihres Einsatzes als rechtschaffene Bürger Eurer nutzlosen Zivilisation ausgeben?' WHERE entry=2000000638; /* content: Does it frighten you to know that there are those that would serve the Legion with such devotion as to remain unwavering citizens of your pointless civilizations until called upon? */
UPDATE db_script_string SET content_loc3='Lebt in Angst oder sterbt durch den Willen Kael\'thas\'... es kommt auf das Gleiche heraus.' WHERE entry=2000000639; /* content: Live in fear, die by the will of Kael\'thas... It\'s all the same. */
UPDATE db_script_string SET content_loc3='Und jetzt werde ich Euch aufschlitzen!' WHERE entry=2000000640; /* content: And now, I cut you! */
UPDATE db_script_string SET content_loc3='Sterbt!' WHERE entry=2000000641; /* content: DIE! */
UPDATE db_script_string SET content_loc3='%s hebt die Nagaflagge auf.' WHERE entry=2000000642; /* content: %s picks up the naga flag. */
UPDATE db_script_string SET content_loc3='Wir werden nicht versagen!' WHERE entry=2000000643; /* content: We will not fail! */
UPDATE db_script_string SET content_loc3='%s stellt die Kiste mit den Teilen auf den Boden.' WHERE entry=2000000644; /* content: %s places the crate of parts on the ground. */
UPDATE db_script_string SET content_loc3='%s greift sich ein Teil und steckt es in die Tasche...' WHERE entry=2000000645; /* content: %s grabs a part and puts it in his pocket... */
UPDATE db_script_string SET content_loc3='So, das sollte reichen...' WHERE entry=2000000646; /* content: There, that should do it... */
UPDATE db_script_string SET content_loc3='Anaya...? Kann ich meinen Augen trauen? Bist du es wirklich?' WHERE entry=2000000647; /* content: Anaya...? Do my eyes deceive me? Is it really you? */
UPDATE db_script_string SET content_loc3='Die Jahre waren unbarmherzig zu dir und zu mir, mein Herz, aber sei versichert, ich bin es wirklich, und nach so langer Zeit sind wir endlich wieder vereint.' WHERE entry=2000000648; /* content: The ages have been cruel to you and I, my love, but be assured, it is, and at long last we are reunited. */
UPDATE db_script_string SET content_loc3='Dass das Schicksal so grausam sein kann, uns nach tausend Jahren der Trennung nur dies zu gestatten...' WHERE entry=2000000649; /* content: That the fates should be so cruel as to permit us only this after a thousand years apart... */
UPDATE db_script_string SET content_loc3='Hasst du mich, oh Liebe meines Lebens? Dafür, dass ich deine lebende Gestalt zerstören musste, damit dein Geist von unglücklicher Knechtschaft befreit wurde?' WHERE entry=2000000650; /* content: Do you hate me, my love? That I was forced to destroy your living form, that your spirit be released from unhappy bondage. */
UPDATE db_script_string SET content_loc3='Lass es nicht dein Herz betrüben, Geliebter. Du hast mich aus der Sklaverei befreit und dafür liebe ich dich umso mehr.' WHERE entry=2000000651; /* content: Let it not trouble your heart, beloved. You have freed me from slavery, and for that I love you all the more. */
UPDATE db_script_string SET content_loc3='Leider kann sogar dies nicht lange dauern... Die Bindungen, die mich in dieser Welt halten, werden schwächer und ziehen mich fort...' WHERE entry=2000000652; /* content: Sadly, even this must be cut short... The ties that bind me to this world weaken, and pull me away... */
UPDATE db_script_string SET content_loc3='Nein! Anaya... Anaya! Verlass mich nicht! Bitte...' WHERE entry=2000000653; /* content: No! Anaya... Anaya! Don\'t leave me! Please... */
UPDATE db_script_string SET content_loc3='Lebe wohl, Cerellean, bis wir wieder vereint sind...' WHERE entry=2000000654; /* content: Farewell, Cerellean, until we are joined once again... */
UPDATE db_script_string SET content_loc3='Wie, Geliebte... wie soll ich die Kraft finden, den Lauf der Zeit zu überdauern, ohne dich an meiner Seite...' WHERE entry=2000000656; /* content: How, my love? How will I find the strength to face the ages of the world without you by my side... */
UPDATE db_script_string SET content_loc3='Die Klippenquellfälle befinden sich in den Bergen Richtung Osten und Norden.' WHERE entry=2000000658; /* content: Cliffspring Falls lies along the mountains to the east and north. */
UPDATE db_script_string SET content_loc3='Geht in den Ring des Blutes, $N. Der Kampf wird bald beginnen!' WHERE entry=2000000659; /* content: The deeds are mine, brother! Soon you shall be out of my way for good! */
UPDATE db_script_string SET content_loc3='TROLL MACHT MICH WÜTEND!' WHERE entry=2000000660; /* content: TROLL MAKE ME ANGRY! */
UPDATE db_script_string SET content_loc3='Das bedeuten Krieg!' WHERE entry=2000000661; /* content: This mean war! */
UPDATE db_script_string SET content_loc3='Ich bin der Herold von Mechazod. Ihr werdet vom Fluch befreit werden!' WHERE entry=2000000663; /* content: I am the herald of Mechazod. You will be decursed! */
UPDATE db_script_string SET content_loc3='Eure Einmischung findet hier ein Ende. Mechazod wird Euch von Eurem Fluch befreien, wenn ich mit Euch fertig bin.' WHERE entry=2000000664; /* content: Your meddling is at an end. Mechazod will relieve your curse once I am done with you. */
UPDATE db_script_string SET content_loc3='ED-210 online!' WHERE entry=2000000665; /* content: ED-210 online! */
UPDATE db_script_string SET content_loc3='Twonkie!' WHERE entry=2000000666; /* content: Twonky! */
UPDATE db_script_string SET content_loc3='Sieg oder Tod! Für die Horde!' WHERE entry=2000000669; /* content: Victory or death! For the Horde! */
UPDATE db_script_string SET content_loc3='Der Kampf wird bald beginnen!' WHERE entry=2000000670; /* content: Honor your heroes! On this day, they have dealt a great blow against one of our most hated enemies! The false Warchief, Rend Blackhand, has fallen! */
UPDATE db_script_string SET content_loc3='$N gegen den grausamen Grollhuf, Schmetterzehe!' WHERE entry=2000000671; /* content: Be bathed in my power! Drink in my might! Battle for the glory of the Horde! */
UPDATE db_script_string SET content_loc3='Ich widerspreche nur ungern, aber wenn Ihr mich fragt, seid ihr Oger einfach nur zu groß! Ich sehe schon, heute werde ich Euch wohl nicht zu einem Umzug bewegen können. Aber glaubt nicht, dass Ihr auf ewig hier wohnen könnt!' WHERE entry=2000000672; /* content: Go swiftly $N. My fate is in your hands. */
UPDATE db_script_string SET content_loc3='Ihr dachtet also Ihr könntet Mor\'ghor zum Narren halten, wie? Bevor Ihr Fürst Illidan übergeben werdet, werdet Ihr Schmerzen fühlen, deren Existenz Euch bisher unbekannt war. Mit Freuden werde ich meine Rache auskosten.' WHERE entry=2000000673; /* content: Wait, $N!!! It looks like we\'re going to need some help here... */
UPDATE db_script_string SET content_loc3='%s beginnt zu tanzen.' WHERE entry=2000000674; /* content: %s begins to dance. */
UPDATE db_script_string SET content_loc3='Habt Ihr Drull schon befreit? Ich weiß nicht, wo er festgehalten wird.' WHERE entry=2000000676; /* content: Have you freed Drull yet? I don\'t know where he\'s being held. */
UPDATE db_script_string SET content_loc3='Ich werde Euch nur aufhalten, aber kein rosahäutiger Mensch kann mir jetzt noch meine Freiheit nehmen!' WHERE entry=2000000677; /* content: I will only slow you down, but no pink-skinned humans will keep me from freedom! */
UPDATE db_script_string SET content_loc3='Hier oben auf dem Hügel, $n.' WHERE entry=2000000679; /* content: Up here on the hill, $N. */
UPDATE db_script_string SET content_loc3='Kommt, $N. Fürst Sturmgrimm erwartet Euch.' WHERE entry=2000000680; /* content: Let me help you with those. */
UPDATE db_script_string SET content_loc3='Krieger des Drachenmals, schart euch zusammen! Einer unter euch hat den Rang des Hochlords erreicht! Verneigt euch aus Ehrfurcht! Zeigt eure Hochachtung und Loyalität gegenüber Hochlord $N!' WHERE entry=2000000681; /* content: Drink this, it will help. */
UPDATE db_script_string SET content_loc3='Fürst Illidan wird hier bald eintreffen.' WHERE entry=2000000682; /* content: You\'re going to be just fine. */
UPDATE db_script_string SET content_loc3='Ihr seid tot, aber nicht begraben. Jetzt gebt Euer Bestes!' WHERE entry=2000000686; /* content: You\'re dead, not buried. Now show me your best! */
UPDATE db_script_string SET content_loc3='Nun gut, ihr traurigen Knochensäcke. Jetzt zeigt mal, was ihr draufhabt. Schlagt euren Gegner und hört erst auf, wenn ich es sage.' WHERE entry=2000000687; /* content: Alright you sorry sacks of bones. Let\'s see what you\'ve got. Strike your opponent, and don\'t stop till I say so. */
UPDATE db_script_string SET content_loc3='Ich hoffe, dass diese Blutelfen gegenüber nicht denken, dass ich sie ausspionieren will!' WHERE entry=2000000688; /* content: I hope those blood elves across the way don\'t think that I\'m spying on them! */
UPDATE db_script_string SET content_loc3='Ich habe dir ein Einweihungsgeschenk mitgebracht, Jarl.' WHERE entry=2000000692; /* content: I brought you a housewarming gift, Jarl. */
UPDATE db_script_string SET content_loc3='Na los, pack\'s aus. Ich glaube, es wird dir gefallen.' WHERE entry=2000000693; /* content: Go on, open it. I think you\'ll like it. */
UPDATE db_script_string SET content_loc3='Jarl?' WHERE entry=2000000694; /* content: Jarl? */
UPDATE db_script_string SET content_loc3='Wenn ich es nicht essen kann, mag ich es nicht.' WHERE entry=2000000695; /* content: Unless it\'s something I can eat, I don\'t want it. */
UPDATE db_script_string SET content_loc3='Jarl, seit ich dich das letzte Mal gesehen habe, bist du wie verwandelt. Was ist los?' WHERE entry=2000000696; /* content: Jarl, you haven\'t been yourself ever since I arrived. What\'s going on? */
UPDATE db_script_string SET content_loc3='Nichts. Hilfst du mir, ein wenig Froschschenkeleintopf sämig zu rühren?' WHERE entry=2000000697; /* content: Nothing. Wanna help me whip up some frog leg stew? */
UPDATE db_script_string SET content_loc3='Ich bin hierhergekommen, um dich zu besuchen, Jarl! Und was finde ich? Dich, in einer leeren Hütte mitten im Nirgendwo, wie du über Frösche plapperst? Ich geb\'s auf!' WHERE entry=2000000698; /* content: I came out here to visit you, Jarl! And I find you living in an empty cabin in the middle of nowhere, babbling about frogs? You\'re hopeless! */
UPDATE db_script_string SET content_loc3='Edward. Tyler. Macht euch für euren ersten Kampf bereit.' WHERE entry=2000000699; /* content: Edward. Tyler. Prepare for your first challenge. */
UPDATE db_script_string SET content_loc3='Lysta, ruft die Gefangenen herein.' WHERE entry=2000000700; /* content: Lysta, summon in the captives. */
UPDATE db_script_string SET content_loc3='Lysta, beschwöre untote Gefangene.' WHERE entry=2000000702; /* content: Lysta, summon in undead captives. */
UPDATE db_script_string SET content_loc3='Lysta, beschwört sie hervor... die Monstrosität!' WHERE entry=2000000704; /* content: Lysta, summon forth... the abomination! */
UPDATE db_script_string SET content_loc3='Gut gemacht, Edward und Tyler. Ihr macht schöne Fortschritte bei eurer Ausbildung. Wir werden schon bald wieder überprüfen, was in euch steckt.' WHERE entry=2000000705; /* content: Well done Edward and Tyler. You are progressing along in your training quite nicely. We shall test your mettle again soon. */
UPDATE db_script_string SET content_loc3='Danke für meine Rettung, $n! Mein Vater wird höchst erfreut sein!' WHERE entry=2000000706; /* content: Thank you for saving me, $N! My father will be delighted! */
UPDATE db_script_string SET content_loc3='Bist du es, $gder:die; mein Abendessen gestört hat? Nun, bist du\'s?' WHERE entry=2000000707; /* content: Are YOU the one who interrupted my dinner? Well, are you? */
UPDATE db_script_string SET content_loc3='Du bist zu hässlich und dürr, um ein Yetiweibchen zu sein! Und jetzt machst du mich sauer...' WHERE entry=2000000708; /* content: You\'re too ugly and scrawny to be a female yeti! And now you\'re making me mad... */
UPDATE db_script_string SET content_loc3='Nochmals danke, $n. Ich werde jetzt einfach hier warten, bis ich ungefährdet gehen kann.' WHERE entry=2000000709; /* content: Thank again, $n.  Now I\'ll just wait here until it\'s safe to leave. */
UPDATE db_script_string SET content_loc3='Die misslungene Mischung fällt auf den Boden und scheint lebendig zu werden!' WHERE entry=2000000710; /* content: The failed concoction spills on the floor and appears to come to life! */
UPDATE db_script_string SET content_loc3='Es lebt!' WHERE entry=2000000711; /* content: It\'s alive! */
UPDATE db_script_string SET content_loc3='Urtrak wird Euch töten!' WHERE entry=2000000715; /* content: Urtrak kill you! */
UPDATE db_script_string SET content_loc3='Die Rache ist unser! Greift an, meine Brüder!' WHERE entry=2000000716; /* content: Vengeance is ours!  Attack my brothers! */
UPDATE db_script_string SET content_loc3='Dann wollen wir doch mal sehen, wie diese Samen in meiner vorbereiteten Erde wachsen...' WHERE entry=2000000718; /* content: Let\'s see how these seeds grow in my prepared soil... */
UPDATE db_script_string SET content_loc3='Hoffentlich wurzelt dieser Wedel bald...' WHERE entry=2000000722; /* content: I hope this frond takes root... */
UPDATE db_script_string SET content_loc3='%s beginnt, den Stein von Relu über den beiden Fossilien zu benutzen.' WHERE entry=2000000723; /* content: %s begins to manipulate the Stone of Relu over the two fossils. */
UPDATE db_script_string SET content_loc3='Wer? Die Tauren Chieftains! Was? Ein Livekonzert! Wo? In der Taverne Weltenend! Wann? Zur vollen Stunde! LASST EUCH DAS NICHT ENTGEHEN!' WHERE entry=2000000724; /* content: Who hath summoned forth Aman? */
UPDATE db_script_string SET content_loc3='Ihr habt gesagt, dass Brogg hier bleiben kann! Ihr habt versprochen, dass Ihr Brogg bei der Rückeroberung des Horts der Steinbrecher helft!' WHERE entry=2000000725; /* content: Ah, I see you toil with relics of the past. */
UPDATE db_script_string SET content_loc3='Hmmm... wo ist mein Angelhaken? Oh, da ist er ja.' WHERE entry=2000000726; /* content: Be warned that even your creators are fallible. */
UPDATE db_script_string SET content_loc3='Mal sehen, ob die Fische anbeißen.' WHERE entry=2000000727; /* content: Digging too deep into your past might bring an abrupt end to your future. */
UPDATE db_script_string SET content_loc3='In Ordnung, los jetzt!' WHERE entry=2000000729; /* content: Ah, that sure does hit the spot! I think i\'ll get myself a couple more...can you watch these barrels for me, $N? */
UPDATE db_script_string SET content_loc3='Oh nein! Sie sind hier!' WHERE entry=2000000730; /* content: How goes the barrel watching...? */
UPDATE db_script_string SET content_loc3='Oh, hallo Grimmklaue.' WHERE entry=2000000731; /* content: Well, back to business for me. But it sure was nice taking that short break, and it\'s always nice drinking Thunder Ale! */
UPDATE db_script_string SET content_loc3='%s legt sein Wolfsfellgewand an.' WHERE entry=2000000732; /* content: %s dons his wolf-fur vestments. */
UPDATE db_script_string SET content_loc3='%s heult als Antwort auf Malukaz\' Ruf.' WHERE entry=2000000733; /* content: %s howls in response to Malukaz\'s call. */
UPDATE db_script_string SET content_loc3='%s drückt sein Wohlwollen aus.' WHERE entry=2000000734; /* content: %s expresses its approval. */
UPDATE db_script_string SET content_loc3='Möge unser Band nie mehr zerreißen, Geist.' WHERE entry=2000000735; /* content: May our bond with you never be broken again, spirit. */
UPDATE db_script_string SET content_loc3='Wir haben es geschafft! Thrall wäre stolz auf uns.' WHERE entry=2000000736; /* content: We have done it! Thrall would be proud. */
UPDATE db_script_string SET content_loc3='Was soll das bedeuten, Mor\'ghor?' WHERE entry=2000000737; /* content: What is the meaning of this, Mor\'ghor? */
UPDATE db_script_string SET content_loc3='Ruhe!' WHERE entry=2000000738; /* content: SILENCE! */
UPDATE db_script_string SET content_loc3='DIES ist Euer Held?' WHERE entry=2000000740; /* content: THIS is your hero? */
UPDATE db_script_string SET content_loc3='Ihr wurdet getäuscht, Schwachkopf.' WHERE entry=2000000741; /* content: You have been deceived, imbecile. */
UPDATE db_script_string SET content_loc3='Diese... gesamte... Operation WURDE KOMPROMITTIERT!' WHERE entry=2000000742; /* content: This... whole... operation... HAS BEEN COMPROMISED! */
UPDATE db_script_string SET content_loc3='Ich erwarte, dieses Insekt innerhalb einer Stunde in Einzelteilen in meinem Gewölbe zu sehen. Versagt, und Euer Schicksal wird viel schlimmer als der Tod sein.' WHERE entry=2000000743; /* content: I expect to see this insect\'s carcass in pieces in my lair within the hour. Fail and you will suffer a fate so much worse than death. */
UPDATE db_script_string SET content_loc3='Ist es jetzt sicher, dieses verdammte Ding zu betreten?' WHERE entry=2000000747; /* content: Is that damned thing safe to go through yet? */
UPDATE db_script_string SET content_loc3='Nicht wirklich.' WHERE entry=2000000748; /* content: Not really. */
UPDATE db_script_string SET content_loc3='Hört Ihr bald auf, mich das zu fragen?' WHERE entry=2000000749; /* content: Will you stop asking that? */
UPDATE db_script_string SET content_loc3='Ok, sieht sicher aus. Rein da, Ingenieur!' WHERE entry=2000000750; /* content: Alright, it looks safe. Get in there, engineer! */
UPDATE db_script_string SET content_loc3='Hier rührt sich gar nichts!' WHERE entry=2000000751; /* content: Here goes nothing! */
UPDATE db_script_string SET content_loc3='Das wird eine Schramme geben...' WHERE entry=2000000752; /* content: This is gonna leave a mark... */
UPDATE db_script_string SET content_loc3='%s tastet sich ab, um sicherzugehen, dass alles heil ist.' WHERE entry=2000000753; /* content: %s runs his hands across his body to make sure everything is intact. */
UPDATE db_script_string SET content_loc3='Ich habe es geschafft, ich habe es geschafft!' WHERE entry=2000000754; /* content: I made it! I made it! */
UPDATE db_script_string SET content_loc3='%s schüttelt ablehnend den Kopf.' WHERE entry=2000000755; /* content: %s shakes his head in disapproval. */
UPDATE db_script_string SET content_loc3='Ich konnte ihn eh nie richtig leiden. Hat zu viel geredet und zu wenig gehandelt.' WHERE entry=2000000756; /* content: I didn\'t like him much anyway. Too much talk, not enough "do." */
UPDATE db_script_string SET content_loc3='Fantastisch, ihr nutzlosen Taugenichtse! Wieder ein Ingenieur zum Teufel!' WHERE entry=2000000757; /* content: Nice going you incompetent morons! Another one of our engineers vaporized! */
UPDATE db_script_string SET content_loc3='Ihr könnt uns nicht davon abhalten, die Öffentlichkeit über Jainas Feigheit aufzuklären!' WHERE entry=2000000758; /* content: You can\'t stop us from exposing the truth about Jaina\'s cowardice! */
UPDATE db_script_string SET content_loc3='Wenn Ihr glaubt, uns aufhalten zu können, dann irrt Ihr Euch, Freundchen.' WHERE entry=2000000759; /* content: If you think you can stop us, you\'re mistaken, \'friend.\' */
UPDATE db_script_string SET content_loc3='Die Leute werden Admiral Prachtmeer als den Helden kennen, der er wirklich war!' WHERE entry=2000000760; /* content: These people will know Admiral Proudmoore for the true hero he was! */
UPDATE db_script_string SET content_loc3='Mit etwas Kreativität verwandeln sie sich in etwas viel Nützlicheres...' WHERE entry=2000000761; /* content: With just a little creative editing, these are much more useful... */
UPDATE db_script_string SET content_loc3='Es wäre doch eine wahre Schande, wenn diese Handzettel auf dem Müll landen würden, oder?' WHERE entry=2000000762; /* content: It would be a shame to let these leaflets go to waste, wouldn\'t it? */
UPDATE db_script_string SET content_loc3='...es soll ein Fond für den Kauf von Haargel eingerichtet werden? Vielen Dank, aber ich mag meine Haare auch so!' WHERE entry=2000000763; /* content: ...to create a fund for the purchase of hair gel? I like my hair the way it is, thanks! */
UPDATE db_script_string SET content_loc3='...vergnügt Euch als Deserteur im Kinderplanschbecken oder bei der Arbeit im Hundesalon? Wie lächerlich!' WHERE entry=2000000764; /* content: ...as a deserter, you\'ll enjoy activities like tethered swimming and dog grooming? How ridiculous! */
UPDATE db_script_string SET content_loc3='Ich würde mich einer Sache wie dieser niemals anschließen. Behaltet das mal lieber. Es könnte Euch auf dem stillen Örtchen noch von Nutzen sein...' WHERE entry=2000000765; /* content: I\'d never join anything like this. Better keep this, though. It\'ll come in handy in the privy... */
UPDATE db_script_string SET content_loc3='...die Deserteure wünschen sich ein Theramore, in dem die Bürger die Freiheit besitzen, jederzeit und überall zu jonglieren? Was ist das für ein Quatsch?' WHERE entry=2000000766; /* content: ...the deserters seek a Theramore where citizens will be free to juggle at all hours of the day? What is this nonsense? */
UPDATE db_script_string SET content_loc3='Das ist das Lustigste, was ich seit langer Zeit gelesen habe. Damit habt Ihr meinen Tag gerettet!' WHERE entry=2000000767; /* content: That\'s the funniest thing I\'ve read in a long time. You\'ve just made my day! */
UPDATE db_script_string SET content_loc3='Das... das ist ein Scherz, nicht wahr?' WHERE entry=2000000768; /* content: This... this is a joke, right? */
UPDATE db_script_string SET content_loc3='Die Geister mögen über Euch wachen, $R!' WHERE entry=2000000770; /* content: Spirits watch over you, $r! */
UPDATE db_script_string SET content_loc3='Vielen Dank, $gHeld:Heldin;.' WHERE entry=2000000771; /* content: Many Thanks, hero! */
UPDATE db_script_string SET content_loc3='Ich danke Euch dafür, dass Ihr mich gerettet habt, aber die anderen sind noch immer in den oberen Etagen!' WHERE entry=2000000774; /* content: Thank you for rescuing me, but the others are still held on the tiers above! */
UPDATE db_script_string SET content_loc3='Diese Eisenzwerge sind verrückt! Sie reden die ganze Zeit davon, ihrem \'Meister Loken\' zu dienen, aber soweit ich das sagen kann, bekommen nur wenige von ihnen Befehle von ihm.' WHERE entry=2000000775; /* content: These irons are crazy! They keep talking about serving their \'master Loken\' but from what I can tell, only a few receive orders from him. */
UPDATE db_script_string SET content_loc3='Gott sei Dank! Ich hatte befürchtet, sie würden mich zum Dienst in ihrem Krieg gegen die Steinriesen zwingen!' WHERE entry=2000000776; /* content: Thank goodness! I was worried they were going to press me into service in their war against the stone giants! */
UPDATE db_script_string SET content_loc3='Ihr wagt es, Drottinn Hrothgar herauszufordern, den König der Meere?' WHERE entry=2000000777; /* content: You dare to challenge Drottinn Hrothgar, king of the seas? */
UPDATE db_script_string SET content_loc3='Erteilt diesen Kultisten eine Lektion, $n. Niemand legt sich mit Kreuzfahrer Kul an und kommt einfach so davon!' WHERE entry=2000000778; /* content: Teach those cultists a lesson, $N. No one messes with Crusader Kul and gets away with it! */
UPDATE db_script_string SET content_loc3='Dem Licht sei Dank! So etwas Dummes tue ich nie wieder!' WHERE entry=2000000779; /* content: Thank the Light! I\'ll never do anything this foolish again! */
UPDATE db_script_string SET content_loc3='Lasst uns den Rest der Streiter retten und zum Lager zurückkehren. Ich werde ein ganzes Fass Bier brauchen nach der Geschichte hier!' WHERE entry=2000000780; /* content: Let\'s rescue the rest of those aspirants and get back to camp. I\'m going to need a whole keg of ale after this! */
UPDATE db_script_string SET content_loc3='Sie waren nur Sekunden davon entfernt, mich auf einen dieser Altäre zu schnallen und meine Seele rauszusaugen. Nichts wie raus hier!' WHERE entry=2000000781; /* content: They were seconds from strapping me to one of those altars and sucking my soul out. Let\'s get out of here! */
UPDATE db_script_string SET content_loc3='Danke, $C.' WHERE entry=2000000782; /* content: Thank you, $C. */
UPDATE db_script_string SET content_loc3='Sie töten die Gefangenen! Schnell, helft den Anderen!' WHERE entry=2000000783; /* content: They\'re killing the prisoners! Hurry, help the others. */
UPDATE db_script_string SET content_loc3='Ich wusste, ich hätte nicht zum Turnier kommen sollen!' WHERE entry=2000000784; /* content: I knew I shouldn\'t have come to the tournament! */
UPDATE db_script_string SET content_loc3='Ich dachte nicht, dass ich hier lebendig herauskomme.' WHERE entry=2000000785; /* content: I thought I\'d never make it out of here alive. */
UPDATE db_script_string SET content_loc3='Ihr seid wahrlich töricht, $C, Ihr könnt die Kvaldir nicht aufhalten!' WHERE entry=2000000786; /* content: Foolish $c, you cannot stop the Kvaldir! */
UPDATE db_script_string SET content_loc3='Verlasst diesen Ort.' WHERE entry=2000000792; /* content: Leave this place. */
UPDATE db_script_string SET content_loc3='Ihr werdet den Frostfürsten nicht davon abhalten können, diese Welt zu betreten. Der Gezeitenjäger wird Ragnaros ein für alle Mal vernichten und Eure Welt in ein gefrorenes Paradies verwandeln!' WHERE entry=2000000797; /* content: You will not stop the Frost Lord from entering this world, mortal. The Tidehunter\'s might will crush that of Ragnaros once and for all, leaving your land a frozen paradise! */
UPDATE db_script_string SET content_loc3='Danke Euch, $R. Ich werde Euren Kampf gegen die Sturmgeschmiedeten unterstützen.' WHERE entry=2000000798; /* content: Thank you, $r. I will join your struggle against the stormforged. */
UPDATE db_script_string SET content_loc3='Kein Datenbankeintrag. Fehlfunktion des Elements. Anweisung: Abschaltung.' WHERE entry=2000000799; /* content: Does not compute. Unit malfunctioning. Directive: shut down. */
UPDATE db_script_string SET content_loc3='Wieder frei? Die Arbeit von Hüter Mimir wartet.' WHERE entry=2000000800; /* content: Free again? Keeper Mimir\'s work awaits. */
UPDATE db_script_string SET content_loc3='Neue Anweisung: Helft beim Kampf gegen die Eisenzwerge.' WHERE entry=2000000801; /* content: New directive: assist in the defeat of the iron dwarves. */
UPDATE db_script_string SET content_loc3='Unverschämter Narr! Ihr wolltet Zelemars Blut stehlen? Dafür werdet Ihr mit Eurem eigenen bezahlen!' WHERE entry=2000000802; /* content: Insolent fool! You thought to steal Zelemar\'s blood? You shall pay with your own! */
UPDATE db_script_string SET content_loc3='Frisches Brot zu verkaufen!' WHERE entry=2000000804; /* content: Get him out of there! */
UPDATE db_script_string SET content_loc3='Greif zu, solange es noch heiß ist!' WHERE entry=2000000805; /* content: Perhaps Ograbisi will use your head as a tiny little hat when I\'m through with you. */
UPDATE db_script_string SET content_loc3='Frisches Brot zu verkaufen!' WHERE entry=2000000806; /* content: Where I come from, you get shanked for opening another inmate\'s cell door! */
UPDATE db_script_string SET content_loc3='Greif zu, solange es noch heiß ist!' WHERE entry=2000000807; /* content: Ograbisi needs new hat. */
UPDATE db_script_string SET content_loc3='Ich habe genug von Eurem ständigen Rumgeheule. Von was seid Ihr eigentlich Kapitän? Ihr habt vielleicht Nerven!' WHERE entry=2000000808; /* content: I\'ve had just about enough of your incessant whining, captain. What are you even a captain of? I mean, The nerve! */
UPDATE db_script_string SET content_loc3='%s zuckt mit den Achseln.' WHERE entry=2000000810; /* content: %s shrugs. */
UPDATE db_script_string SET content_loc3='Na gut, also zurück zum Zeichenbrett.' WHERE entry=2000000811; /* content: Oh well, back to the drawing board. */
UPDATE db_script_string SET content_loc3='Hoffen wir, dass er das Licht sieht und er seine Energien von nun ab für den Aufbau des einst so schönen Quel\'Thalas einsetzt.' WHERE entry=2000000816; /* content: I can only hope that he will see the Light and instead turn his energies to restoring once-beautiful Quel\'Thalas. */
UPDATE db_script_string SET content_loc3='Hel\'gesh Neider! Was ein Künstler! Das Wirtshaus ist die ganze Nacht offen, also bleibt und macht es Euch gemütlich!' WHERE entry=2000000818; /* content: The boss wants all hands on high alert. The rusty anchor sinks tonight. */
UPDATE db_script_string SET content_loc3='Puh! Da ist ja meine Glückskappe. Ich habe schon überall danach gesucht.' WHERE entry=2000000819; /* content: Phew! There\'s my lucky hat. I\'ve been looking for it everywhere. */
UPDATE db_script_string SET content_loc3='Was habe ich mir nur dabei gedacht, ohne mein Schwert aus dem Haus zu gehen. Ich hätte es mitgenommen, wenn ich es hier gesehen hätte...' WHERE entry=2000000820; /* content: I don\'t know what I was thinking, going out without my sword. I would\'ve put it on if I\'d seen it here... */
UPDATE db_script_string SET content_loc3='Ich kann ohne meinen Mantel einfach nicht aus dem Haus gehen. Ich hoffe, er ist hier drin...' WHERE entry=2000000821; /* content: I can\'t possibly go out without my cloak. I hope it\'s in here... */
UPDATE db_script_string SET content_loc3='Da ist er ja! Ich hätte schwören können, dass er das letzte Mal, als ich nachgesehen habe, nicht da war.' WHERE entry=2000000822; /* content: There it is! I could\'ve sworn it wasn\'t here last time I checked... */
UPDATE db_script_string SET content_loc3='Ich kann nicht ohne Gutenachtgeschichte einschlafen. Jetzt kann ich sicher gut ruhen.' WHERE entry=2000000823; /* content: I can\'t sleep without a good bedtime story. Now I\'m certain to rest well. */
UPDATE db_script_string SET content_loc3='Was war das für ein Krach? Es klang wie Gebrüll.' WHERE entry=2000000824; /* content: What was that noise? It sounded like a roar. */
UPDATE db_script_string SET content_loc3='Grimmklaue?! Ganz ruhig, mein Freund... wo ist Euer Meister Volcor?' WHERE entry=2000000826; /* content: Grimclaw?! Easy there, my friend... where is your master Volcor? */
UPDATE db_script_string SET content_loc3='Hoh, hoh, mein Freund. Einen Moment...' WHERE entry=2000000828; /* content: Whoa, whoa there, my friend. One moment... */
UPDATE db_script_string SET content_loc3='So... das sollte helfen. Jetzt sagt mir, was geschehen ist, Grimmklaue.' WHERE entry=2000000830; /* content: There... that should help. Now, tell me what\'s happened, Grimclaw. */
UPDATE db_script_string SET content_loc3='Ich verstehe, mein Freund. Ich werde jemanden finden, der Eurem Meister helfen kann. Geht jetzt zu ihm zurück oder bleibt wenigstens in seiner Nähe.' WHERE entry=2000000832; /* content: I understand, my friend. I shall find someone to help your master. Go back to him now, or at least stay close. */
UPDATE db_script_string SET content_loc3='Grimmklaue und sein Meister Volcor könnten Eure Hilfe gebrauchen, wenn Ihr die Zeit erübrigen könnt. Sprecht mit mir, falls Ihr Interesse habt...' WHERE entry=2000000834; /* content: If you have the time, Grimclaw and his master Volcor could use your help. If you\'re interested, speak with me further... */
UPDATE db_script_string SET content_loc3='Bitte entschuldigt mich, während ich meine Tests beginne...' WHERE entry=2000000835; /* content: Please excuse me while I begin my tests... */
UPDATE db_script_string SET content_loc3='Interessant...' WHERE entry=2000000836; /* content: Interesting... */
UPDATE db_script_string SET content_loc3='Aber was ist das?' WHERE entry=2000000837; /* content: But what is this? */
UPDATE db_script_string SET content_loc3='Oh je!' WHERE entry=2000000838; /* content: Oh, my! */
UPDATE db_script_string SET content_loc3='Wenn mein Blut nicht schon vor langer Zeit geronnen wäre, würde es jetzt vor Zorn kochen...' WHERE entry=2000000839; /* content: If my blood hadn\'t clotted long ago, it would be boiling with rage right now... */
UPDATE db_script_string SET content_loc3='Gruß, Gezhe! Siebzehn neue Bestellungen aus dem Hara\'samidsektor. Der Markt für schweres Veldarit ist alles andere als zusammengebrochen, wie Ihr es vorausgesagt habt.' WHERE entry=2000000842; /* content: Hail, Gezhe!  Seventeen more orders have been placed in the Hara\'samid Sector.  The markets of heavy veldarite have all but collapsed, as you predicted. */
UPDATE db_script_string SET content_loc3='Heraaziexporte steigen, Herr. Wir haben schon die zuständigen Steuerbeamten bestochen. Die Schmuggelware wird in ein paar Tagen eintreffen.' WHERE entry=2000000843; /* content: Heraazi exports are on the rise, sir.  We\'ve already bribed the appropriate taxation officers and contraband will begin to arrive within days. */
UPDATE db_script_string SET content_loc3='Das überrascht mich nicht. Kommt das nächste Mal mit echten Nachrichten, hört Ihr?' WHERE entry=2000000844; /* content: Nothing surprising there.  Come back with some real news, will you? */
UPDATE db_script_string SET content_loc3='Wie ich erwartet habe. Ihr könnt gehen.' WHERE entry=2000000845; /* content: As expected.  Very well, dismissed. */
UPDATE db_script_string SET content_loc3='Wenn Ihr gestattet.' WHERE entry=2000000846; /* content: By your leave, sir. */
UPDATE db_script_string SET content_loc3='Eure Anwesenheit hier bedeutet wohl, dass Ihr bereit seid, das Studium des Beschwörens von Dämonenkohorten, die Euch gehorchen, zu beginnen. Wir werden mit der niedersten Kreatur beginnen, die ihr rufen könnt, und uns dann weiter vorarbeiten. Lasst uns beginnen.' WHERE entry=2000000847; /* content: If you\'re here, then it means you are prepared to begin the study of summoning demonic cohorts to do your bidding. We will start with the lowliest creatures you will be able to call and continue from there. Let us begin. */
UPDATE db_script_string SET content_loc3='Die einfachste Kreatur zum Beschwören ist der Wichtel. Ihr solltet bereits in der Lage sein, diesen Diener hervorzubringen, aber der Vollständigkeit halber möchte ich mit ihm beginnen.' WHERE entry=2000000848; /* content: The easiest creature for you to summon is the imp. You should already be able to bring forth this minion but for completeness\' sake I will start with him. */
UPDATE db_script_string SET content_loc3='Diese üble kleine Kreatur ist ein Wichtel. Er ist klein und schwach, was ihn als lebendigen Schild beinahe unbrauchbar macht, und sein Schadenspotenzial ist bestenfalls mittelmäßig. Diese Kreatur wird am besten als Unterstützung für eine größere Gruppe eingesetzt.' WHERE entry=2000000849; /* content: This foul little beast is the imp. It is small and weak, making it almost useless as a meatshield, and its damage output is mediocre at best. This creature is best used for support of a larger group. */
UPDATE db_script_string SET content_loc3='Da Ihr jetzt Gelegenheit hattet, den Wichtel zu studieren, wenden wir uns jetzt dem nächsten Diener zu, den Ihr beschwören könnt, dem Leerwandler.' WHERE entry=2000000850; /* content: Now that you have had a chance to study the imp, let us move on to the next minion you will be able to summon, the voidwalker. */
UPDATE db_script_string SET content_loc3='Dieses dämonische Wesen wird Leerwandler genannt. Seine Kraft und Belastbarkeit sind beeindruckend, was ihn ideal für Verteidigungszwecke macht. Schickt ihn aus, um Feinde anzugreifen, und benutzt ihn dann als Schild, während Ihr Eure Zauber und Fähigkeiten einsetzt, um Eurem Gegner Leben zu entziehen.' WHERE entry=2000000851; /* content: This demonic entity is known as the Voidwalker. Its strength and endurance are significant, making it ideal for defense. Send it to attack your enemy, then use it as a shield while you use your spells and abilities to drain away your opponent\'s life. */
UPDATE db_script_string SET content_loc3='Wenn Ihr noch nie einen gesehen habt, ist es ein beeindruckender Anblick. Eine sehr eindrucksvolle Kreatur, sowohl auf dem Schlachtfeld als auch außerhalb davon. Als Nächstes werfen wir einen Blick auf das, worauf alle männlichen Studenten wohl schon lange gewartet haben. Den Sukkubus.' WHERE entry=2000000852; /* content: If you\'ve never seen one, it is a sight to behold. A very impressive creature both on and off the field of battle. Next, let us take a look at what I am sure all you male students have been waiting for. The succubus. */
UPDATE db_script_string SET content_loc3='Nun gut. Neben der offensichtlichen Ablenkung Eurer männlichen Feinde, die ein solcher Diener mit sich bringt, ist sie auch in der Lage, erheblichen Schaden zuzufügen. Ihre schwache Belastbarkeit macht sie allerdings als Schild fast nutzlos.' WHERE entry=2000000853; /* content: All right now. Aside from the obvious distractions a minion like this will provide against your more masculine foes, she is also capable of dealing out impressive amounts of damage. However, her fragile endurance makes her almost useless as a shield. */
UPDATE db_script_string SET content_loc3='Studiert eifrig und Ihr werdet vielleicht eines Tages selbst in der Lage sein, einen zu beschwören, aber jetzt ist es an der Zeit, mit dem Teufelsjäger weiterzumachen.' WHERE entry=2000000854; /* content: Study hard and you might one day be able to summon one on your own, but for now it\'s time to move on to the felhunter. */
UPDATE db_script_string SET content_loc3='Was Ihr vor Euch seht, ist ein Teufelsjäger. Die natürlichen Talente dieser Kreatur umfassen \'Zaubersperre\' und andere Fähigkeiten, die ihn angesichts eines magisch begabten Gegners unübertreffbar machen.' WHERE entry=2000000855; /* content: What you see before you is a felhunter. This creature\'s natural talents include spell lock and other abilities which make it unequalled when facing a magically attuned opponent. */
UPDATE db_script_string SET content_loc3='Wenn Ihr Euch einem Zauberer gegenüberseht, ist diese wilde Bestie Euer bester Freund. Doch schauen wir uns jetzt etwas ganz anderes an. Die nächste Kreatur hilft Euch, wenn Ihr unterwegs seid, und wird Eure zukünftigen Reisen um einiges einfacher machen. Werfen wir mal einen Blick auf ein Teufelsross.' WHERE entry=2000000856; /* content: When facing a spellcaster of any kind, this feral beast will be your best friend. Now, let us take a look at something a bit different. This next creature will aid your travels and make your future journeys much easier. Let\'s take a look at a felsteed. */
UPDATE db_script_string SET content_loc3='Das war\'s. Unsere Lektion im Beschwören ist zu Ende. Eine neue Klasse wird in Kürze beginnen. Wenn Ihr das Gelernte auffrischen möchtet, könnt Ihr in der Nähe bleiben.' WHERE entry=2000000858; /* content: There you have it. Our lesson on summoning has come to an end. A new class will begin shortly, so if you wish to brush up, feel free to stay around. */
UPDATE db_script_string SET content_loc3='Der Priester verharrt im stillen Gebet.' WHERE entry=2000000859; /* content: The priest kneels in silent prayer. */
UPDATE db_script_string SET content_loc3='Oh heiliges Licht, gib mir Kraft.' WHERE entry=2000000860; /* content: Light, give me strength. */
UPDATE db_script_string SET content_loc3='Heiliges Licht, führe die Gefallen aus der Dunkelheit in das Licht und vom Tode in die Unsterblichkeit!' WHERE entry=2000000861; /* content: Light, lead the fallen from untruth to truth, from darkness to Light and from death to immortality. */
UPDATE db_script_string SET content_loc3='Neiiiinnnn!' WHERE entry=2000000865; /* content: NOOOOO!!! */
UPDATE db_script_string SET content_loc3='Meine Brüder... ich habe versagt...' WHERE entry=2000000866; /* content: Friends... I\'ve failed you... */
UPDATE db_script_string SET content_loc3='Oh nein, mein Freund... wir haben dich enttäuscht... wir haben dich enttäuscht.' WHERE entry=2000000867; /* content: No my friend... we failed you... we failed you. */
UPDATE db_script_string SET content_loc3='Die schwarzen Drachkin tauchten aus dem Nichts auf und überfielen uns, bevor wir reagieren konnten.' WHERE entry=2000000870; /* content: The Black Dragonkin materialized from thin air, and set upon us before we could react. */
UPDATE db_script_string SET content_loc3='Sie schlachteten uns mit kalter Effizienz ab, doch ihr wahres Ziel schienen die Eier zu sein, die sich im Sanktum befinden.' WHERE entry=2000000872; /* content: They slaughtered us with cold efficiency, but the true focus of their interest seemed to be the eggs kept here in the sanctum. */
UPDATE db_script_string SET content_loc3='Eindringlinge, euer Sieg währt nur kurz. Ich bin Kommandant Varos Wolkenwanderer. Meine Drachen kontrollieren den Himmel und schützen diese Energiezufuhr. Ich persönlich werde dafür sorgen, dass das Oculus euch nicht in die Hände fällt!' WHERE entry=2000000877; /* content: Intruders, your victory will be short-lived. I am Commander Varos Cloudstrider. My drakes control the skies and protect this conduit. I will see to it personally that the Oculus does not fall into your hands! */
UPDATE db_script_string SET content_loc3='Die, die sich an verlorenen Seelen laben, kennen nur den Hunger.' WHERE entry=2000000880; /* content: They who dine on lost souls know only hunger. */
UPDATE db_script_string SET content_loc3='Blickt in das Nichts. Es ist die Ewigkeit, in der sie ruhen.' WHERE entry=2000000881; /* content: Gaze into the void. It is the perpetuity in which they dwell. */
UPDATE db_script_string SET content_loc3='Sie trinken Eure Furcht: es ist das Blut des Lebens.' WHERE entry=2000000882; /* content: They drink your fear: it is the blood of life. */
UPDATE db_script_string SET content_loc3='Sie sind das Wispern im fröstelnden Wind.' WHERE entry=2000000883; /* content: They are the whisper on the shivering wind. */
UPDATE db_script_string SET content_loc3='Sie sterben nicht; sie leben nicht. Sie stehen außerhalb des Kreislaufs.' WHERE entry=2000000884; /* content: They do not die; they do not live. They are outside the cycle. */
UPDATE db_script_string SET content_loc3='Wo einer fällt, werden viele seinen Platz einnehmen.' WHERE entry=2000000885; /* content: Where one falls, many shall take its place. */
UPDATE db_script_string SET content_loc3='Wir werden uns eines Tages wiedersehen.' WHERE entry=2000000888; /* content: We will meet again... Someday. */
UPDATE db_script_string SET content_loc3='Die Verkörperung des Todes wurde ein weiteres Mal auf die Welt losgelassen! Teron Blutschatten ist zurückgekehrt...' WHERE entry=2000000889; /* content: Death incarnate has been released upon the world once more! Teron Gorefiend has returned... */
UPDATE db_script_string SET content_loc3='Versteckt Euch! Versteckt Euch, bevor sie Euch sehen können! Zurück!' WHERE entry=2000000892; /* content: Hide! Hide before they see you! Back up! */
UPDATE db_script_string SET content_loc3='Es ist an der Zeit zu handeln. Vernichtet Icarius.' WHERE entry=2000000893; /* content: The time to strike is at hand. Terminate Icarius. */
UPDATE db_script_string SET content_loc3='Halt!' WHERE entry=2000000894; /* content: Halt! */
UPDATE db_script_string SET content_loc3='Was ist, mein Herr?' WHERE entry=2000000895; /* content: What is it, my lord? */
UPDATE db_script_string SET content_loc3='Zarath, Ihr müsst sofort zum Schwarzen Tempel zurückkehren! Ich... Ich habe wie es scheint Fürst Illidans Befehle verlegt. Schnell!' WHERE entry=2000000896; /* content: Zarath you must return to the Black Temple at once! I... I seem to have misplaced Lord Illidan\'s orders. Quickly! */
UPDATE db_script_string SET content_loc3='Mein Herr, Ihr erwartet doch nicht, dass ich Euch unbewacht lasse. Fürst Illidan würde mich köpfen lassen, wenn Euch etwas zustieße.' WHERE entry=2000000897; /* content: My lord, surely you do not expect me to leave you unattended. Lord Illidan would have my head if anything were to happen to you. */
UPDATE db_script_string SET content_loc3='Zarath, ich bin sehr wohl in der Lage, die Straße zur Stätte der Mondfinsternis zu bewältigen. Wenn wir den Brief nicht abliefern, wird Fürst Illidan uns beide töten lassen! Und nun fort mit Euch!' WHERE entry=2000000898; /* content: Zarath, I am perfectly capable of making it up this road to Eclipse Point. If we do not deliver the missive, Lord Illidan will have both of our heads! You are dismissed! */
UPDATE db_script_string SET content_loc3='Wie Ihr wünscht, mein Herr.' WHERE entry=2000000899; /* content: As you wish, my lord. */
UPDATE db_script_string SET content_loc3='Ah, süße, süße Blutdistel... Wahrscheinlich hat sie einer der verdammten Abhängigen von der Stätte der Mondfinsternis verloren.' WHERE entry=2000000900; /* content: Ah, sweet, sweet bloodthistle... Probably left behind by one of those filthy addicts at Eclipse Point. */
UPDATE db_script_string SET content_loc3='Ihr Verlust ist mein Gewinn...' WHERE entry=2000000901; /* content: Their loss is most definitely my gain... */
UPDATE db_script_string SET content_loc3='%s verstaut das Blutdistelbündel in seiner Robe.' WHERE entry=2000000902; /* content: %s puts the bundle of bloodthistle in his robe. */
UPDATE db_script_string SET content_loc3='Tretet zurück, $n. Die Geister könnten um sich schlagen.' WHERE entry=2000000903; /* content: Stand back, $N. The spirits may lash out. */
UPDATE db_script_string SET content_loc3='Warum habt Ihr uns gefangen?' WHERE entry=2000000904; /* content: Why have you captured us? */
UPDATE db_script_string SET content_loc3='Was hofft Ihr dadurch zu erreichen?' WHERE entry=2000000905; /* content: What is it that you hope to gain from this? */
UPDATE db_script_string SET content_loc3='%s verneigt sich.' WHERE entry=2000000906; /* content: %s bows. */
UPDATE db_script_string SET content_loc3='Wir wollen Euch helfen, Geist. Wir suchen Eure Weisheit.' WHERE entry=2000000907; /* content: We wish to help you, spirit. We seek your wisdom. */
UPDATE db_script_string SET content_loc3='Weisheit? Es war Euer Volk, das den unauslöschlichen Zorn von Cyrukh erregte.' WHERE entry=2000000908; /* content: Wisdom? It was your people that caused the insufferable wrath of Cyrukh. */
UPDATE db_script_string SET content_loc3='Warum sollten wir Euch helfen? Die Welt liegt in Trümmern. Zerstört.' WHERE entry=2000000909; /* content: Why should we help you? The world is ruined. Destroyed. */
UPDATE db_script_string SET content_loc3='%s nickt.' WHERE entry=2000000910; /* content: %s nods. */
UPDATE db_script_string SET content_loc3='Ihr habt Recht, Geist. Es war mein Volk, welches Euch dies angetan hat. Erlaubt mir, es wiedergutzumachen.' WHERE entry=2000000911; /* content: You are right, spirit. It was my people that did this to you. Allow us to make it right. */
UPDATE db_script_string SET content_loc3='Bitte sagt uns: Wer oder was ist Cyrukh?' WHERE entry=2000000912; /* content: Please tell us this: Who or what is Cyrukh? */
UPDATE db_script_string SET content_loc3='Narr! Cyrukh der Feuerlord! Als die Verbindung durchtrennt wurde, wachte Cyrukh auf. Es sind die Tränen von Cyrukh, die dieses Land mit geschmolzenem Stein füllen.' WHERE entry=2000000913; /* content: Fool! Cyrukh the fire lord! When the tie was severed, Cyrukh awoke. It is the tears of Cyrukh that fill this land with molten rock. */
UPDATE db_script_string SET content_loc3='Der... der Feuerlord? Ein Elementarlord wurde empor gerufen? Natürlich... Gul\'dan, Ihr wart ein verdammter Narr!' WHERE entry=2000000914; /* content: The... The fire lord? An elemental lord was unearthed? Of course... Gul\'dan you were a damned fool! */
UPDATE db_script_string SET content_loc3='Er ist verrückt. Die Zersplitterung hat ihn in den Wahnsinn getrieben. Es gibt keine Ruhe für die Elemente.' WHERE entry=2000000915; /* content: He is insane now. Driven mad during the shattering. There is no rest for the elements. */
UPDATE db_script_string SET content_loc3='Es muss einen Weg geben.' WHERE entry=2000000916; /* content: There must be a way. */
UPDATE db_script_string SET content_loc3='Ja, einen Weg. Zerstört Cyrukh. Zerstört einen Elementarlord.' WHERE entry=2000000917; /* content: Yes, a way. Destroy Cyrukh. Destroy an elemental lord. */
UPDATE db_script_string SET content_loc3='Zweifelt nicht an mir und meinen Kameraden, Geist. Sagt uns, wie wir den Feuerlord beschwören können. Meine Verbündeten werden ihn zerstören.' WHERE entry=2000000918; /* content: Do not doubt my comrades and I, spirit. Tell us how to conjure the fire lord. My allies will destroy him. */
UPDATE db_script_string SET content_loc3='Ihr müsst die Worte sagen...' WHERE entry=2000000919; /* content: You must utter the words... */
UPDATE db_script_string SET content_loc3='Ja, ja... die Worte. Die Worte der Verdammnis. Die Worte des Dunklen. Eures Meisters.' WHERE entry=2000000920; /* content: Yes... Yes... the words. The words of damnation. The words spoken by the dark one. Your master. */
UPDATE db_script_string SET content_loc3='Ja... Gul\'dan...' WHERE entry=2000000921; /* content: Yes... Gul\'dan... */
UPDATE db_script_string SET content_loc3='Wir werden diese Worte finden. Wir werden Euren Meister beschwören und ihn zerstören.' WHERE entry=2000000922; /* content: We will find these words. We will summon your master and we will destroy him. */
UPDATE db_script_string SET content_loc3='Gerede... nur Gerede. Lasst uns frei, Sterblicher.' WHERE entry=2000000923; /* content: Talk. More talk... Now release us, mortal. */
UPDATE db_script_string SET content_loc3='Und nun zur Wiederentdeckung der verlorenen Worte!' WHERE entry=2000000924; /* content: Now to recover the lost words! */
UPDATE db_script_string SET content_loc3='Ich habe keine Lust mehr, Euch nett zu fragen, Fischmann!' WHERE entry=2000000925; /* content: I\'m through asking nicely, fish-man. */
UPDATE db_script_string SET content_loc3='Wo ist die Litanei der Verdammnis? Ich weiß, dass sich ein Drittel davon in diesem Dorf befindet.' WHERE entry=2000000926; /* content: Where is the Cipher of Damnation? I know a third of it is in this village. */
UPDATE db_script_string SET content_loc3='Wasss... wovon ssssprecht Ihr, Weichhaut?' WHERE entry=2000000927; /* content: Whhaaat... are you sssspeaking of, ssssoft-sskin? */
UPDATE db_script_string SET content_loc3='Die Naga haben... diessse Litanei... nicht...' WHERE entry=2000000928; /* content: The naga do nottt... have thissss... cipherrr... */
UPDATE db_script_string SET content_loc3='Nein... Natürlich nicht. Wie komme ich nur darauf. Warum solltet Ihr wohl ein Drittel des Worts der Verdammnis besitzen?' WHERE entry=2000000929; /* content: No... Of course not. What was I thinking? Why would you have a third of the Cipher of Damnation? */
UPDATE db_script_string SET content_loc3='Ich bin ja so dumm. Vielleicht sollte ich Euch losketten und freilassen?' WHERE entry=2000000930; /* content: I feel so stupid. Maybe I should unchain you and set you free? */
UPDATE db_script_string SET content_loc3='Ja... Ja... lasssst mich frei...' WHERE entry=2000000931; /* content: Yesss... Yesss... Ssset me freee... */
UPDATE db_script_string SET content_loc3='HALTET IHR MICH WIRKLICH FÜR SO DUMM, NAGA? JETZT FÜHLT DEN SCHMERZ!' WHERE entry=2000000932; /* content: DO I HONESTLY LOOK THAT STUPID TO YOU, NAGA? NOW YOU FEEL PAIN! */
UPDATE db_script_string SET content_loc3='%s spuckt verächtlich auf den Naga der Echsennarbe.' WHERE entry=2000000933; /* content: %s spits on the Coilskar naga. */
UPDATE db_script_string SET content_loc3='STEH AUF DU STINKENDES STÜCK DRECK! STEH AUF, BEVOR ICH DICH VERNICHTE!' WHERE entry=2000000934; /* content: GET UP YOU ROTTEN PIECE OF TRASH! GET UP BEFORE I END YOU! */
UPDATE db_script_string SET content_loc3='%s erlangt das Bewusstsein zurück.' WHERE entry=2000000935; /* content: %s regains consciousness. */
UPDATE db_script_string SET content_loc3='Schlagt den Kommandanten der Echsssennarbe nicht länger, Orc! Ich werde reden! Die Litanei... ist in einer der Truhen in der Echsssennarbe versssteckt... Die Naga dort haben die Schlüssssel...' WHERE entry=2000000937; /* content: Do not beat the Coilsssskaarrr commander anymore, orc! I will tell you! The cipherrr... It is hidden in one of the chests at Coilssskarrr Point... The naga there hold the keysss... */
UPDATE db_script_string SET content_loc3='ECHSSSENNARBE!' WHERE entry=2000000938; /* content: COILSSSKARRR! */
UPDATE db_script_string SET content_loc3='Was wollt Ihr von mir, Schamane?' WHERE entry=2000000939; /* content: What is it that you want from me, shaman? */
UPDATE db_script_string SET content_loc3='Habt Ihr keine Augen im Kopf? Seht Ihr nicht die Dämonen direkt vor unserer Türschwelle? Ich kann keine Soldaten entbehren!' WHERE entry=2000000940; /* content: Do you not have eyes? Did you not see the demons on our very doorstep? I do not have the soldiers to spare! */
UPDATE db_script_string SET content_loc3='Betet zu Euren Göttern, Schamane. Vielleicht kommen die Euch zu Hilfe, die Horde ist dazu jedoch nicht in der Lage...' WHERE entry=2000000941; /* content: Pray to your gods, shaman. Perhaps they will come to your aid. The Horde, however, cannot... */
UPDATE db_script_string SET content_loc3='Alles ist bereit, Kriegsrufer.' WHERE entry=2000000942; /* content: Everything is in readiness, warbringer. */
UPDATE db_script_string SET content_loc3='Verdammnislord Kazzak wird zufrieden sein. Ihr müsst die Häufigkeit Eurer Angriffe erhöhen. Zerstört die orcischen und zwergischen Festungen so schnell wie möglich!' WHERE entry=2000000943; /* content: Doom Lord Kazzak will be pleased. You are to increase the pace of your attacks. Destroy the orcish and dwarven strongholds with all haste. */
UPDATE db_script_string SET content_loc3='Kriegshetzer, dafür werden wir all unsere Höllenbestien brauchen. Dann wären wir auf einen Gegenangriff nicht mehr gerüstet.' WHERE entry=2000000944; /* content: Warbringer, that will require the use of all the hold\'s infernals. It may leave us vulnerable to a counterattack. */
UPDATE db_script_string SET content_loc3='Keine Sorge. Ich habe die Produktion in der Todesschmiede steigern lassen. Ihr werdet alle Höllenbestien zu Verfügung haben, die Ihr für Eure Befehle braucht. Versagt nicht, Jovaan.' WHERE entry=2000000945; /* content: Don\'t worry about that. I\'ve increased production at the Deathforge. You\'ll have all the infernals you need to carry out your orders. Don\'t fail, Jovaan. */
UPDATE db_script_string SET content_loc3='Es geschehe, wie Ihr es wünscht, Kriegsrufer. Eine letzte Frage noch, wenn Ihr erlaubt...' WHERE entry=2000000946; /* content: It shall be as you say, warbringer. One last question, if I may... */
UPDATE db_script_string SET content_loc3='Ja?' WHERE entry=2000000947; /* content: Yes? */
UPDATE db_script_string SET content_loc3='Was ist in der Kiste?' WHERE entry=2000000948; /* content: What\'s in the crate? */
UPDATE db_script_string SET content_loc3='Kiste? Ich habe Euch keine Kiste geschickt, Jovaan. Habt Ihr nicht Wichtigeres, worum Ihr Euch kümmern müsst? Dann kümmert Euch drum!' WHERE entry=2000000949; /* content: Crate? I didn\'t send you a crate, Jovaan. Don\'t you have more important things to worry about? Go see to them! */
UPDATE db_script_string SET content_loc3='Wenn Ihr den Schlüssel haben wollt, müsst Ihr mich schon fangen!' WHERE entry=2000000950; /* content: If you want the key, you\'ll have to catch me! */
UPDATE db_script_string SET content_loc3='Verfolgt mich, wenn Ihr wollt! Rennt, wenn Ihr Euch schert.' WHERE entry=2000000951; /* content: Chase me if you dare! I run without a care! */
UPDATE db_script_string SET content_loc3='Warum würdet Ihr mir je schaden wollen? Komm! Freunde könnten wir sein!' WHERE entry=2000000952; /* content: Why would you ever want to harm me!? Come. Friends we can be! */
UPDATE db_script_string SET content_loc3='STIRB! Du bringst Pusillin zum Weinen!' WHERE entry=2000000953; /* content: DIE?! You make Pusillin cry! */
UPDATE db_script_string SET content_loc3='Sagt \'Hallo!\' zu meinen kleinen Freunden!' WHERE entry=2000000954; /* content: Say hello to my little friends! */
UPDATE db_script_string SET content_loc3='Gut, ich bin so weit!' WHERE entry=2000000956; /* content: Ok, here I go! */
UPDATE db_script_string SET content_loc3='Gehen wir weiter!' WHERE entry=2000000961; /* content: Let\'s move forward! */
UPDATE db_script_string SET content_loc3='Der Eisbrocken ist geschmolzen!' WHERE entry=2000000962; /* content: The Ice Stone has melted! */
UPDATE db_script_string SET content_loc3='Ahune, stärker sollst du nicht mehr werden!' WHERE entry=2000000963; /* content: Ahune, your strength grows no more! */
UPDATE db_script_string SET content_loc3='Niemals wird Kälte herrschen!' WHERE entry=2000000964; /* content: Your frozen reign will not come to pass! */
UPDATE db_script_string SET content_loc3='Es stirbt! Der zweite Teil des Wortes der Verdammnis gehört uns! Ich...' WHERE entry=2000000965; /* content: It is dying! The second part of the Cipher of Damnation is ours. I... */
UPDATE db_script_string SET content_loc3='Ich schwinde... Kehrt zu Ar\'tor zurück... Keh.. rt... zu...' WHERE entry=2000000966; /* content: I am fading... Return to Ar\'tor... Ret... rn... to... */
UPDATE db_script_string SET content_loc3='Eine tiefe, schauderhafte Stimme ertönt vom %s...' WHERE entry=2000000967; /* content: A deep, bone chilling voice echoes from the %s... */
UPDATE db_script_string SET content_loc3='Seid still! Die Zersplitterung wird bald beginnen!' WHERE entry=2000000970; /* content: Be silent! The shattering is soon to come! */
UPDATE db_script_string SET content_loc3='Werdet Zeugen der unbestreitbaren Macht unseres dunklen Meisters!' WHERE entry=2000000971; /* content: Bear witness to the undeniable power of our dark master! */
UPDATE db_script_string SET content_loc3='Mit seinem Geschenk werde ich dieses Land auslöschen und neu formen!' WHERE entry=2000000972; /* content: With his gift, I shall raze this land and reform it! */
UPDATE db_script_string SET content_loc3='Seht! Seht, wie der Boden vor uns zerbricht! Seht, wie die Energie strömt! Sie wird unsere Armeen stärken...' WHERE entry=2000000973; /* content: Watch! See the ground shatter before us! Watch as the energy flows! It will feed our armies... */
UPDATE db_script_string SET content_loc3='Wir werden niemals wieder ohne Macht sein! Ich habe unsere Zukunft gesichert! Nehmt teil an meinem Ruhm!' WHERE entry=2000000974; /* content: We will never be without power! I have secured our future! Bask in my glory! */
UPDATE db_script_string SET content_loc3='Es... ist... vollbracht...' WHERE entry=2000000975; /* content: It... is... done... */
UPDATE db_script_string SET content_loc3='Zerreißt das Land in Stücke, Gul\'dan! Durchtrennt die Verbindung!' WHERE entry=2000000976; /* content: Tear the land asunder, Gul\'dan! Sever the tie! */
UPDATE db_script_string SET content_loc3='Tut es nicht, Gul\'dan! Die Elemente werden uns niemals vergeben!' WHERE entry=2000000977; /* content: Do not do this, Gul\'dan! The elements will never forgive us! */
UPDATE db_script_string SET content_loc3='Sie haben uns im Stich gelassen! Vergesst das nicht!' WHERE entry=2000000979; /* content: They have abandoned us! Do not forget! */
UPDATE db_script_string SET content_loc3='Wir werden uns vom Land nehmen, was es sich weigert, uns zu geben!' WHERE entry=2000000980; /* content: We will take from the land if it refuses to give! */
UPDATE db_script_string SET content_loc3='Aufhören! Ihr sollt aufhören!' WHERE entry=2000000981; /* content: Stop! You must stop! */
UPDATE db_script_string SET content_loc3='Ihr habt uns alle verdammt... Wir sind verloren.' WHERE entry=2000000982; /* content: You have damned us all... We are lost. */
UPDATE db_script_string SET content_loc3='%s beginnt einen Ritus der Entstehung.' WHERE entry=2000000983; /* content: By your command! */
UPDATE db_script_string SET content_loc3='Ich habe das Totem geschaffen. Ihr, $N, müsst es gut reinigen.' WHERE entry=2000000984; /* content: Wait here, $N. Spybot will make Lescovar come out as soon as possible. Be ready! Attack only after you\'ve overheard their conversation. */
UPDATE db_script_string SET content_loc3='%s erschafft ein neues Totem...' WHERE entry=2000000985; /* content: Good day to you both. I would speak to Lord Lescovar. */
UPDATE db_script_string SET content_loc3='Das Totem ist fertig, $N' WHERE entry=2000000986; /* content: Of course. He awaits you in the library. */
UPDATE db_script_string SET content_loc3='%s verwandelt die Gegenstände, welche $N ihm gab, in ein Totem der Reinigung' WHERE entry=2000000987; /* content: Thank you. The Light be with you both. */
UPDATE db_script_string SET content_loc3='Das Totem wird erschaffen. Es kann nun gut gereinigt werden.' WHERE entry=2000000988; /* content: Milord, your guest has arrived. He awaits your presence. */
UPDATE db_script_string SET content_loc3='Danke, $N. Und nun, um den Test durchzuführen...' WHERE entry=2000000989; /* content: Ah, thank you kindly. I will leave you to the library while I tend to this small matter. */
UPDATE db_script_string SET content_loc3='Ah, ich verstehe...' WHERE entry=2000000990; /* content: I shall use this time wisely, milord. Thank you. */
UPDATE db_script_string SET content_loc3='Ja, das ist ungünstig.' WHERE entry=2000000991; /* content: It\'s time for my meditation, leave me. */
UPDATE db_script_string SET content_loc3='$N, ich kann bestätigen, dass dieses Blut vom Orcklan der Knochenmalmer stammt, der unter dämonischem Einfluss steht. Ich fürchte aber, mit meiner momentanen Ausrüstung kann ich dieses Geheimnis nicht näher ergründen...' WHERE entry=2000000992; /* content: Yes, sir! */
UPDATE db_script_string SET content_loc3='Sprecht mit Schwadronskommandant Brack, er wird Euch einen unserer Flügeldrachenzerstörer geben. Und, viel Glück!' WHERE entry=2000000993; /* content: %s waits for the guards to be out of sight. */
UPDATE db_script_string SET content_loc3='Vielen Dank, $N! Es sieht ganz so aus, als kämen sie wunderbar zurecht.' WHERE entry=2000000994; /* content: There you are. What news from Westfall? */
UPDATE db_script_string SET content_loc3='Ich habe eine Idee. Lasst uns diesen Splitter in die Erde stecken und schauen, was passiert.' WHERE entry=2000000995; /* content: VanCleef sends word that the plans are underway. But he\'s heard rumors about someone snooping about. */
UPDATE db_script_string SET content_loc3='Das Böse, das der Kristall ausstrahlt, hat einen der Kolosse angelockt. Vernichtet ihn, schnell!' WHERE entry=2000000996; /* content: Hmm, it could be that meddler Shaw. I will see what I can discover. Be off with you. I\'ll contact you again soon. */
UPDATE db_script_string SET content_loc3='Dort wird es vollendet.' WHERE entry=2000000997; /* content: Very well. I will return then. */
UPDATE db_script_string SET content_loc3='Samia, wisst Ihr wer Kolphis Dunkelschuppe ist?' WHERE entry=2000000998; /* content: That\'s it! That\'s what you were waiting for! KILL THEM! */
UPDATE db_script_string SET content_loc3='Der König reiste per Schiff an, im Geheimen. Aber das Schiff erreichte niemals sein Ziel und König Wrynn verschwand.' WHERE entry=2000001001; /* content: The king was to travel here by ship, in secret. But the ship never arrived, and King Wrynn disappeared. */
UPDATE db_script_string SET content_loc3='In der Zwischenzeit gelang es dem König, seinen Geiselnehmern zu entkommen, und nach Sturmwind zurückzukehren, um sich Onyxia vorzunehmen.' WHERE entry=2000001003; /* content: Meanwhile, the king managed to escape his captors, and returned to Stormwind to deal with Onyxia. */
UPDATE db_script_string SET content_loc3='Danke, $N.' WHERE entry=2000001008; /* content: Thank you, $N. */
UPDATE db_script_string SET content_loc3='Vielleicht ist eine Erklärung vonnöten...' WHERE entry=2000001014; /* content: Perhaps I should explain... */
UPDATE db_script_string SET content_loc3='Los!' WHERE entry=2000001021; /* content: Go! */
UPDATE db_script_string SET content_loc3='Schwächliche Sterbliche, Onyxia gibt sich nicht mit solch Gewürm wie Euch ab!' WHERE entry=2000001026; /* content: Pitiful mortal, Onyxia answers to no one! */
UPDATE db_script_string SET content_loc3='Eure lächerliche Herausforderung wurde zur Kenntnis genommen. Es wird mir viel Freude bereiten mit Euch zu spielen, bevor ich Euch einem langsamen Tod überantworte!' WHERE entry=2000001027; /* content: Your pathetic challenge has not gone unnoticed. I shall enjoy toying with you before you die. */
UPDATE db_script_string SET content_loc3='Vielen Dank! Wer weiß, was diese Wahnsinnigen noch mit mir gemacht hätten.' WHERE entry=2000001028; /* content: Thank you! There\'s no telling what those brutes would\'ve done to me. */
UPDATE db_script_string SET content_loc3='Wir können uns an ihnen vorbei schleichen. Wenn wir das Konstrukt vor dem Tor ausschalten können, sollten wir hineingelangen können.' WHERE entry=2000001029; /* content: We can sneak past them. As long as we can take down that construct in front of the gate, we should be able to get inside. */
UPDATE db_script_string SET content_loc3='Geischeltrooolle werd\'n geröschtet...' WHERE entry=2000001031; /* content: Shcourged trooolls roashting.... */
UPDATE db_script_string SET content_loc3='Das war\'s! So weid wie isch komm\'... W-was meint Ihr?' WHERE entry=2000001034; /* content: That\'s it! Far as I got... Whatd\'ya think? */
UPDATE db_script_string SET content_loc3='Das stimmt so überhaupt nicht, $n. In der Sage, die mir Windzähmerin Barah erzählt hat, war Sturmhuf ein Held, kein Verbrecher.' WHERE entry=2000001042; /* content: This isn\'t right at all, $N. In the tale Wind Tamer Barah told me, Stormhoof was a hero, not a villain. */
UPDATE db_script_string SET content_loc3='Der Nordwind ist der ewige Gegner der Taunka, ein Herrscher unter den Elementen, und die Kraft, welche das Leben aus diesem uralten Volk saugt.' WHERE entry=2000001043; /* content: The North Wind is the ancient enemy of the taunka, a ruler among the elements, and the very force which bleeds the life from these ancient people. */
UPDATE db_script_string SET content_loc3='Sie würden lieber sterben, als sich vor dem Nordwind zu verbeugen. Etwas stimmt hier nicht.' WHERE entry=2000001044; /* content: They would rather die than bow before the North Wind. Something is amiss here. */
UPDATE db_script_string SET content_loc3='Der Lehrenhammer...' WHERE entry=2000001045; /* content: The Lorehammer... */
UPDATE db_script_string SET content_loc3='Das bestätigt unsere Vermutungen. Die Fäden der Zeit wurden gekappt und in einem falschen Muster wieder aneinandergefügt.' WHERE entry=2000001046; /* content: It confirms our suspicions. The threads of time have been severed and rewoven in the wrong pattern. But, how? */
UPDATE db_script_string SET content_loc3='ACK! Oh nein, ich habe mich verfangen! Es klebt! MEIN HAAR!' WHERE entry=2000001048; /* content: ACK! Oh no, I webbed myself! It\'s sticky. MY HAIR! */
UPDATE db_script_string SET content_loc3='Es ist nur...' WHERE entry=2000001061; /* content: It\'s just... */
UPDATE db_script_string SET content_loc3='Sie bewahren ihre Werkzeuge in Kästen wie diesen auf. Sucht nach ihnen in der Nähe der Pavillons.' WHERE entry=2000001065; /* content: They keep their tools in boxes that look like this. Look for them near the pavilions. */
UPDATE db_script_string SET content_loc3='Ich werde nur die Runen mit einer Prägung versehen, bei denen ich meine, dass sie Euch die Kontrolle über einen Riesen geben werden.' WHERE entry=2000001066; /* content: I\'m only going to carve the runes I think will give you command over a giant. */
UPDATE db_script_string SET content_loc3='Da, bitteschön, die Rune des Befehls... hoffentlich. Zwar nicht annähernd so gemein wie ihr eisenzwergischer Vetter, doch funktionsfähig.' WHERE entry=2000001068; /* content: There we go, the Rune of Command... hopefully. Not nearly as vicious as its Iron Dwarf cousin, but functional. */
UPDATE db_script_string SET content_loc3='Hurra!' WHERE entry=2000001090; /* content: Hooray! */
UPDATE db_script_string SET content_loc3='EHRE DER ALLIANZ!' WHERE entry=2000001094; /* content: GLORY TO THE ALLIANCE! */
UPDATE db_script_string SET content_loc3='Schleichen?! Was glaubt Ihr denn, was wir sind? Mäuse?' WHERE entry=2000001100; /* content: Sneak?! What do you think we are, marmots? */
UPDATE db_script_string SET content_loc3='Gut. Wenn unsere Verbündeten diejenigen sein sollen, die sich die Hände schmutzig machen, dann überlassen wir auch ihnen die Entscheidung, wie sie vorgehen wollen.' WHERE entry=2000001102; /* content: Fine. If our allies are going to be the ones getting their hands dirty, we\'ll leave it to them to decide how to proceed. */
UPDATE db_script_string SET content_loc3='Was... Was habt Ihr da gerade getan, $n?! Brann! Braaaaannn!' WHERE entry=2000001103; /* content: What... What did you just do, $n?! Brann! Braaaaannn! */
UPDATE db_script_string SET content_loc3='Brann! $n hat gerade die orbitalen Verteidigungssysteme aktiviert! Wenn wir hier nicht bald rauskommen, werden wir geröstet!' WHERE entry=2000001104; /* content: Brann! $n just activated the orbital defense system! If we don\'t get out of here soon, we\'re going to be toast! */
UPDATE db_script_string SET content_loc3='Magier der Kirin Tor, auf Branns Zeichen hin fahrt Ihr den Schild herunter! Verteidigt diese Plattform und unsere Verbündeten mit Eurem Leben! Für Dalaran!' WHERE entry=2000001108; /* content: Mages of the Kirin Tor, on Brann\'s Command, release the shield! Defend this platform and our allies with your lives! For Dalaran! */
UPDATE db_script_string SET content_loc3='Unsere Verbündeten sind bereit. Angriff! Öffnet den Schild und tretet zur Seite!' WHERE entry=2000001109; /* content: Our allies are ready. Bring down the shield and make way! */
UPDATE db_script_string SET content_loc3='Wir haben es geschafft, Leute! Wir sind vor Algalon hier. Vielleicht können wir die Systeme manipulieren, um seine Analyse zu beeinflussen...' WHERE entry=2000001111; /* content: We did it, lads! We got here before Algalon\'s arrival. Maybe we can rig the systems to interfere with his analysis-- */
UPDATE db_script_string SET content_loc3='Ich gehe zurück zum Archivum und versuche, das Signal zu stören. Vielleicht kann ich uns etwas Zeit verschaffen, während Ihr Euch um ihn kümmert.' WHERE entry=2000001112; /* content: I\'ll head back to the Archivum and see if I can jam his signal. I might be able to buy us some time while you take care of him. */
UPDATE db_script_string SET content_loc3='Die Temperatur beträgt 122 Grad Kraklenheit.' WHERE entry=2000001113; /* content: The temperature is 122 degrees Kraklenheit. */
UPDATE db_script_string SET content_loc3='Die Temperatur beträgt 9280 Grad Kraklenheit! Das ist HEISS!' WHERE entry=2000001114; /* content: The temperature is 9280 degrees Kraklenheit! That\'s HOT! */
UPDATE db_script_string SET content_loc3='DING! 428.000 Grad Kraklenheit exakt! Naaja, ungefähr. Fast. Irgendwo in der Größenordnung...' WHERE entry=2000001116; /* content: DING! 428,000 degrees Kraklenheit, exactly! Well, approximately. Almost. Somewhere around there... */
UPDATE db_script_string SET content_loc3='Hey! Hört auf, in meinem Keller herumzuwühlen! Raus hier!' WHERE entry=2000001119; /* content: Hey! Stop rooting around in my cellar! Clear out! */
UPDATE db_script_string SET content_loc3='Was habt Ihr denn in meinem Keller zu suchen gehabt? Es herrscht Nahrungsmittelknappheit und das Letzte, was ich brauche, sind Fremde, die in meinen Waren herumwühlen! Husch, husch!' WHERE entry=2000001120; /* content: What were you doing in my cellar? There\'s a food scare going on, and the last thing I need is strangers rummaging around in my goods! Shoo! */
UPDATE db_script_string SET content_loc3='Die ganze Situation wirkt etwas paranoid, glaubt Ihr nicht?' WHERE entry=2000001121; /* content: This whole situation seems a bit paranoid, don\'t you think? */
UPDATE db_script_string SET content_loc3='Befehl ist Befehl. Wenn der Prinz "Spring!" sagt...' WHERE entry=2000001122; /* content: Orders are orders. If the Prince says jump... */
UPDATE db_script_string SET content_loc3='Ein seltsamer Befehl, das könnt Ihr nicht abstreiten. Verdächtiges Essen? Nach der Definition solltet Ihr Belfast verhaften!' WHERE entry=2000001123; /* content: It\'s a strange order, you can\'t deny. Suspicious food? Under that definition, you should arrest Belfast! */
UPDATE db_script_string SET content_loc3='DAS HABE ICH GEHÖRT! Kein Bier mehr für Euch! Nicht einen Tropfen!' WHERE entry=2000001124; /* content: I HEARD THAT! No more ale for you! Not a drop! */
UPDATE db_script_string SET content_loc3='Genug, Michael. Das Geschäft leidet schon so genug unter dieser ganzen Hysterie. Wir können jedes Kupferstück gebrauchen.' WHERE entry=2000001125; /* content: Enough, Michael. Business is hurting enough with this scare as it is. We can use every copper. */
UPDATE db_script_string SET content_loc3='Die Soldaten machen eine wichtige Arbeit. Die Sicherheit der Leute ist sehr viel wichtiger, Mal, wenn du daran interessiert bist, dass deine Kunden auch den nächsten Tag noch erleben.' WHERE entry=2000001126; /* content: The soldiers are doing important work. The safety of the people is more important, Mal, if you\'re interested in your customers living to spend another day. */
UPDATE db_script_string SET content_loc3='%s nickt widerwillig.' WHERE entry=2000001127; /* content: %s grudgingly nods. */
UPDATE db_script_string SET content_loc3='Dagegen kann ich nichts sagen.' WHERE entry=2000001128; /* content: I can\'t argue with that. */
UPDATE db_script_string SET content_loc3='Macht Euch nicht zu viele Sorgen. Zu der Zeit, als ich Feierabend gemacht habe, hatten wir nicht ein Fitzelchen verseuchten Weizen gefunden.' WHERE entry=2000001129; /* content: Don\'t worry too much. By the time I went off duty, we hadn\'t found a scrap of befouled grain here. */
UPDATE db_script_string SET content_loc3='Dem Licht sei Dank dafür.' WHERE entry=2000001130; /* content: Thank the Light for that. */
UPDATE db_script_string SET content_loc3='%s nickt.' WHERE entry=2000001131; /* content: %s nods. */
UPDATE db_script_string SET content_loc3='Na, so was. Ein erneutes hallo!' WHERE entry=2000001132; /* content: Why, hello again! */
UPDATE db_script_string SET content_loc3='Nun gut, genug der Arbeit für diesen Moment. Zeit für Erfrischungen und ein wenig Unterhaltung im Wirtshaus.' WHERE entry=2000001133; /* content: Ok, enough work for now. Time for refreshments and a little conversation in the inn. */
UPDATE db_script_string SET content_loc3='Wartet, was ist das für ein Geruch?' WHERE entry=2000001134; /* content: Wait, what\'s that smell? */
UPDATE db_script_string SET content_loc3='Ich kann das nicht sein, ich hab\' erst vor 3 Tagen gebadet!' WHERE entry=2000001135; /* content: Can\'t be me, I took a bath 3 days ago! */
UPDATE db_script_string SET content_loc3='Knapp daneben. Es ist nur das Getreide hier.' WHERE entry=2000001136; /* content: Oh, close call. It\'s just the grain here. */
UPDATE db_script_string SET content_loc3='Moment mal! Getreide sollte nicht SO riechen! Ich hole besser mal eine Wache.' WHERE entry=2000001137; /* content: Wait a second. Grain isn\'t supposed to smell like THAT! I better go find a guard. */
