UPDATE db_script_string SET content_loc3='Wartet, was ist dies? Ihr habt mir doch etwas verschwiegen, Perelli!' WHERE entry=2000001140; /* content: Wait, what is this? You\'ve been holding out on me, Perelli! */
UPDATE db_script_string SET content_loc3='Wovon sprecht Ihr, Unteroffizier?' WHERE entry=2000001141; /* content: What are you talking about, Sergeant! */
