UPDATE db_script_string SET content_loc3='Ihr müsst mir glauben, ich bin unschuldig!' WHERE entry=2000001143; /* content: You have to believe me, I\'m innocent! */
UPDATE db_script_string SET content_loc3='Das werden wir noch sehen, Perelli. Das werden wir noch sehen.' WHERE entry=2000001144; /* content: We\'ll see about that, Perelli. We\'ll see about that. */
UPDATE db_script_string SET content_loc3='Martha, ich habe kein Mehl zum Brotbacken mehr. Ihr habt nicht zufällig noch etwas Korn von der letzten Lieferung?' WHERE entry=2000001145; /* content: Martha, I\'m out of flour for bread. You wouldn\'t happen to have any grain from that recent shipment, would you? */
UPDATE db_script_string SET content_loc3='Oh, hallo Jena. Natürlich kannst du etwas Getreide ausleihen. Nimm dir.' WHERE entry=2000001146; /* content: Oh hello, Jena. Of course you can borrow some grain. Help yourself. */
