UPDATE db_script_string SET content_loc1='새매가 까마귀석의 위치 추적에 성공한 이후로, 엘라이라가 무척 먼 거리에 내 목소리를 전하기 위해 다른 새매 녀석을 훈련시키고 있답니다. 그가 당신이 서판에서 밝혀진 새 영혼을 조사하면서 우리와 교신하는데 도움을 줄 겁니다.' WHERE entry=2000000000;
UPDATE db_script_string SET content_loc1='예상대로 이 경전에서 필요한 정보를 얻어냈습니다. 놈이 에메랄드의 꿈에 더 피해를 주기 전에, 이 정보를 이용해 안주를 끌어내 쓰러뜨려야 합니다!' WHERE entry=2000000001;
UPDATE db_script_string SET content_loc1='이거야! 진짜 까마귀 경전을 우리 손에 넣었네요!' WHERE entry=2000000004;
