UPDATE db_script_string SET content_loc1='$R|1이여;여;, 놈들이 날 쫓고 있어! 놈들에게 발각되기 전에 이곳에 왜 왔는지부터 말하게! 놈들이 날 잡으면, 나와 그대는 끝장이란 말이야!' WHERE entry=2000000006;
UPDATE db_script_string SET content_loc1='그대는... 저들과 한패가 아니로군! 놈들이 우릴 발각하기 전에 어서 벗어나자고! 서두르게!' WHERE entry=2000000007;
UPDATE db_script_string SET content_loc1='침입자여, 이세라 님의 영역에서 뭘 하는 거지? 우리를 떠나게! 이곳은 그대 종족을 환영하지 않아!' WHERE entry=2000000008;
UPDATE db_script_string SET content_loc1='위험은 끝났습니다! $N|1이;가; 우리를 구했습니다!' WHERE entry=2000000009;
UPDATE db_script_string SET content_loc1='도대체 얼마나 많은 보병이 시체가 되어 고향으로 돌아가야만 불타는 군단의 살인 부대로 뛰어드는 것이 자살 행위라는 것을 알겠는가? 그리고 마치 내 말을 이해한다는 듯이 그 큰 머리 좀 끄덕이지 말라!' WHERE entry=2000000010;
UPDATE db_script_string SET content_loc1='이 전지전능한 나이비께 머리를 조아려 예를 갖추라! 머리를 숙여 나의 지옥불... 닭?' WHERE entry=2000000013;
UPDATE db_script_string SET content_loc1='물러서! 비키라고! 지옥불정령은 공간이 많이 필요할 거야!' WHERE entry=2000000014;
UPDATE db_script_string SET content_loc1='입 다물어라... 하인 주제에 감히! 복수하고 말 테다! 스톰윈드에 죽음을! 닭으로 죽음을!' WHERE entry=2000000015;
