UPDATE db_script_string SET content_loc1='마을에 거의 다 왔군요. 주위를 잘 살펴보십시오.' WHERE entry=2000000020;
UPDATE db_script_string SET content_loc1='호수 바닥이 어떻게 된 거지요? 이상한 힘이 흘러나오고 있는데요.' WHERE entry=2000000023;
UPDATE db_script_string SET content_loc1='대단한 전투였죠! 보셨습니까, 리디안 님?!' WHERE entry=2000000024;
