UPDATE db_script_string SET content_loc1='여기, 누가 이 개구리 좀 잡아 주세요!' WHERE entry=2000000231;
UPDATE db_script_string SET content_loc1='운명을 놓고 우물쭈물할 시간이 있는가? 그만큼 불확실한 미래가 늘어날 뿐인데...' WHERE entry=2000000240;
UPDATE db_script_string SET content_loc1='이런 비밀을 너 같은 필멸의 존재에게 밝히는 일은 신성 모독에 가깝지만, 선택의 여지가 없구나.$B$B티탄들의 딸인 프레이야가 나를 이곳에 남겨 놓았을 때부터, 나는 절대 실패해서는 안 되는 임무를 하나 맡았다. 생명은 어떤 대가를 치르더라도 보호받아야 한다.$B$B이곳과 매우 흡사한 장소를 연결하는 차원문이 있다. 우리가 찾는 무기를 발견할 수 있는 곳이 바로 거기이다.' WHERE entry=2000000243;
UPDATE db_script_string SET content_loc1='부름의 양초는 앞에 있는 상자에 있네, $N.' WHERE entry=2000000244;
UPDATE db_script_string SET content_loc1='주인님께서는 엑소다르의 소재에 관한 정보를 지키려고 엄청난 노력을 하셨다. 너 때문에 내부 첩자가 발각될 뻔했을 뿐만 아니라 우리 계획도 전부 엉망이 될 뻔했다는 걸 아나?' WHERE entry=2000000248;
UPDATE db_script_string SET content_loc1='흠...' WHERE entry=2000000251;
UPDATE db_script_string SET content_loc1='이제 가시오, $N. 가서 볼코르를 찾으시오. 그리고 망토를 쓰면 대화만 할 수 있소. 누군가와 맞서 싸우게 될 경우 그대를 보호하는 마법이 사라진다는 사실을 명심하시오.' WHERE entry=2000000253;
UPDATE db_script_string SET content_loc1='이 영혼석을 깨는 건 아주 간단한 일이지!' WHERE entry=2000000255;
UPDATE db_script_string SET content_loc1='이제 애스리쿠스 나라신을 처단해야 합니다. 영혼이여, 우리가 그의 마법을 풀 영혼의 보석을 발견할 수 있겠습니까?' WHERE entry=2000000257;
UPDATE db_script_string SET content_loc1='지금 당장 시작해야겠소, $N.' WHERE entry=2000000259;
UPDATE db_script_string SET content_loc1='흠... 내 실패랑 바늘이 어디 있었던가? 아! 여기 있었군그래.' WHERE entry=2000000260;
UPDATE db_script_string SET content_loc1='서두르시오, $N. 차원문을 통과하시오!' WHERE entry=2000000261;
UPDATE db_script_string SET content_loc1='자, 어디 한번 볼까...' WHERE entry=2000000262;
UPDATE db_script_string SET content_loc1='아, 이런...' WHERE entry=2000000263;
UPDATE db_script_string SET content_loc1='돌발톱 산맥의 정령들이 아직도 분노하고 있어요.' WHERE entry=2000000264;
UPDATE db_script_string SET content_loc1='영원히 그들을 진정시키지 못하는 건 아닐까 걱정이에요...' WHERE entry=2000000265;
UPDATE db_script_string SET content_loc1='아차!' WHERE entry=2000000267;
UPDATE db_script_string SET content_loc1='도와주셔서 다시 한번 감사드립니다. 앞으로 평탄한 여행길이 되기를...' WHERE entry=2000000269;
UPDATE db_script_string SET content_loc1='%s|1이;가; $N의 말에 귀를 기울입니다.' WHERE entry=2000000270;
UPDATE db_script_string SET content_loc1='%s|1이;가; 독약을 만듭니다...' WHERE entry=2000000272;
UPDATE db_script_string SET content_loc1='%s|1이;가; 용기에 독가스를 채웁니다.' WHERE entry=2000000274;
UPDATE db_script_string SET content_loc1='독가스가 준비됐소, $N.' WHERE entry=2000000275;
UPDATE db_script_string SET content_loc1='좋아, 친구들! 이제 이 뼈들을 차에 설치하세.' WHERE entry=2000000276;
UPDATE db_script_string SET content_loc1='%s|1이;가; 코로 꿈가루 한 줌을 들이마십니다...' WHERE entry=2000000277;
UPDATE db_script_string SET content_loc1='%s|1이;가; 정신이 몽롱한 듯 눈을 깜박거립니다...' WHERE entry=2000000278;
UPDATE db_script_string SET content_loc1='에취!' WHERE entry=2000000279;
UPDATE db_script_string SET content_loc1='정말 시원하군. 자, 얘기해 줄 정보는 말이야...' WHERE entry=2000000282;
UPDATE db_script_string SET content_loc1='%s|1이;가; 고통으로 몸부림칩니다.' WHERE entry=2000000284;
UPDATE db_script_string SET content_loc1='으아아아아아아!' WHERE entry=2000000285;
UPDATE db_script_string SET content_loc1='음... 이상하군...' WHERE entry=2000000286;
UPDATE db_script_string SET content_loc1='어휴, 그게 어디 갔지?' WHERE entry=2000000287;
UPDATE db_script_string SET content_loc1='바보 같은 마법사들... 재료를 끝까지 다 쓰고는 한 번도 채워놓은 적이 없다니까, 쯧.' WHERE entry=2000000288;
UPDATE db_script_string SET content_loc1='몇 분 안에 물약이 준비될 거요, $N.' WHERE entry=2000000289;
UPDATE db_script_string SET content_loc1='거의 다 됐소...' WHERE entry=2000000290;
UPDATE db_script_string SET content_loc1='자, 물약이 완성됐소, $N. 할 말이 있으니 가까이 오라고.' WHERE entry=2000000291;
UPDATE db_script_string SET content_loc1='진정하라고? 캘타스 님께서 그분께 실망을 안겨준 자들에게 어떻게 하시는지 알고나 하는 소린가, 기즐? 영원한 고통과 괴로움에 몸부림치는 거다... 이번 일을 망치면 알아서 해, 멍청한 고블린 녀석.' WHERE entry=2000000292;
UPDATE db_script_string SET content_loc1='우리 핏빛안개 척후병들이 우리 정보원의 위치를 파악했다. 그 멍청이 벨렌은 곧 무방비 상태가 될 것이다. 우리가 습격하기에 충분한 시간이지! 이제, 네놈을 없애 버리기 전에 눈앞에서 사라져라...' WHERE entry=2000000293;
UPDATE db_script_string SET content_loc1='마침내 풀려났다! 당장 전쟁노래 요새로 돌아가야겠소!' WHERE entry=2000000294;
UPDATE db_script_string SET content_loc1='영원히 갚지 못할 빚을 져버렸습니다.' WHERE entry=2000000295;
UPDATE db_script_string SET content_loc1='고마워요, 친구.' WHERE entry=2000000296;
UPDATE db_script_string SET content_loc1='그대에게 감사를 드리오, 이방인.' WHERE entry=2000000297;
UPDATE db_script_string SET content_loc1='뭔가... 다르군.' WHERE entry=2000000299;
UPDATE db_script_string SET content_loc1='착한 울바르 씨는 이제 안녕이다!' WHERE entry=2000000300;
UPDATE db_script_string SET content_loc1='성난발톱 일족 자유다! 트롤은 다 죽었어!' WHERE entry=2000000301;
UPDATE db_script_string SET content_loc1='외래종 금속 견본이 감지되었습니다.' WHERE entry=2000000303;
UPDATE db_script_string SET content_loc1='이 금속 견본은 사로나이트와 무쇠 합금 물질임이 확인되었습니다.' WHERE entry=2000000305;
UPDATE db_script_string SET content_loc1='이 견본에 들어 있는 사로나이트는 거의 순수한 광석 자체입니다. 이와 같은 광석은 본래 아제로스의 지면에서 발견되지 않습니다.' WHERE entry=2000000307;
UPDATE db_script_string SET content_loc1='접근이 거부되었습니다?!' WHERE entry=2000000309;
UPDATE db_script_string SET content_loc1='추가적인 정보는 관리자 로켄의 명령에 의해서만 울두아르 내에 있는 저장고에서 접근할 수 있습니다.' WHERE entry=2000000310;
UPDATE db_script_string SET content_loc1='한 가지 확실한 점은 이 금속의 크기, 두께, 볼트 구멍이 거대한 구조물을 위한 갑옷에 사용된다는 겁니다.' WHERE entry=2000000311;
UPDATE db_script_string SET content_loc1='명령을 완료했습니다. 대기 상태로 전환합니다.' WHERE entry=2000000312;
UPDATE db_script_string SET content_loc1='오... 무슨 말인지 이해하겠습니다! 이제 정말 대화를 나눌 수 있겠군요. 제게 설명해 주셔야 할 일이 있습니다!' WHERE entry=2000000313;
UPDATE db_script_string SET content_loc1='어떻게 탐험가 연맹에서 제 통신기를 구한 거지요?' WHERE entry=2000000314;
UPDATE db_script_string SET content_loc1='무쇠 드워프를 손봐 주셨나 보군요. 감사합니다. 하지만, 왜 호드가 제게 관심을 두는 거지요?' WHERE entry=2000000315;
UPDATE db_script_string SET content_loc1='그 척후병이 설마 보르타크는 아니겠지요? 그가 무자비하다는 소문은 저 같은 사람도 들어 봤다고요. 그가 저를 붙잡으려고 하나요?' WHERE entry=2000000316;
UPDATE db_script_string SET content_loc1='일지를 보셨다면, 제가 어떤 작업에 매달려 왔는지 아실 텐데요. 우리는 서로 칼을 겨누고 있지만, 둘 다 티탄의 창조물입니다!' WHERE entry=2000000317;
UPDATE db_script_string SET content_loc1='로켄과 무쇠 드워프들이 토석인을 내쫓고 울두아르를 차지했습니다.' WHERE entry=2000000318;
UPDATE db_script_string SET content_loc1='우리가 계속 서로 싸우는 데 힘을 소비하면, 로켄이 울두아르의 자원을 이용해 호드와 얼라이언스 둘 다를 무너뜨릴 겁니다.' WHERE entry=2000000319;
UPDATE db_script_string SET content_loc1='탐험가 연맹에서 사람을 보냈는지 어떤지는 모르겠습니다. 저는 단지 무쇠 드워프가 점령한 야영지에서 당신의 쪽지를 발견했을 뿐입니다.' WHERE entry=2000000321;
UPDATE db_script_string SET content_loc1='한 척후병이 토르 모단에서 당신의 일지를 발견했습니다. 이후로 호드는 계속 당신을 찾고 있었지요.' WHERE entry=2000000322;
UPDATE db_script_string SET content_loc1='흠... 이걸 배달한 다음에 저 치즈 가게에 들러 뭘 좀 먹어야겠군.' WHERE entry=2000000327;
UPDATE db_script_string SET content_loc1='정말, 장사가 아주 잘 되더라고!' WHERE entry=2000000340;
UPDATE db_script_string SET content_loc1='치즈 고맙습니다!' WHERE entry=2000000341;
UPDATE db_script_string SET content_loc1='다녀왔소!' WHERE entry=2000000343;
UPDATE db_script_string SET content_loc1='우에에오오와악!' WHERE entry=2000000344;
UPDATE db_script_string SET content_loc1='난 아무것도 두렵지 않다. 어디 해 보자!' WHERE entry=2000000347;
UPDATE db_script_string SET content_loc1='대지에서 일어나라, 형제들이여!' WHERE entry=2000000348;
UPDATE db_script_string SET content_loc1='강철의 주인께서 우리에게 임무를 내리셨다!' WHERE entry=2000000349;
UPDATE db_script_string SET content_loc1='나를 따라 산으로 간다! 주인님의 계획을 실천하는 거다!' WHERE entry=2000000350;
UPDATE db_script_string SET content_loc1='조각 분석을 진행 중입니다.' WHERE entry=2000000357;
UPDATE db_script_string SET content_loc1='개체 식별. 고대 왕의 망치, 발아니르입니다.' WHERE entry=2000000358;
UPDATE db_script_string SET content_loc1='티탄이 직접 창조한 발아니르는 첫 번째 토석인 왕 우렐 스톤하트에게 주어졌습니다. 스톤하트는 망치로 형제들을 창조하고 생명을 불어 넣었습니다.' WHERE entry=2000000359;
UPDATE db_script_string SET content_loc1='발아니르는 토석인과 무쇠 드워프의 첫 번째 전쟁 때 파괴되었습니다. 무기의 파편은 유실되었다고 알려졌습니다.' WHERE entry=2000000360;
UPDATE db_script_string SET content_loc1='이론적으로 요그사론이라는 존재 내부에는 사로나이트를 액체로 변환하는 강력한 산성 물질이 수용되어 있습니다. 티탄이 만든 합금을 그 안에 담그면 금속 조직을 재결합할 수 있습니다.' WHERE entry=2000000362;
UPDATE db_script_string SET content_loc1='감시자 프레이야 상태 분석을 시작합니다.' WHERE entry=2000000363;
UPDATE db_script_string SET content_loc1='장로 돌나무껍질은 마법 사용을 방해하는 음파 효과로 방어에 기여합니다.' WHERE entry=2000000366;
UPDATE db_script_string SET content_loc1='감시자 호디르 상태 분석을 시작합니다.' WHERE entry=2000000370;
UPDATE db_script_string SET content_loc1='호디르의 인장은 유물 보관함 안에 있는 것으로 보입니다.' WHERE entry=2000000371;
UPDATE db_script_string SET content_loc1='감시자 호디르는 쉽게 흥분하고 불안정한 행동을 하므로, 전투가 길어지면 보관함을 파괴할 가능성이 매우 큽니다.' WHERE entry=2000000372;
UPDATE db_script_string SET content_loc1='호디르의 인장을 되찾으려면 보관함을 보호해야 합니다.' WHERE entry=2000000374;
UPDATE db_script_string SET content_loc1='감시자 미미론 상태 분석을 시작합니다.' WHERE entry=2000000375;
UPDATE db_script_string SET content_loc1='미미론의 인장을 회수하려면 자폭 장치 가동 후 폭발 전까지 미미론을 처치하십시오.' WHERE entry=2000000378;
UPDATE db_script_string SET content_loc1='행성 재창조가 뭐야? 알아듣게 말해라, 구닥다리 기계야!' WHERE entry=2000000379;
UPDATE db_script_string SET content_loc1='감시자 토림 상태 분석을 시작합니다.' WHERE entry=2000000380;
UPDATE db_script_string SET content_loc1='와... 이런 걸 바로 끝내주는 발견이라고 하죠!' WHERE entry=2000000384;
UPDATE db_script_string SET content_loc1='내가 권한을 보여주지, 이 망할 기계야. 자, 여기 노르간논의 쐐기돌이다!' WHERE entry=2000000385;
UPDATE db_script_string SET content_loc1='제가 전에 발견한 티탄의 다른 기록관과 비슷하군요. 음... 더 낡았고... 더 크다는 점만 빼면요.' WHERE entry=2000000386;
UPDATE db_script_string SET content_loc1='뭐? 신호를 받는 쪽이 누구지? 그리고 무슨 일이 벌어지나?' WHERE entry=2000000389;
UPDATE db_script_string SET content_loc1='그럼 알갈론은 어떤 식으로 진단 결과를 내지? 우린 희망이 없는 거냐?' WHERE entry=2000000391;
UPDATE db_script_string SET content_loc1='큰일 났네요. 우리 곧 싸워야 할 것 같습니다.' WHERE entry=2000000392;
UPDATE db_script_string SET content_loc1='행성과 생명체를 해체해 금속, 암석, 기체 등의 기본적인 요소로 되돌리는 작업을 뜻합니다. 이후에, 각 요소를 초기 행성 청사진에 맞게 재구성하는 절차가 뒤따릅니다.' WHERE entry=2000000398;
UPDATE db_script_string SET content_loc1='99.99 퍼센트입니다.' WHERE entry=2000000401;
UPDATE db_script_string SET content_loc1='새로운 명령이다! 별자리 투영관 입구를 냉큼 열어라.' WHERE entry=2000000403;
UPDATE db_script_string SET content_loc1='네 감시자? 인장? 나랑 수수께끼 놀이하자는 거냐, 이 괘씸한 기계야!' WHERE entry=2000000404;
UPDATE db_script_string SET content_loc1='그 인장은 어떻게 구하란 말이냐? 감시자한테 가서 좀 달라 그럴까?' WHERE entry=2000000405;
UPDATE db_script_string SET content_loc1='그럼 손상을 되돌리는 방법을 얘기해 주겠군, 그렇지?' WHERE entry=2000000406;
UPDATE db_script_string SET content_loc1='수년 동안 티탄의 전당에서 활보할 순간만을 꿈꿨는데, 티탄의 창조물 때문에 위험해질 줄이야...' WHERE entry=2000000407;
UPDATE db_script_string SET content_loc1='요청이 거부되었습니다. 네 감시자의 인장을 수동으로 확인하는 과정이 필요합니다.' WHERE entry=2000000408;
UPDATE db_script_string SET content_loc1='손상이 발견되었습니다. 외부 존재가 감시자를 장악하고 있습니다. 인장이 손상되었습니다.' WHERE entry=2000000411;
UPDATE db_script_string SET content_loc1='무력을 충분히 행사하면 감시자 기능이 초기화되어 외부 존재의 영향을 제거할 수 있습니다.' WHERE entry=2000000412;
UPDATE db_script_string SET content_loc1='이 과정에서 인장을 위험으로부터 보호하는 방법에 대해서는, 각 감시자의 상태를 분석한 계산 결과를 참고하십시오.' WHERE entry=2000000413;
UPDATE db_script_string SET content_loc1='자넨 끝내주는 구경거리를 놓쳤다네, 도리안! 수십 년에 한 번 볼까 말까 한 광경이었는데!' WHERE entry=2000000414;
UPDATE db_script_string SET content_loc1='우린 쿵쿵이 등에 타고 전속력으로 놈을 향해 달려갔지...' WHERE entry=2000000415;
UPDATE db_script_string SET content_loc1='사방 천지가 흔들리고 있었소. 총을 제대로 겨눌 수조차 없었지.' WHERE entry=2000000416;
UPDATE db_script_string SET content_loc1='그리고 놈을 본 거야. 그 끔찍한 짐승을!' WHERE entry=2000000417;
UPDATE db_script_string SET content_loc1='$N|1이;가; 가진 기술을 모두 동원해야 겨우 쿵쿵이를 다룰 수 있었지.' WHERE entry=2000000418;
UPDATE db_script_string SET content_loc1='피와 총알과 땀이 뒤범벅된 싸움이었지. 여왕은 점점 더 빠르게 공격해왔어!' WHERE entry=2000000419;
UPDATE db_script_string SET content_loc1='하지만, 결국 $N|1과;와; 나는 지혜와 협동심으로 놈을 해치웠지. 그리고 이제 우리에게는 승리했다는 기념으로 이 영광스러운 전리품이 남았다네.' WHERE entry=2000000420;
UPDATE db_script_string SET content_loc1='좀 부러워하는 것 같은데, 도리안?' WHERE entry=2000000421;
UPDATE db_script_string SET content_loc1='글쎄요, 모르겠습니다. 전 여기서 모닥불을 지키면서 주트피즐과 톱니바퀴 고장에 대해 얘기를 나눴지요. 그것도 나름대로 아주 근사한 일이랍니다.' WHERE entry=2000000422;
UPDATE db_script_string SET content_loc1='%s|1이;가; 토템을 만드는 의식을 시작합니다...' WHERE entry=2000000424;
UPDATE db_script_string SET content_loc1='%s|1이;가; 새로운 토템을 만듭니다...' WHERE entry=2000000426;
UPDATE db_script_string SET content_loc1='토템이 완성되었소, $N.' WHERE entry=2000000427;
UPDATE db_script_string SET content_loc1='%s|1이;가; $N|1이;가; 가져온 재료들을 정화 토템으로 변화시킵니다.' WHERE entry=2000000428;
UPDATE db_script_string SET content_loc1='%s|1이;가; 북서쪽을 바라보며 긴 울음을 내지릅니다.' WHERE entry=2000000430;
UPDATE db_script_string SET content_loc1='%s|1이;가; 앞쪽으로 아주 큰 울음을 내지릅니다.' WHERE entry=2000000431;
UPDATE db_script_string SET content_loc1='늦었군요, 밀사여. 아니, 변명은 듣지 않겠습니다. 이 임무는 너무나도 중요하니까요.' WHERE entry=2000000432;
UPDATE db_script_string SET content_loc1='우리의 대표자가 고룡쉼터 사원에서 가져온 고서입니다. 당장 이걸 미랄리온 선블레이즈에게 가져가십시오.' WHERE entry=2000000433;
UPDATE db_script_string SET content_loc1='뒤를 조심하십시오, 밀사여. 이 책의 가치를 다시 알려 드릴 필요는 없겠죠. 이걸 차지할 수만 있다면 은빛 서약단이 무슨 짓이든 하리란 것도 잘 아시리라 믿습니다.' WHERE entry=2000000434;
UPDATE db_script_string SET content_loc1='여기 책이 있습니다. 무슨 일이 벌어지는 건지 선리버에서 눈치채기 전에 반드시 이 책을 얼음왕관에 있는 칼라디스 브라이트스피어에게 가져다주십시오.' WHERE entry=2000000435;
UPDATE db_script_string SET content_loc1='어서 움직이십시오! 자기들이 찾는 정보를 우리가 쥐고 있다는 걸 알면, 선리버 놈들은 곧바로 행동을 개시할 겁니다.' WHERE entry=2000000436;
UPDATE db_script_string SET content_loc1='아, 그래...' WHERE entry=2000000438;
UPDATE db_script_string SET content_loc1='안타깝군.' WHERE entry=2000000439;
UPDATE db_script_string SET content_loc1='좋아, 완성됐다.' WHERE entry=2000000444;
UPDATE db_script_string SET content_loc1='아라코아들이 사방에... 숨어 있다!' WHERE entry=2000000447;
UPDATE db_script_string SET content_loc1='내가 패했군요.' WHERE entry=2000000449;
UPDATE db_script_string SET content_loc1='준비되셨습니까?' WHERE entry=2000000450;
UPDATE db_script_string SET content_loc1='시합을 시작합시다!' WHERE entry=2000000451;
UPDATE db_script_string SET content_loc1='정말 감사합니다!' WHERE entry=2000000454;
UPDATE db_script_string SET content_loc1='잠시 후 페리 가트너가 벌이는 희극을 맘껏 즐겨주십시오!' WHERE entry=2000000458;
UPDATE db_script_string SET content_loc1='배꼽이 빠져라 웃고 싶은 분은 세상의 끝 선술집으로 오십시오!' WHERE entry=2000000459;
UPDATE db_script_string SET content_loc1='지금, 샤트라스에 타우렌 족장들을 모셨습니다! 그들이 매시간 정각에 세상의 끝 선술집에서 펼치는 멋진 공연에 여러분 모두를 초대합니다!' WHERE entry=2000000460;
UPDATE db_script_string SET content_loc1='다시 돌아온다면 광란심장 일족 손에 죽을 것이다. 우리는 이제 너 환영하지 않는다!' WHERE entry=2000000462;
UPDATE db_script_string SET content_loc1='고맙습니다! 아빠가 당신에게 크게 보상할 거예요! 그럼 안녕히!' WHERE entry=2000000464;
UPDATE db_script_string SET content_loc1='두 번 다시 잡히지 않을 거예요! 약속해요! 그럼 또 안녕히!' WHERE entry=2000000465;
UPDATE db_script_string SET content_loc1='%s|1이;가; 고기 냄새를 맡고 잠에서 깨어나 분노에 찬 포효를 내지릅니다!' WHERE entry=2000000466;
UPDATE db_script_string SET content_loc1='대체 무슨 근거로 박쥐가 저 각도로 안전히 착지할 거로 생각하는 건지 모르겠단 말이야. 남는 건 부러진 목에 냄새 나는 박쥐 스튜밖에 없을 거라고.' WHERE entry=2000000467;
UPDATE db_script_string SET content_loc1='크아아아!!' WHERE entry=2000000470;
UPDATE db_script_string SET content_loc1='어둠골... 난 배가 고프다!!' WHERE entry=2000000471;
UPDATE db_script_string SET content_loc1='조심하세요, $C님. 이 트롤 녀석들은 자기네가 섬기던 뱀 신마저 죽였답니다!' WHERE entry=2000000473;
UPDATE db_script_string SET content_loc1='그럼 또 만나세나.' WHERE entry=2000000474;
UPDATE db_script_string SET content_loc1='네. 하사관님께 돌아가겠습니다.' WHERE entry=2000000475;
UPDATE db_script_string SET content_loc1='고맙습니다. $C님.' WHERE entry=2000000476;
UPDATE db_script_string SET content_loc1='고맙습니다, $R|1이여;여;. 이 비열한 놈은 제가 데려가도록 하지요.' WHERE entry=2000000477;
UPDATE db_script_string SET content_loc1='라리크 너한테 교훈 가르쳐 준다!' WHERE entry=2000000478;
UPDATE db_script_string SET content_loc1='플루우우우우우운! 쿠쉬쿠쉬 아무것도 못 받았다! 죽어라!' WHERE entry=2000000479;
UPDATE db_script_string SET content_loc1='저는 세 번째 방법을 선택하겠습니다. 죽어라!' WHERE entry=2000000480;
UPDATE db_script_string SET content_loc1='네가 바란다면...' WHERE entry=2000000481;
UPDATE db_script_string SET content_loc1='파헤쳐진 땅에서 성스러운 물건을 도둑맞았다. 애시리테스의 방패를 잃어버렸다. 안식은 영원히 오지 않을 것이다.' WHERE entry=2000000486;
UPDATE db_script_string SET content_loc1='풀어 주셔서 감사합니다, $N! 저는 샤트라스에 있는 오빠에게 가겠습니다!' WHERE entry=2000000491;
UPDATE db_script_string SET content_loc1='얼굴을 보니 마음이 놓입니다, 밀사여. 크라서스가 고대의 용이 벼려낸 검이라는 책을 우리에게 하나 내줬습니다.' WHERE entry=2000000492;
UPDATE db_script_string SET content_loc1='[펄볼그어] 감사합니다, $N!' WHERE entry=2000000493;
UPDATE db_script_string SET content_loc1='[펄볼그어] 오, 예언이 사실이로군요! 구해주셔서 감사합니다.' WHERE entry=2000000494;
UPDATE db_script_string SET content_loc1='[펄볼그어] 감사합니다. 너울소나무 요새에 남아 있는 이들이 당신을 영웅으로 환영할 겁니다!' WHERE entry=2000000495;
UPDATE db_script_string SET content_loc1='[펄볼그어] 너울소나무 펄볼그는 당신의 용감한 행동을 잊지 못할 것입니다!' WHERE entry=2000000496;
UPDATE db_script_string SET content_loc1='아서스의 일부가 살아 있을 줄 알았어요! 갇힌 채... 괴로워하며... 오, 아서스!' WHERE entry=2000000500;
UPDATE db_script_string SET content_loc1='어쩌면... 그가 예전 모습을 기억해 냈을지도 몰라요.' WHERE entry=2000000501;
UPDATE db_script_string SET content_loc1='빛이시여, 그가 끔찍한 검의 차가운 손아귀에서 벗어나 평안하기를.' WHERE entry=2000000502;
UPDATE db_script_string SET content_loc1='이렇게, 끝났구나.' WHERE entry=2000000503;
UPDATE db_script_string SET content_loc1='내 느낌을 믿을 수가 없었다. 너무나 여러 번 리치 왕에게 속았기 때문에 말이다.' WHERE entry=2000000504;
UPDATE db_script_string SET content_loc1='드디어, 내 백성에게 저질렀던 수많은 죄악의 대가를 치렀구나.' WHERE entry=2000000505;
UPDATE db_script_string SET content_loc1='약함과 자존심 때문에 치렀던 끔찍한 대가를 아제로스가 잊지 않기를.' WHERE entry=2000000506;
UPDATE db_script_string SET content_loc1='영웅이여, 이제 어찌해야 하는가? 그의 손아귀에서 벗어났지만, 여전히 필멸의 굴레에 묶인 이들은?' WHERE entry=2000000507;
UPDATE db_script_string SET content_loc1='생각할 것이 많다.' WHERE entry=2000000509;
UPDATE db_script_string SET content_loc1='아서스...' WHERE entry=2000000510;
UPDATE db_script_string SET content_loc1='아아, 아제로스의 영웅이여. 내게 아주 큰 선물을 주었네.' WHERE entry=2000000511;
UPDATE db_script_string SET content_loc1='오랫동안, 끔찍한 죄악을 저지른 그 왕자를 용서하려고 애썼지.' WHERE entry=2000000512;
UPDATE db_script_string SET content_loc1='참을 수 없는 불안과 어두운 생각으로... 빛에서 멀어진 내 영혼은 괴롭기 그지없었네.' WHERE entry=2000000513;
UPDATE db_script_string SET content_loc1='내 앞에 섰을 때, 그의 눈에 빛나던 긍지를 똑똑히 기억하네. 빛의 적들을 물리치겠다는 간절함과...' WHERE entry=2000000514;
UPDATE db_script_string SET content_loc1='어떻게해서든 자기 백성을 지키겠다는 간절함도.' WHERE entry=2000000515;
UPDATE db_script_string SET content_loc1='이것이 내가 마음속에 간직하기로 한 아서스의 기억일세.' WHERE entry=2000000516;
UPDATE db_script_string SET content_loc1='그대에게 진 빚은 영원히 남을 걸세.' WHERE entry=2000000517;
UPDATE db_script_string SET content_loc1='감사합니다.' WHERE entry=2000000518;
UPDATE db_script_string SET content_loc1='오, 이런...' WHERE entry=2000000519;
UPDATE db_script_string SET content_loc1='그 끝없던 로데론의 날들을 어떻게 잊겠나...' WHERE entry=2000000520;
UPDATE db_script_string SET content_loc1='...자네가 이 무딘 검과 함께 기술을 갈고 닦으며...' WHERE entry=2000000521;
UPDATE db_script_string SET content_loc1='...위대한 운명의 부름에 맞서고자 자신을 병기로 단련하던 그때를 말이네.' WHERE entry=2000000522;
UPDATE db_script_string SET content_loc1='하... 그렇게 익힌 기술은 써 봤을 테지. 안 그런가?' WHERE entry=2000000523;
UPDATE db_script_string SET content_loc1='그날 자네를 막을 수만 있었다면, 상황이 얼마나 달라졌을까 싶네.' WHERE entry=2000000524;
UPDATE db_script_string SET content_loc1='내가 그 저주받은 검을 찾아내지만 않았어도...' WHERE entry=2000000525;
UPDATE db_script_string SET content_loc1='잘 가게, 아서스... 내 형제여.' WHERE entry=2000000526;
UPDATE db_script_string SET content_loc1='다리온, 내 아들아.' WHERE entry=2000000527;
UPDATE db_script_string SET content_loc1='드디어, 이 눈으로 다시 너를 보는 날이 왔구나.' WHERE entry=2000000528;
UPDATE db_script_string SET content_loc1='다리온, 리치 왕은 내게 끝없는 고통을 주었다.' WHERE entry=2000000529;
UPDATE db_script_string SET content_loc1='끝없이, 내 의지를 꺾어 자신을 섬기고 그 검과 결속되게 하려 애쓰더구나...' WHERE entry=2000000530;
UPDATE db_script_string SET content_loc1='결국, 다른 일에 모든 관심을 쏟아야 하자 나를 떠났다.' WHERE entry=2000000531;
UPDATE db_script_string SET content_loc1='내가 매달렸던, 날 견딜 수 있게 했던 그 한 가지는... 다리온...' WHERE entry=2000000532;
UPDATE db_script_string SET content_loc1='영원한 재앙에서 나를 다시 구해낸 네 희생에 대한 기억이었다.' WHERE entry=2000000533;
UPDATE db_script_string SET content_loc1='아버지...' WHERE entry=2000000534;
UPDATE db_script_string SET content_loc1='아버지. 아버지 정신이... 온전하실지 걱정스러웠습니다.' WHERE entry=2000000535;
UPDATE db_script_string SET content_loc1='아버지... 아버지를 위해서라면 천 번이라도 목숨을 바칠 겁니다.' WHERE entry=2000000536;
UPDATE db_script_string SET content_loc1='%s|1이;가; 비약의 냄새를 맡고는 게걸스럽게 먹습니다!' WHERE entry=2000000537;
UPDATE db_script_string SET content_loc1='그리고 고맙네, $N. 자네 도움이 없었다면 결코 포세이큰의 길로 접어들지 못했을 걸세.' WHERE entry=2000000538;
UPDATE db_script_string SET content_loc1='잘 가게, 친구.' WHERE entry=2000000539;
UPDATE db_script_string SET content_loc1='오랜만일세, 베소르, 내 친구여.' WHERE entry=2000000540;
UPDATE db_script_string SET content_loc1='언제 시간 날 때 더 얘기 나누세. 할 얘기가 너무 많다네.' WHERE entry=2000000541;
UPDATE db_script_string SET content_loc1='%s|1이;가; 전령을 소환합니다...' WHERE entry=2000000542;
UPDATE db_script_string SET content_loc1='%s|1이;가; 연금술사 자마에게 전갈을 전합니다.' WHERE entry=2000000544;
UPDATE db_script_string SET content_loc1='그래도 브록에게 맞는 집 여기밖에 없다! 고블린은 집 너무 작게 만든다!!' WHERE entry=2000000546;
UPDATE db_script_string SET content_loc1='이 얘기는 이미 했다고 생각하는데, 브록. 이 건물 안에서 지낼 수는 없어. 너를 재울 수도 있지만, 그러면 다른 사람들은 아무도 못 잔다고!' WHERE entry=2000000547;
UPDATE db_script_string SET content_loc1='그래, 여기서 지내도 좋다고 우리가 그랬는데, 하지만 이 건물은 우리 본부라고! 우리 작전 설명서와 계획서가 여기 전부 있단 말이야!' WHERE entry=2000000548;
UPDATE db_script_string SET content_loc1='오우거가 너무 큰 거라고 말하고 싶어! 너를 설득해서 당장 나가게 할 수 없다는 건 아는데, 그렇다고 영원히 이대로 머물 수 있을 거라고는 생각하지 말라고!' WHERE entry=2000000549;
UPDATE db_script_string SET content_loc1='브록 여기서 지내도 된다고 네가 말했다! 돌망치 일족 땅 다시 찾게 도와준다고 너 브록한테 약속했다!' WHERE entry=2000000550;
UPDATE db_script_string SET content_loc1='대지를 감싸는 공포 속에서 아메스아란은 분리의 고통을 겪었어. 그 속에선 살아남을 수 있는 희망은 거의 없었지.' WHERE entry=2000000551;
UPDATE db_script_string SET content_loc1='그때 애스리쿠스가 왔어. 그는 우리를 고통에서 구해주겠다고 했지. 그러곤 보호의 마법을 시전했어.' WHERE entry=2000000552;
UPDATE db_script_string SET content_loc1='마법의 진동이 멈추자 그의 숨겨진 목적이 밝혀졌어. 우린 함정에 빠졌고 그는 천천히 우리의 힘을 빼앗아갔어.' WHERE entry=2000000553;
UPDATE db_script_string SET content_loc1='수백 명이 그의 주문에 갇혀 버렸지. 하지만 이제 그의 주문에 속박된 자는 얼마 안 남게 됐지. 그래도 그는 가끔 우리에게 말을 걸곤 했어.' WHERE entry=2000000554;
UPDATE db_script_string SET content_loc1='그는 자신의 힘이 약해질까 봐 걱정했지. 그리고 자신에게 마지막으로 남은 귀중한 영혼의 보석인 우리마저 사라져 최후를 맞이할까 봐 걱정하고 있었어.' WHERE entry=2000000555;
UPDATE db_script_string SET content_loc1='흐음... 낚싯바늘을 어디다 뒀더라? 아, 저기 있네.' WHERE entry=2000000557;
UPDATE db_script_string SET content_loc1='고기들이 좀 무는지 어디 한번 볼까.' WHERE entry=2000000558;
UPDATE db_script_string SET content_loc1='흐음... 더 좋은 미끼가 필요할 것 같아. 아빠 짐마차에 좀 다녀와야겠어.' WHERE entry=2000000559;
UPDATE db_script_string SET content_loc1='가시멧돼지 고기야! 좋았어! 물고기들은 돼지고기라면 아주 환장하지.' WHERE entry=2000000560;
UPDATE db_script_string SET content_loc1='한 마리 잡았다! 혹시 경비병들이 배고플지도 몰라. 가자, 검둥아!' WHERE entry=2000000561;
UPDATE db_script_string SET content_loc1='물고기 한 마리 잡았는데 혹시 배고프면...' WHERE entry=2000000562;
UPDATE db_script_string SET content_loc1='월척이네! 호드 어린이의 명예를 드높이는 꼬마로군! 고맙다!' WHERE entry=2000000563;
UPDATE db_script_string SET content_loc1='뭐 좀 더 드실래요?' WHERE entry=2000000564;
UPDATE db_script_string SET content_loc1='빵 더 가져와!' WHERE entry=2000000565;
UPDATE db_script_string SET content_loc1='알았어요.' WHERE entry=2000000566;
UPDATE db_script_string SET content_loc1='여기 빵 가지고 왔어요.' WHERE entry=2000000567;
UPDATE db_script_string SET content_loc1='이야... 좋아, 좋아. 그럼 가봐!' WHERE entry=2000000568;
UPDATE db_script_string SET content_loc1='알았어요.' WHERE entry=2000000569;
UPDATE db_script_string SET content_loc1='아빠, 목걸이 만들 돌멩이 모으러 나가도 돼요?' WHERE entry=2000000570;
UPDATE db_script_string SET content_loc1='다부! 조심할게요, 아빠!' WHERE entry=2000000572;
UPDATE db_script_string SET content_loc1='와아! 이 돌은 모양도 색깔도 정말 예쁘네. 이런 게 더 있나 어디 한번 볼까...' WHERE entry=2000000573;
UPDATE db_script_string SET content_loc1='흠... 저 돌은 예쁜데 너무 작아.' WHERE entry=2000000574;
UPDATE db_script_string SET content_loc1='이 돌은 창 만드는 친구한테 줘야겠다! 아주 날카로운 게... 그루크가 진짜 좋아 할거야.' WHERE entry=2000000576;
UPDATE db_script_string SET content_loc1='뭐... 이 정도면 목걸이 하나는 만들고도 남겠지. 얼른 집에 가야겠다.' WHERE entry=2000000577;
UPDATE db_script_string SET content_loc1='은빛십자군이 여는 마상시합에 여러분을 초대합니다. 다른 아제로스의 용사들과 명예로운 전투를 치르고 자신의 실력을 가늠해 보십시오.' WHERE entry=2000000580;
UPDATE db_script_string SET content_loc1='전 세계의 용사들이 도전자들과 맞서기 위해 북동쪽 얼음왕관에 있는 은빛십자군 마상시합 광장에 모여들고 있습니다.' WHERE entry=2000000581;
UPDATE db_script_string SET content_loc1='늦었구나, 감독관이여.' WHERE entry=2000000582;
UPDATE db_script_string SET content_loc1='운명은 기다려주지 않는다. 바위 거인 지도자와 그의 군대가 울두아르에 도착하기 전에 전쟁 골렘을 더 많이 생산해야 한다.' WHERE entry=2000000583;
UPDATE db_script_string SET content_loc1='룬대장장이들에게 계속해서 바위 거인들을 변환하라고 일러라. 굴복하지 않는 놈들은 모두 제거해 버려라.' WHERE entry=2000000584;
UPDATE db_script_string SET content_loc1='울두아르에서 벌이는 우리 일을 바위 거인들이 방해하면, 너와 네 영주에게 책임을 묻겠다. 필멸의 존재들이 거인을 도우러 오게 놔둬서는 안 된다.' WHERE entry=2000000585;
UPDATE db_script_string SET content_loc1='돌아가서 일을 계속하라, 감독관. 일꾼들을 재촉하는 일을 잊지 말아라.' WHERE entry=2000000586;
UPDATE db_script_string SET content_loc1='그래, 점쟁이가 너를 돕기로 했다는 말이구나, 이방인. 좋다, 내가 아는 걸 말해 주마.' WHERE entry=2000000587;
UPDATE db_script_string SET content_loc1='네 동료는 우두머리 수정뿔 코뿔소인 파룬을 찾고 있다. 녀석은 자기 형제들에게서 멀리 떨어져 있지.' WHERE entry=2000000588;
UPDATE db_script_string SET content_loc1='너희 동료에게 돌아가라. 계속 나아가서 파룬을 처치하고 전리품을 취하라고 일러라.' WHERE entry=2000000590;
UPDATE db_script_string SET content_loc1='일을 마치거든 조용히 이 땅을 떠나라고 전해라. 그는 이 땅에 속한 자가 아니다.' WHERE entry=2000000591;
UPDATE db_script_string SET content_loc1='[펄볼그] 적이 저지른 이 야만스러운 짓의 증인이 돼라, $N. 그들이 우리의 젊은이들을... 힘없는 자들을... 여기에 이 뾰족가지 일족의 우리에 가둬놨다. 그들을 구하라, 예언의 현신이여!' WHERE entry=2000000592;
UPDATE db_script_string SET content_loc1='아직 확실하게는 모르겠지만 이 온천에는 뭔가 이상한 점이 있어요...' WHERE entry=2000000593;
UPDATE db_script_string SET content_loc1='%s|1이;가; 얕은 물웅덩이 속으로 칼을 던집니다.' WHERE entry=2000000594;
UPDATE db_script_string SET content_loc1='이 온천수에 뭔가를 넣었다가 꺼내면 성능이 엄청나게 향상된다는 걸 최근에 알아냈어요.' WHERE entry=2000000595;
UPDATE db_script_string SET content_loc1='이유야 어쨌건 간에 아주 효과가 좋더라고요!' WHERE entry=2000000596;
UPDATE db_script_string SET content_loc1='그래서 더 연구를 해보려고요... 또 제 도움이 필요하시면 이곳으로 언제든지 절 찾아오세요.' WHERE entry=2000000597;
UPDATE db_script_string SET content_loc1='자, 저를 따라 오세요.' WHERE entry=2000000598;
UPDATE db_script_string SET content_loc1='겨울 히아신스인가요? 제게 주시려고요?' WHERE entry=2000000599;
UPDATE db_script_string SET content_loc1='여행자가 꽃다발을 들고 찾아온 지 너무 오래됐어요.' WHERE entry=2000000600;
UPDATE db_script_string SET content_loc1='지난 몇 년간 호수는 너무나 외로운 곳이었답니다. 여행자들은 발길을 끊고 악이 물가에 찾아들었지요.' WHERE entry=2000000601;
UPDATE db_script_string SET content_loc1='당신의 선물은 보기 드문 친절이로군요, 여행자님. 제 감사와 함께 이 검을 가져가세요. 오래전, 다른 여행자 한 분이 여기 놓고 가셨지만 제겐 쓸모가 없네요.' WHERE entry=2000000602;
UPDATE db_script_string SET content_loc1='제게는 불운만을 안겨준 검입니다. 어쩌면 당신은 그 힘을 다룰 이를 찾아낼지도 모르죠.' WHERE entry=2000000604;
UPDATE db_script_string SET content_loc1='이런 거짓말쟁이 $R같으니! 시로나스 님께서 너와 네 종족을 몰살하실 것이다!' WHERE entry=2000000606;
UPDATE db_script_string SET content_loc1='나의 형제 스톰후프는 나보다 훨씬 위대한 전사였네. 내가 부족장이 되기 위한 훈련을 받는 동안 그는 더욱 중대한 임무를 준비했지.' WHERE entry=2000000607;
UPDATE db_script_string SET content_loc1='그런데 어째서 위험에 빠지게 된 거지? 그는 그저 부족의 골칫거리인 정령을 지배할 힘을 얻으려 한 것뿐이라네.' WHERE entry=2000000608;
UPDATE db_script_string SET content_loc1='그는 어떤 뿔피리를 찾으려 했던 것 같네. 오랜 기간 마을을 떠나 그 유물을 찾으러 다녔지.' WHERE entry=2000000609;
UPDATE db_script_string SET content_loc1='그가 뿔피리를 손에 얻자 무서운 적이 그를 추격하기 시작했는데... 기억이 나질 않는군... 그다음에... 무슨 일이 일어났는지 말이야.' WHERE entry=2000000610;
UPDATE db_script_string SET content_loc1='마치 내 기억이 안갯속에 가려진 것 같다네. 그에게 무슨 일이 있었는지조차 생각나질 않아. 이것이 혹시 그가 위험에 처한 이유인가?' WHERE entry=2000000611;
UPDATE db_script_string SET content_loc1='아마도 묘지에서 일어난 문제와 관련이 있는 게 틀림없네. 주위를 둘러보게, $N. 저들이 보이는가?' WHERE entry=2000000612;
UPDATE db_script_string SET content_loc1='그래, 잘못은 그들에게 있지만 나에겐 그를 막을 힘이 없다네. 툰카로 주민에게 경고해주겠나, 이방인이여? 그들의 선조와 과거가 위험에 처했다고 말이야.' WHERE entry=2000000613;
UPDATE db_script_string SET content_loc1='가르침의 망치가 반응하지 않습니다. 이것은 스톰후프의 시대에서 온 것이 아닙니다.' WHERE entry=2000000615;
UPDATE db_script_string SET content_loc1='가장 강한 자가 승리할 것이다!' WHERE entry=2000000617;
UPDATE db_script_string SET content_loc1='그대는 저를 꺾을 수 없습니다!' WHERE entry=2000000618;
UPDATE db_script_string SET content_loc1='너는 토림을 위해 일할 자격이 없다!' WHERE entry=2000000619;
UPDATE db_script_string SET content_loc1='이교도가 정신없이 말을 쏟아냅니다.' WHERE entry=2000000621;
UPDATE db_script_string SET content_loc1='주인님.' WHERE entry=2000000622;
UPDATE db_script_string SET content_loc1='보고하거라.' WHERE entry=2000000623;
UPDATE db_script_string SET content_loc1='요툰하임에서 소식이 들어왔습니다. 수면의 감시자들이 상당히 중요한 인물을 발견한 모양입니다.' WHERE entry=2000000624;
UPDATE db_script_string SET content_loc1='좀 더 자세히 설명해 보아라. 어떻게 생겼지?' WHERE entry=2000000625;
UPDATE db_script_string SET content_loc1='생김새요? 글쎄요, 브리쿨처럼 보이던데요. 저도 아직 본 적은 없어서요. 어둠의 무기고 때 이후로 통...' WHERE entry=2000000626;
UPDATE db_script_string SET content_loc1='변명은 여전하구나. 그나저나 이름은 알아 왔겠지?' WHERE entry=2000000627;
UPDATE db_script_string SET content_loc1='이름이요? 아, 맞다. 그 이름이... 이스칼더였던 것 같은데요.' WHERE entry=2000000628;
UPDATE db_script_string SET content_loc1='요툰하임으로 돌아가 전해라. 내가 도착할 때까지 그분을 깨우지 말라고.내 눈으로 직접 확인해 봐야겠다.' WHERE entry=2000000630;
UPDATE db_script_string SET content_loc1='쏜살처럼 다녀오겠습니다, 주인님.' WHERE entry=2000000631;
UPDATE db_script_string SET content_loc1='모라에... 당신이오?' WHERE entry=2000000632;
UPDATE db_script_string SET content_loc1='아, 당신은 내 아내가 아니로군. 하지만, 그녀와 만난 적이 있군. 그녀를 만나거든 내가 추락에서는 살아남았으나 이곳 침투요원들에게 죽임을 당했다고 전해 주시오.' WHERE entry=2000000633;
UPDATE db_script_string SET content_loc1='그리고 아내에게... 내가 죽기 전까지 그녀만을 생각했다고 전해 주시오...' WHERE entry=2000000634;
UPDATE db_script_string SET content_loc1='그러지!' WHERE entry=2000000635;
UPDATE db_script_string SET content_loc1='이 기름은 나한테 아무짝에도 소용이 없군! 이 그렉이 로브나 뒤집어쓰고 다니는 약골로 보이나?' WHERE entry=2000000636;
UPDATE db_script_string SET content_loc1='%s|1이;가; 웃습니다.' WHERE entry=2000000637;
UPDATE db_script_string SET content_loc1='하찮은 너희 문명사회에서 조용한 시민인 척하다가, 부름만 받으면 이렇게 헌신적으로 불타는 군단을 섬길 이들이 있다는 게 놀랍지 않나?' WHERE entry=2000000638;
UPDATE db_script_string SET content_loc1='두려움에 떨면서 살든가 캘타스 님의 뜻에 따라 죽어라... 결국 죽기는 마찬가지가 아닌가?' WHERE entry=2000000639;
UPDATE db_script_string SET content_loc1='이제 널 베겠다!' WHERE entry=2000000640;
UPDATE db_script_string SET content_loc1='죽어라!' WHERE entry=2000000641;
UPDATE db_script_string SET content_loc1='%s|1이;가; 나가 깃발을 집어듭니다.' WHERE entry=2000000642;
UPDATE db_script_string SET content_loc1='우리에게 실패란 없다!' WHERE entry=2000000643;
UPDATE db_script_string SET content_loc1='%s|1이;가; 부품 상자를 바닥에 내려놓습니다.' WHERE entry=2000000644;
UPDATE db_script_string SET content_loc1='%s|1이;가; 부품을 집어 주머니에 넣습니다...' WHERE entry=2000000645;
UPDATE db_script_string SET content_loc1='그래, 이거면 될 거야...' WHERE entry=2000000646;
UPDATE db_script_string SET content_loc1='아나야? 내가 지금 꿈을 꾸고 있는 건가? 정말 당신이오?' WHERE entry=2000000647;
UPDATE db_script_string SET content_loc1='우리에겐 지나간 세월이 너무 가혹했던 것 같아요. 내 사랑, 결국 이렇게 당신과 다시 마주하게 되었군요.' WHERE entry=2000000648;
UPDATE db_script_string SET content_loc1='천 년을 떨어져 지내고 난 후에도 이 정도밖에 허락하지 않다니... 가혹한 운명이 너무나 원망스럽구려.' WHERE entry=2000000649;
UPDATE db_script_string SET content_loc1='날 미워하는 거요, 내 사랑? 그 고통스러운 불행의 속박에서 당신의 영혼을 풀어주기 위해서 다른 이의 손을 빌려 당신을 해칠 수밖에 없었던 나를...' WHERE entry=2000000650;
UPDATE db_script_string SET content_loc1='나 때문에 마음 아파하지 마세요, 내 사랑. 당신은 나를 속박에서 자유롭게 해주었고... 이제 저는 당신을 더욱 깊게 사랑하게 되었어요.' WHERE entry=2000000651;
UPDATE db_script_string SET content_loc1='슬프게도, 재회의 시간도 곧 끝나겠군요... 나를 이 세계에 묶고 있는 끈이 약해지고 있어요. 나를 먼 곳으로 데려 가려 하네요...' WHERE entry=2000000652;
UPDATE db_script_string SET content_loc1='안 돼! 아나야... 아나야! 나를 떠나지 마시오! 제발...' WHERE entry=2000000653;
UPDATE db_script_string SET content_loc1='잘 있어요, 세렐리안. 다시 만날 때까지...' WHERE entry=2000000654;
UPDATE db_script_string SET content_loc1='어떻게... 나더러 당신 없이 이 세상을 살아가란 말이오, 내 사랑...' WHERE entry=2000000656;
UPDATE db_script_string SET content_loc1='절벽 폭포는 산을 따라 동쪽, 그리고 약간 북쪽에 있소.' WHERE entry=2000000658;
UPDATE db_script_string SET content_loc1='땅문서는 내 겁니다, 형님! 내 앞길을 가로막지 못하도록 곧 영원히 제거해 드리지요!' WHERE entry=2000000659;
UPDATE db_script_string SET content_loc1='트롤이 날 화나게 했다!' WHERE entry=2000000660;
UPDATE db_script_string SET content_loc1='전쟁이다!' WHERE entry=2000000661;
UPDATE db_script_string SET content_loc1='나는 메카조드 님이 보낸 전령이다. 네게 걸린 저주를 풀어주마!' WHERE entry=2000000663;
UPDATE db_script_string SET content_loc1='쓸데없는 참견도 이걸로 끝이다. 내가 널 쓰러뜨리고 나면 메카조드 님이 저주를 풀어주실 것이다.' WHERE entry=2000000664;
UPDATE db_script_string SET content_loc1='ED-210 작동 개시!' WHERE entry=2000000665;
UPDATE db_script_string SET content_loc1='트웡키!' WHERE entry=2000000666;
UPDATE db_script_string SET content_loc1='승리가 아니면 죽음뿐! 호드를 위하여!' WHERE entry=2000000669;
UPDATE db_script_string SET content_loc1='온몸으로 나의 힘을 느껴라! 나의 힘에 도취하라! 호드의 영광을 위해 싸우라!' WHERE entry=2000000671;
UPDATE db_script_string SET content_loc1='잠깐, 친구!!! 아무래도 여기 도움이 필요하게 될 것 같은데...' WHERE entry=2000000673;
UPDATE db_script_string SET content_loc1='%s|1이;가; 춤을 추기 시작합니다.' WHERE entry=2000000674;
UPDATE db_script_string SET content_loc1='드럴은 아직 풀어 주지 않았소? 그가 어디에 갇혀 있는지는 나도 잘 모르오.' WHERE entry=2000000676;
UPDATE db_script_string SET content_loc1='난 당신의 임무에 걸림돌이 될 지도 모르오. 난 저 분홍빛 인간들에게 잡히진 않을 것이니 걱정하지 말고 어서 가시오!' WHERE entry=2000000677;
UPDATE db_script_string SET content_loc1='여기 위에 언덕으로 오세요, $N 님.' WHERE entry=2000000679;
UPDATE db_script_string SET content_loc1='내가 도와줄게요.' WHERE entry=2000000680;
UPDATE db_script_string SET content_loc1='이걸 마시세요. 한결 나아질 거예요.' WHERE entry=2000000681;
UPDATE db_script_string SET content_loc1='곧 다 나을 거예요.' WHERE entry=2000000682;
UPDATE db_script_string SET content_loc1='이것들 봐라. 땅속에 묻히지만 않았지 완전히 죽은 녀석들 같군. 죽을 힘을 다해서 해 보란 말이야!' WHERE entry=2000000686;
UPDATE db_script_string SET content_loc1='좋아, 말라깽이 친구들. 얼마나 잘 배웠는지 한번 볼까? 내가 멈추라고 할 때까지 계속 공격하도록.' WHERE entry=2000000687;
UPDATE db_script_string SET content_loc1='길 건너에 있는 블러드 엘프들이 내가 첩자라고 오해하지 말아야 할 텐데!' WHERE entry=2000000688;
UPDATE db_script_string SET content_loc1='집들이 선물 가져왔네, 자를.' WHERE entry=2000000692;
UPDATE db_script_string SET content_loc1='어서 열어보게. 맘에 들 것 같은데...' WHERE entry=2000000693;
UPDATE db_script_string SET content_loc1='자를?' WHERE entry=2000000694;
UPDATE db_script_string SET content_loc1='먹을 게 아니면, 받고 싶지 않네.' WHERE entry=2000000695;
UPDATE db_script_string SET content_loc1='자를, 내가 왔을 때부터 여태까지 계속 정신을 못 차리고 있잖아. 무슨 일이라도 있나?' WHERE entry=2000000696;
UPDATE db_script_string SET content_loc1='아무 일도 없네. 이 개구리 다리 스튜 만드는 거나 도와주겠나?' WHERE entry=2000000697;
UPDATE db_script_string SET content_loc1='여기까지 온 건 자넬 보고 싶어서네, 자를! 그랬더니 이런 알 수 없는 곳에 텅텅 빈 오두막집에서 개구리 타령이나 하며 살고 있다니! 절망 그 자체로군!' WHERE entry=2000000698;
UPDATE db_script_string SET content_loc1='에드워드. 타일러. 첫 번째 시험을 준비하도록.' WHERE entry=2000000699;
UPDATE db_script_string SET content_loc1='리스타, 포로를 소환하도록!' WHERE entry=2000000700;
UPDATE db_script_string SET content_loc1='리스타, 언데드 포로들을 불러내라.' WHERE entry=2000000702;
UPDATE db_script_string SET content_loc1='리스타, 누더기골렘을 데려오너라!' WHERE entry=2000000704;
UPDATE db_script_string SET content_loc1='에드워드, 타일러. 잘했다. 훈련 성과가 꽤 좋군. 얼마 후에 자네들의 기질을 다시 한번 시험해 볼 테니 준비하고 있도록.' WHERE entry=2000000705;
UPDATE db_script_string SET content_loc1='구해 주셔서 정말 감사합니다, $N! 우리 아버지께서 기뻐하실 거예요!' WHERE entry=2000000706;
UPDATE db_script_string SET content_loc1='네놈이 내 저녁 식사를 방해한 것이냐? 네놈 짓이냐?' WHERE entry=2000000707;
UPDATE db_script_string SET content_loc1='설인 암컷치고는 너무 못생기고 왜소하다 싶었다! 감히 나를 화나게 하다니...' WHERE entry=2000000708;
UPDATE db_script_string SET content_loc1='다시 한번 감사드립니다, $N. 여기서 기다렸다가 좀 더 잠잠해지면 그때 떠나야겠어요.' WHERE entry=2000000709;
UPDATE db_script_string SET content_loc1='실험에 실패한 혼합물이 바닥에 엎질러지자 살아서 움직입니다!' WHERE entry=2000000710;
UPDATE db_script_string SET content_loc1='살아있습니다!' WHERE entry=2000000711;
UPDATE db_script_string SET content_loc1='우트락 너 죽이겠다!' WHERE entry=2000000715;
UPDATE db_script_string SET content_loc1='복수는 우리 것이다! 형제들이여, 공격하라!' WHERE entry=2000000716;
UPDATE db_script_string SET content_loc1='내가 준비한 토양에서 이 씨들이 잘 자라는지 한번 봅시다.' WHERE entry=2000000718;
UPDATE db_script_string SET content_loc1='싹이 트고 뿌리를 내려야 할 텐데...' WHERE entry=2000000722;
UPDATE db_script_string SET content_loc1='두 개의 화석 위로 %s|1이;가; 렐루의 돌을 이리저리 맞춰 보기 시작합니다.' WHERE entry=2000000723;
UPDATE db_script_string SET content_loc1='이 아만을 이곳으로 부른 자가 누구인가?' WHERE entry=2000000724;
UPDATE db_script_string SET content_loc1='그대들은 과거의 비밀을 밝히려 애쓰고 있구나.' WHERE entry=2000000725;
UPDATE db_script_string SET content_loc1='경고하건대 그대를 창조한 자도 잘못을 범할 수 있다.' WHERE entry=2000000726;
UPDATE db_script_string SET content_loc1='과거를 너무 깊이 들추다 보면 앞날에 갑작스러운 최후를 맞이할 수도 있느니.' WHERE entry=2000000727;
UPDATE db_script_string SET content_loc1='맥주통은 잘 지키고 있나...?' WHERE entry=2000000730;
UPDATE db_script_string SET content_loc1='%s|1이;가; 늑대 가죽으로 만든 예복을 입습니다.' WHERE entry=2000000732;
UPDATE db_script_string SET content_loc1='%s|1이;가; 말루카즈의 부름에 응답하여 포효합니다.' WHERE entry=2000000733;
UPDATE db_script_string SET content_loc1='%s|1이;가; 자신이 도착했음을 울음소리로 표현합니다.' WHERE entry=2000000734;
UPDATE db_script_string SET content_loc1='정령이여, 우리의 결속이 또다시 깨어지는 일이 없기를...' WHERE entry=2000000735;
UPDATE db_script_string SET content_loc1='우리가 해냈습니다! 스랄 님도 자랑스러워하실 겁니다.' WHERE entry=2000000736;
UPDATE db_script_string SET content_loc1='모르고르, 이게 무슨 짓이냐?' WHERE entry=2000000737;
UPDATE db_script_string SET content_loc1='닥쳐라!' WHERE entry=2000000738;
UPDATE db_script_string SET content_loc1='이자가 네놈의 용사란 말이냐?' WHERE entry=2000000740;
UPDATE db_script_string SET content_loc1='너는 속았다, 바보 같은 녀석.' WHERE entry=2000000741;
UPDATE db_script_string SET content_loc1='이... 이번 일 전체가... 위험할 뻔했단 말이다!' WHERE entry=2000000742;
UPDATE db_script_string SET content_loc1='이 벌레의 시체를 조각내어 한 시간 내로 내게 가져와라. 만일 실패하거든 죽음보다 더한 고통에 떨게 해 주겠다.' WHERE entry=2000000743;
UPDATE db_script_string SET content_loc1='저 망할 문을 안전히 통과할 수 있게 하려면 아직 멀었나?' WHERE entry=2000000747;
UPDATE db_script_string SET content_loc1='뭐, 그런 건 아니고요.' WHERE entry=2000000748;
UPDATE db_script_string SET content_loc1='그만 좀 물어보시겠습니까?' WHERE entry=2000000749;
UPDATE db_script_string SET content_loc1='좋아, 안전해 보이는군. 안으로 들어가라, 기술자!' WHERE entry=2000000750;
UPDATE db_script_string SET content_loc1='까짓것 한번 해보죠!' WHERE entry=2000000751;
UPDATE db_script_string SET content_loc1='최소한 이력서에 쓸 건 하나 더 늘겠군.' WHERE entry=2000000752;
UPDATE db_script_string SET content_loc1='%s|1이;가; 몸을 더듬으며 어디 잘못된 곳은 없는지 확인합니다.' WHERE entry=2000000753;
UPDATE db_script_string SET content_loc1='해냈어! 내가 해냈어!' WHERE entry=2000000754;
UPDATE db_script_string SET content_loc1='%s|1이;가; 불만스럽다는 듯 고개를 젓습니다.' WHERE entry=2000000755;
UPDATE db_script_string SET content_loc1='어쨌든 별로 마음에 안 드는 놈이었다. 말만 많고 \'하는 일\'은 별로 없었지.' WHERE entry=2000000756;
UPDATE db_script_string SET content_loc1='잘하는 짓들이다, 이 무능한 놈들아! 기술자 한 명이 또 증발했잖아!' WHERE entry=2000000757;
UPDATE db_script_string SET content_loc1='방해해 봤자 소용없다! 제이나가 얼마나 비겁한지 우리가 세상에 알리고 말 테다!' WHERE entry=2000000758;
UPDATE db_script_string SET content_loc1='우리를 막을 수 있다고 생각했다면 큰 오산이다, \'친구\'.' WHERE entry=2000000759;
UPDATE db_script_string SET content_loc1='여기 있는 모든 이는 프라우드무어 제독님이 진정한 영웅이셨다는 걸 알게 될 것이다!' WHERE entry=2000000760;
UPDATE db_script_string SET content_loc1='조금만 창의적으로 손보면 훨씬 유용하답니다...' WHERE entry=2000000761;
UPDATE db_script_string SET content_loc1='이 유인물을 그냥 쓰레기통에 버리는 건 너무 아깝잖아요. 그렇지 않나요?' WHERE entry=2000000762;
UPDATE db_script_string SET content_loc1='... 헤어젤을 구매하기 위한 기금을 만들자? 필요 없소. 난 내 머리 모양에 만족하오!' WHERE entry=2000000763;
UPDATE db_script_string SET content_loc1='... 탈영병이 된 여러분은 밧줄에 묶여 수영하기나 개 목욕시키기 등의 여러 여가 활동을 즐기실 수 있습니다? 이거 웃기는군요!' WHERE entry=2000000764;
UPDATE db_script_string SET content_loc1='저는 절대 이런 일에 동참하지 않을 거예요. 하지만 이건 가져가는 게 좋겠네요. 마침 휴지가 다 떨어졌으니 말이죠...' WHERE entry=2000000765;
UPDATE db_script_string SET content_loc1='... 탈영병들은 모든 시민이 온종일 곡예를 할 수 있는 테라모어를 원합니다? 이게 대체 무슨 소리예요?' WHERE entry=2000000766;
UPDATE db_script_string SET content_loc1='지금껏 읽어본 것들 중에 제일 웃겼어요. 아주 즐거웠어요!' WHERE entry=2000000767;
UPDATE db_script_string SET content_loc1='지금 농담하시는 거죠? 그렇죠?' WHERE entry=2000000768;
UPDATE db_script_string SET content_loc1='정령들이 당신을 보살피길!' WHERE entry=2000000770;
UPDATE db_script_string SET content_loc1='정말 고맙습니다, 영웅이여!' WHERE entry=2000000771;
UPDATE db_script_string SET content_loc1='구해줘서 고맙습니다. 하지만 다른 이들이 아직 저 위쪽에 잡혀 있습니다!' WHERE entry=2000000774;
UPDATE db_script_string SET content_loc1='이자들은 모두 미쳤어요! 자기들의 주인이라면서 로켄을 섬긴다는 말은 계속 중얼대지만, 정작 직접 명령을 받는 자들은 얼마 되지 않는 것 같아요.' WHERE entry=2000000775;
UPDATE db_script_string SET content_loc1='정말 고맙네! 놈들이 날 바위 거인들과 싸우도록 강요할까 봐 무척 걱정하고 있었다네.' WHERE entry=2000000776;
UPDATE db_script_string SET content_loc1='감히 바다의 왕인 드로틴 흐로스가르에게 도전하느냐?' WHERE entry=2000000777;
UPDATE db_script_string SET content_loc1='이교도 놈들에게 쓴맛을 보여주세요, $N 님. 이 성전사 쿨 님을 귀찮게 하면 호된 꼴을 면할 수 없지!' WHERE entry=2000000778;
UPDATE db_script_string SET content_loc1='빛이여, 감사합니다! 이런 미친 짓은 다시는 하지 않겠습니다!' WHERE entry=2000000779;
UPDATE db_script_string SET content_loc1='남은 지원자를 구출해서 돌아갑시다. 이 일이 끝나고 나면 에일을 한 주전자는 마셔야겠어요!' WHERE entry=2000000780;
UPDATE db_script_string SET content_loc1='놈들은 제단에 저를 묶어 영혼을 빨아내려던 참이었습니다. 어서 여길 빠져나갑시다!' WHERE entry=2000000781;
UPDATE db_script_string SET content_loc1='감사해요, $C님.' WHERE entry=2000000782;
UPDATE db_script_string SET content_loc1='저들이 갇힌 이들을 죽이고 있어요! 서둘러 도와주세요.' WHERE entry=2000000783;
UPDATE db_script_string SET content_loc1='마상시합 따위에 나오지 말았어야 했어요!' WHERE entry=2000000784;
UPDATE db_script_string SET content_loc1='여기에서 살아서 나가리라고는 꿈에도 생각하지 못했어요.' WHERE entry=2000000785;
UPDATE db_script_string SET content_loc1='멍청한 $C 같으니. 크발디르를 막을 순 없다!' WHERE entry=2000000786;
UPDATE db_script_string SET content_loc1='이곳을 떠나라!' WHERE entry=2000000792;
UPDATE db_script_string SET content_loc1='필멸의 존재여, 서리 군주님께서 이 세계에 오시는 것을 막지는 못하리라. 바다사냥꾼님이 휘두르는 권력이 라그나로스의 힘을 완전히 무너뜨리고, 네 땅을 동토의 왕국으로 만들리라!' WHERE entry=2000000797;
UPDATE db_script_string SET content_loc1='감사합니다. 저도 무쇠 드워프를 상대하는 데 힘을 보태겠습니다.' WHERE entry=2000000798;
UPDATE db_script_string SET content_loc1='계산할 수 없습니다. 기능에 문제가 있습니다. 지령. 작동 중지.' WHERE entry=2000000799;
UPDATE db_script_string SET content_loc1='자유라고요? 미미르 님께서 시키신 일을 마저 해야겠군요.' WHERE entry=2000000800;
UPDATE db_script_string SET content_loc1='새 지령. 무쇠 드워프를 쓰러뜨립니다.' WHERE entry=2000000801;
UPDATE db_script_string SET content_loc1='이 오만하고 멍청한 놈! 감히 젤레마르의 피를 훔치려고 했나? 그 대가를 치르게 해주마!' WHERE entry=2000000802;
UPDATE db_script_string SET content_loc1='그를 어서 꺼내주시오!' WHERE entry=2000000804;
UPDATE db_script_string SET content_loc1='당신과 볼일이 끝나면 오그라비시가 당신 머리를 작은 모자로 쓸 지도 모르겠군.' WHERE entry=2000000805;
UPDATE db_script_string SET content_loc1='내가 살던 곳에서는 다른 죄수의 감옥문을 열어주다 걸리면 큰일난다고!' WHERE entry=2000000806;
UPDATE db_script_string SET content_loc1='두목님의 끝없는 불평은 이미 들을 만큼 들었습니다. 두목의 역할이 뭐라고 생각하십니까? 그러니까, 정신이 사납다고요!' WHERE entry=2000000808;
UPDATE db_script_string SET content_loc1='%s|1이;가; 어깨를 으쓱합니다.' WHERE entry=2000000810;
UPDATE db_script_string SET content_loc1='음, 처음부터 다시 해봐야겠어.' WHERE entry=2000000811;
UPDATE db_script_string SET content_loc1='내가 바라는 것은 오직 그가 성스러운 빛의 정의를 깨닫고 열정을 다해 한때 아름다웠던 쿠엘탈라스를 재건하는 것뿐이라네.' WHERE entry=2000000816;
UPDATE db_script_string SET content_loc1='석공... 에.. 그리고 데피아즈단은 주의하라. 오늘 밤 녹슨 닻이 내려질 것이다.' WHERE entry=2000000817;
UPDATE db_script_string SET content_loc1='휴! 내 행운의 모자가 여기 있었네. 사방을 뒤져도 없더니만.' WHERE entry=2000000819;
UPDATE db_script_string SET content_loc1='검을 두고 나가다니 제정신이 아닌가 봅니다. 그런데 제가 검이 여기 있는 것을 봤다면 그냥 놔두고 갔을 리는 없는데...' WHERE entry=2000000820;
UPDATE db_script_string SET content_loc1='망토 없이 외출할 수는 없지. 이 안에 망토가 있으면 좋겠어...' WHERE entry=2000000821;
UPDATE db_script_string SET content_loc1='여기 있었군! 마지막에 확인할 때만 해도 분명 여기 없었는데...' WHERE entry=2000000822;
UPDATE db_script_string SET content_loc1='저는 침대에서 재미있는 얘기를 읽어야 잠이 옵니다. 이제 편히 잠들 수 있겠네요.' WHERE entry=2000000823;
UPDATE db_script_string SET content_loc1='저 소리는 뭐였지? 울부짖는 소리 같았는데...' WHERE entry=2000000824;
UPDATE db_script_string SET content_loc1='잔혹발톱?! 진정해... 너의 주인 볼코르는 어디 있지?' WHERE entry=2000000826;
UPDATE db_script_string SET content_loc1='워, 워, 진정하라고. 잠시만...' WHERE entry=2000000828;
UPDATE db_script_string SET content_loc1='그래... 그만하면 됐어. 이제 무슨 일이 있었는지 말해 봐, 잔혹발톱.' WHERE entry=2000000830;
UPDATE db_script_string SET content_loc1='알았어, 네 주인을 도와줄 이를 찾아보지. 이제 돌아가서 주인 곁을 지켜.' WHERE entry=2000000832;
UPDATE db_script_string SET content_loc1='잔혹발톱과 그의 주인 볼코르를 좀 도와주시오. 관심이 있다면 내 얘기를 좀 더 자세히 들어 보는 게 어떻겠소...' WHERE entry=2000000834;
UPDATE db_script_string SET content_loc1='실험을 시작할 테니 잠시 기다리게...' WHERE entry=2000000835;
UPDATE db_script_string SET content_loc1='이건... 흥미롭군.' WHERE entry=2000000836;
UPDATE db_script_string SET content_loc1='아니, 이건 뭐야?' WHERE entry=2000000837;
UPDATE db_script_string SET content_loc1='맙소사!' WHERE entry=2000000838;
UPDATE db_script_string SET content_loc1='오래전 내 피가 차갑게 굳어버리지 않았다면 지금 엄청나게 끓어올랐을 거야...' WHERE entry=2000000839;
UPDATE db_script_string SET content_loc1='인사드립니다, 게제 님! 하라사미드 구역에서 17건의 주문이 추가로 들어왔습니다. 예측하신 대로 벨다라이트 시장이 붕괴됐습니다.' WHERE entry=2000000842;
UPDATE db_script_string SET content_loc1='헤라지 수출이 증가 추세입니다. 이미 세관원을 매수해 놨으니 며칠 안으로 밀수품이 도착할 겁니다.' WHERE entry=2000000843;
UPDATE db_script_string SET content_loc1='놀랄 만한 내용은 하나도 없군. 제대로 된 소식을 가지고 오도록. 알겠나?' WHERE entry=2000000844;
UPDATE db_script_string SET content_loc1='예상한 대로군. 아주 좋아. 이만 가 보도록.' WHERE entry=2000000845;
UPDATE db_script_string SET content_loc1='그럼 물러가도록 하겠습니다.' WHERE entry=2000000846;
UPDATE db_script_string SET content_loc1='여기 온 걸 보니 악의 무리를 소환하여 부리는 법을 공부할 준비가 됐나 보군. 자네가 부를 수 있을 만한 가장 저급한 생물부터 시작하지. 자, 시작해 볼까?' WHERE entry=2000000847;
UPDATE db_script_string SET content_loc1='가장 소환하기 쉬운 생물은 임프라네. 자네도 이미 임프 정도는 소환할 줄 알겠지만, 빠진 것이 없도록 이놈부터 설명하겠네.' WHERE entry=2000000848;
UPDATE db_script_string SET content_loc1='이 사악하고 작은 짐승이 바로 임프지. 작고 약해서 고기 방패처럼 거의 쓸모가 없어. 물론 공격력도 보잘것없고. 이 짐승은 파티를 지원하는 정도의 용도로 사용하는 게 가장 좋지.' WHERE entry=2000000849;
UPDATE db_script_string SET content_loc1='임프는 공부했으니 이제 다음 소환수를 소환해 보지. 다음은 공허방랑자라네.' WHERE entry=2000000850;
UPDATE db_script_string SET content_loc1='이 악마는 공허방랑자라고 알려져 있지. 힘과 맷집이 대단해서 방어용으로 이상적이야. 공허방랑자가 먼저 적을 공격하게 한 뒤, 녀석을 방패 삼아 자네의 주문이나 능력을 사용하게.' WHERE entry=2000000851;
UPDATE db_script_string SET content_loc1='자, 한 번도 본 적이 없다면 잘 봐 두라고. 전장 안팎에서 모두 아주 유용한 녀석이니까 말이야. 다음은 남성들이라면 모두 기대하고 있을 서큐버스를 소환해 보지.' WHERE entry=2000000852;
UPDATE db_script_string SET content_loc1='좋아. 서큐버스는 남성 적들을 매혹시켜 교란을 유도할 수 있을 뿐만 아니라 상당한 타격도 입힐 수 있지. 하지만 방어력이 약해서 방패로는 거의 쓸모가 없어.' WHERE entry=2000000853;
UPDATE db_script_string SET content_loc1='열심히 공부하다 보면 언젠가 직접 소환할 수 있을 걸세. 하지만 지금은 우선 지옥사냥개로 넘어가 보지.' WHERE entry=2000000854;
UPDATE db_script_string SET content_loc1='앞에 보이는 것이 지옥사냥개일세. 이 야수는 주문 잠금을 비롯한 여러 가지 능력을 타고났기 때문에, 마법을 사용하는 적을 상대하는 데 탁월하지.' WHERE entry=2000000855;
UPDATE db_script_string SET content_loc1='마법을 사용하는 모든 적들을 상대할 때는 이 무시무시한 짐승이 가장 유용할 거야. 이제 약간 다른 녀석을 살펴볼까? 자네의 여행을 도와주고 앞으로 여정을 훨씬 편하게 해줄 친구지. 자, 지옥마를 살펴보자고.' WHERE entry=2000000856;
UPDATE db_script_string SET content_loc1='자, 이것으로 소환에 대한 수업은 모두 마치도록 하겠네. 곧 다음 수업이 시작될 테니, 복습하고 싶다면 여기 계속 머물러 있어도 좋네.' WHERE entry=2000000858;
UPDATE db_script_string SET content_loc1='사제가 무릎을 꿇고 조용히 기도합니다.' WHERE entry=2000000859;
UPDATE db_script_string SET content_loc1='성스러운 빛이여, 제게 힘을 주소서!' WHERE entry=2000000860;
UPDATE db_script_string SET content_loc1='성스러운 빛이여, 타락한 이들을 거짓에서 진실로, 어둠에서 빛으로, 죽음에서 영생으로 이끌어 주소서.' WHERE entry=2000000861;
UPDATE db_script_string SET content_loc1='안 돼애애애애!!!!' WHERE entry=2000000865;
UPDATE db_script_string SET content_loc1='동지들이여... 실망시켜서 미안합니다...' WHERE entry=2000000866;
UPDATE db_script_string SET content_loc1='아니오... 우리가 당신을 실망시켰소... 바로 우리가 말이오...' WHERE entry=2000000867;
UPDATE db_script_string SET content_loc1='검은용군단이 느닷없이 나타나, 우리가 미처 손쓸 새도 없이 공격을 퍼붓더구나.' WHERE entry=2000000870;
UPDATE db_script_string SET content_loc1='저들은 무자비하게 우리를 학살했지만, 진짜 목적은 성소 안 이곳에 보관된 알에 있는 듯하더구나.' WHERE entry=2000000872;
UPDATE db_script_string SET content_loc1='침입자들아, 너희 승리는 덧없는 것이다. 나는 사령관인 바로스 클라우드스트라이더다. 내 비룡들이 하늘을 지키고, 이 마력의 통로를 보호하지. 분명히 말하건대, 마력의 눈을 너희에게 내주지는 않을 것이다.' WHERE entry=2000000877;
UPDATE db_script_string SET content_loc1='교활한 마법사 군주 우롬이 세 번째 고리를 보호하고 있어요. 놈은 무방비 상태로 혼자 있는 것처럼 보이지만, 겉보기만으로 속아 넘어가서는 안 돼요! 우롬은 실체 없는 생물 군단을 지휘하는 강력한 요술사예요. 저 위에서 그를 찾아보세요.' WHERE entry=2000000878;
UPDATE db_script_string SET content_loc1='가장 까다로운 도전이 눈앞에 놓였어요. 지맥 수호자 에레고스는 엄청난 힘을 지닌 푸른용이에요. 에레고스는 가장 높이 있는 고리 위를 선회하고 있을 거예요.' WHERE entry=2000000879;
UPDATE db_script_string SET content_loc1='길 잃은 영혼을 맛본 자는 영원히 굶주림에 시달리게 되지.' WHERE entry=2000000880;
UPDATE db_script_string SET content_loc1='공허를 들여다보아라. 그들이 속한 불멸의 영역이지.' WHERE entry=2000000881;
UPDATE db_script_string SET content_loc1='그들은 공포를 마신다. 그것은 생명이다.' WHERE entry=2000000882;
UPDATE db_script_string SET content_loc1='그들은 매서운 바람의 속삭임이다.' WHERE entry=2000000883;
UPDATE db_script_string SET content_loc1='그들은 죽지 않는다. 그들은 살지 않는다. 그들은 생명의 순환을 벗어난 존재다.' WHERE entry=2000000884;
UPDATE db_script_string SET content_loc1='하나가 죽으면, 수많은 이가 그 자리를 대신하지.' WHERE entry=2000000885;
UPDATE db_script_string SET content_loc1='언젠가... 우린 다시 만날 것이다.' WHERE entry=2000000888;
UPDATE db_script_string SET content_loc1='이 죽음의 화신이 다시 한 번 세상에 나타났다! 나 테론 고어핀드가 돌아왔느니...' WHERE entry=2000000889;
UPDATE db_script_string SET content_loc1='숨어! 놈들이 자네를 발견하기 전에 어서! 물러나란 말이야!' WHERE entry=2000000892;
UPDATE db_script_string SET content_loc1='공격 시간이 얼마 안 남았다. 이카리우스를 끝장내라.' WHERE entry=2000000893;
UPDATE db_script_string SET content_loc1='멈춰라!' WHERE entry=2000000894;
UPDATE db_script_string SET content_loc1='무슨 일입니까?' WHERE entry=2000000895;
UPDATE db_script_string SET content_loc1='자라스, 즉시 검은 사원으로 돌아가라! 내가... 일리단 님의 명령서를 놓고 온 것 같다. 서둘러라!' WHERE entry=2000000896;
UPDATE db_script_string SET content_loc1='그럼 호위는 누가 합니까? 무슨 일이라도 생기면 일리단 님께서 절 가만두지 않으실 겁니다.' WHERE entry=2000000897;
UPDATE db_script_string SET content_loc1='자라스, 나 혼자서도 얼마든지 해그늘 주둔지까지 갈 수 있다. 서한을 전달하지 않으면 일리단 님께 우리 모두 죽게 될 것이다! 이만 돌아가도록!' WHERE entry=2000000898;
UPDATE db_script_string SET content_loc1='분부대로 하겠습니다, 주인님.' WHERE entry=2000000899;
UPDATE db_script_string SET content_loc1='오, 맛있는 내 피엉겅퀴... 아마도 해그늘 주둔지의 더러운 중독자들 중 한 놈이 흘리고 갔겠지.' WHERE entry=2000000900;
UPDATE db_script_string SET content_loc1='놈들의 손실이 내게 이렇게 큰 기쁨이 되는구나...' WHERE entry=2000000901;
UPDATE db_script_string SET content_loc1='%s|1이;가; 로브 속에 피엉겅퀴 한 묶음을 넣습니다.' WHERE entry=2000000902;
UPDATE db_script_string SET content_loc1='정령들이 공격할지도 모르니 물러서시오, $N.' WHERE entry=2000000903;
UPDATE db_script_string SET content_loc1='우리를 왜 사로잡았지?' WHERE entry=2000000904;
UPDATE db_script_string SET content_loc1='이렇게 해서 얻으려는 게 뭔가?' WHERE entry=2000000905;
UPDATE db_script_string SET content_loc1='%s|1이;가; 절을 합니다.' WHERE entry=2000000906;
UPDATE db_script_string SET content_loc1='정령이여, 우리는 그대들을 돕고 싶소. 그대들의 지혜를 구합니다.' WHERE entry=2000000907;
UPDATE db_script_string SET content_loc1='지혜라고 했나? 사이루크의 억제할 수 없는 분노를 부른 것은 자네 종족이었잖아.' WHERE entry=2000000908;
UPDATE db_script_string SET content_loc1='우리가 왜 너희를 도와야 하지? 이 세계는 폐허가 되었어. 멸망했단 말이지.' WHERE entry=2000000909;
UPDATE db_script_string SET content_loc1='%s|1이;가; 고개를 끄덕입니다.' WHERE entry=2000000910;
UPDATE db_script_string SET content_loc1='그대들의 말이 맞소, 정령이여... 그대들에게 그런 짓을 한 것은 나의 동족이었소. 우리가 그 잘못을 바로잡게 해주시오.' WHERE entry=2000000911;
UPDATE db_script_string SET content_loc1='제발 이것만은 알려주시오. 대체 사이루크가 뭐요?' WHERE entry=2000000912;
UPDATE db_script_string SET content_loc1='바보 같은! 불의 군주 사이루크 말일세! 연결 고리가 끊어졌을 때 사이루크가 깨어났지. 사이루크의 눈물이 흘러내려 이 땅을 용암으로 채운 걸세.' WHERE entry=2000000913;
UPDATE db_script_string SET content_loc1='불... 불의 군주라고 하셨소? 정령들의 군주가 깨어났다는 말이오? 그렇군... 굴단 당신은 정말 바보였소!' WHERE entry=2000000914;
UPDATE db_script_string SET content_loc1='지금 그는 제정신이 아닐세. 세계가 파괴되는 동안 미쳐버렸지. 우리 정령들이 쉴 수가 없어.' WHERE entry=2000000915;
UPDATE db_script_string SET content_loc1='분명 방법이 있을 거요.' WHERE entry=2000000916;
UPDATE db_script_string SET content_loc1='그래, 방법이 있어. 바로 사이루크를 해치우는 걸세. 정령의 군주를 해치워야 해.' WHERE entry=2000000917;
UPDATE db_script_string SET content_loc1='정령이여, 나와 동료를 믿어주시오. 그리고 불의 군주를 소환할 방법을 알려주시오. 우리 동맹이 놈을 쓰러뜨릴 거요.' WHERE entry=2000000918;
UPDATE db_script_string SET content_loc1='주문을 외워야 하네...' WHERE entry=2000000919;
UPDATE db_script_string SET content_loc1='그래, 맞아! 그 주문... 파멸의 주문... 자네의 스승이었던 어둠의 존재가 사용했던 그 주문!' WHERE entry=2000000920;
UPDATE db_script_string SET content_loc1='그래... 굴단...' WHERE entry=2000000921;
UPDATE db_script_string SET content_loc1='우리는 반드시 그 주문을 찾아낼 거요. 그리고 놈을 소환해 해치우겠소.' WHERE entry=2000000922;
UPDATE db_script_string SET content_loc1='대화... 대화가 필요하오... 필멸의 생명체여, 이제 우리를 풀어주시오.' WHERE entry=2000000923;
UPDATE db_script_string SET content_loc1='이제 잃어버린 주문을 되찾을 시간이오!' WHERE entry=2000000924;
UPDATE db_script_string SET content_loc1='순순히 불어라, 물고기 인간아...' WHERE entry=2000000925;
UPDATE db_script_string SET content_loc1='파멸의 암호는 어디 있느냐? 세 번째 조각이 이 마을에 있다는 걸 알고 있다.' WHERE entry=2000000926;
UPDATE db_script_string SET content_loc1='무슨... 말을 하는 거냐, 부드러운 비늘을 가진 자여?' WHERE entry=2000000927;
UPDATE db_script_string SET content_loc1='우리 나가는... 암호를... 갖고 있지 않다...' WHERE entry=2000000928;
UPDATE db_script_string SET content_loc1='아니라고? 물론 그렇게 대답하겠지. 내가 무슨 생각을 한 줄 아느냐? 파멸의 암호의 세 번째 조각을 지키려는 이유가 뭐지?' WHERE entry=2000000929;
UPDATE db_script_string SET content_loc1='내가 어리석었군. 족쇄를 풀고 널 놓아주면 대답을 하겠지?' WHERE entry=2000000930;
UPDATE db_script_string SET content_loc1='그래... 그래... 날 풀어다오...' WHERE entry=2000000931;
UPDATE db_script_string SET content_loc1='나가야, 내가 그렇게 멍청하게 보였나? 고통을 보여주마!' WHERE entry=2000000932;
UPDATE db_script_string SET content_loc1='%s|1이;가; 갈퀴흉터 나가에게 침을 뱉습니다.' WHERE entry=2000000933;
UPDATE db_script_string SET content_loc1='일어나, 이 썩어 문드러진 쓰레기 같은 놈아! 끝장내기 전에 일어서!' WHERE entry=2000000934;
UPDATE db_script_string SET content_loc1='%s|1이;가; 정신을 차립니다.' WHERE entry=2000000935;
UPDATE db_script_string SET content_loc1='오크여, 더 이상 때리지 마라! 말할 테니까! 암호는... 갈퀴흉터 거점에 있는 상자들 중 하나에 숨겨져 있다... 그곳에 있는 나가가 열쇠를 지니고 있다...' WHERE entry=2000000937;
UPDATE db_script_string SET content_loc1='갈퀴흉터 거점 말이다!' WHERE entry=2000000938;
UPDATE db_script_string SET content_loc1='치유사여, 원하는 게 무엇이오?' WHERE entry=2000000939;
UPDATE db_script_string SET content_loc1='눈은 장식품으로 달고 다니나? 바로 우리 코앞에 악마가 있는 게 안 보이시오? 여유 병력이 없소!' WHERE entry=2000000940;
UPDATE db_script_string SET content_loc1='당신이 믿는 신에게 기도나 올리시오. 아마도 도와주러 올 거요. 하지만, 호드는 갈 수 없소...' WHERE entry=2000000941;
UPDATE db_script_string SET content_loc1='모든 준비가 완료되었습니다, 전쟁인도자님.' WHERE entry=2000000942;
UPDATE db_script_string SET content_loc1='좋아, 파멸의 군주 카자크께서 기뻐하실 것이다. 공격 속도를 높여라. 최대한 빨리 오크와 드워프의 요새를 파괴해라.' WHERE entry=2000000943;
UPDATE db_script_string SET content_loc1='전쟁인도자님, 그러려면 요새의 지옥불정령을 모조리 동원해야 합니다. 자칫하면 역습을 당할 수가 있습니다.' WHERE entry=2000000944;
UPDATE db_script_string SET content_loc1='그 문제는 걱정하지 마라. 죽음의 괴철로의 생산량을 늘려 놓았다. 네가 명령을 수행하기에 충분한 지옥불정령을 거느릴 수 있을 것이다. 실패하지 마라, 조바안.' WHERE entry=2000000945;
UPDATE db_script_string SET content_loc1='말씀하신 대로 행하겠습니다, 전쟁인도자님. 질문이 하나 있습니다만 여쭤도 되올지요...' WHERE entry=2000000946;
UPDATE db_script_string SET content_loc1='뭔가?' WHERE entry=2000000947;
UPDATE db_script_string SET content_loc1='저 상자 안에 뭐가 들어 있는 겁니까?' WHERE entry=2000000948;
UPDATE db_script_string SET content_loc1='상자? 너한테 상자를 보낸 적이 없다, 조바안. 이 일보다 더욱 중요한 일이 있지 않던가? 가서 그 일이나 처리하라!' WHERE entry=2000000949;
UPDATE db_script_string SET content_loc1='열쇠를 가지려면 먼저 날 잡아야 할걸!' WHERE entry=2000000950;
UPDATE db_script_string SET content_loc1='따라올 테면 따라와 봐! 뒤도 안 돌아보고 튈 테니까!' WHERE entry=2000000951;
UPDATE db_script_string SET content_loc1='왜 날 해치려 들까? 우린 사이 좋은 친구가 될 수도 있는데 말이야!' WHERE entry=2000000952;
UPDATE db_script_string SET content_loc1='으앙! 이 푸실린을 울리다니!' WHERE entry=2000000953;
UPDATE db_script_string SET content_loc1='내 깜찍한 친구들한테 인사나 하시지!' WHERE entry=2000000954;
UPDATE db_script_string SET content_loc1='자, 그럼 시작해볼까?' WHERE entry=2000000956;
UPDATE db_script_string SET content_loc1='모두 전진!' WHERE entry=2000000961;
UPDATE db_script_string SET content_loc1='얼음 기둥이 녹아 내렸다!' WHERE entry=2000000962;
UPDATE db_script_string SET content_loc1='아훈, 네 힘은 더 커지지 못한다!' WHERE entry=2000000963;
UPDATE db_script_string SET content_loc1='얼어붙은 통치의 시대는 오지 않을 것이다!' WHERE entry=2000000964;
UPDATE db_script_string SET content_loc1='놈이 죽었군! 두 번째 파멸의 암호 조각은 이제 우리 것이다. 나... 나는...' WHERE entry=2000000965;
UPDATE db_script_string SET content_loc1='난 사라지고 있어... 알토르에게 돌아... 가... 십시오...' WHERE entry=2000000966;
UPDATE db_script_string SET content_loc1='%s|1로부터;으로부터; 음침하고 오싹한 목소리가 울려 퍼집니다...' WHERE entry=2000000967;
UPDATE db_script_string SET content_loc1='조용히 해! 파멸의 날이 머지않았다!' WHERE entry=2000000970;
UPDATE db_script_string SET content_loc1='거부할 수 없는 힘을 갖고 계신 어둠의 주인님을 맞이하라!' WHERE entry=2000000971;
UPDATE db_script_string SET content_loc1='그분께서 선사하는 능력으로 나는 이 땅을 파괴한 후 재건할 것이니라!' WHERE entry=2000000972;
UPDATE db_script_string SET content_loc1='보아라! 우리 앞의 땅이 갈라진다! 에너지의 흐름을 주시해라! 적에게 힘을 주는 원천이니...' WHERE entry=2000000973;
UPDATE db_script_string SET content_loc1='우리는 결코 힘을 잃지 않는다! 난 우리의 미래를 지켜왔다! 너희는 나와 함께 영광을 누리리라!' WHERE entry=2000000974;
UPDATE db_script_string SET content_loc1='이제 모든 게... 끝났다...' WHERE entry=2000000975;
UPDATE db_script_string SET content_loc1='대지를 산산이 갈라버리십시오, 굴단! 연결 고리를 제거하십시오!' WHERE entry=2000000976;
UPDATE db_script_string SET content_loc1='이러지 마십시오, 굴단! 정령들이 결코 우리를 용서하지 않을 것이오!' WHERE entry=2000000977;
UPDATE db_script_string SET content_loc1='잊지 마십시오! 그들은 우리를 버렸어요!' WHERE entry=2000000979;
UPDATE db_script_string SET content_loc1='순순히 말을 듣지 않겠다면 억지로라도 이 땅에서 끌어낼 겁니다!' WHERE entry=2000000980;
UPDATE db_script_string SET content_loc1='그만! 그만 멈추세요!' WHERE entry=2000000981;
UPDATE db_script_string SET content_loc1='당신은 우리 모두를 파멸의 구렁텅이에 빠뜨렸어요. 우리 모두 패배했단 말입니다.' WHERE entry=2000000982;
UPDATE db_script_string SET content_loc1='명을 받들겠습니다!' WHERE entry=2000000983;
UPDATE db_script_string SET content_loc1='첩보로봇이 녀석을 나오게 할 테니까 여기서 기다리라고, $N. 단단히 준비하라고! 그들의 대화를 엿들은 후에 공격해야 해!' WHERE entry=2000000984;
UPDATE db_script_string SET content_loc1='두 분 수고하십니다. 전 라스코발 경을 만나 뵈려고 왔습니다.' WHERE entry=2000000985;
UPDATE db_script_string SET content_loc1='어서 들어가십시오. 경께서는 도서관에 기다리고 계십니다.' WHERE entry=2000000986;
UPDATE db_script_string SET content_loc1='감사합니다. 빛의 영광이 함께하시길.' WHERE entry=2000000987;
UPDATE db_script_string SET content_loc1='라스코발 경. 경을 만나기 위해 손님이 기다리고 계십니다.' WHERE entry=2000000988;
UPDATE db_script_string SET content_loc1='아, 친절에 감사드리오. 내가 잠시 볼 일을 볼 동안 도서관에서 기다려 주시겠소?' WHERE entry=2000000989;
UPDATE db_script_string SET content_loc1='명상에 들어갈 시간이군. 방해하지 말도록.' WHERE entry=2000000991;
UPDATE db_script_string SET content_loc1='알겠어요!' WHERE entry=2000000992;
UPDATE db_script_string SET content_loc1='%s|1이;가; 경비병들이 보이지 않을 때까지 기다립니다.' WHERE entry=2000000993;
UPDATE db_script_string SET content_loc1='왔군. 그래, 서부 몰락지대에서 어떤 소식을 가져왔나?' WHERE entry=2000000994;
UPDATE db_script_string SET content_loc1='밴클리프 님께서 계획을 진행 중이라고 알려 오셨습니다. 또 주위를 염탐하는 자가 있다는 소문을 들으셨다고 합니다.' WHERE entry=2000000995;
UPDATE db_script_string SET content_loc1='흠.. 참견하기 좋아하는 쇼가 아닐까 싶군. 내 나름대로 알아보지. 우선 가보게나. 곧 다시 연락하지.' WHERE entry=2000000996;
UPDATE db_script_string SET content_loc1='잘 알겠습니다. 다음에 다시 오겠습니다.' WHERE entry=2000000997;
UPDATE db_script_string SET content_loc1='그렇지! 바로 그걸 기다리고 있었던 거야! 처치해 버려!' WHERE entry=2000000998;
UPDATE db_script_string SET content_loc1='바리안 국왕은 비밀리에 수로를 통해 이곳으로 오려 하셨는데 배가 도착하지 않았습니다. 그다음에 바리안 국왕은 종적을 감췄지요.' WHERE entry=2000001001;
UPDATE db_script_string SET content_loc1='한편 국왕은 납치범에게서 도망쳐 나와 오닉시아와 담판을 짓고자 스톰윈드로 돌아갔습니다.' WHERE entry=2000001003;
UPDATE db_script_string SET content_loc1='고마워요, $N.' WHERE entry=2000001008;
UPDATE db_script_string SET content_loc1='설명을 해야겠군요...' WHERE entry=2000001014;
UPDATE db_script_string SET content_loc1='가세요!' WHERE entry=2000001021;
UPDATE db_script_string SET content_loc1='가엾은 필멸의 존재여, 오닉시아는 아무에게도 대답하지 않는다!' WHERE entry=2000001026;
UPDATE db_script_string SET content_loc1='한심하기 짝이 없는 네 도전 따위는 누구 하나 알아주지 않고 끝났다. 이제 네 목숨을 빼앗기 전에 좀 가지고 놀아야겠다.' WHERE entry=2000001027;
UPDATE db_script_string SET content_loc1='감사해요! 구해주시지 않았더라면 저 짐승 같은 녀석들이 제게 무슨 짓을 했을지 몰라요.' WHERE entry=2000001028;
UPDATE db_script_string SET content_loc1='그냥 숨어들어 갈 수도 있을 것 같거든요. 문앞에 있는 저 기계만 처리하면 안쪽으로 들어가는 덴 문제없어요.' WHERE entry=2000001029;
UPDATE db_script_string SET content_loc1='수컬지 트롤들이 불타고 있네...' WHERE entry=2000001031;
UPDATE db_script_string SET content_loc1='끝입니다! 지금까지 쓴 내용이에요... 어떤가요?' WHERE entry=2000001034;
UPDATE db_script_string SET content_loc1='전혀 옳지 않아, $N. 바람의 조련사 바라가 해준 이야기에 따르면 스톰후프는 악당이 아닌 영웅이었네.' WHERE entry=2000001042;
UPDATE db_script_string SET content_loc1='북풍은 예로부터 타운카의 적이었네. 그는 정령의 군주이자 부족민의 목숨을 오랫동안 앗아간 존재라네.' WHERE entry=2000001043;
UPDATE db_script_string SET content_loc1='그들은 북풍에 굴하기는커녕 죽음을 택했을 걸세. 뭔가 잘못되었어.' WHERE entry=2000001044;
UPDATE db_script_string SET content_loc1='가르침의 망치라...' WHERE entry=2000001045;
UPDATE db_script_string SET content_loc1='더욱 의심스러워지는군. 시간의 실마리가 꼬이며 잘못된 무늬를 만들어내 버렸어. 하지만 어떻게 말인가?' WHERE entry=2000001046;
UPDATE db_script_string SET content_loc1='아악! 안 돼... 몸에 거미줄이 붙어버렸어요! 정말 끈적거려요. 내 머리!' WHERE entry=2000001048;
UPDATE db_script_string SET content_loc1='이건 그냥...' WHERE entry=2000001061;
UPDATE db_script_string SET content_loc1='무쇠 드워프는 이렇게 생긴 상자에 도구를 보관합니다. 천막 근처에서 찾아보세요.' WHERE entry=2000001065;
UPDATE db_script_string SET content_loc1='거인을 지배할 수 있으리라 생각하는 룬을 새기려는 참입니다.' WHERE entry=2000001066;
UPDATE db_script_string SET content_loc1='여기 있습니다, 지배의 룬입니다... 그 이름대로 작동한다면 말이지만 말입니다. 무쇠 드워프가 만든 것만큼 사악한 의도는 없지만 쓸 만할 겁니다.' WHERE entry=2000001068;
UPDATE db_script_string SET content_loc1='만세!' WHERE entry=2000001090;
UPDATE db_script_string SET content_loc1='얼라이언스에 영광을!' WHERE entry=2000001094;
UPDATE db_script_string SET content_loc1='숨어들어 가요?! 우리가 무슨 생쥡니까?' WHERE entry=2000001100;
UPDATE db_script_string SET content_loc1='뭐, 그렇죠. 그럼 어차피 우리 동료들이 실제로 일을 처리할 테니, 진행 방식도 그들한테 맡기기로 해요.' WHERE entry=2000001102;
UPDATE db_script_string SET content_loc1='헉... 방금 대체 무슨 짓을 한 거죠, $N 님?! 브란! 브라아아아안!' WHERE entry=2000001103;
UPDATE db_script_string SET content_loc1='브란! $N|1이;가; 방금 궤도적 방어 시스템을 활성화했어요! 빨리 여기서 빠져나가지 않으면 우린 끝장날 거예요!' WHERE entry=2000001104;
UPDATE db_script_string SET content_loc1='키린 토의 마법사여, 브란의 명령에 따라 보호막을 해제하라! 목숨을 걸고 이곳과 우리의 동맹을 지켜라! 달라란을 위하여!' WHERE entry=2000001108;
UPDATE db_script_string SET content_loc1='우리 동료는 준비를 마쳤습니다. 보호막을 걷고 길을 만드세요!' WHERE entry=2000001109;
UPDATE db_script_string SET content_loc1='우리가 해냈습니다! 알갈론보다 먼저 도착했다고요. 아마 시스템을 조작하면 알갈론의 분석을 방해할 수 있을...' WHERE entry=2000001111;
UPDATE db_script_string SET content_loc1='저는 고대 기록관으로 돌아가 신호를 방해해 볼게요. 당신들이 그를 상대하는 동안 시간을 좀 벌어 보죠.' WHERE entry=2000001112;
UPDATE db_script_string SET content_loc1='온도는 크래클씨 122도입니다.' WHERE entry=2000001113;
UPDATE db_script_string SET content_loc1='온도는 크래클씨 9280도입니다. 아주 뜨거운 거죠!' WHERE entry=2000001114;
UPDATE db_script_string SET content_loc1='땡! 정확히 크래클씨 428,000도입니다! 아니, 거의 그렇군요. 대략...' WHERE entry=2000001116;
UPDATE db_script_string SET content_loc1='이봐요! 제 저장고에서 그만 알짱거리십시오! 당장 나가란 말입니다!' WHERE entry=2000001119;
UPDATE db_script_string SET content_loc1='제 저장고에서 뭘 하고 있었습니까? 요즘은 음식을 안심하고 먹을 수 없는 때입니다. 내 물건을 샅샅이 뒤지는 낯선 이는 정말이지 보고 싶지 않습니다! 저리 꺼지십시오!' WHERE entry=2000001120;
UPDATE db_script_string SET content_loc1='지금 전부 공황 상태로 가는 것 같은데. 그렇게 생각하지 않나?' WHERE entry=2000001121;
UPDATE db_script_string SET content_loc1='명령은 명령입니다. 왕자님께서 뛰어들라 하시면...' WHERE entry=2000001122;
UPDATE db_script_string SET content_loc1='그건 말도 안 되는 명령이니 거부할 수 있소. 의심 가는 음식이라고? 그 말대로라면 벨패스트부터 체포해야 할 것이오!' WHERE entry=2000001123;
UPDATE db_script_string SET content_loc1='방금 뭐라 했는지 들었습니다! 앞으로 여기서 맥주 먹을 꿈도 꾸지 마십시오. 단 한 방울도 말입니다!' WHERE entry=2000001124;
UPDATE db_script_string SET content_loc1='그만두십시오, 마이클. 지금의 무서운 상황만으로도 장사가 충분히 타격을 받고 있습니다. 한 푼이라도 아끼면 됩니다.' WHERE entry=2000001125;
UPDATE db_script_string SET content_loc1='병사들은 중요한 일을 하고 있습니다. 사람들의 안전은 더욱 중요하죠, 말. 당신에게는 고객이 하루라도 돈을 쓰는 게 중요하니 말입니다.' WHERE entry=2000001126;
UPDATE db_script_string SET content_loc1='%s|1이;가; 마지못해 고개를 끄덕입니다.' WHERE entry=2000001127;
UPDATE db_script_string SET content_loc1='그에 대해 이야기하고 싶지 않습니다.' WHERE entry=2000001128;
UPDATE db_script_string SET content_loc1='너무 걱정하지 마십시오. 제가 쉬던 날까지 여기서 더럽혀진 곡식이 발견된 적은 한 번도 없습니다.' WHERE entry=2000001129;
UPDATE db_script_string SET content_loc1='오오, 신이여, 다행이군요.' WHERE entry=2000001130;
UPDATE db_script_string SET content_loc1='%s|1이;가; 고개를 끄덕입니다.' WHERE entry=2000001131;
UPDATE db_script_string SET content_loc1='어머, 안녕하세요!' WHERE entry=2000001132;
UPDATE db_script_string SET content_loc1='좋아, 일은 할 만큼 했어. 이제 여관에서 쉬면서 수다나 떨어야겠군.' WHERE entry=2000001133;
UPDATE db_script_string SET content_loc1='잠깐, 이게 무슨 냄새지?' WHERE entry=2000001134;
UPDATE db_script_string SET content_loc1='난 아닐 거야. 사흘 전에 목욕했는걸!' WHERE entry=2000001135;
UPDATE db_script_string SET content_loc1='오, 다행이군. 여기 있는 곡식에서 나는 냄새잖아.' WHERE entry=2000001136;
UPDATE db_script_string SET content_loc1='잠깐, 곡식에서 이런 냄새가 나면 안 되지! 경비병을 찾으러 가야겠군.' WHERE entry=2000001137;
UPDATE db_script_string SET content_loc1='제가 상품을 좀 살펴봐도 상관없으시겠지요?' WHERE entry=2000001138;
UPDATE db_script_string SET content_loc1='없습니다, 하사관님.' WHERE entry=2000001139;
UPDATE db_script_string SET content_loc1='잠깐, 이게 뭐지? 당신 뭔가 숨기고 있었군!' WHERE entry=2000001140;
UPDATE db_script_string SET content_loc1='무슨 말씀이십니까, 하사관님!' WHERE entry=2000001141;
UPDATE db_script_string SET content_loc1='우린 오염된 식량이 있는지 조사하던 중이었습니다. 이 수상한 곡물을 압수하겠습니다, 페렐리 씨. 아서스 왕자님께서 이걸 확인하시는 동안 얌전히 있는 게 좋을 겁니다.' WHERE entry=2000001142;
UPDATE db_script_string SET content_loc1='믿어 주세요, 전 모르는 일입니다!' WHERE entry=2000001143;
UPDATE db_script_string SET content_loc1='그건 우리가 조사할 겁니다, 페렐리 씨. 조사해 보면 알겠지요.' WHERE entry=2000001144;
UPDATE db_script_string SET content_loc1='마사, 빵 구울 밀가루가 다 떨어졌어요. 얼마 전에 들여온 곡식 좀 남았나요?' WHERE entry=2000001145;
UPDATE db_script_string SET content_loc1='안녕하세요, 제나. 물론 빌려 드릴게요. 얼마든지 가져가세요.' WHERE entry=2000001146;
UPDATE db_script_string SET content_loc1='고마워요, 마사! 신세를 지네요.' WHERE entry=2000001147;
UPDATE db_script_string SET content_loc1='마사, 곡식이 좀 이상해요! 왕자님의 병사들이 이걸 찾고 있던데요. 가서 누굴 좀 데려와야겠어요.' WHERE entry=2000001148;
UPDATE db_script_string SET content_loc1='오, 이런.' WHERE entry=2000001149;
UPDATE db_script_string SET content_loc1='어머나...' WHERE entry=2000001150;
UPDATE db_script_string SET content_loc1='폭풍우가 몰아닥칠 것 같구나, 복슬아...' WHERE entry=2000001151;
UPDATE db_script_string SET content_loc1='%s|1이;가; 으르렁댑니다...' WHERE entry=2000001152;
UPDATE db_script_string SET content_loc1='왜 그러니?' WHERE entry=2000001153;
UPDATE db_script_string SET content_loc1='뭘 찾은 거냐?' WHERE entry=2000001154;
UPDATE db_script_string SET content_loc1='이거 큰일이구나, 복슬아. 여기서 집을 지키고 있어라. 난 병사를 찾아볼 테니.' WHERE entry=2000001155;
UPDATE db_script_string SET content_loc1='이 곡식은 정말 골칫거리야!' WHERE entry=2000001156;
UPDATE db_script_string SET content_loc1='이것 때문에 정시 퇴근이 날아가게 생겼잖아!' WHERE entry=2000001157;
UPDATE db_script_string SET content_loc1='게다가 원래 오늘은 쉬는 날이었다고!' WHERE entry=2000001158;
UPDATE db_script_string SET content_loc1='뭐, 할 수 없지. 마차에 다시 실어야지...' WHERE entry=2000001159;
UPDATE db_script_string SET content_loc1='이런 젠장! 마차는 망가지고, 말 편자는 사라지고, 이제 짐까지 상하다니!' WHERE entry=2000001160;
UPDATE db_script_string SET content_loc1='당신이 날 구해 줬구려! 다시 한 번 바람이 우리 일족에게 말을 하고 힘을 주고 있소이다. 정말 고맙소.' WHERE entry=2000001200;
UPDATE db_script_string SET content_loc1='완벽하군. 닥치는 대로 베고 쓰러트리며 다니지만 않으면 놈들은 차이를 알아채지 못할 거요.' WHERE entry=2000001214;
UPDATE db_script_string SET content_loc1='저더러 뭘 하라고 했다고요?' WHERE entry=2000001215;
