UPDATE db_script_string SET content_loc8='Билли говорит, что прежде Пшикс был большим волшебником. Но он был преобразован в кролика из-за заклинания, которое подействовало не так, как надо.' WHERE entry=2000000190; /* content: Billy says that formerly Fizzle was a large wizard. But it was transformed into rabbit because of a spell which went wrong. */
