UPDATE db_script_string SET content_loc7='La toxina ya está lista, $n.' WHERE entry=2000000275; /* content: The toxin is ready, $N. */
UPDATE db_script_string SET content_loc7='Está bien, equipo. Vamos a trabajar e instalar estos huesos.' WHERE entry=2000000276; /* content: Okay, crew. Get to work on getting these bones installed. */
UPDATE db_script_string SET content_loc7='%s esnifa unas pizcas de polvo onírico...' WHERE entry=2000000277; /* content: %s snorts a speck of dream dust up his nose... */
UPDATE db_script_string SET content_loc7='%s está aturdido...' WHERE entry=2000000278; /* content: %s is dazed... */
UPDATE db_script_string SET content_loc7='¡ACHÚ!' WHERE entry=2000000279; /* content: ACHOO! */
UPDATE db_script_string SET content_loc7='Qué refrescante... Tengo información que debo daros...' WHERE entry=2000000282; /* content: That was refreshing. Now there\'s information that needs to be told... */
UPDATE db_script_string SET content_loc7='%s se retuerce de dolor.' WHERE entry=2000000284; /* content: %s writhes in pain. */
UPDATE db_script_string SET content_loc7='¡AAAAAAAAAAAAAAAARGH!' WHERE entry=2000000285; /* content: AAAAAAAAAAAAAAAARGH! */
UPDATE db_script_string SET content_loc7='Hmm... Me pregunto...' WHERE entry=2000000286; /* content: Hmm... I wonder... */
UPDATE db_script_string SET content_loc7='Ahora, ¿dónde está?' WHERE entry=2000000287; /* content: Now, where is it? */
UPDATE db_script_string SET content_loc7='Estúpidos magos... siempre acaban con los componentes y nunca los reponen.' WHERE entry=2000000288; /* content: Stupid mages... always using the last of a reagent and never replacing it. */
UPDATE db_script_string SET content_loc7='El suero estará listo en pocos minutos, $n.' WHERE entry=2000000289; /* content: The serum will only take a few minutes to prepare, $N. */
UPDATE db_script_string SET content_loc7='Ya casi está...' WHERE entry=2000000290; /* content: Almost done... */
UPDATE db_script_string SET content_loc7='Por fin, el suero está listo, $n. Ahora acércate. Tenemos que hablar.' WHERE entry=2000000291; /* content: At last, the serum is complete, $N. Now, come closer. We must talk. */
UPDATE db_script_string SET content_loc7='¿Relajarme? ¿Sabes lo que hace Kael\'thas con los que le fallan, Geezle? Sufrimiento y dolor eternos... ¡Así que ten cuidado, estúpido!' WHERE entry=2000000292; /* content: Relax? Do you know what Kael\'thas does to those that fail him, Geezle? Eternal suffering and pain... Do NOT screw this up, fool. */
UPDATE db_script_string SET content_loc7='Nuestros exploradores de Bruma de Sangre han encontrado a nuestro contacto. El estúpido, Velen, pronto se dejará abierto e indefenso – ¡el tiempo suficiente para que ataquemos! Ahora sal de mi vista antes de que te evapore...' WHERE entry=2000000293; /* content: Our Bloodmyst scouts have located our contact. The fool, Velen, will soon leave himself open and defenseless -- long enough for us to strike! Now get out of my sight before I vaporize you... */
UPDATE db_script_string SET content_loc7='¡Al fin libre! ¡He de regresar inmediatamente al Bastión Grito de Guerra!' WHERE entry=2000000294; /* content: Freedom at last! I must return to Warsong Hold at once! */
UPDATE db_script_string SET content_loc7='Estoy en deuda contigo para toda la vida, camarada.' WHERE entry=2000000295; /* content: I am forever indebted to you, friend. */
UPDATE db_script_string SET content_loc7='Gracias, $gamigo:amiga;.' WHERE entry=2000000296; /* content: Thank you, friend. */
UPDATE db_script_string SET content_loc7='Te lo agradezco, visitante.' WHERE entry=2000000297; /* content: You have my thanks, stranger. */
UPDATE db_script_string SET content_loc7='Me siento... diferente.' WHERE entry=2000000299; /* content: I feel... different. */
UPDATE db_script_string SET content_loc7='¡Se acabo ser el lobo bueno!' WHERE entry=2000000300; /* content: No more mister nice wolvar! */
UPDATE db_script_string SET content_loc7='¡Garrafuria libre! ¡Trol morir ahora!' WHERE entry=2000000301; /* content: Rageclaw free! Troll die now! */
UPDATE db_script_string SET content_loc7='Detectado espécimen metálico de origen extraño.' WHERE entry=2000000303; /* content: Metallic specimen of foreign origin detected. */
UPDATE db_script_string SET content_loc7='Espécimen identificado como una aleación de saronita y hierro.' WHERE entry=2000000305; /* content: Specimen identified as an alloy of saronite and iron. */
UPDATE db_script_string SET content_loc7='La saronita de esta muestra es casi pura. Minerales como este no aparecen de forma natural en la superficie de Azeroth.' WHERE entry=2000000307; /* content: The saronite in this sample is nearly pure. Ores like this do not occur naturally on the surface of Azeroth. */
UPDATE db_script_string SET content_loc7='¡¿Acceso denegado?!' WHERE entry=2000000309; /* content: Access denied?! */
UPDATE db_script_string SET content_loc7='Solo se puede obtener más información desde los archivos de Ulduar y con órdenes del guardián Loken.' WHERE entry=2000000310; /* content: Further information can only be accessed from the archives within Ulduar, by order of Keeper Loken. */
UPDATE db_script_string SET content_loc7='Una cosa es segura: el tamaño, el grosor y los agujeros para los clavos de esta placa apuntan a que formaba parte de la armadura de una estructura colosal.' WHERE entry=2000000311; /* content: But one thing is for certain: this plate\'s size, thickness, and bolt holes point to a use as armor for a colossal structure. */
UPDATE db_script_string SET content_loc7='Directiva completada. Volviendo al modo de espera.' WHERE entry=2000000312; /* content: Directive completed. Returning to standby mode. */
UPDATE db_script_string SET content_loc7='Yo... ¡ahora puedo entenderte! Bueno, ahora que podemos hablar, ¡tienes que explicar algunas cosas!' WHERE entry=2000000313; /* content: I... I can understand you now! Well, now that we can talk to each other, you have some explaining to do! */
UPDATE db_script_string SET content_loc7='¿Cómo encontraste mi comunicador de la Liga de Expedicionarios?' WHERE entry=2000000314; /* content: How did you get my communicator from the Explorers\' League? */
UPDATE db_script_string SET content_loc7='Te agradezco que te hayas encargado de los enanos férreos. Pero, ¿por qué tiene interés la Horda en mí?' WHERE entry=2000000315; /* content: You have my thanks for dispatching the iron dwarves. But why would the Horde have an interest in me? */
UPDATE db_script_string SET content_loc7='Ese no sería el explorador Vor\'takh, ¿verdad? Incluso yo conozco su reputación de despiadado. ¿Entonces quiere secuestrarme?' WHERE entry=2000000316; /* content: That wouldn\'t be Scout Vor\'takh, would it? Even I know of his reputation for ruthlessness. He means to abduct me, then? */
UPDATE db_script_string SET content_loc7='Si has visto el diario, ya sabes lo que he descubierto. ¡Las creaciones de los titanes están en guerra unas contra otras!' WHERE entry=2000000317; /* content: If you\'ve seen the journal, then you know what I\'ve been discovering. The titans\' own creations are at war with each other! */
UPDATE db_script_string SET content_loc7='Loken y sus esbirros enanos férreos han expulsado a los terráneos de Ulduar y han tomado la ciudad.' WHERE entry=2000000318; /* content: Loken and his iron dwarf minions have ousted the Earthen from Ulduar and taken over the city. */
UPDATE db_script_string SET content_loc7='Si perdemos el tiempo y nuestra fuerza luchando uno contra el otro, Loken usará los recursos de Ulduar para destruir a la Horda y la Alianza.' WHERE entry=2000000319; /* content: If we spend our time and strength fighting with each other, Loken will use Ulduar\'s resources to destroy both Horde and Alliance. */
UPDATE db_script_string SET content_loc7='Si la Liga de Expedicionarios mandó hombres, no he visto rastro alguno de ellos. Encontré tu nota enterrada en un campamento arrasado por enanos férreos.' WHERE entry=2000000321; /* content: If the Explorers\' League sent men, I didn\'t see any trace of them. I found your note buried in a camp overrun by iron dwarves. */
UPDATE db_script_string SET content_loc7='Un explorador encontró los restos de tu diario en Thor Modan. Te llevamos buscando desde entonces.' WHERE entry=2000000322; /* content: A scout found the remains of your journal in Thor Modan. We\'ve been looking for you ever since. */
UPDATE db_script_string SET content_loc7='Mmm... después de entregar esto, me iré a comer un tentempié en esa quesería.' WHERE entry=2000000327; /* content: Hm...after dropping this off, I think I\'ll head to that cheese shop for a snack. */
UPDATE db_script_string SET content_loc7='¡Sí, señora, el negocio va viento en popa!' WHERE entry=2000000340; /* content: Yes ma\'am, business is brisk! */
UPDATE db_script_string SET content_loc7='¡Gracias por el queso!' WHERE entry=2000000341; /* content: Thanks for the cheese! */
UPDATE db_script_string SET content_loc7='¡Estoy de vuelta!' WHERE entry=2000000343; /* content: I\'m back! */
UPDATE db_script_string SET content_loc7='¡AYYYYYYYYY!' WHERE entry=2000000344; /* content: YEEOOWCH! */
UPDATE db_script_string SET content_loc7='No le tengo miedo a nada, ¡vamos!' WHERE entry=2000000347; /* content: I\'m not afraid of anything -- bring it on! */
UPDATE db_script_string SET content_loc7='¡Alzaos de la tierra, hermanos míos!' WHERE entry=2000000348; /* content: Arise from the earth, my brothers! */
UPDATE db_script_string SET content_loc7='¡Nuestros maestros de hierro tienen una misión para nosotros!' WHERE entry=2000000349; /* content: Our iron masters have a mission for us! */
UPDATE db_script_string SET content_loc7='¡Seguidme a las montañas para ejecutar el plan de los maestros!' WHERE entry=2000000350; /* content: Follow me into the mountains to carry out the plan of the masters! */
UPDATE db_script_string SET content_loc7='Análisis del fragmento iniciado.' WHERE entry=2000000357; /* content: Fragment analysis underway. */
UPDATE db_script_string SET content_loc7='Identificado: Val\'anyr, martillo de los reyes ancestrales.' WHERE entry=2000000358; /* content: Object identified: Val\'anyr, hammer of ancient kings. */
UPDATE db_script_string SET content_loc7='Creado por los mismos titanes, Val\'anyr fue dado al primer rey terráneo, Urel Corazondepiedra, para que creara y diera vida al resto de sus hermanos.' WHERE entry=2000000359; /* content: Created by the titans themselves, Val\'anyr was given to the first earthen king, Urel Stoneheart. With the hammer he was to create and give life to the rest of his brethren. */
UPDATE db_script_string SET content_loc7='Val\'anyr se destrozó durante la primera guerra entre los terráneos y los enanos férreos. Se creía que sus restos se habían perdido en el conflicto.' WHERE entry=2000000360; /* content: Val\'anyr was shattered during the first war between the earthen and the iron dwarves. The weapon\'s remnants were believed lost in the conflict. */
UPDATE db_script_string SET content_loc7='Hipotéticamente, el contenido ácido dentro del ser llamado Yogg-Saron causaría la licuación de la saronita. La inmersión en esta sustancia podría bastar para restaurar una aleación de origen titánico.' WHERE entry=2000000362; /* content: Powerful acidic content theoretically found inside the being known as Yogg-Saron would account for the liquification of saronite. Submersion in this substance might be sufficient to rebind an alloy of titan origin. */
UPDATE db_script_string SET content_loc7='Comenzando el análisis del vigía Freya.' WHERE entry=2000000363; /* content: Commencing Watcher Freya status analysis. */
UPDATE db_script_string SET content_loc7='El Ancestro Cortezapiedra posee una defensa sónica que altera el uso de magia.' WHERE entry=2000000366; /* content: Elder Stonebark analysis reveals a sonic defense that disrupts use of magic. */
UPDATE db_script_string SET content_loc7='Comenzando el análisis del vigía Hodir.' WHERE entry=2000000370; /* content: Commencing Watcher Hodir status analysis. */
UPDATE db_script_string SET content_loc7='El sello de Hodir parece estar en un depósito de artefactos.' WHERE entry=2000000371; /* content: Hodir\'s sigil appears to be located inside a cache of artifacts. */
UPDATE db_script_string SET content_loc7='La personalidad de Hodir es altamente inestable. Destrucción del depósito durante un combate prolongado: altamente probable.' WHERE entry=2000000372; /* content: Watcher Hodir\'s temperament and behavior highly unstable. Destruction of cache highly probable during prolonged combat. */
UPDATE db_script_string SET content_loc7='Preservar el depósito es esencial para obtener el sello de Hodir.' WHERE entry=2000000374; /* content: Preservation of cache is essential to recovering Hodir\'s sigil. */
UPDATE db_script_string SET content_loc7='Comenzando el análisis del vigía Mimiron.' WHERE entry=2000000375; /* content: Commencing Watcher Mimiron status analysis. */
UPDATE db_script_string SET content_loc7='Para obtener el sello de Mimiron, inicien la autodestrucción y venzan a Mimiron antes de que se complete.' WHERE entry=2000000378; /* content: To retrieve Mimiron\'s sigil, initiate self-destruct sequence and defeat Mimiron before its completion. */
UPDATE db_script_string SET content_loc7='¿Reoriginación planetaria? ¡Sé clara, maldita máquina!' WHERE entry=2000000379; /* content: Planetary re-origination? Speak plainly, ye blasted machine! */
UPDATE db_script_string SET content_loc7='Comenzando el análisis del vigía Thorim.' WHERE entry=2000000380; /* content: Commencing Watcher Thorim status analysis. */
UPDATE db_script_string SET content_loc7='¡A esto le llamo un hallazgo!' WHERE entry=2000000384; /* content: Now, this is what we call a nice find! */
UPDATE db_script_string SET content_loc7='Vas a ver acceso, máquina infeliz. Aquí está otra vez: ¡la llave de Norgannon!' WHERE entry=2000000385; /* content: I\'ll show ye access, ye wretched machine. Here it is again: Norgannon\'s key! */
UPDATE db_script_string SET content_loc7='Es como los otros archivos titánicos que encontramos... Aunque más viejo... Y más grande.' WHERE entry=2000000386; /* content: Looks just like all the other titan archives me lads have found... only older... and bigger. */
UPDATE db_script_string SET content_loc7='¿Y? ¿Quién está recibiendo la señal? ¿Qué va a pasar?' WHERE entry=2000000389; /* content: Well? Who\'s on the listening side of the signal? What\'s going to happen? */
UPDATE db_script_string SET content_loc7='¿Y qué va a estar buscando en su diagnóstico Algalon? ¿Qué chances tenemos?' WHERE entry=2000000391; /* content: Well, what\'s this Algalon going to be looking for with his diagnostics? What are our chances? */
UPDATE db_script_string SET content_loc7='¡Diablos! Parece que tenemos otra pelea por delante.' WHERE entry=2000000392; /* content: Blast it, lads. Looks like we\'ve got a fight ahead of us. */
UPDATE db_script_string SET content_loc7='Descomposición del planeta y sus organismos en elementos básicos: metales, rocas, gases; seguido por un período de reconstitución de los elementos según el diseño planetario original.' WHERE entry=2000000398; /* content: The decomposition of the planet and its living organisms into base elements: metals, rocks, gases. This is followed by a period of reconstitution of each element into the original planetary blueprint. */
UPDATE db_script_string SET content_loc7='Noventa y nueve coma nueve, nueve porciento.' WHERE entry=2000000401; /* content: Ninety-nine point nine nine percent. */
UPDATE db_script_string SET content_loc7='¡Nueva orden! Abre la entrada del Planetario Celestial.' WHERE entry=2000000403; /* content: New orders! Unlock the entry to the Celestial Planetarium. */
UPDATE db_script_string SET content_loc7='¿Cuatro vigías? ¿Sellos? ¿Puedes ser menos críptica, condenada máquina?' WHERE entry=2000000404; /* content: Four watchers? Sigils? Could ye be a little less cryptic, confounded machine? */
UPDATE db_script_string SET content_loc7='¿Y cómo los obtenemos? ¿Vamos y les pedimos si por favor nos los dan?' WHERE entry=2000000405; /* content: How do we get the sigils from them, then? Just walk up to them and ask them nicely? */
UPDATE db_script_string SET content_loc7='Y tú vas a decirnos cómo "descomprometer" los sellos, ¿no?' WHERE entry=2000000406; /* content: You\'re going to tell us how to... "un-compromise" the sigils, aren\'t you? */
UPDATE db_script_string SET content_loc7='Siempre soñé con andar por los salones de los titanes. Nunca creí que enfrentaría a sus creaciones.' WHERE entry=2000000407; /* content: I\'ve dreamed of roaming the halls of the titans for years. I never thought I\'d be pitted against their creations. */
UPDATE db_script_string SET content_loc7='Orden negada. El acceso requiere los sellos de los cuatro vigías.' WHERE entry=2000000408; /* content: Request denied. Access requires manual verification of the four watchers\' sigils. */
UPDATE db_script_string SET content_loc7='Corrupción encontrada. Influencia externa invadiendo a los vigías. Sellos comprometidos.' WHERE entry=2000000411; /* content: Corruption found. External influence gaining control over watchers. Sigils compromised. */
UPDATE db_script_string SET content_loc7='Las funciones de los vigías pueden reiniciarse a la fuerza, eliminando la influencia exterior.' WHERE entry=2000000412; /* content: Sufficient use of force would trigger a reset in watcher functions, removing the external influence. */
UPDATE db_script_string SET content_loc7='Para evitar que dañen los sellos en el proceso, puedo analizar el estado de cada vigía y facilitarles la información al completarse los cálculos.' WHERE entry=2000000413; /* content: As for not endangering the sigils during this process, I can analyze each watcher\'s status and make that information available to you upon completion of calculations. */
UPDATE db_script_string SET content_loc7='¡Qué pelea te has perdido, Dorian! ¡La mejor desde hace mucho!' WHERE entry=2000000414; /* content: What a fight you missed, Dorian! It was one for the ages! */
UPDATE db_script_string SET content_loc7='Allí estábamos, cabalgando hacia la batalla a toda velocidad a lomos de Estampi...' WHERE entry=2000000415; /* content: There we were, riding into battle at full speed on the back of Stampy... */
UPDATE db_script_string SET content_loc7='Todo temblaba en aquel lugar. Casi no podía estabilizar mi pistola.' WHERE entry=2000000416; /* content: Everything was shaking all over the place. I could hardly steady my gun. */
UPDATE db_script_string SET content_loc7='¡Y entonces vimos a la temible bestia!' WHERE entry=2000000417; /* content: And then we saw her, the fearsome beast! */
UPDATE db_script_string SET content_loc7='$n tuvo que usar toda su habilidad para controlar a Estampi.' WHERE entry=2000000418; /* content: It took all of $N\'s skill to maintain control of Stampy. */
UPDATE db_script_string SET content_loc7='La batalla fue una nube de sangre, plomo y sudor. ¡Los ataques de la madre de linaje se volvieron más y más rápidos!' WHERE entry=2000000419; /* content: The battle was a haze of blood, lead, and sweat. The broodmother\'s attacks came faster and faster! */
UPDATE db_script_string SET content_loc7='Pero, al final, $n y yo prevalecimos gracias a nuestro ingenio y trabajo en equipo. Y ahora tenemos este glorioso trofeo como recuerdo de nuestra victoria.' WHERE entry=2000000420; /* content: But, in the end, $N and I prevailed through our wits and teamwork. And now we have this glorious trophy as a memento of our victory. */
UPDATE db_script_string SET content_loc7='¿Detecto celos, Dorian?' WHERE entry=2000000421; /* content: Do I detect a bit of jealousy, Dorian? */
UPDATE db_script_string SET content_loc7='Bueno, no sé. Yo estaba ocupándome del fuego y hablando sobre los inconvenientes de los piñones con Zootsilbo. Eso también es bastante épico, a su manera.' WHERE entry=2000000422; /* content: Well, I don\'t know. I was here tending the fire and discussing the drawbacks of cogs with Zootfizzle. That\'s pretty epic, too, in its own way. */
UPDATE db_script_string SET content_loc7='%s inicia un rito de creación...' WHERE entry=2000000424; /* content: %s begins a rite of creation... */
UPDATE db_script_string SET content_loc7='%s crea un nuevo tótem...' WHERE entry=2000000426; /* content: %s creates a new totem... */
UPDATE db_script_string SET content_loc7='El tótem está hecho, $n.' WHERE entry=2000000427; /* content: The totem is made, $N. */
UPDATE db_script_string SET content_loc7='%s transforma los objetos que $n le dio en un tótem de limpieza...' WHERE entry=2000000428; /* content: %s transforms the items $N gave him into a totem of cleansing... */
UPDATE db_script_string SET content_loc7='%s mira en dirección al noroeste y lanza un aullido interminable.' WHERE entry=2000000430; /* content: %s stares to the northwest and lets out a long, drawn out howl. */
UPDATE db_script_string SET content_loc7='%s lanza un poderoso aullido.' WHERE entry=2000000431; /* content: %s lets forth a mighty howl. */
UPDATE db_script_string SET content_loc7='Llegas tarde, $gmensajero:mensajera;. No, no quiero excusas: esta tarea es demasiado importante.' WHERE entry=2000000432; /* content: You\'re late, courier. No, I don\'t want any excuses; this errand is far too important. */
UPDATE db_script_string SET content_loc7='Este es el escrito que trajo nuestro representante de Reposo del Dragón. Llévaselo a Myralion Resplandor inmediatamente.' WHERE entry=2000000433; /* content: Here\'s the tome our representative brought from Wyrmrest. Get this to Myralion Sunblaze immediately. */
UPDATE db_script_string SET content_loc7='Vigila tu espalda, $gmensajero:mensajera;. No tengo que recordarte el valor del libro, ni el hecho de que El Pacto de Plata no se detendrá ante nada para quitárnoslo.' WHERE entry=2000000434; /* content: Watch your back, courier. I needn\'t remind you of the value of the book, nor the fact that the Silver Covenant will stop at nothing to take it from us. */
UPDATE db_script_string SET content_loc7='Aquí está tu libro. Asegúrate de que llegue a Caladis Lanza Reluciente en Corona de Hielo antes de que los Atracasol se den cuenta de lo que está ocurriendo.' WHERE entry=2000000435; /* content: Here is the book. You must ensure it gets to Caladis Brightspear in Icecrown before the Sunreavers realize what is happening. */
UPDATE db_script_string SET content_loc7='¡Viaja rápido! Los Atracasol no desperdiciarán ni un minuto en cuanto sepan que tenemos la información que buscan.' WHERE entry=2000000436; /* content: Travel quickly! The Sunreavers will not waste any time once they realize that we have the information they seek. */
UPDATE db_script_string SET content_loc7='Ah, ya veo...' WHERE entry=2000000438; /* content: Ah, I see... */
UPDATE db_script_string SET content_loc7='Sí, es inoportuno.' WHERE entry=2000000439; /* content: Yes, this is unfortunate. */
UPDATE db_script_string SET content_loc7='Toma, está terminado.' WHERE entry=2000000444; /* content: There, it is completed. */
UPDATE db_script_string SET content_loc7='¡Los arakkoa están escondidos... en todas partes!' WHERE entry=2000000447; /* content: The arakkoa are hidden... everywhere! */
UPDATE db_script_string SET content_loc7='Me rindo ante ti.' WHERE entry=2000000449; /* content: I yield to you. */
UPDATE db_script_string SET content_loc7='¡Prepárate!' WHERE entry=2000000450; /* content: Prepare yourself! */
UPDATE db_script_string SET content_loc7='¡Que comience!' WHERE entry=2000000451; /* content: Let it begin! */
UPDATE db_script_string SET content_loc7='¡Te lo agradezco!' WHERE entry=2000000454; /* content: You have my thanks! */
UPDATE db_script_string SET content_loc7='¡Disfruta de las actuaciones de Perry Gatner en unos instantes!' WHERE entry=2000000458; /* content: Check out the comedy stylings of Perry Gatner in just a few minutes! */
UPDATE db_script_string SET content_loc7='Si estás $glisto:lista; para divertirte, vete a la taberna del Fin del Mundo.' WHERE entry=2000000459; /* content: If you are ready to laugh, head over to the World\'s End Tavern! */
UPDATE db_script_string SET content_loc7='Ahora en la Ciudad de Shattrath: ¡los Tauren Chieftains! ¡Actuarán en la Taberna del Fin del Mundo en solo unos minutos!' WHERE entry=2000000460; /* content: Now, in Shattrath City: The Tauren Chieftains! Playing in the World\'s End Tavern at the top of the hour! */
UPDATE db_script_string SET content_loc7='Corazón Frenético matarte si tú volver. ¡Tú ya no $gbienvenido:bienvenida; aquí!' WHERE entry=2000000462; /* content: Frenzyheart kill you if you come back. You no welcome here no more! */
UPDATE db_script_string SET content_loc7='¡Gracias, $r! ¡Estoy seguro de que mi padre te recompensará con generosidad! ¡Adiós!' WHERE entry=2000000464; /* content: Thanks, $R! I\'m sure my dad will reward you greatly! Bye! */
UPDATE db_script_string SET content_loc7='¡Esta es la última vez que me pillan! ¡Lo prometo! ¡Adiós!' WHERE entry=2000000465; /* content: This is the last time I get caught! I promise! Bye! */
UPDATE db_script_string SET content_loc7='¡%s huele la carne, despierta de su letargo y ruge furioso!' WHERE entry=2000000466; /* content: %s smells the meat, awakens from his slumber, and roars in anger! */
UPDATE db_script_string SET content_loc7='¿Qué esperan si hacen que los murciélagos entren desde ese ángulo? Todo lo que conseguirán son cuellos rotos y papilla de murciélago.' WHERE entry=2000000467; /* content: What do they expect, making the bats come in at that angle? Broken necks and gamey bat stew, that\'s what they get. */
UPDATE db_script_string SET content_loc7='¡¡GRRRRRRR!!' WHERE entry=2000000470; /* content: ROARRRRR!! */
UPDATE db_script_string SET content_loc7='¡¡VILLA OSCURA!!... ¡¡TENGO HAMBRE!!' WHERE entry=2000000471; /* content: DARKSHIRE...I HUNGER!! */
UPDATE db_script_string SET content_loc7='Ten cuidado, $c. ¡Estos trols mataron a su propio dios serpiente!' WHERE entry=2000000473; /* content: Careful here, $c. These trolls killed their own snake god! */
UPDATE db_script_string SET content_loc7='Nos vemos.' WHERE entry=2000000474; /* content: See you around. */
UPDATE db_script_string SET content_loc7='De acuerdo. Entonces será mejor que me presente ante el Sargento.' WHERE entry=2000000475; /* content: Right. I\'d better get back to the sergeant then. */
UPDATE db_script_string SET content_loc7='Gracias, $c.' WHERE entry=2000000476; /* content: Thanks, $c. */
UPDATE db_script_string SET content_loc7='Gracias, $r. Ahora te quitaré de encima a este miserable bellaco.' WHERE entry=2000000477; /* content: Thank you, $r. I will take this miserable cur from you now. */
UPDATE db_script_string SET content_loc7='¡Raliq darte lección ahora!' WHERE entry=2000000478; /* content: Raliq teach you lesson now! */
UPDATE db_script_string SET content_loc7='¡F\'woooroonn! ¡Coosh\'coosh pagar nada! ¡Morir!' WHERE entry=2000000479; /* content: F\'woooroonn! Coosh\'coosh pay nothing! Die! */
UPDATE db_script_string SET content_loc7='Elijo la tercera opción: ¡MATARTE!' WHERE entry=2000000480; /* content: I choose the third option: KILLING YOU! */
UPDATE db_script_string SET content_loc7='Como desees...' WHERE entry=2000000481; /* content: As you wish... */
UPDATE db_script_string SET content_loc7='El sacramento fue robado sobre suelo destrozado. El escudo de los Aesirites se ha perdido. El malestar será eterno.' WHERE entry=2000000486; /* content: From sundered ground the sacrament was stolen. The Shield of Aesirites is lost. Unrest will be eternal. */
UPDATE db_script_string SET content_loc7='¡Gracias por liberarme, $n! ¡Me voy a Shattrath!' WHERE entry=2000000491; /* content: Thank you for freeing me, $N! I\'m going to make my way to Shattrath! */
UPDATE db_script_string SET content_loc7='Me alegro de verte al fin, $gmensajero:mensajera;. Krasus nos ha confiado una de las pocas copias que quedan de Hojas antiguas forjadas por los dragones.' WHERE entry=2000000492; /* content: It\'s good to finally see you, courier. Krasus has entrusted us with one of the few remaining copies of Ancient Dragonforged Blades. */
UPDATE db_script_string SET content_loc7='[Fúrbolg] ¡Gracias, $n!' WHERE entry=2000000493; /* content: [Furbolg] Thank you, $n! */
UPDATE db_script_string SET content_loc7='[Fúrbolg] ¡La profecía es cierta!' WHERE entry=2000000494; /* content: [Furbolg] The prophecy is true! */
UPDATE db_script_string SET content_loc7='[Fúrbolg] ¡Aquellos que permanezcan en el Bastión Semprepino te darán la bienvenida como a un héroe!' WHERE entry=2000000495; /* content: [Furbolg] Those remaining at Stillpine Hold will welcome you as a hero! */
UPDATE db_script_string SET content_loc7='[Fúrbolg] ¡Los fúrbolgs de Semprepino tardarán en olvidar tu valentía!' WHERE entry=2000000496; /* content: [Furbolg] The Stillpine furbolgs will not soon forget your bravery! */
UPDATE db_script_string SET content_loc7='Sentía que parte de él seguía vivo. Atrapado... luchando... ¡Oh, Arthas!' WHERE entry=2000000500; /* content: I sensed a part of him still alive! Trapped... struggling... Oh, Arthas! */
UPDATE db_script_string SET content_loc7='Quizá, quizá un día podrá recordar quién fue una vez.' WHERE entry=2000000501; /* content: Perhaps - perhaps he might someday remember what he once was. */
UPDATE db_script_string SET content_loc7='Por la Luz, que encuentre reposo, libre de la garra gélida de esa terrible espada.' WHERE entry=2000000502; /* content: By the Light, may he at last find rest, free from the icy grip of that terrible blade. */
UPDATE db_script_string SET content_loc7='Está listo, entonces.' WHERE entry=2000000503; /* content: So, it is done. */
UPDATE db_script_string SET content_loc7='No me atrevería a confiar en mis sentidos; no sería la primera vez que el Rey Exánime me hace quedar como una tonta.' WHERE entry=2000000504; /* content: I had not dared to trust my senses - too many times has the Lich King made me to be a fool. */
UPDATE db_script_string SET content_loc7='Al fin se le ha hecho pagar por las atrocidades que desató sobre mi gente.' WHERE entry=2000000505; /* content: Finally, he has been made to pay for the atrocities he imposed upon my people. */
UPDATE db_script_string SET content_loc7='Que Azeroth nunca olvide el precio terrible que hemos pagado por nuestra debilidad... por nuestro orgullo.' WHERE entry=2000000506; /* content: May Azeroth never fail to remember the terrible price we paid for our weakness... for our pride. */
UPDATE db_script_string SET content_loc7='Pero ¿y ahora qué, héroe? ¿Qué pasará con esos que liberaste de su control, pero siguen condenados a vivir en la muerte?' WHERE entry=2000000507; /* content: But what now, hero? What of those freed from his grasp, but still shackled to their mortal coils? */
UPDATE db_script_string SET content_loc7='Tengo mucho que reflexionar.' WHERE entry=2000000509; /* content: I have much to ponder. */
UPDATE db_script_string SET content_loc7='Arthas...' WHERE entry=2000000510; /* content: Arthas... */
UPDATE db_script_string SET content_loc7='Héroe de Azeroth, me has dado un obsequio más grande de lo que crees.' WHERE entry=2000000511; /* content: Alas, hero of Azeroth, you give me a greater gift than you know. */
UPDATE db_script_string SET content_loc7='Mucho tiempo he luchado por perdonar al príncipe por sus terribles transgresiones.' WHERE entry=2000000512; /* content: Long have I struggled to forgive the prince for his terrible transgressions. */
UPDATE db_script_string SET content_loc7='Mi alma ha sido devastada con una ansiedad insoportable, con pensamientos oscuros... que me alejaron de la Luz.' WHERE entry=2000000513; /* content: My soul has been wracked with unbearable anxiety, dark thoughts... distancing me from the Light. */
UPDATE db_script_string SET content_loc7='Recuerdo claramente el brillo de orgullo en sus ojos cuando se paraba frente a mí, ansioso por vencer a los enemigos de la Luz…' WHERE entry=2000000514; /* content: I recall clearly the gleam of pride in his eye as he stood before me, eager to defeat the enemies of the Light... */
UPDATE db_script_string SET content_loc7='Ansioso por defender a su gente, sin importar el costo...' WHERE entry=2000000515; /* content: Eager to defend his people, no matter the cost. */
UPDATE db_script_string SET content_loc7='Es esta memoria de Arthas la que elijo guardar en mi corazón.' WHERE entry=2000000516; /* content: It is this memory of Arthas that I choose to keep in my heart. */
UPDATE db_script_string SET content_loc7='Estaré en deuda contigo por siempre, amigo.' WHERE entry=2000000517; /* content: I shall always be in your debt, friend. */
UPDATE db_script_string SET content_loc7='Te lo agradezco.' WHERE entry=2000000518; /* content: Thank you. */
UPDATE db_script_string SET content_loc7='Oh, muchacho...' WHERE entry=2000000519; /* content: Oh, lad... */
UPDATE db_script_string SET content_loc7='Cómo extraño esos días eternos en Lordaeron...' WHERE entry=2000000520; /* content: How I miss those endless days in Lordaeron... */
UPDATE db_script_string SET content_loc7='…afinando tu habilidad con esa espada roma…' WHERE entry=2000000521; /* content: ...sharpenin\' yer skill with this dull blade... */
UPDATE db_script_string SET content_loc7='...moldeándote en un arma que debía soportar las exigencias de un gran destino.' WHERE entry=2000000522; /* content: ...forgin\' ya into a weapon meant to withstand the demands of a great destiny. */
UPDATE db_script_string SET content_loc7='Sí... Sí, utilizaste bien esas habilidades, ¿verdad, muchacho?' WHERE entry=2000000523; /* content: Heh... Yeh sure put them skills ta use, didn\'t yeh, lad? */
UPDATE db_script_string SET content_loc7='Si hubiera podido pararte aquel día, qué diferentes habrían sido las cosas.' WHERE entry=2000000524; /* content: If only I\'da been able ta stop yeh that day, how different things mighta been. */
UPDATE db_script_string SET content_loc7='Si nunca hubiera descubierto esa maldita espada...' WHERE entry=2000000525; /* content: If only I\'da never discovered that accursed blade... */
UPDATE db_script_string SET content_loc7='Adiós, Arthas... hermano mío.' WHERE entry=2000000526; /* content: Farewell, Arthas... my brother. */
UPDATE db_script_string SET content_loc7='Darion, hijo mío.' WHERE entry=2000000527; /* content: Darion, my son. */
UPDATE db_script_string SET content_loc7='Por fin puedo verte otra vez.' WHERE entry=2000000528; /* content: At last, I am able to lay my eyes upon you again. */
UPDATE db_script_string SET content_loc7='El Rey Exánime me atormentó sin descanso, Darion.' WHERE entry=2000000529; /* content: The Lich King tormented me without end, Darion. */
UPDATE db_script_string SET content_loc7='Sin fin, buscó destruir mi voluntad para obligarme a servirle y vincularme a su espada…' WHERE entry=2000000530; /* content: Endlessly, he sought to break my will, to force me to serve him, to bind me to his blade... */
UPDATE db_script_string SET content_loc7='Al final, cuando la situación reclamó toda su atención, me dejó.' WHERE entry=2000000531; /* content: Finally, when events demanded his full attention, he left me. */
UPDATE db_script_string SET content_loc7='El pensamiento al que me aferraba, Darion... El recuerdo que evitó que cediera...' WHERE entry=2000000532; /* content: The one memory, I clung to, Darion... The one thought that kept me from giving in... */
UPDATE db_script_string SET content_loc7='Fue tu sacrificio, hijo mío, que de nuevo me salvó del peligro eterno.' WHERE entry=2000000533; /* content: It was your sacrifice, my son, that again saved me from eternal peril. */
UPDATE db_script_string SET content_loc7='Padre...' WHERE entry=2000000534; /* content: Father... */
UPDATE db_script_string SET content_loc7='Padre. Temía por tu... tu cordura.' WHERE entry=2000000535; /* content: Father. I feared for your... your sanity. */
UPDATE db_script_string SET content_loc7='Padre... por ti, daría mi vida mil veces.' WHERE entry=2000000536; /* content: Father... For you, I would give my life a thousand times. */
UPDATE db_script_string SET content_loc7='¡%s olfatea el elixir y luego lo apura con avidez!' WHERE entry=2000000537; /* content: %s sniffs the elixir then eagerly digs in! */
UPDATE db_script_string SET content_loc7='Y gracias, $n. Sin tu ayuda, jamás habría encontrado el camino hasta los Renegados.' WHERE entry=2000000538; /* content: And thank you, $N.  Without your aid I may never have found my way to the Forsaken. */
UPDATE db_script_string SET content_loc7='Adiós, $gamigo mío:amiga mía;.' WHERE entry=2000000539; /* content: Farewell, my friend. */
UPDATE db_script_string SET content_loc7='Cuanto tiempo, mi querido amigo Bethor.' WHERE entry=2000000540; /* content: It has been a long time, Bethor, my friend. */
UPDATE db_script_string SET content_loc7='Si el tiempo lo permite tenemos que hablar con calma. Hay mucho que discutir.' WHERE entry=2000000541; /* content: When time permits, we must speak at length.  For we have much to discuss. */
UPDATE db_script_string SET content_loc7='%s llama a un mensajero...' WHERE entry=2000000542; /* content: %s summons a messenger... */
UPDATE db_script_string SET content_loc7='%s lleva el mensaje a la boticaria Zamah.' WHERE entry=2000000544; /* content: %s gives message to Apothecary Zamah. */
UPDATE db_script_string SET content_loc7='¡Pero este es el único edificio que sirve a Brogg! ¡Los goblins hacen los edificios muy pequeños!' WHERE entry=2000000546; /* content: But this the only building that fit Brogg! Goblins make buildings too small! */
UPDATE db_script_string SET content_loc7='Creía que ya habíamos hablado de esto, Brogg. No puedes quedarte en este edificio. Claro que puede cobijarte, pero no queda sitio para nadie más.' WHERE entry=2000000547; /* content: I thought we talked about this, Brogg. You can\'t stay in this building. Sure, it can house you, but there\'s no room for anyone else! */
UPDATE db_script_string SET content_loc7='Sí, dijimos que podías quedarte aquí, ¡pero este edificio es nuestro cuartel general! ¡Todos nuestros manuales y anteproyectos están guardados aquí!' WHERE entry=2000000548; /* content: Yes, we said you can stay here, but this building is our headquarters! All of our manuals and blueprints are stored here! */
UPDATE db_script_string SET content_loc7='¡Me atrevería a decir que hicieron a los ogros demasiado grandes! ¡Veo que no voy a convencerte de que te mudes, pero no creas que esto es permanente!' WHERE entry=2000000549; /* content: I\'m inclined to say they made ogres too big! I can see I\'m not going to convince you to move now, but don\'t think this is permanent! */
UPDATE db_script_string SET content_loc7='¡Dijiste que Brogg podía quedarse! ¡Prometiste a Brogg que ayudarías a recuperar el montículo del clan Quebrantarrocas!' WHERE entry=2000000550; /* content: You said Brogg could stay here! You promised Brogg you\'d help regain Stonemaul clan mound! */
UPDATE db_script_string SET content_loc7='En medio del Cataclismo, Ameth\'Aran quedó a merced de la voluntad del terror que subyugaba la tierra. Había pocas esperanzas de que sobreviviera.' WHERE entry=2000000551; /* content: In the throes of the Sundering, Ameth\'Aran was at the whim of the terror that gripped the land. There was little hope for survival. */
UPDATE db_script_string SET content_loc7='Athrikus vino a vernos. Nos dijo que podía protegernos del peligro. Nos lanzó un hechizo para protegernos del peligro.' WHERE entry=2000000552; /* content: Athrikus came to us. He told us that he could save us from harm. He cast a spell upon us to protect us from harm. */
UPDATE db_script_string SET content_loc7='Cuando acabaron las sacudidas, se revelaron sus verdaderos motivos. Quedamos atrapados y él poco a poco fue absorbiendo nuestros poderes.' WHERE entry=2000000553; /* content: When the shaking stopped, his true motives were revealed. We were trapped, and he was slowly draining our powers. */
UPDATE db_script_string SET content_loc7='Cientos fueron encarcelados por obra de este hechizo. Ahora solo unos cuantos permanecen en sus prisiones. Él hablaba a veces con nosotros.' WHERE entry=2000000554; /* content: There were hundreds imprisoned by his spell. Now only a few remain in their prisons. He would speak to us sometimes. */
UPDATE db_script_string SET content_loc7='Le preocupaba que su poder se debilitara, que el brillo de sus valiosas gemas de almas desapareciera para siempre.' WHERE entry=2000000555; /* content: He worried that his power weakened, that soon even we last of his precious soulgems would fade and die. */
UPDATE db_script_string SET content_loc7='Mmmm... ¿Dónde está mi caña de pescar? Oh, ahí está.' WHERE entry=2000000557; /* content: Hmmm... Where is my fishing hook? Oh, there it is. */
UPDATE db_script_string SET content_loc7='Veamos si los peces pican...' WHERE entry=2000000558; /* content: Let\'s see if the fish are biting. */
UPDATE db_script_string SET content_loc7='Mmmm... Creo que necesito preparar cebos. Iré a mirar en el vagón de mi padre.' WHERE entry=2000000559; /* content: Hmmm... I think I need better bait. I\'ll check my father\'s wagon. */
UPDATE db_script_string SET content_loc7='¡Desechos de jabaespín! Eso debería de servir. A los peces les encantan los jabaespines.' WHERE entry=2000000560; /* content: Quillboar scraps! These should do the trick. Fish love Quillboar. */
UPDATE db_script_string SET content_loc7='¡He cogido uno! Voy a ver si alguno de los guardias tiene hambre. ¡Vamos, Colmillo!' WHERE entry=2000000561; /* content: Caught one! I\'ll see if any of the guards are hungry. Come on Fang! */
UPDATE db_script_string SET content_loc7='He pescado un pez... Si tienes hambre' WHERE entry=2000000562; /* content: I caught a fish if you are hungry... */
UPDATE db_script_string SET content_loc7='¡Muy bien hecho! ¡Traes honor a la Horda, chico, te lo agradecemos!' WHERE entry=2000000563; /* content: A nice catch it is at that! You bring honor to the Horde boy, We thank you! */
UPDATE db_script_string SET content_loc7='¿Quiere algo más de comer, señor?' WHERE entry=2000000564; /* content: Would you like something more to eat sir? */
UPDATE db_script_string SET content_loc7='¡Más pan, chico!' WHERE entry=2000000565; /* content: More bread boy! */
UPDATE db_script_string SET content_loc7='Ahora mismo, señor.' WHERE entry=2000000566; /* content: Right away sir. */
UPDATE db_script_string SET content_loc7='Aquí está su pan, señor.' WHERE entry=2000000567; /* content: Here is your bread sir. */
UPDATE db_script_string SET content_loc7='Aaaah... Muy bien. ¡Ahora, fuera!' WHERE entry=2000000568; /* content: Aaahh... Very good. Now scat! */
UPDATE db_script_string SET content_loc7='Como desees, señor.' WHERE entry=2000000569; /* content: As you wish sir. */
UPDATE db_script_string SET content_loc7='¿Puedo ir a buscar más piedras para mi collar, papá?' WHERE entry=2000000570; /* content: Can I go collect more rocks for my necklace, Papa? */
UPDATE db_script_string SET content_loc7='¡Dabu! ¡Tendré cuidado, papá!' WHERE entry=2000000572; /* content: Dabu! I will be careful Papa! */
UPDATE db_script_string SET content_loc7='Ooohh... Esta piedra es bonita y me gusta mucho el color. A ver si puedo encontrar más iguales...' WHERE entry=2000000573; /* content: Ooohh... This rock is nice and colorful. Let\'s see if I can find more like it... */
UPDATE db_script_string SET content_loc7='Mmm... Esa piedra es bonita, pero demasiado pequeña.' WHERE entry=2000000574; /* content: Hmm... That rock is nice, but too small. */
UPDATE db_script_string SET content_loc7='Seguro que a mi amigo Gruk le gustaría esta piedra para la lanza que está haciendo. Es muy afilada... Espero que le guste.' WHERE entry=2000000576; /* content: I bet my friend Gruk would like this rock for the spear he is making! It\'s very sharp... I hope he likes it. */
UPDATE db_script_string SET content_loc7='Ya está... Con esto debería tener suficientes piedras para hacer un collar. De todas formas, ya debería volver a casa...' WHERE entry=2000000577; /* content: There... That should be enough rocks to make a necklace. I should probably get home now anyway. */
UPDATE db_script_string SET content_loc7='La Cruzada Argenta te invita a que pongas a prueba tus habilidades en honorable combate contra otros héroes de Azeroth en el Torneo Argenta.' WHERE entry=2000000580; /* content: The Argent Crusade invites you to test your skills in honorable combat against other heroes of Azeroth at the Argent Tournament. */
UPDATE db_script_string SET content_loc7='Los campeones de las grandes naciones del mundo se han reunido en los Campos del Torneo Argenta al noreste de Corona de Hielo para enfrentarse a todos los contendientes.' WHERE entry=2000000581; /* content: The champions of the world\'s great nations have gathered at the Argent Tournament Grounds in northeastern Icecrown to face all challengers. */
UPDATE db_script_string SET content_loc7='Llegas tarde, sobrestante.' WHERE entry=2000000582; /* content: You\'re late, overseer. */
UPDATE db_script_string SET content_loc7='El destino no esperará. Tus artesanos han de incrementar la producción de gólems de piedra antes de que el anfitrión gigante de piedra llegue a Ulduar.' WHERE entry=2000000583; /* content: Destiny will not wait. Your craftsmen must increase their production of the war golems before the stone giant leader and his army reach Ulduar. */
UPDATE db_script_string SET content_loc7='Di a tus forjadores de runas que continúen transformando a tantos gigantes de piedra como puedan. Los que no se dobleguen deberán ser destruidos.' WHERE entry=2000000584; /* content: Tell your rune-smiths to continue converting what stone giants you can. Those that will not submit must be destroyed. */
UPDATE db_script_string SET content_loc7='Si los gigantes de piedra interfieren con nuestro trabajo en Ulduar, os haré responsables a ti y a tu señor feudal. No se debe permitir a los mortales que ayuden a los gigantes.' WHERE entry=2000000585; /* content: If the stone giants interfere with our work at Ulduar, I will hold you and your thane responsible. Mortals must not be allowed to come to the aid of the giants. */
UPDATE db_script_string SET content_loc7='Vuelve a tu trabajo, sobrestante. Asegúrate de transmitir a tus trabajadores la urgencia de sus tareas.' WHERE entry=2000000586; /* content: Return to your duties, overseer. Be certain to impress upon your workers the urgency of their tasks. */
UPDATE db_script_string SET content_loc7='Así que el Oráculo ha decidido ayudarte, $gforastero:forastera;. Muy bien, te ofreceré mi conocimiento.' WHERE entry=2000000587; /* content: So the oracle has decided to aid you, outlander. Very well, I will offer you what i know. */
UPDATE db_script_string SET content_loc7='Tu compañero busca al patriarca de los trozocuerno, Farunn, quien se mantiene apartado de sus hermanos.' WHERE entry=2000000588; /* content: Your companion seeks the shardhorn patriarch Farunn, who holds himself aloof from his brothers. */
UPDATE db_script_string SET content_loc7='Vuelve con tu compañero. Dile que continúe y reclame su premio con Farunn.' WHERE entry=2000000590; /* content: Return to your companion. Tell him to go forth and claim his prize in Farunn. */
UPDATE db_script_string SET content_loc7='Cuando haya hecho eso, dile que deje esta tierra en paz. No pinta nada aquí.' WHERE entry=2000000591; /* content: Once he has done so, tell him to leave this land in peace. He does not belong here. */
UPDATE db_script_string SET content_loc7='[Fúrbolg] Doy fe de los actos salvajes de nuestros adversarios, $n. Roban a nuestros jóvenes... nuestros débiles... Encarcelados aquí, están a merced de los ardides de Brazolanudo. ¡Libéralos, $gPrometido:Prometida;!' WHERE entry=2000000592; /* content: [Furbolg] Bear witness to the savage acts of our adversaries, $N. They steal our young... our weak... Imprisoned here, they are left to the devices of the Bristlelimb. Set them free, Promised One! */
UPDATE db_script_string SET content_loc7='Nunca he entendido qué es, pero hay algo muy extraño en las fuentes termales...' WHERE entry=2000000593; /* content: I haven\'t really figured out what it is, but there\'s something strange about the hot springs... */
UPDATE db_script_string SET content_loc7='%s sumerge la espada en las aguas poco profundas.' WHERE entry=2000000594; /* content: %s tosses the sword into the shallow pool. */
UPDATE db_script_string SET content_loc7='He descubierto que si arrojo algo al agua mejora de forma increíble cuando lo vuelvo a sacar.' WHERE entry=2000000595; /* content: I\'ve found that when I throw something into this water, it has improved incredibly when I retrieve it. */
UPDATE db_script_string SET content_loc7='Bien, sea lo que sea, ¡funciona bastante bien!' WHERE entry=2000000596; /* content: Well, whatever it is, it works quite well! */
UPDATE db_script_string SET content_loc7='Y no descansaré hasta llegar al final de este asunto... Así que si necesitas mi ayuda de nuevo, ya sabes dónde encontrarme.' WHERE entry=2000000597; /* content: And I am determined to find out more... So if you need my help again, you know where I\'ll be. */
UPDATE db_script_string SET content_loc7='Por favor, sígueme.' WHERE entry=2000000598; /* content: Please, follow me. */
UPDATE db_script_string SET content_loc7='¿Eso son jacintos de invierno? ¿Para mí?' WHERE entry=2000000599; /* content: Are those winter hyacinths? For me? */
UPDATE db_script_string SET content_loc7='Ha pasado mucho tiempo desde que un viajero trajo flores aquí.' WHERE entry=2000000600; /* content: It\'s been so long since a traveler has come here bearing flowers. */
UPDATE db_script_string SET content_loc7='El lago ha estado muy solitario estos últimos años. Los viajeros dejaron de venir y el mal se apoderó de estas aguas.' WHERE entry=2000000601; /* content: The lake has been too lonely these past years. The travelers stopped coming and evil came to these waters. */
UPDATE db_script_string SET content_loc7='Tu don es un extraño favor, caminante. Por favor, toma esta hoja con mi gratitud. Hace mucho otro viajero la dejó aquí, pero a mí no me sirve de mucho.' WHERE entry=2000000602; /* content: Your gift is a rare kindness, traveler. Please take this blade with my gratitude. Long ago, another traveler left it here, but I have little use for it. */
UPDATE db_script_string SET content_loc7='No me ha traído más que desgracias. Quizá encuentres a alguien que pueda contener su poder.' WHERE entry=2000000604; /* content: It has brought me naught but ill. Mayhap you can find someone who will contain its power. */
UPDATE db_script_string SET content_loc7='¡Cría traidora! ¡Sironas te destruirá a ti y a tu pueblo!' WHERE entry=2000000606; /* content: Treacherous whelp! Sironas will destroy you and your people! */
UPDATE db_script_string SET content_loc7='Mi hermano, Pezuña Tempestuosa, era mucho mejor guerrero que yo. Mientras yo estudiaba para ser cabecilla de nuestro clan, él se preparaba para una misión mucho mayor.' WHERE entry=2000000607; /* content: My brother, Stormhoof, was a far greater warrior than I. While I trained to be the chieftain of our clan, he prepared for a larger quest. */
UPDATE db_script_string SET content_loc7='¿Cómo puede estar él en peligro? Lo único que quería era darnos poder sobre los elementos que hacen que la vida sea dura para nosotros aquí.' WHERE entry=2000000608; /* content: How could he be in danger? All he wanted was to give us power over the elements that made life harsh for us here. */
UPDATE db_script_string SET content_loc7='Buscaba un artefacto... un cuerno, creo. Dejó el pueblo para realizar un largo viaje en su busca.' WHERE entry=2000000609; /* content: He sought an artifact... a horn, I think. He left the village on a long journey in search of it. */
UPDATE db_script_string SET content_loc7='Sé que un temible enemigo le persiguió después de que consiguiera el cuerno, pero... no recuerdo qué pasó... después.' WHERE entry=2000000610; /* content: I know that a terrible enemy pursued him after he won the horn, but I... I don\'t recall... what happened... next. */
UPDATE db_script_string SET content_loc7='Es como si mis recuerdos estuvieran cubiertos de niebla. Ni siquiera puedo recordar qué le pasó a mi hermano. ¿Por esto dices que está en peligro?' WHERE entry=2000000611; /* content: It is as though my memories are shrouded in mist. I cannot even recall what became of my brother. Is this how you mean that he is in danger? */
UPDATE db_script_string SET content_loc7='Las alteraciones en la tumba... ellos tienen que estar involucrados. Mira alrededor, $n. ¿Los ves?' WHERE entry=2000000612; /* content: The disturbances in the tomb, they must be involved. Look around you, $N. Do you see them? */
UPDATE db_script_string SET content_loc7='Sí, es culpa suya, pero no tengo poder para detenerlos. ¿Avisarás a la gente de Tunka\'lo de que su pasado y sus ancestros están en peligro, $gforastero:forastera;?' WHERE entry=2000000613; /* content: Yes, they are at fault, but I am powerless to stop them. Will you warn the people of Tunka\'lo, stranger, that their past and their ancestors are in danger? */
UPDATE db_script_string SET content_loc7='El martillo de conocimiento está callado. Esto no es de la época de Pezuña Tempestuosa.' WHERE entry=2000000615; /* content: The Lorehammer is silent. This isn\'t from Stormhoof\'s time. */
UPDATE db_script_string SET content_loc7='¡Que gane la mejor!' WHERE entry=2000000617; /* content: May the best win! */
UPDATE db_script_string SET content_loc7='¡No me derrotarás!' WHERE entry=2000000618; /* content: You will not defeat me! */
UPDATE db_script_string SET content_loc7='¡No eres $gdigno:digna; de Thorim!' WHERE entry=2000000619; /* content: You\'re not worthy of Thorim! */
UPDATE db_script_string SET content_loc7='El cultor está que echa chispas.' WHERE entry=2000000621; /* content: The cultist practically sputters. */
UPDATE db_script_string SET content_loc7='Mi señora.' WHERE entry=2000000622; /* content: My lady. */
UPDATE db_script_string SET content_loc7='Informa.' WHERE entry=2000000623; /* content: Report. */
UPDATE db_script_string SET content_loc7='Hay noticias de Jotunheim. Los vigías del sueño de esa zona creen que han encontrado algo importante.' WHERE entry=2000000624; /* content: There is word from Jotunheim. The sleep-watchers there believe that they have found someone of significance. */
UPDATE db_script_string SET content_loc7='Describe a ese vrykul. ¿Qué aspecto tenía?' WHERE entry=2000000625; /* content: Describe this vrykul. What does he look like? */
UPDATE db_script_string SET content_loc7='¿Cómo, mi señora? Un vrykul, supongo. A mí no me lo mostraron. Desde La Cámara de las Sombras...' WHERE entry=2000000626; /* content: Look like, my lady? A vrykul, I suppose. They did not actually show him to me. Ever since The Shadow Vault.... */
UPDATE db_script_string SET content_loc7='No me interesan las excusas. ¿Te dieron un nombre?' WHERE entry=2000000627; /* content: I am not interested in excuses. Perhaps they gave you a name? */
UPDATE db_script_string SET content_loc7='¿Un nombre? ¡Ah, claro, su nombre! Creo que se llamaba Iskalder.' WHERE entry=2000000628; /* content: A name? Oh, yes, the name! I believe it was Iskalder. */
UPDATE db_script_string SET content_loc7='Vuelve a Jotunheim y diles que lo mantengan dormido hasta que yo llegue. Juzgaré a ese vrykul con mis propios ojos.' WHERE entry=2000000630; /* content: Return to Jotunheim and tell them to keep him asleep until I arrive. I will judge this vrykul with my own eyes. */
UPDATE db_script_string SET content_loc7='Enseguida, mi señora.' WHERE entry=2000000631; /* content: Right away, my lady. */
UPDATE db_script_string SET content_loc7='Morae... ¿Eres tú?' WHERE entry=2000000632; /* content: Morae... Is that you? */
UPDATE db_script_string SET content_loc7='No... No eres Morae, pero siento que os conocéis. Cuando la veas, dile que sobreviví al accidente para caer en manos de los infiltradores.' WHERE entry=2000000633; /* content: No... you\'re not Morae, but I sense that you have met. When you see her, tell her that I survived the crash, only to be done in by the infiltrators. */
UPDATE db_script_string SET content_loc7='Hazle saber... que mi último pensamiento fue para ella...' WHERE entry=2000000634; /* content: Let her know... my last thought was of her... */
UPDATE db_script_string SET content_loc7='¡Grek probar!' WHERE entry=2000000635; /* content: Grek try! */
UPDATE db_script_string SET content_loc7='¡Este aceite no vale para Grek! ¿Qué crees que es Grek? ¿Un debilucho con túnica?' WHERE entry=2000000636; /* content: This oil no good for Grek! What Grek look like to you, some weakling in robes? */
UPDATE db_script_string SET content_loc7='%s ríe.' WHERE entry=2000000637; /* content: %s laughs. */
UPDATE db_script_string SET content_loc7='¿Te asusta saber que hay quienes son capaces de servir a la Legión con tal fervor y devoción que esperan la llamada actuando como ciudadanos ejemplares y sin descubrirse?' WHERE entry=2000000638; /* content: Does it frighten you to know that there are those that would serve the Legion with such devotion as to remain unwavering citizens of your pointless civilizations until called upon? */
UPDATE db_script_string SET content_loc7='Vivir con miedo, morir por voluntad de Kael\'thas... Es lo mismo.' WHERE entry=2000000639; /* content: Live in fear, die by the will of Kael\'thas... It\'s all the same. */
UPDATE db_script_string SET content_loc7='¡Y ahora, te rajo!' WHERE entry=2000000640; /* content: And now, I cut you! */
UPDATE db_script_string SET content_loc7='¡Mueran!' WHERE entry=2000000641; /* content: DIE! */
UPDATE db_script_string SET content_loc7='%s coge la bandera naga.' WHERE entry=2000000642; /* content: %s picks up the naga flag. */
UPDATE db_script_string SET content_loc7='¡No fracasaremos!' WHERE entry=2000000643; /* content: We will not fail! */
UPDATE db_script_string SET content_loc7='%s coloca el cajón con las piezas el suelo.' WHERE entry=2000000644; /* content: %s places the crate of parts on the ground. */
UPDATE db_script_string SET content_loc7='%s coge una pieza y la guarda en el bolsillo...' WHERE entry=2000000645; /* content: %s grabs a part and puts it in his pocket... */
UPDATE db_script_string SET content_loc7='Ya está, esto deberá servir...' WHERE entry=2000000646; /* content: There, that should do it... */
UPDATE db_script_string SET content_loc7='¿Anaia?... ¿Me engañan mis ojos? ¿Eres tú de verdad?' WHERE entry=2000000647; /* content: Anaya...? Do my eyes deceive me? Is it really you? */
UPDATE db_script_string SET content_loc7='Él ha sido cruel con nosotros, amor mío, pero ten por seguro que ahora sí permaneceremos unidos.' WHERE entry=2000000648; /* content: The ages have been cruel to you and I, my love, but be assured, it is, and at long last we are reunited. */
UPDATE db_script_string SET content_loc7='El destino ha sido muy cruel al permitirnos volver a estar juntos solo tras miles de años de separación...' WHERE entry=2000000649; /* content: That the fates should be so cruel as to permit us only this after a thousand years apart... */
UPDATE db_script_string SET content_loc7='¿Me odias, amor mío? Por haber tenido que destruir tu forma viviente y por liberar tu espíritu de tan triste cautiverio...' WHERE entry=2000000650; /* content: Do you hate me, my love? That I was forced to destroy your living form, that your spirit be released from unhappy bondage. */
UPDATE db_script_string SET content_loc7='No permitas que esto atormente tu corazón, amor mío. Me has liberado de la esclavitud y por ello te amo más aún.' WHERE entry=2000000651; /* content: Let it not trouble your heart, beloved. You have freed me from slavery, and for that I love you all the more. */
UPDATE db_script_string SET content_loc7='Desafortunadamente, esto también debe interrumpirse... Los lazos que me atan a este mundo se debilitan y me arrastran...' WHERE entry=2000000652; /* content: Sadly, even this must be cut short... The ties that bind me to this world weaken, and pull me away... */
UPDATE db_script_string SET content_loc7='¡No! Anaia... ¡Anaia! ¡No me abandones! Por favor...' WHERE entry=2000000653; /* content: No! Anaya... Anaya! Don\'t leave me! Please... */
UPDATE db_script_string SET content_loc7='Adiós, Cerellean, hasta que volvamos a reunirnos...' WHERE entry=2000000654; /* content: Farewell, Cerellean, until we are joined once again... */
UPDATE db_script_string SET content_loc7='¿Cómo, amor mío? ¿Cómo encontraré fuerzas para enfrentarme a las edades del mundo sin ti a mi lado...?' WHERE entry=2000000656; /* content: How, my love? How will I find the strength to face the ages of the world without you by my side... */
UPDATE db_script_string SET content_loc7='Salto de Fonroca se encuentra en las montañas al Este y Norte.' WHERE entry=2000000658; /* content: Cliffspring Falls lies along the mountains to the east and north. */
UPDATE db_script_string SET content_loc7='¡Las escrituras son mías, hermano! ¡Pronto estarás fuera de mi camino para siempre!' WHERE entry=2000000659; /* content: The deeds are mine, brother! Soon you shall be out of my way for good! */
UPDATE db_script_string SET content_loc7='¡TROL CABREARME!' WHERE entry=2000000660; /* content: TROLL MAKE ME ANGRY! */
UPDATE db_script_string SET content_loc7='¡Esto es la guerra!' WHERE entry=2000000661; /* content: This mean war! */
UPDATE db_script_string SET content_loc7='Soy el heraldo de Mecazod. ¡Te libraré de la maldición!' WHERE entry=2000000663; /* content: I am the herald of Mechazod. You will be decursed! */
UPDATE db_script_string SET content_loc7='Tu intromisión está llegando a su fin. Mecazod levantará tu maldición cuando yo haya acabado contigo.' WHERE entry=2000000664; /* content: Your meddling is at an end. Mechazod will relieve your curse once I am done with you. */
UPDATE db_script_string SET content_loc7='¡ED-210 conectado!' WHERE entry=2000000665; /* content: ED-210 online! */
UPDATE db_script_string SET content_loc7='¡Twonky!' WHERE entry=2000000666; /* content: Twonky! */
UPDATE db_script_string SET content_loc7='¡Victoria o muerte! ¡Por la Horda!' WHERE entry=2000000669; /* content: Victory or death! For the Horde! */
UPDATE db_script_string SET content_loc7='¡Báñate en mi poder! ¡Bebe de mi fuerza! ¡Combate por la gloria de la Horda!' WHERE entry=2000000671; /* content: Be bathed in my power! Drink in my might! Battle for the glory of the Horde! */
UPDATE db_script_string SET content_loc7='¡¡¡Espera, $n!!! Parece que vamos a necesitar algo más de ayuda...' WHERE entry=2000000673; /* content: Wait, $N!!! It looks like we\'re going to need some help here... */
UPDATE db_script_string SET content_loc7='%s comienza a bailar.' WHERE entry=2000000674; /* content: %s begins to dance. */
UPDATE db_script_string SET content_loc7='¿Ya liberaste a Drull? No sé dónde le han retenido.' WHERE entry=2000000676; /* content: Have you freed Drull yet? I don\'t know where he\'s being held. */
UPDATE db_script_string SET content_loc7='Haré que vayáis más lentos, ¡pero ningún humano de piel rosada me privará de libertad!' WHERE entry=2000000677; /* content: I will only slow you down, but no pink-skinned humans will keep me from freedom! */
UPDATE db_script_string SET content_loc7='Aquí arriba, en la colina, $n.' WHERE entry=2000000679; /* content: Up here on the hill, $N. */
UPDATE db_script_string SET content_loc7='Déjame ayudarte con eso.' WHERE entry=2000000680; /* content: Let me help you with those. */
UPDATE db_script_string SET content_loc7='Tómate esto, te ayudará.' WHERE entry=2000000681; /* content: Drink this, it will help. */
UPDATE db_script_string SET content_loc7='Ya verás cómo te curas.' WHERE entry=2000000682; /* content: You\'re going to be just fine. */
UPDATE db_script_string SET content_loc7='Estáis muertos, pero no enterrados. ¡Dad lo mejor que tenéis!' WHERE entry=2000000686; /* content: You\'re dead, not buried. Now show me your best! */
UPDATE db_script_string SET content_loc7='Bueno, lo lamentarán, sacos de huesos. Veamos lo que tienen. Golpeen a su oponente y no paren hasta que yo lo ordene.' WHERE entry=2000000687; /* content: Alright you sorry sacks of bones. Let\'s see what you\'ve got. Strike your opponent, and don\'t stop till I say so. */
UPDATE db_script_string SET content_loc7='Espero que esos elfos de sangre no crean que les estoy espiando.' WHERE entry=2000000688; /* content: I hope those blood elves across the way don\'t think that I\'m spying on them! */
UPDATE db_script_string SET content_loc7='Te he traído un regalo de bienvenida, Jarl.' WHERE entry=2000000692; /* content: I brought you a housewarming gift, Jarl. */
UPDATE db_script_string SET content_loc7='Vamos, ábrelo. Creo que te gustará.' WHERE entry=2000000693; /* content: Go on, open it. I think you\'ll like it. */
UPDATE db_script_string SET content_loc7='¿Jarl?' WHERE entry=2000000694; /* content: Jarl? */
UPDATE db_script_string SET content_loc7='A menos que sea algo que se pueda comer, no lo quiero.' WHERE entry=2000000695; /* content: Unless it\'s something I can eat, I don\'t want it. */
UPDATE db_script_string SET content_loc7='Jarl, no has sido tú mismo desde que llegué. ¿Qué está pasando?' WHERE entry=2000000696; /* content: Jarl, you haven\'t been yourself ever since I arrived. What\'s going on? */
UPDATE db_script_string SET content_loc7='Nada. ¿Quieres ayudarme a preparar un estofado de anca de rana?' WHERE entry=2000000697; /* content: Nothing. Wanna help me whip up some frog leg stew? */
UPDATE db_script_string SET content_loc7='¡He venido a visitarte, Jarl! ¡Y te encuentro viviendo en una cabaña vacía en medio de la nada balbuceando sobre ranas! ¡Eres un caso perdido!' WHERE entry=2000000698; /* content: I came out here to visit you, Jarl! And I find you living in an empty cabin in the middle of nowhere, babbling about frogs? You\'re hopeless! */
UPDATE db_script_string SET content_loc7='Edward. Tyler. Prepárense para su primer desafío.' WHERE entry=2000000699; /* content: Edward. Tyler. Prepare for your first challenge. */
UPDATE db_script_string SET content_loc7='Lysta, invoca a los cautivos.' WHERE entry=2000000700; /* content: Lysta, summon in the captives. */
UPDATE db_script_string SET content_loc7='Lysta, invoca a los cautivos no-muertos.' WHERE entry=2000000702; /* content: Lysta, summon in undead captives. */
UPDATE db_script_string SET content_loc7='Lysta, invoca... ¡a la abominación!' WHERE entry=2000000704; /* content: Lysta, summon forth... the abomination! */
UPDATE db_script_string SET content_loc7='Bien hecho, Edward y Tyler. Sus avances en la instrucción son bastante buenos. Pronto volveremos a probar su entereza.' WHERE entry=2000000705; /* content: Well done Edward and Tyler. You are progressing along in your training quite nicely. We shall test your mettle again soon. */
UPDATE db_script_string SET content_loc7='Gracias por salvarme, $n. ¡Mi padre se pondrá muy contento!' WHERE entry=2000000706; /* content: Thank you for saving me, $N! My father will be delighted! */
UPDATE db_script_string SET content_loc7='¿Eres TÚ quien ha interrumpido mi cena? ¿Lo eres?' WHERE entry=2000000707; /* content: Are YOU the one who interrupted my dinner? Well, are you? */
UPDATE db_script_string SET content_loc7='¡Eres demasiado fea y flaca para ser una hembra yeti! Y ahora me estás enfadando...' WHERE entry=2000000708; /* content: You\'re too ugly and scrawny to be a female yeti! And now you\'re making me mad... */
UPDATE db_script_string SET content_loc7='Gracias de nuevo, $n. Esperaré aquí hasta que sea seguro partir.' WHERE entry=2000000709; /* content: Thank again, $n.  Now I\'ll just wait here until it\'s safe to leave. */
UPDATE db_script_string SET content_loc7='¡El brebaje inservible cae al suelo y parece cobrar vida!' WHERE entry=2000000710; /* content: The failed concoction spills on the floor and appears to come to life! */
UPDATE db_script_string SET content_loc7='¡Tiene vida!' WHERE entry=2000000711; /* content: It\'s alive! */
UPDATE db_script_string SET content_loc7='¡Urtrak mata a ti!' WHERE entry=2000000715; /* content: Urtrak kill you! */
UPDATE db_script_string SET content_loc7='¡La venganza es nuestra! ¡Atacad hermanos!' WHERE entry=2000000716; /* content: Vengeance is ours!  Attack my brothers! */
UPDATE db_script_string SET content_loc7='Vamos a ver cómo crecen estas semillas en la tierra que he preparado...' WHERE entry=2000000718; /* content: Let\'s see how these seeds grow in my prepared soil... */
UPDATE db_script_string SET content_loc7='Espero que esta planta eche raíces...' WHERE entry=2000000722; /* content: I hope this frond takes root... */
UPDATE db_script_string SET content_loc7='%s comienza a manipular la Piedra de Relu sobre los dos fósiles.' WHERE entry=2000000723; /* content: %s begins to manipulate the Stone of Relu over the two fossils. */
UPDATE db_script_string SET content_loc7='¿Quién ha invocado a Aman?' WHERE entry=2000000724; /* content: Who hath summoned forth Aman? */
UPDATE db_script_string SET content_loc7='Ah, veo que trabajas arduamente con las reliquias del pasado.' WHERE entry=2000000725; /* content: Ah, I see you toil with relics of the past. */
UPDATE db_script_string SET content_loc7='Te advierto que incluso tus creadores pueden equivocarse.' WHERE entry=2000000726; /* content: Be warned that even your creators are fallible. */
UPDATE db_script_string SET content_loc7='Si escarbas demasiado en tu pasado, tu futuro puede tener un fin repentino.' WHERE entry=2000000727; /* content: Digging too deep into your past might bring an abrupt end to your future. */
UPDATE db_script_string SET content_loc7='¿Qué tal se te da vigilar los barriles...?' WHERE entry=2000000730; /* content: How goes the barrel watching...? */
UPDATE db_script_string SET content_loc7='%s se pone sus prendas de piel de lobo.' WHERE entry=2000000732; /* content: %s dons his wolf-fur vestments. */
UPDATE db_script_string SET content_loc7='%s aúlla en respuesta a la llamada de Malukaz.' WHERE entry=2000000733; /* content: %s howls in response to Malukaz\'s call. */
UPDATE db_script_string SET content_loc7='%s expresa su aprobación.' WHERE entry=2000000734; /* content: %s expresses its approval. */
UPDATE db_script_string SET content_loc7='Que nuestro vínculo no vuelva a romperse nunca, espíritu.' WHERE entry=2000000735; /* content: May our bond with you never be broken again, spirit. */
UPDATE db_script_string SET content_loc7='¡Lo hemos conseguido! Thrall estaría orgulloso.' WHERE entry=2000000736; /* content: We have done it! Thrall would be proud. */
UPDATE db_script_string SET content_loc7='¿Qué significa esto, Mor\'ghor?' WHERE entry=2000000737; /* content: What is the meaning of this, Mor\'ghor? */
UPDATE db_script_string SET content_loc7='¡Silencio!' WHERE entry=2000000738; /* content: SILENCE! */
UPDATE db_script_string SET content_loc7='¿ESTO es tu héroe?' WHERE entry=2000000740; /* content: THIS is your hero? */
UPDATE db_script_string SET content_loc7='Te han engañado, imbécil.' WHERE entry=2000000741; /* content: You have been deceived, imbecile. */
UPDATE db_script_string SET content_loc7='¡La... operación... entera... HA SIDO COMPROMETIDA!' WHERE entry=2000000742; /* content: This... whole... operation... HAS BEEN COMPROMISED! */
UPDATE db_script_string SET content_loc7='Espero ver el caparazón de ese insecto hecho pedazos en mi guarida dentro de una hora. Fracasa y tu destino será mucho peor que la muerte.' WHERE entry=2000000743; /* content: I expect to see this insect\'s carcass in pieces in my lair within the hour. Fail and you will suffer a fate so much worse than death. */
UPDATE db_script_string SET content_loc7='¿Ya es seguro pasar por ese maldito chisme?' WHERE entry=2000000747; /* content: Is that damned thing safe to go through yet? */
UPDATE db_script_string SET content_loc7='En realidad, no.' WHERE entry=2000000748; /* content: Not really. */
UPDATE db_script_string SET content_loc7='¿Puedes parar de preguntar eso?' WHERE entry=2000000749; /* content: Will you stop asking that? */
UPDATE db_script_string SET content_loc7='Está bien, parece seguro. ¡Entra ahí, ingeniero!' WHERE entry=2000000750; /* content: Alright, it looks safe. Get in there, engineer! */
UPDATE db_script_string SET content_loc7='¡A ver qué pasa!' WHERE entry=2000000751; /* content: Here goes nothing! */
UPDATE db_script_string SET content_loc7='Esto dejará marca...' WHERE entry=2000000752; /* content: This is gonna leave a mark... */
UPDATE db_script_string SET content_loc7='%s se toca con sus manos para comprobar que todo está en su sitio.' WHERE entry=2000000753; /* content: %s runs his hands across his body to make sure everything is intact. */
UPDATE db_script_string SET content_loc7='¡Lo conseguí! ¡Lo conseguí!' WHERE entry=2000000754; /* content: I made it! I made it! */
UPDATE db_script_string SET content_loc7='%s desaprueba con la cabeza.' WHERE entry=2000000755; /* content: %s shakes his head in disapproval. */
UPDATE db_script_string SET content_loc7='No me gustaba mucho, de todas formas. Hablaba mucho y hacía poco.' WHERE entry=2000000756; /* content: I didn\'t like him much anyway. Too much talk, not enough "do." */
UPDATE db_script_string SET content_loc7='¡Que os vaya bien, estúpidos incompetentes! ¡Otro ingeniero vaporizado!' WHERE entry=2000000757; /* content: Nice going you incompetent morons! Another one of our engineers vaporized! */
UPDATE db_script_string SET content_loc7='¡No puedes evitar que expongamos la verdad acerca de la cobardía de Jaina!' WHERE entry=2000000758; /* content: You can\'t stop us from exposing the truth about Jaina\'s cowardice! */
UPDATE db_script_string SET content_loc7='Si crees que puedes detenernos, te equivocas "colega".' WHERE entry=2000000759; /* content: If you think you can stop us, you\'re mistaken, \'friend.\' */
UPDATE db_script_string SET content_loc7='¡Esta gente conocerá al almirante Valiente como el autentico héroe que fue!' WHERE entry=2000000760; /* content: These people will know Admiral Proudmoore for the true hero he was! */
UPDATE db_script_string SET content_loc7='Con un poquito de edición creativa son mucho más útiles...' WHERE entry=2000000761; /* content: With just a little creative editing, these are much more useful... */
UPDATE db_script_string SET content_loc7='Sería una pena desaprovechar estos panfletos, ¿verdad?' WHERE entry=2000000762; /* content: It would be a shame to let these leaflets go to waste, wouldn\'t it? */
UPDATE db_script_string SET content_loc7='¿... crear un fondo para la compra de gomina? Me gusta mi pelo tal y como está, gracias.' WHERE entry=2000000763; /* content: ...to create a fund for the purchase of hair gel? I like my hair the way it is, thanks! */
UPDATE db_script_string SET content_loc7='¿... como desertor, disfrutarás de actividades como nadar atado y cuidar perros? ¡Qué ridículo!' WHERE entry=2000000764; /* content: ...as a deserter, you\'ll enjoy activities like tethered swimming and dog grooming? How ridiculous! */
UPDATE db_script_string SET content_loc7='Nunca me uniría a nada así. Mejor guardar esto. Puede ser de utilidad en el baño...' WHERE entry=2000000765; /* content: I\'d never join anything like this. Better keep this, though. It\'ll come in handy in the privy... */
UPDATE db_script_string SET content_loc7='¿... los desertores buscan un Theramore donde los ciudadanos puedan pasar el día haciendo malabarismos? ¿Qué es esta tontería?' WHERE entry=2000000766; /* content: ...the deserters seek a Theramore where citizens will be free to juggle at all hours of the day? What is this nonsense? */
UPDATE db_script_string SET content_loc7='Esto es lo más divertido que he leído en mucho tiempo. ¡Me has alegrado el día!' WHERE entry=2000000767; /* content: That\'s the funniest thing I\'ve read in a long time. You\'ve just made my day! */
UPDATE db_script_string SET content_loc7='Esto... esto es una broma, ¿verdad?' WHERE entry=2000000768; /* content: This... this is a joke, right? */
UPDATE db_script_string SET content_loc7='¡Que los espíritus te cuiden, $r!' WHERE entry=2000000770; /* content: Spirits watch over you, $r! */
UPDATE db_script_string SET content_loc7='¡Muchas gracias, héroe!' WHERE entry=2000000771; /* content: Many Thanks, hero! */
UPDATE db_script_string SET content_loc7='Gracias por rescatarme, ¡pero los demás aún están cautivos en los pisos de arriba!' WHERE entry=2000000774; /* content: Thank you for rescuing me, but the others are still held on the tiers above! */
UPDATE db_script_string SET content_loc7='¡Están locos estos férreos! No hacen más que hablar de servir a su \'maestro Loken\' pero, por lo que yo sé, solo algunos reciben órdenes suyas.' WHERE entry=2000000775; /* content: These irons are crazy! They keep talking about serving their \'master Loken\' but from what I can tell, only a few receive orders from him. */
UPDATE db_script_string SET content_loc7='¡Gracias al cielo! ¡Me preocupaba que me fueran a obligar a luchar en su guerra contra los gigantes de piedra!' WHERE entry=2000000776; /* content: Thank goodness! I was worried they were going to press me into service in their war against the stone giants! */
UPDATE db_script_string SET content_loc7='¿Te atreves a retar a Drottinn Hrothgar, rey de los mares?' WHERE entry=2000000777; /* content: You dare to challenge Drottinn Hrothgar, king of the seas? */
UPDATE db_script_string SET content_loc7='Enseña una lección a esos cultores, $n. ¡Nadie se mete con el cruzado Kul y sale impune!' WHERE entry=2000000778; /* content: Teach those cultists a lesson, $N. No one messes with Crusader Kul and gets away with it! */
UPDATE db_script_string SET content_loc7='¡Gracias a la Luz! ¡Jamás volveré a hacer algo tan insensato!' WHERE entry=2000000779; /* content: Thank the Light! I\'ll never do anything this foolish again! */
UPDATE db_script_string SET content_loc7='Rescatemos a los demás aspirantes y volvamos al campamento. ¡Voy a necesitar un barril de cerveza entero después de esto!' WHERE entry=2000000780; /* content: Let\'s rescue the rest of those aspirants and get back to camp. I\'m going to need a whole keg of ale after this! */
UPDATE db_script_string SET content_loc7='Unos segundos más y me hubieran atado a uno de esos altares para absorber mi alma. ¡Salgamos de aquí!' WHERE entry=2000000781; /* content: They were seconds from strapping me to one of those altars and sucking my soul out. Let\'s get out of here! */
UPDATE db_script_string SET content_loc7='Gracias, $c.' WHERE entry=2000000782; /* content: Thank you, $C. */
UPDATE db_script_string SET content_loc7='¡Están matando a los prisioneros! De prisa, ayuda a los demás.' WHERE entry=2000000783; /* content: They\'re killing the prisoners! Hurry, help the others. */
UPDATE db_script_string SET content_loc7='¡Sabía que no debía haber venido al torneo!' WHERE entry=2000000784; /* content: I knew I shouldn\'t have come to the tournament! */
UPDATE db_script_string SET content_loc7='Pensé que no saldría de ahí con vida.' WHERE entry=2000000785; /* content: I thought I\'d never make it out of here alive. */
UPDATE db_script_string SET content_loc7='¡$gEstúpido:Estúpida;, $c! ¡No puedes detener a los Kvaldir!' WHERE entry=2000000786; /* content: Foolish $c, you cannot stop the Kvaldir! */
UPDATE db_script_string SET content_loc7='Abandona este lugar.' WHERE entry=2000000792; /* content: Leave this place. */
UPDATE db_script_string SET content_loc7='No podrás evitar que el Señor de la Escarcha entre en este mundo, mortal. El poder del cazamareas aplastará el de Ragnaros para siempre y tu tierra se convertirá en un paraíso helado.' WHERE entry=2000000797; /* content: You will not stop the Frost Lord from entering this world, mortal. The Tidehunter\'s might will crush that of Ragnaros once and for all, leaving your land a frozen paradise! */
UPDATE db_script_string SET content_loc7='Gracias, $r. Me uniré a tu lucha contra los Tronaforjado.' WHERE entry=2000000798; /* content: Thank you, $r. I will join your struggle against the stormforged. */
UPDATE db_script_string SET content_loc7='Imposible calcular. Fallo en la unidad. Directiva: apagar.' WHERE entry=2000000799; /* content: Does not compute. Unit malfunctioning. Directive: shut down. */
UPDATE db_script_string SET content_loc7='¿Libre de nuevo? El trabajo del guardián Mimir aguarda.' WHERE entry=2000000800; /* content: Free again? Keeper Mimir\'s work awaits. */
UPDATE db_script_string SET content_loc7='Nueva directiva: ayudar a derrotar a los enanos férreos.' WHERE entry=2000000801; /* content: New directive: assist in the defeat of the iron dwarves. */
UPDATE db_script_string SET content_loc7='¡Estúpido insolente! ¿Pensaste en robar la sangre de Zelemar? ¡Pagarás con la tuya!' WHERE entry=2000000802; /* content: Insolent fool! You thought to steal Zelemar\'s blood? You shall pay with your own! */
UPDATE db_script_string SET content_loc7='¡Sacadle de ahí!' WHERE entry=2000000804; /* content: Get him out of there! */
UPDATE db_script_string SET content_loc7='Quizás Ograbisi use tu cabeza como pequeño sombrero cuando acabe contigo.' WHERE entry=2000000805; /* content: Perhaps Ograbisi will use your head as a tiny little hat when I\'m through with you. */
UPDATE db_script_string SET content_loc7='¡En mi tierra te colgarían de un pie si abres la puerta de la celda de un interno!' WHERE entry=2000000806; /* content: Where I come from, you get shanked for opening another inmate\'s cell door! */
UPDATE db_script_string SET content_loc7='Ya he tenido bastante de tanto lloriqueo incesante, capitán. ¿Y de qué eres capitán tú? ¡Lo que hay que ver!' WHERE entry=2000000808; /* content: I\'ve had just about enough of your incessant whining, captain. What are you even a captain of? I mean, The nerve! */
UPDATE db_script_string SET content_loc7='%s se encoge de hombros.' WHERE entry=2000000810; /* content: %s shrugs. */
UPDATE db_script_string SET content_loc7='Vaya. Otra vez a revisar el diseño.' WHERE entry=2000000811; /* content: Oh well, back to the drawing board. */
UPDATE db_script_string SET content_loc7='Solo me queda esperar que vea la Luz y, en su lugar, centre sus energías en restaurar la antes bella Quel\'Thalas.' WHERE entry=2000000816; /* content: I can only hope that he will see the Light and instead turn his energies to restoring once-beautiful Quel\'Thalas. */
UPDATE db_script_string SET content_loc7='¡Uf! Aquí está mi sombrero de la suerte. Lo había buscado por todas partes.' WHERE entry=2000000819; /* content: Phew! There\'s my lucky hat. I\'ve been looking for it everywhere. */
UPDATE db_script_string SET content_loc7='No sé en qué estaba pensando. Mira que salir sin mi espada... Me la habría puesto si la hubiera visto aquí...' WHERE entry=2000000820; /* content: I don\'t know what I was thinking, going out without my sword. I would\'ve put it on if I\'d seen it here... */
UPDATE db_script_string SET content_loc7='No puedo seguir sin mi capa. Espero que esté aquí...' WHERE entry=2000000821; /* content: I can\'t possibly go out without my cloak. I hope it\'s in here... */
UPDATE db_script_string SET content_loc7='¡Aquí está! Juraría que no estaba aquí la última vez que miré.' WHERE entry=2000000822; /* content: There it is! I could\'ve sworn it wasn\'t here last time I checked... */
UPDATE db_script_string SET content_loc7='No puedo dormir si no me cuentan un buen cuento. Ahora, seguro que duermo bien.' WHERE entry=2000000823; /* content: I can\'t sleep without a good bedtime story. Now I\'m certain to rest well. */
UPDATE db_script_string SET content_loc7='¿Qué ha sido ese ruido? Parecía un rugido.' WHERE entry=2000000824; /* content: What was that noise? It sounded like a roar. */
UPDATE db_script_string SET content_loc7='¡¿Zarpadura?! Ahí mismo $gamigo mío:amiga mía;... ¿dónde está tu maestro Volcor?' WHERE entry=2000000826; /* content: Grimclaw?! Easy there, my friend... where is your master Volcor? */
UPDATE db_script_string SET content_loc7='¡Guau! Espera. Un momento...' WHERE entry=2000000828; /* content: Whoa, whoa there, my friend. One moment... */
UPDATE db_script_string SET content_loc7='Ahí está... esto deberá servir. Ahora dime qué ha pasado, Zarpadura.' WHERE entry=2000000830; /* content: There... that should help. Now, tell me what\'s happened, Grimclaw. */
UPDATE db_script_string SET content_loc7='Ya entiendo, $gamigo mío:amiga mía;. Encontraré a alguien que ayude a tu maestro. Vuelve junto a él ahora, o al menos permanece cerca de él.' WHERE entry=2000000832; /* content: I understand, my friend. I shall find someone to help your master. Go back to him now, or at least stay close. */
UPDATE db_script_string SET content_loc7='Si dispones de tiempo, podrías ayudar a Zarpadura y a su maestro Volcor. Si te interesa, habla conmigo más adelante...' WHERE entry=2000000834; /* content: If you have the time, Grimclaw and his master Volcor could use your help. If you\'re interested, speak with me further... */
UPDATE db_script_string SET content_loc7='Por favor, discúlpame mientras comienzo mis pruebas...' WHERE entry=2000000835; /* content: Please excuse me while I begin my tests... */
UPDATE db_script_string SET content_loc7='Interesante...' WHERE entry=2000000836; /* content: Interesting... */
UPDATE db_script_string SET content_loc7='Pero, ¿qué es esto?' WHERE entry=2000000837; /* content: But what is this? */
UPDATE db_script_string SET content_loc7='¡Oh, vaya!' WHERE entry=2000000838; /* content: Oh, my! */
UPDATE db_script_string SET content_loc7='Si la sangre no se me hubiera coagulado hace tiempo, estaría hirviendo de rabia ahora mismo...' WHERE entry=2000000839; /* content: If my blood hadn\'t clotted long ago, it would be boiling with rage right now... */
UPDATE db_script_string SET content_loc7='¡Saludos, Gezhe! Se han enviado diecisiete pedidos más al sector Hara\'samid. Los mercados de la veladrita pesada se han venido abajo, tal y como predijiste.' WHERE entry=2000000842; /* content: Hail, Gezhe!  Seventeen more orders have been placed in the Hara\'samid Sector.  The markets of heavy veldarite have all but collapsed, as you predicted. */
UPDATE db_script_string SET content_loc7='Las exportaciones de Heraazi están subiendo. Ya hemos untado a los oficiales de aduanas que hacía falta y el contrabando empezará a llegar en cuestión de días.' WHERE entry=2000000843; /* content: Heraazi exports are on the rise, sir.  We\'ve already bribed the appropriate taxation officers and contraband will begin to arrive within days. */
UPDATE db_script_string SET content_loc7='No me sorprende nada. Vuelve con alguna noticia de verdad, ¿de acuerdo?' WHERE entry=2000000844; /* content: Nothing surprising there.  Come back with some real news, will you? */
UPDATE db_script_string SET content_loc7='Lo que pensaba. Está bien. Puedes irte.' WHERE entry=2000000845; /* content: As expected.  Very well, dismissed. */
UPDATE db_script_string SET content_loc7='Con tu venia, señor.' WHERE entry=2000000846; /* content: By your leave, sir. */
UPDATE db_script_string SET content_loc7='Si estás aquí, significa que ya puedes empezar a estudiar la invocación de cohortes demoníacas para hacer lo que te plazca. Empezaremos por las criaturas más humildes que podrás invocar y continuaremos a partir de ahí. Comencemos.' WHERE entry=2000000847; /* content: If you\'re here, then it means you are prepared to begin the study of summoning demonic cohorts to do your bidding. We will start with the lowliest creatures you will be able to call and continue from there. Let us begin. */
UPDATE db_script_string SET content_loc7='La criatura más fácil de invocar para ti es el diablillo. A estas alturas, ya deberías ser capaz de crear al esbirro, pero por razones de complejidad, empezaré por él.' WHERE entry=2000000848; /* content: The easiest creature for you to summon is the imp. You should already be able to bring forth this minion but for completeness\' sake I will start with him. */
UPDATE db_script_string SET content_loc7='Esta pequeña bestia hedionda es el diablillo. Es pequeño y débil, lo que lo convierte en un ser prácticamente inútil como escudo de carne y hueso y su capacidad de destrucción es realmente mediocre. Esta criatura resulta más útil como apoyo de un grupo más grande.' WHERE entry=2000000849; /* content: This foul little beast is the imp. It is small and weak, making it almost useless as a meatshield, and its damage output is mediocre at best. This creature is best used for support of a larger group. */
UPDATE db_script_string SET content_loc7='Ahora que has podido estudiar el diablillo, pasemos al siguiente esbirro que podrás invocar, el abisario.' WHERE entry=2000000850; /* content: Now that you have had a chance to study the imp, let us move on to the next minion you will be able to summon, the voidwalker. */
UPDATE db_script_string SET content_loc7='Este ente demoníaco se conoce como abisario. Si fuerza y resistencia son importantes, resultando ideal para la defensa. Envíalo al frente para atacar a tu enemigo y luego úsalo como escudo mientras utilizas tus encantamientos y habilidades para extinguir la vida de tu oponente.' WHERE entry=2000000851; /* content: This demonic entity is known as the Voidwalker. Its strength and endurance are significant, making it ideal for defense. Send it to attack your enemy, then use it as a shield while you use your spells and abilities to drain away your opponent\'s life. */
UPDATE db_script_string SET content_loc7='Merece la pena contemplarlo, por si no lo has visto antes. Se trata de una criatura realmente impresionante dentro como fuera del campo de batalla. Ahora, echemos un vistazo a lo que estoy seguro que todos ustedes, alumnos varones, han estado esperando. El súcubo.' WHERE entry=2000000852; /* content: If you\'ve never seen one, it is a sight to behold. A very impressive creature both on and off the field of battle. Next, let us take a look at what I am sure all you male students have been waiting for. The succubus. */
UPDATE db_script_string SET content_loc7='Muy bien. Además de la distracción evidente que un esbirro como este supondrá para sus enemigos masculinos, ella también tiene una capacidad de destrucción impresionante. No obstante, su frágil resistencia la hace casi inservible como escudo.' WHERE entry=2000000853; /* content: All right now. Aside from the obvious distractions a minion like this will provide against your more masculine foes, she is also capable of dealing out impressive amounts of damage. However, her fragile endurance makes her almost useless as a shield. */
UPDATE db_script_string SET content_loc7='Estudia mucho y quizás algún día podrás invocar a tu propio esbirro, pero ahora pasaremos a estudiar al manáfago.' WHERE entry=2000000854; /* content: Study hard and you might one day be able to summon one on your own, but for now it\'s time to move on to the felhunter. */
UPDATE db_script_string SET content_loc7='Lo que tenéis delante es un manáfago. Entre los talentos naturales de esta criatura está la capacidad de neutralizar encantamientos y otras habilidades, convirtiéndola en un ser inigualable a la hora de enfrentarse a un oponente con poderes mágicos.' WHERE entry=2000000855; /* content: What you see before you is a felhunter. This creature\'s natural talents include spell lock and other abilities which make it unequalled when facing a magically attuned opponent. */
UPDATE db_script_string SET content_loc7='A la hora de enfrentarse a cualquier taumaturgo, esta bestia salvaje será tu mejor amigo. Veamos ahora algo ligeramente distinto. La siguiente criatura te ayudará en tus viajes y hará más agradables tus viajes futuros. Vamos a echar un vistazo a un corcel vil.' WHERE entry=2000000856; /* content: When facing a spellcaster of any kind, this feral beast will be your best friend. Now, let us take a look at something a bit different. This next creature will aid your travels and make your future journeys much easier. Let\'s take a look at a felsteed. */
UPDATE db_script_string SET content_loc7='Aquí lo tenéis. Nuestra lección sobre las invocaciones ha llegado a su fin. En breve iniciaremos otra clase, así que si queréis poneros al día, os podéis quedar por aquí.' WHERE entry=2000000858; /* content: There you have it. Our lesson on summoning has come to an end. A new class will begin shortly, so if you wish to brush up, feel free to stay around. */
UPDATE db_script_string SET content_loc7='El sacerdote se arrodilla y reza en silencio.' WHERE entry=2000000859; /* content: The priest kneels in silent prayer. */
UPDATE db_script_string SET content_loc7='Luz, dame fuerza.' WHERE entry=2000000860; /* content: Light, give me strength. */
UPDATE db_script_string SET content_loc7='Luz, guía a los caídos de la falsedad a la verdad, de la oscuridad hacia la luz y de la muerte a la vida infinita.' WHERE entry=2000000861; /* content: Light, lead the fallen from untruth to truth, from darkness to Light and from death to immortality. */
UPDATE db_script_string SET content_loc7='¡¡Nooooo!!' WHERE entry=2000000865; /* content: NOOOOO!!! */
UPDATE db_script_string SET content_loc7='Amigos... Os he fallado...' WHERE entry=2000000866; /* content: Friends... I\'ve failed you... */
UPDATE db_script_string SET content_loc7='No, amigo mío... Nosotros te hemos fallado. Te hemos fallado.' WHERE entry=2000000867; /* content: No my friend... we failed you... we failed you. */
UPDATE db_script_string SET content_loc7='El Dragonante Negro se materializó de la nada y nos embistió antes de que pudiéramos reaccionar.' WHERE entry=2000000870; /* content: The Black Dragonkin materialized from thin air, and set upon us before we could react. */
UPDATE db_script_string SET content_loc7='Nos masacraron con una eficacia fría, pero parecían más interesados en los huevos guardados aquí, en el Sagrario.' WHERE entry=2000000872; /* content: They slaughtered us with cold efficiency, but the true focus of their interest seemed to be the eggs kept here in the sanctum. */
UPDATE db_script_string SET content_loc7='Intrusos: su victoria será efímera. Soy el Comandante Varos Cloudstrider. Mis dracos controlan los cielos y protegen este conducto. ¡Me encargaré personalmente de que el Óculo no caiga en sus manos!' WHERE entry=2000000877; /* content: Intruders, your victory will be short-lived. I am Commander Varos Cloudstrider. My drakes control the skies and protect this conduit. I will see to it personally that the Oculus does not fall into your hands! */
UPDATE db_script_string SET content_loc7='Solo aquellos que se alimentan de almas perdidas conocen el hambre.' WHERE entry=2000000880; /* content: They who dine on lost souls know only hunger. */
UPDATE db_script_string SET content_loc7='Contempla el vacío. Esa es la perpetuidad en la que moran.' WHERE entry=2000000881; /* content: Gaze into the void. It is the perpetuity in which they dwell. */
UPDATE db_script_string SET content_loc7='Se beben tu temor: es la sangre de la vida.' WHERE entry=2000000882; /* content: They drink your fear: it is the blood of life. */
UPDATE db_script_string SET content_loc7='Son el susurro del escalofriante viento.' WHERE entry=2000000883; /* content: They are the whisper on the shivering wind. */
UPDATE db_script_string SET content_loc7='No mueren, no viven. Están fuera del ciclo.' WHERE entry=2000000884; /* content: They do not die; they do not live. They are outside the cycle. */
UPDATE db_script_string SET content_loc7='Donde uno cae, muchos podrán ocupar su puesto.' WHERE entry=2000000885; /* content: Where one falls, many shall take its place. */
UPDATE db_script_string SET content_loc7='Volveremos a encontrarnos... Algún día.' WHERE entry=2000000888; /* content: We will meet again... Someday. */
UPDATE db_script_string SET content_loc7='¡La encarnación de la muerte ha sido liberada una vez más sobre el mundo! Teron Sanguino ha vuelto...' WHERE entry=2000000889; /* content: Death incarnate has been released upon the world once more! Teron Gorefiend has returned... */
UPDATE db_script_string SET content_loc7='¡Escóndete! ¡Escóndete antes de que te vean! ¡Arriba!' WHERE entry=2000000892; /* content: Hide! Hide before they see you! Back up! */
UPDATE db_script_string SET content_loc7='El momento de atacar se acerca. Acabad con Icarius.' WHERE entry=2000000893; /* content: The time to strike is at hand. Terminate Icarius. */
UPDATE db_script_string SET content_loc7='¡Alto!' WHERE entry=2000000894; /* content: Halt! */
UPDATE db_script_string SET content_loc7='¿Qué sucede, mi señor?' WHERE entry=2000000895; /* content: What is it, my lord? */
UPDATE db_script_string SET content_loc7='¡Zarath, debes volver al Templo Oscuro de inmediato! Pa... parece que he extraviado las órdenes de Lord Illidan. ¡Rápido!' WHERE entry=2000000896; /* content: Zarath you must return to the Black Temple at once! I... I seem to have misplaced Lord Illidan\'s orders. Quickly! */
UPDATE db_script_string SET content_loc7='Mi señor, sin duda no esperas que te deje desatendido. Lord Illidan me cortaría la cabeza si te ocurriera algo.' WHERE entry=2000000897; /* content: My lord, surely you do not expect me to leave you unattended. Lord Illidan would have my head if anything were to happen to you. */
UPDATE db_script_string SET content_loc7='Zarath, soy perfectamente capaz de subir este camino hasta Punta Eclipse. ¡Si no entregamos la misiva, Lord Illidan nos cortará la cabeza a los dos! ¡Puedes marcharte!' WHERE entry=2000000898; /* content: Zarath, I am perfectly capable of making it up this road to Eclipse Point. If we do not deliver the missive, Lord Illidan will have both of our heads! You are dismissed! */
UPDATE db_script_string SET content_loc7='Como desee, mi señor.' WHERE entry=2000000899; /* content: As you wish, my lord. */
UPDATE db_script_string SET content_loc7='Ah, dulce cardo de sangre... Probablemente olvidado por uno de esos mugrientos adictos de Punta Eclipse.' WHERE entry=2000000900; /* content: Ah, sweet, sweet bloodthistle... Probably left behind by one of those filthy addicts at Eclipse Point. */
UPDATE db_script_string SET content_loc7='Su pérdida es, sin duda, mi ganancia...' WHERE entry=2000000901; /* content: Their loss is most definitely my gain... */
UPDATE db_script_string SET content_loc7='%s se mete el fardo de cardos de sangre en la toga.' WHERE entry=2000000902; /* content: %s puts the bundle of bloodthistle in his robe. */
UPDATE db_script_string SET content_loc7='Atrás, $n. Los espíritus pueden atacar.' WHERE entry=2000000903; /* content: Stand back, $N. The spirits may lash out. */
UPDATE db_script_string SET content_loc7='¿Por qué nos has capturado?' WHERE entry=2000000904; /* content: Why have you captured us? */
UPDATE db_script_string SET content_loc7='¿Qué esperas obtener de esto?' WHERE entry=2000000905; /* content: What is it that you hope to gain from this? */
UPDATE db_script_string SET content_loc7='%s se inclina.' WHERE entry=2000000906; /* content: %s bows. */
UPDATE db_script_string SET content_loc7='Queremos ayudarte, espíritu. Buscamos tu sabiduría.' WHERE entry=2000000907; /* content: We wish to help you, spirit. We seek your wisdom. */
UPDATE db_script_string SET content_loc7='¿Sabiduría? Tu pueblo fue el que provocó la terrible ira de Cyrukh.' WHERE entry=2000000908; /* content: Wisdom? It was your people that caused the insufferable wrath of Cyrukh. */
UPDATE db_script_string SET content_loc7='¿Por qué deberíamos ayudarte? El mundo está arrasado. Destruido.' WHERE entry=2000000909; /* content: Why should we help you? The world is ruined. Destroyed. */
UPDATE db_script_string SET content_loc7='%s asiente.' WHERE entry=2000000910; /* content: %s nods. */
UPDATE db_script_string SET content_loc7='Tienes razón, espíritu. Mi pueblo os hizo esto. Déjanos enmendar nuestros errores.' WHERE entry=2000000911; /* content: You are right, spirit. It was my people that did this to you. Allow us to make it right. */
UPDATE db_script_string SET content_loc7='Solo dinos esto: ¿qué o quién es Cyrukh?' WHERE entry=2000000912; /* content: Please tell us this: Who or what is Cyrukh? */
UPDATE db_script_string SET content_loc7='¡Insensato! ¡Cyrukh, el señor de Fuego! Cuando se rompió el vínculo, Cyrukh despertó. Son las lágrimas de Cyrukh las que llenan este mundo de roca fundida.' WHERE entry=2000000913; /* content: Fool! Cyrukh the fire lord! When the tie was severed, Cyrukh awoke. It is the tears of Cyrukh that fill this land with molten rock. */
UPDATE db_script_string SET content_loc7='El... ¿El Señor del Fuego? ¿Despertó a un señor elemental? Claro... ¡Maldito Gul\'dan!' WHERE entry=2000000914; /* content: The... The fire lord? An elemental lord was unearthed? Of course... Gul\'dan you were a damned fool! */
UPDATE db_script_string SET content_loc7='Ahora ha perdido la razón. Se volvió loco durante la rotura. No hay descanso para los elementos.' WHERE entry=2000000915; /* content: He is insane now. Driven mad during the shattering. There is no rest for the elements. */
UPDATE db_script_string SET content_loc7='Tiene que haber una forma.' WHERE entry=2000000916; /* content: There must be a way. */
UPDATE db_script_string SET content_loc7='Sí, una forma. Destruir a Cyrukh. Destruir a un señor elemental.' WHERE entry=2000000917; /* content: Yes, a way. Destroy Cyrukh. Destroy an elemental lord. */
UPDATE db_script_string SET content_loc7='No dudes de mis camaradas ni de mí, espíritu. Dinos cómo invocar al señor elemental. Mis aliados y yo lo destruiremos.' WHERE entry=2000000918; /* content: Do not doubt my comrades and I, spirit. Tell us how to conjure the fire lord. My allies will destroy him. */
UPDATE db_script_string SET content_loc7='Debéis pronunciar las palabras...' WHERE entry=2000000919; /* content: You must utter the words... */
UPDATE db_script_string SET content_loc7='Sí... Sí..., las palabras. Las palabras de la maldición. Las palabras pronunciadas por el oscuro, tu maestro.' WHERE entry=2000000920; /* content: Yes... Yes... the words. The words of damnation. The words spoken by the dark one. Your master. */
UPDATE db_script_string SET content_loc7='Sí... Gul\'dan...' WHERE entry=2000000921; /* content: Yes... Gul\'dan... */
UPDATE db_script_string SET content_loc7='Encontraremos esas palabras. Invocaremos a tu maestro y lo destruiremos.' WHERE entry=2000000922; /* content: We will find these words. We will summon your master and we will destroy him. */
UPDATE db_script_string SET content_loc7='Hablas. Solo hablas... Ahora libéranos, mortal.' WHERE entry=2000000923; /* content: Talk. More talk... Now release us, mortal. */
UPDATE db_script_string SET content_loc7='Ahora, a recuperar las palabras perdidas.' WHERE entry=2000000924; /* content: Now to recover the lost words! */
UPDATE db_script_string SET content_loc7='Estoy harto de pedir las cosas con cortesía, hombre pez.' WHERE entry=2000000925; /* content: I\'m through asking nicely, fish-man. */
UPDATE db_script_string SET content_loc7='¡¿Dónde está la Clave de Condenación?! Sé que un tercio está en este pueblo.' WHERE entry=2000000926; /* content: Where is the Cipher of Damnation? I know a third of it is in this village. */
UPDATE db_script_string SET content_loc7='¿De qué me hablasss, piel ssuave?' WHERE entry=2000000927; /* content: Whhaaat... are you sssspeaking of, ssssoft-sskin? */
UPDATE db_script_string SET content_loc7='Los nagasss no tienen ninguna cccclave...' WHERE entry=2000000928; /* content: The naga do nottt... have thissss... cipherrr... */
UPDATE db_script_string SET content_loc7='No... Claro que no. ¿En qué estaría pensando? ¿Para qué quieren ustedes un tercio de la Clave de Condenación?' WHERE entry=2000000929; /* content: No... Of course not. What was I thinking? Why would you have a third of the Cipher of Damnation? */
UPDATE db_script_string SET content_loc7='Qué tonto. Quizás debería soltarte y dejarte marchar...' WHERE entry=2000000930; /* content: I feel so stupid. Maybe I should unchain you and set you free? */
UPDATE db_script_string SET content_loc7='Sssí..., sssí... Déjame marcharrr...' WHERE entry=2000000931; /* content: Yesss... Yesss... Ssset me freee... */
UPDATE db_script_string SET content_loc7='¡¿DE VERDAD CREES QUE SOY TAN ESTÚPIDO, NAGA?! ¡SUFRE!' WHERE entry=2000000932; /* content: DO I HONESTLY LOOK THAT STUPID TO YOU, NAGA? NOW YOU FEEL PAIN! */
UPDATE db_script_string SET content_loc7='%s escupe sobre el naga Cicatriz Espiral.' WHERE entry=2000000933; /* content: %s spits on the Coilskar naga. */
UPDATE db_script_string SET content_loc7='¡LEVÁNTATE, BASURA INMUNDA! ¡LEVÁNTATE ANTES DE QUE TE MATE!' WHERE entry=2000000934; /* content: GET UP YOU ROTTEN PIECE OF TRASH! GET UP BEFORE I END YOU! */
UPDATE db_script_string SET content_loc7='%s recupera la conciencia.' WHERE entry=2000000935; /* content: %s regains consciousness. */
UPDATE db_script_string SET content_loc7='¡No vuelvas a pegarle al comandante Cicatriz Essspiral, orco! ¡Te lo diré! La clave... essstá essscondido en el Alto Cicatriz Espiral. Los nagasss de allí lo guardan bajo llave...' WHERE entry=2000000937; /* content: Do not beat the Coilsssskaarrr commander anymore, orc! I will tell you! The cipherrr... It is hidden in one of the chests at Coilssskarrr Point... The naga there hold the keysss... */
UPDATE db_script_string SET content_loc7='¡CICATRIZ ESSSPIRAL!' WHERE entry=2000000938; /* content: COILSSSKARRR! */
UPDATE db_script_string SET content_loc7='¿Qué es lo que quieres de mí, chamán?' WHERE entry=2000000939; /* content: What is it that you want from me, shaman? */
UPDATE db_script_string SET content_loc7='¿Acaso no tienes ojos? ¿No has visto a los demonios en nuestro mismo umbral? ¡No puedo prescindir de ningún soldado!' WHERE entry=2000000940; /* content: Do you not have eyes? Did you not see the demons on our very doorstep? I do not have the soldiers to spare! */
UPDATE db_script_string SET content_loc7='Reza a tus dioses, chamán. Quizás vengan en tu ayuda. Sin embargo, la Horda no puede...' WHERE entry=2000000941; /* content: Pray to your gods, shaman. Perhaps they will come to your aid. The Horde, however, cannot... */
UPDATE db_script_string SET content_loc7='Todo está dispuesto, belisario.' WHERE entry=2000000942; /* content: Everything is in readiness, warbringer. */
UPDATE db_script_string SET content_loc7='El señor de fatalidad Kazzak estará satisfecho. Vas a aumentar el ritmo de tus ataques. Destruid a los orcos y los bastiones de los enanos se precipitarán.' WHERE entry=2000000943; /* content: Doom Lord Kazzak will be pleased. You are to increase the pace of your attacks. Destroy the orcish and dwarven strongholds with all haste. */
UPDATE db_script_string SET content_loc7='Belisario, eso requerirá el uso de todos los infernales del bastión. Puede que nos deje vulnerables ante un contraataque.' WHERE entry=2000000944; /* content: Warbringer, that will require the use of all the hold\'s infernals. It may leave us vulnerable to a counterattack. */
UPDATE db_script_string SET content_loc7='No te preocupes por eso. He aumentado la producción en la Forja Muerta. Tendrás a todos los infernales que quieras para que sigan tus órdenes. No fracases, Jovaan.' WHERE entry=2000000945; /* content: Don\'t worry about that. I\'ve increased production at the Deathforge. You\'ll have all the infernals you need to carry out your orders. Don\'t fail, Jovaan. */
UPDATE db_script_string SET content_loc7='Se hará como dices, belisario. Una última pregunta, si se me permite...' WHERE entry=2000000946; /* content: It shall be as you say, warbringer. One last question, if I may... */
UPDATE db_script_string SET content_loc7='¿Pero?' WHERE entry=2000000947; /* content: Yes? */
UPDATE db_script_string SET content_loc7='¿Qué hay en la caja?' WHERE entry=2000000948; /* content: What\'s in the crate? */
UPDATE db_script_string SET content_loc7='¿Caja? No te envié una caja, Jovaan. ¿No tienes cosas más importantes de las que preocuparte? ¡Ve a ocuparte de ellas!' WHERE entry=2000000949; /* content: Crate? I didn\'t send you a crate, Jovaan. Don\'t you have more important things to worry about? Go see to them! */
UPDATE db_script_string SET content_loc7='¡Si quieres la llave tendrás que cogerme!' WHERE entry=2000000950; /* content: If you want the key, you\'ll have to catch me! */
UPDATE db_script_string SET content_loc7='¡Cógeme si puedes! ¡Corro tan rápido como el viento!' WHERE entry=2000000951; /* content: Chase me if you dare! I run without a care! */
UPDATE db_script_string SET content_loc7='¿Por qué ibas a querer hacerme daño? ¡Vamos! ¡Podemos ser amigos!' WHERE entry=2000000952; /* content: Why would you ever want to harm me!? Come. Friends we can be! */
UPDATE db_script_string SET content_loc7='¿MORIR? ¡Estás haciendo llorar a Pusillín!' WHERE entry=2000000953; /* content: DIE?! You make Pusillin cry! */
UPDATE db_script_string SET content_loc7='¡Saluda a mis amiguitos!' WHERE entry=2000000954; /* content: Say hello to my little friends! */
UPDATE db_script_string SET content_loc7='¡Vale, allá voy!' WHERE entry=2000000956; /* content: Ok, here I go! */
UPDATE db_script_string SET content_loc7='¡Venga, en marcha!' WHERE entry=2000000961; /* content: Let\'s move forward! */
UPDATE db_script_string SET content_loc7='¡La piedra de hielo se ha derretido!' WHERE entry=2000000962; /* content: The Ice Stone has melted! */
UPDATE db_script_string SET content_loc7='¡Ahune, tu fuerza ya no aumenta!' WHERE entry=2000000963; /* content: Ahune, your strength grows no more! */
UPDATE db_script_string SET content_loc7='¡Tu reino helado no tendrá lugar!' WHERE entry=2000000964; /* content: Your frozen reign will not come to pass! */
UPDATE db_script_string SET content_loc7='¡Se está muriendo! La segunda parte de la Clave de Condenación es nuestra. Yo...' WHERE entry=2000000965; /* content: It is dying! The second part of the Cipher of Damnation is ours. I... */
UPDATE db_script_string SET content_loc7='Me desvanezco... Vuelve a Ar\'tor... Vuel... ve... a...' WHERE entry=2000000966; /* content: I am fading... Return to Ar\'tor... Ret... rn... to... */
UPDATE db_script_string SET content_loc7='Una voz tenebrosa y profunda surge de %s...' WHERE entry=2000000967; /* content: A deep, bone chilling voice echoes from the %s... */
UPDATE db_script_string SET content_loc7='¡Silencio! ¡Ahora llega la destrucción!' WHERE entry=2000000970; /* content: Be silent! The shattering is soon to come! */
UPDATE db_script_string SET content_loc7='¡Contempla el innegable poder de nuestro oscuro amo!' WHERE entry=2000000971; /* content: Bear witness to the undeniable power of our dark master! */
UPDATE db_script_string SET content_loc7='¡Con este don, arrasaré la tierra para recrearla!' WHERE entry=2000000972; /* content: With his gift, I shall raze this land and reform it! */
UPDATE db_script_string SET content_loc7='¡Mira! ¡Mira cómo se abre el suelo ante nosotros! ¡Mira cómo fluye la energía! Alimentará a nuestro ejército...' WHERE entry=2000000973; /* content: Watch! See the ground shatter before us! Watch as the energy flows! It will feed our armies... */
UPDATE db_script_string SET content_loc7='¡Nunca nos faltará poder! He asegurado nuestro futuro. ¡Someteos a mi gloria!' WHERE entry=2000000974; /* content: We will never be without power! I have secured our future! Bask in my glory! */
UPDATE db_script_string SET content_loc7='Ya... está... hecho...' WHERE entry=2000000975; /* content: It... is... done... */
UPDATE db_script_string SET content_loc7='¡Parte la tierra, Gul\'dan! ¡Corta el vínculo!' WHERE entry=2000000976; /* content: Tear the land asunder, Gul\'dan! Sever the tie! */
UPDATE db_script_string SET content_loc7='¡No lo hagas, Gul\'dan! ¡Los elementos no nos perdonarán nunca!' WHERE entry=2000000977; /* content: Do not do this, Gul\'dan! The elements will never forgive us! */
UPDATE db_script_string SET content_loc7='¡Nos han abandonado! ¡No olvides!' WHERE entry=2000000979; /* content: They have abandoned us! Do not forget! */
UPDATE db_script_string SET content_loc7='¡Tomaremos por la fuerza lo que la tierra nos niegue!' WHERE entry=2000000980; /* content: We will take from the land if it refuses to give! */
UPDATE db_script_string SET content_loc7='¡Para! ¡Detente de una vez!' WHERE entry=2000000981; /* content: Stop! You must stop! */
UPDATE db_script_string SET content_loc7='Nos has condenado a todos... Estamos perdidos.' WHERE entry=2000000982; /* content: You have damned us all... We are lost. */
UPDATE db_script_string SET content_loc7='¡A tus órdenes!' WHERE entry=2000000983; /* content: By your command! */
UPDATE db_script_string SET content_loc7='Espera aquí, $n. El robot espía hará salir a Lescovar lo antes posible. ¡Prepárate! Ataca solo después de escuchar su conversación.' WHERE entry=2000000984; /* content: Wait here, $N. Spybot will make Lescovar come out as soon as possible. Be ready! Attack only after you\'ve overheard their conversation. */
UPDATE db_script_string SET content_loc7='Que tengáis los dos un buen día. Me gustaría hablar con el señor Lescovar.' WHERE entry=2000000985; /* content: Good day to you both. I would speak to Lord Lescovar. */
UPDATE db_script_string SET content_loc7='Claro. Él te espera en la biblioteca.' WHERE entry=2000000986; /* content: Of course. He awaits you in the library. */
UPDATE db_script_string SET content_loc7='Gracias. Que la Luz os acompañe a los dos.' WHERE entry=2000000987; /* content: Thank you. The Light be with you both. */
UPDATE db_script_string SET content_loc7='Señor, su invitado ha llegado. Le está esperando.' WHERE entry=2000000988; /* content: Milord, your guest has arrived. He awaits your presence. */
UPDATE db_script_string SET content_loc7='Ah, muchas gracias. Te acompaño hasta la biblioteca mientras me ocupo de este asuntillo.' WHERE entry=2000000989; /* content: Ah, thank you kindly. I will leave you to the library while I tend to this small matter. */
UPDATE db_script_string SET content_loc7='He de meditar, dejadme a solas.' WHERE entry=2000000991; /* content: It\'s time for my meditation, leave me. */
UPDATE db_script_string SET content_loc7='¡Enseguida!' WHERE entry=2000000992; /* content: Yes, sir! */
UPDATE db_script_string SET content_loc7='%s espera hasta perder de vista a los guardias.' WHERE entry=2000000993; /* content: %s waits for the guards to be out of sight. */
UPDATE db_script_string SET content_loc7='Aquí estás. ¿Traes noticias de los Páramos de Poniente?' WHERE entry=2000000994; /* content: There you are. What news from Westfall? */
UPDATE db_script_string SET content_loc7='VanCleef manda decir que los planes siguen en pie. Sin embargo, ha oído rumores de que alguien anda husmeando por ahí.' WHERE entry=2000000995; /* content: VanCleef sends word that the plans are underway. But he\'s heard rumors about someone snooping about. */
UPDATE db_script_string SET content_loc7='Mmm, podría ser ese entrometido de Shaw. Veré lo que puedo descubrir. Y ahora, vete. Me pondré en contacto contigo pronto.' WHERE entry=2000000996; /* content: Hmm, it could be that meddler Shaw. I will see what I can discover. Be off with you. I\'ll contact you again soon. */
UPDATE db_script_string SET content_loc7='Muy bien. Entonces regresaré.' WHERE entry=2000000997; /* content: Very well. I will return then. */
UPDATE db_script_string SET content_loc7='¡Eso es! ¡Eso es lo que estabas esperando! ¡MÁTALOS!' WHERE entry=2000000998; /* content: That\'s it! That\'s what you were waiting for! KILL THEM! */
UPDATE db_script_string SET content_loc7='El rey debía llegar aquí en barco, en secreto. Pero el barco nunca llegó y el rey Wrynn desapareció.' WHERE entry=2000001001; /* content: The king was to travel here by ship, in secret. But the ship never arrived, and King Wrynn disappeared. */
UPDATE db_script_string SET content_loc7='Mientras tanto, el rey logró escapar de sus captores y regresó a Ventormenta para encargarse de Onyxia.' WHERE entry=2000001003; /* content: Meanwhile, the king managed to escape his captors, and returned to Stormwind to deal with Onyxia. */
UPDATE db_script_string SET content_loc7='Gracias, $n.' WHERE entry=2000001008; /* content: Thank you, $N. */
UPDATE db_script_string SET content_loc7='Tal vez debería explicar…' WHERE entry=2000001014; /* content: Perhaps I should explain... */
UPDATE db_script_string SET content_loc7='¡Ahora!' WHERE entry=2000001021; /* content: Go! */
UPDATE db_script_string SET content_loc7='Lamentable mortal, ¡Onyxia no responde ante nadie!' WHERE entry=2000001026; /* content: Pitiful mortal, Onyxia answers to no one! */
UPDATE db_script_string SET content_loc7='Tu patético reto no ha pasado desapercibido. Disfrutaré jugando contigo antes de que mueras.' WHERE entry=2000001027; /* content: Your pathetic challenge has not gone unnoticed. I shall enjoy toying with you before you die. */
UPDATE db_script_string SET content_loc7='¡Gracias! Mejor no pensar en lo que esos soldados me habrían hecho.' WHERE entry=2000001028; /* content: Thank you! There\'s no telling what those brutes would\'ve done to me. */
UPDATE db_script_string SET content_loc7='Podemos pasar de largo sin que nos vean. Mientras podamos abatir el ensamblaje frente a la puerta, debiéramos ser capaces de entrar.' WHERE entry=2000001029; /* content: We can sneak past them. As long as we can take down that construct in front of the gate, we should be able to get inside. */
UPDATE db_script_string SET content_loc7='Tossshtando trolesssshhh plagadossshh...' WHERE entry=2000001031; /* content: Shcourged trooolls roashting.... */
UPDATE db_script_string SET content_loc7='¡Eso es! Todo lo que tengo... ¿Qué te parece?' WHERE entry=2000001034; /* content: That\'s it! Far as I got... Whatd\'ya think? */
UPDATE db_script_string SET content_loc7='Esto no está nada bien, $n. Es la historia que me contó la domadora del viento Barah, Pezuña Tempestuosa era un héroe, no un villano.' WHERE entry=2000001042; /* content: This isn\'t right at all, $N. In the tale Wind Tamer Barah told me, Stormhoof was a hero, not a villain. */
UPDATE db_script_string SET content_loc7='El Viento del Norte es el ancestral enemigo de los taunka, un soberano entre los elementos y la fuerza que arrebata la vida a este antiguo pueblo.' WHERE entry=2000001043; /* content: The North Wind is the ancient enemy of the taunka, a ruler among the elements, and the very force which bleeds the life from these ancient people. */
UPDATE db_script_string SET content_loc7='Preferirían morir antes que inclinarse ante el Viento del Norte. Algo va mal aquí.' WHERE entry=2000001044; /* content: They would rather die than bow before the North Wind. Something is amiss here. */
UPDATE db_script_string SET content_loc7='El martillo de conocimiento...' WHERE entry=2000001045; /* content: The Lorehammer... */
UPDATE db_script_string SET content_loc7='Esto confirma nuestras sospechas. Los hilos del tiempo han sido cortados y vueltos a tejer con un patrón incorrecto. Pero... ¿cómo?' WHERE entry=2000001046; /* content: It confirms our suspicions. The threads of time have been severed and rewoven in the wrong pattern. But, how? */
UPDATE db_script_string SET content_loc7='¡AGH! ¡Vaya, me he atrapado yo sola! Es pegajoso. ¡MI PELO!' WHERE entry=2000001048; /* content: ACK! Oh no, I webbed myself! It\'s sticky. MY HAIR! */
UPDATE db_script_string SET content_loc7='Es solo...' WHERE entry=2000001061; /* content: It\'s just... */
UPDATE db_script_string SET content_loc7='Guardan sus armas en cajas con este aspecto. Búscalas cerca de los pabellones.' WHERE entry=2000001065; /* content: They keep their tools in boxes that look like this. Look for them near the pavilions. */
UPDATE db_script_string SET content_loc7='Solo voy a grabar las runas que creo que te proporcionarán el control de un gigante.' WHERE entry=2000001066; /* content: I\'m only going to carve the runes I think will give you command over a giant. */
UPDATE db_script_string SET content_loc7='Ahí está, la runa de mando... espero. No tan perversa como su prima enana férrea, pero funcional.' WHERE entry=2000001068; /* content: There we go, the Rune of Command... hopefully. Not nearly as vicious as its Iron Dwarf cousin, but functional. */
UPDATE db_script_string SET content_loc7='¡Hurra!' WHERE entry=2000001090; /* content: Hooray! */
UPDATE db_script_string SET content_loc7='¡GLORIA PARA LA ALIANZA!' WHERE entry=2000001094; /* content: GLORY TO THE ALLIANCE! */
UPDATE db_script_string SET content_loc7='¿¡Escabullirnos!? ¿Qué crees que somos, marmotas?' WHERE entry=2000001100; /* content: Sneak?! What do you think we are, marmots? */
UPDATE db_script_string SET content_loc7='Bien. Si nuestros aliados son los que van a ensuciarse las manos, dejaremos que sean ellos quienes decidan cómo proceder.' WHERE entry=2000001102; /* content: Fine. If our allies are going to be the ones getting their hands dirty, we\'ll leave it to them to decide how to proceed. */
UPDATE db_script_string SET content_loc7='¡¿Qué... Qué has hecho, $n?! ¡Brann! ¡Braaaaaannnn!' WHERE entry=2000001103; /* content: What... What did you just do, $n?! Brann! Braaaaannn! */
UPDATE db_script_string SET content_loc7='¡Brann! ¡$n acaba de activar el sistema de defensa orbital! ¡Si no nos largamos pronto de aquí, estamos fritos!' WHERE entry=2000001104; /* content: Brann! $n just activated the orbital defense system! If we don\'t get out of here soon, we\'re going to be toast! */
UPDATE db_script_string SET content_loc7='¡Magos del Kirin Tor, cuando Brann dé la orden, abran el escudo! ¡Defiendan esta plataforma y nuestros aliados con sus vidas! ¡Por Dalaran! ¡Por Dalaran!' WHERE entry=2000001108; /* content: Mages of the Kirin Tor, on Brann\'s Command, release the shield! Defend this platform and our allies with your lives! For Dalaran! */
UPDATE db_script_string SET content_loc7='Nuestros aliados están preparados. ¡Desactiven el escudo y abran paso!' WHERE entry=2000001109; /* content: Our allies are ready. Bring down the shield and make way! */
UPDATE db_script_string SET content_loc7='¡Lo logramos! Llegamos antes que Algalon. Tal vez podamos alterar el sistema para interferir en su análisis…' WHERE entry=2000001111; /* content: We did it, lads! We got here before Algalon\'s arrival. Maybe we can rig the systems to interfere with his analysis-- */
UPDATE db_script_string SET content_loc7='Volveré al Archivum y trataré de interferir su señal. Tal vez nos de algo de tiempo mientras ustedes se ocupan de él.' WHERE entry=2000001112; /* content: I\'ll head back to the Archivum and see if I can jam his signal. I might be able to buy us some time while you take care of him. */
UPDATE db_script_string SET content_loc7='¡La temperatura es de 122 grados Kraklenheit!' WHERE entry=2000001113; /* content: The temperature is 122 degrees Kraklenheit. */
UPDATE db_script_string SET content_loc7='¡La temperatura es de 9,280 grados Kraklenheit! ¡Es muy alta!' WHERE entry=2000001114; /* content: The temperature is 9280 degrees Kraklenheit! That\'s HOT! */
UPDATE db_script_string SET content_loc7='¡BINGO! ¡Exactamente 428,000 grados Kraklenheit! Bueno... aproximadamente. Casi. Más o menos...' WHERE entry=2000001116; /* content: DING! 428,000 degrees Kraklenheit, exactly! Well, approximately. Almost. Somewhere around there... */
UPDATE db_script_string SET content_loc7='¡Oye! ¡Deja de merodear por mi sótano! ¡Lárgate!' WHERE entry=2000001119; /* content: Hey! Stop rooting around in my cellar! Clear out! */
UPDATE db_script_string SET content_loc7='¿Qué estabas haciendo en mi sótano? ¡Hay problemas con la comida y lo último que necesito son extraños enredando en mis bienes! ¡Fuera!' WHERE entry=2000001120; /* content: What were you doing in my cellar? There\'s a food scare going on, and the last thing I need is strangers rummaging around in my goods! Shoo! */
UPDATE db_script_string SET content_loc7='Toda esta situación resulta un poco paranoica, ¿no te parece?' WHERE entry=2000001121; /* content: This whole situation seems a bit paranoid, don\'t you think? */
UPDATE db_script_string SET content_loc7='Las órdenes son las órdenes. Si el Príncipe dice salta...' WHERE entry=2000001122; /* content: Orders are orders. If the Prince says jump... */
UPDATE db_script_string SET content_loc7='No puedes negar que es una orden rara. ¿Comida sospechosa? ¡Basándote en esa definición, deberías arrestar a Belfast!' WHERE entry=2000001123; /* content: It\'s a strange order, you can\'t deny. Suspicious food? Under that definition, you should arrest Belfast! */
UPDATE db_script_string SET content_loc7='¡TE HE OÍDO! ¡Se acabó la cerveza para ti! ¡Ni una gota más!' WHERE entry=2000001124; /* content: I HEARD THAT! No more ale for you! Not a drop! */
UPDATE db_script_string SET content_loc7='Ya basta, Michael. El negocio ya está sufriendo bastante con este susto. Podemos usar hasta la última moneda de cobre.' WHERE entry=2000001125; /* content: Enough, Michael. Business is hurting enough with this scare as it is. We can use every copper. */
UPDATE db_script_string SET content_loc7='Los soldados están realizando un gran trabajo. Mal, si lo que te interesa es que tus clientes vivan otro día más, la seguridad del pueblo es lo más importante.' WHERE entry=2000001126; /* content: The soldiers are doing important work. The safety of the people is more important, Mal, if you\'re interested in your customers living to spend another day. */
UPDATE db_script_string SET content_loc7='%s asiente a regañadientes.' WHERE entry=2000001127; /* content: %s grudgingly nods. */
UPDATE db_script_string SET content_loc7='Eso no lo puedo discutir.' WHERE entry=2000001128; /* content: I can\'t argue with that. */
UPDATE db_script_string SET content_loc7='No te preocupes demasiado. Para cuando terminé la guardia no habíamos encontrado ni un poquito de ese grano.' WHERE entry=2000001129; /* content: Don\'t worry too much. By the time I went off duty, we hadn\'t found a scrap of befouled grain here. */
UPDATE db_script_string SET content_loc7='Gracias a la Luz.' WHERE entry=2000001130; /* content: Thank the Light for that. */
UPDATE db_script_string SET content_loc7='%s asiente.' WHERE entry=2000001131; /* content: %s nods. */
UPDATE db_script_string SET content_loc7='¡Hombre! ¡Hola de nuevo!' WHERE entry=2000001132; /* content: Why, hello again! */
UPDATE db_script_string SET content_loc7='Vale, basta de trabajo por el momento. Es hora de tomar unos refrescos y charlar un rato en la posada.' WHERE entry=2000001133; /* content: Ok, enough work for now. Time for refreshments and a little conversation in the inn. */
UPDATE db_script_string SET content_loc7='Espera, ¿qué es ese olor?' WHERE entry=2000001134; /* content: Wait, what\'s that smell? */
UPDATE db_script_string SET content_loc7='No puedo ser yo. ¡Me bañé hace tres días!' WHERE entry=2000001135; /* content: Can\'t be me, I took a bath 3 days ago! */
UPDATE db_script_string SET content_loc7='Oh, por los pelos. Solo es el grano.' WHERE entry=2000001136; /* content: Oh, close call. It\'s just the grain here. */
UPDATE db_script_string SET content_loc7='Espera un segundo. ¡Se supone que el grano no debería oler ASÍ! Será mejor que vaya a buscar a un guardia.' WHERE entry=2000001137; /* content: Wait a second. Grain isn\'t supposed to smell like THAT! I better go find a guard. */
UPDATE db_script_string SET content_loc7='¿No te importa que eche un ojo a tu mercancía por si ha sido manipulada?' WHERE entry=2000001138; /* content: You don\'t mind me checking out your merchandise for signs of tampering, do you? */
UPDATE db_script_string SET content_loc7='No, Sargento.' WHERE entry=2000001139; /* content: No, Sergeant. */
UPDATE db_script_string SET content_loc7='Un momento, ¿qué es esto? ¡Me has mentido, Perelli!' WHERE entry=2000001140; /* content: Wait, what is this? You\'ve been holding out on me, Perelli! */
UPDATE db_script_string SET content_loc7='¡De qué habla, Sargento!' WHERE entry=2000001141; /* content: What are you talking about, Sergeant! */
UPDATE db_script_string SET content_loc7='Voy a confiscar este grano sospechoso, Perelli. Buscábamos comida manipulada y será mejor que no hagas tonterías mientras el príncipe Arthas comprueba esto.' WHERE entry=2000001142; /* content: I\'m confiscating this suspicious grain, Perelli. We were looking for signs of tampered food, and it would be in your best interest to stay put while Prince Arthas checks this out. */
UPDATE db_script_string SET content_loc7='Tienes que creerme, ¡soy inocente!' WHERE entry=2000001143; /* content: You have to believe me, I\'m innocent! */
UPDATE db_script_string SET content_loc7='Ya veremos, Perelli. Ya veremos.' WHERE entry=2000001144; /* content: We\'ll see about that, Perelli. We\'ll see about that. */
UPDATE db_script_string SET content_loc7='Martha, no me queda harina para hacer pan. ¿No tendrás algo de grano de ese envío que ha llegado hace poco?' WHERE entry=2000001145; /* content: Martha, I\'m out of flour for bread. You wouldn\'t happen to have any grain from that recent shipment, would you? */
UPDATE db_script_string SET content_loc7='Oh, hola, Jena. Claro que puedes tomar prestado algo de grano. Cógelo.' WHERE entry=2000001146; /* content: Oh hello, Jena. Of course you can borrow some grain. Help yourself. */
UPDATE db_script_string SET content_loc7='¡Gracias, Martha! Te debo una.' WHERE entry=2000001147; /* content: Thanks, Martha! I owe you one. */
UPDATE db_script_string SET content_loc7='¡Martha, a este grano le pasa algo raro! Algunos de los soldados del Príncipe lo estaban buscando. Voy a buscar a uno.' WHERE entry=2000001148; /* content: Martha, something\'s wrong with this grain! Some of the Prince\'s soldiers were looking for this. I\'m going to go look for one. */
UPDATE db_script_string SET content_loc7='Oh, cielos.' WHERE entry=2000001149; /* content: Oh, dear. */
UPDATE db_script_string SET content_loc7='Oh, no.' WHERE entry=2000001150; /* content: Oh, my. */
UPDATE db_script_string SET content_loc7='Parece que va a haber tormenta, Desaliñado...' WHERE entry=2000001151; /* content: Looks like a storm\'s coming in, Scruffy... */
UPDATE db_script_string SET content_loc7='%s comienza a gruñir...' WHERE entry=2000001152; /* content: %s begins to growl... */
UPDATE db_script_string SET content_loc7='¿Qué pasa, colega?' WHERE entry=2000001153; /* content: What\'s wrong, pal? */
UPDATE db_script_string SET content_loc7='¿Qué has encontrado, chico?' WHERE entry=2000001154; /* content: What did you find, boy? */
UPDATE db_script_string SET content_loc7='Esto no tiene buena pinta, Desaliñado. Quédate aquí y cuida de la casa, tengo que encontrar a un soldado.' WHERE entry=2000001155; /* content: This is no good, Scruffy. Stay here and guard the house, I need to go find a soldier. */
UPDATE db_script_string SET content_loc7='¡Este envío de grano no nos ha dado más que problemas!' WHERE entry=2000001156; /* content: This grain shipment has been nothing but trouble! */
UPDATE db_script_string SET content_loc7='¡Voy a perder mi bonificación de puntualidad por esto!' WHERE entry=2000001157; /* content: I\'m going to lose my on time bonus because of this! */
UPDATE db_script_string SET content_loc7='¡Ni siquiera debería estar aquí hoy!' WHERE entry=2000001158; /* content: I wasn\'t even supposed to be here today! */
UPDATE db_script_string SET content_loc7='Bueno, supongo que debería volver a cargar todo en la vagoneta.' WHERE entry=2000001159; /* content: Well, guess I should load everything back into the cart. */
UPDATE db_script_string SET content_loc7='¡Oh, vamos! Se me ha estropeado la vagoneta, mi caballo ha perdido una herradura ¡y ahora la mercancía se me estropea!' WHERE entry=2000001160; /* content: Oh, come on! My cart broke, my horse lost a shoe, and now the cargo goes bad! */
