UPDATE `db_script_string` SET `content_loc7`='[Fúrbolg] ¡Aquellos que permanezcan en el Bastión Semprepino te darán la bienvenida como a un héroe!' WHERE `entry`=2000000495;
UPDATE `db_script_string` SET `content_loc7`='[Fúrbolg] ¡Los fúrbolgs de Semprepino tardarán en olvidar tu valentía!' WHERE `entry`=2000000496;
UPDATE `db_script_string` SET `content_loc7`='Sentía que parte de él seguía vivo. Atrapado... luchando... ¡Oh, Arthas!' WHERE `entry`=2000000500;
UPDATE `db_script_string` SET `content_loc7`='Quizá, quizá un día podrá recordar quién fue una vez.' WHERE `entry`=2000000501;
UPDATE `db_script_string` SET `content_loc7`='Por la Luz, que encuentre reposo, libre de la garra gélida de esa terrible espada.' WHERE `entry`=2000000502;
UPDATE `db_script_string` SET `content_loc7`='Está listo, entonces.' WHERE `entry`=2000000503;
UPDATE `db_script_string` SET `content_loc7`='No me atrevería a confiar en mis sentidos; no sería la primera vez que el Rey Exánime me hace quedar como una tonta.' WHERE `entry`=2000000504;
UPDATE `db_script_string` SET `content_loc7`='Al fin se le ha hecho pagar por las atrocidades que desató sobre mi gente.' WHERE `entry`=2000000505;
UPDATE `db_script_string` SET `content_loc7`='Que Azeroth nunca olvide el precio terrible que hemos pagado por nuestra debilidad... por nuestro orgullo.' WHERE `entry`=2000000506;
UPDATE `db_script_string` SET `content_loc7`='Pero ¿y ahora qué, héroe? ¿Qué pasará con esos que liberaste de su control, pero siguen condenados a vivir en la muerte?' WHERE `entry`=2000000507;
UPDATE `db_script_string` SET `content_loc7`='Tengo mucho que reflexionar.' WHERE `entry`=2000000509;
UPDATE `db_script_string` SET `content_loc7`='Héroe de Azeroth, me has dado un obsequio más grande de lo que crees.' WHERE `entry`=2000000511;
UPDATE `db_script_string` SET `content_loc7`='Mucho tiempo he luchado por perdonar al príncipe por sus terribles transgresiones.' WHERE `entry`=2000000512;
UPDATE `db_script_string` SET `content_loc7`='Mi alma ha sido devastada con una ansiedad insoportable, con pensamientos oscuros... que me alejaron de la Luz.' WHERE `entry`=2000000513;
UPDATE `db_script_string` SET `content_loc7`='Recuerdo claramente el brillo de orgullo en sus ojos cuando se paraba frente a mí, ansioso por vencer a los enemigos de la Luz…' WHERE `entry`=2000000514;
UPDATE `db_script_string` SET `content_loc7`='Ansioso por defender a su gente, sin importar el costo...' WHERE `entry`=2000000515;
UPDATE `db_script_string` SET `content_loc7`='Es esta memoria de Arthas la que elijo guardar en mi corazón.' WHERE `entry`=2000000516;
UPDATE `db_script_string` SET `content_loc7`='Estaré en deuda contigo por siempre, amigo.' WHERE `entry`=2000000517;
UPDATE `db_script_string` SET `content_loc7`='Te lo agradezco.' WHERE `entry`=2000000518;
UPDATE `db_script_string` SET `content_loc7`='Oh, muchacho...' WHERE `entry`=2000000519;
UPDATE `db_script_string` SET `content_loc7`='Cómo extraño esos días eternos en Lordaeron...' WHERE `entry`=2000000520;
UPDATE `db_script_string` SET `content_loc7`='…afinando tu habilidad con esa espada roma…' WHERE `entry`=2000000521;
UPDATE `db_script_string` SET `content_loc7`='...moldeándote en un arma que debía soportar las exigencias de un gran destino.' WHERE `entry`=2000000522;
UPDATE `db_script_string` SET `content_loc7`='Sí... Sí, utilizaste bien esas habilidades, ¿verdad, muchacho?' WHERE `entry`=2000000523;
UPDATE `db_script_string` SET `content_loc7`='Si hubiera podido pararte aquel día, qué diferentes habrían sido las cosas.' WHERE `entry`=2000000524;
UPDATE `db_script_string` SET `content_loc7`='Si nunca hubiera descubierto esa maldita espada...' WHERE `entry`=2000000525;
UPDATE `db_script_string` SET `content_loc7`='Adiós, Arthas... hermano mío.' WHERE `entry`=2000000526;
UPDATE `db_script_string` SET `content_loc7`='Darion, hijo mío.' WHERE `entry`=2000000527;
UPDATE `db_script_string` SET `content_loc7`='Por fin puedo verte otra vez.' WHERE `entry`=2000000528;
UPDATE `db_script_string` SET `content_loc7`='El Rey Exánime me atormentó sin descanso, Darion.' WHERE `entry`=2000000529;
UPDATE `db_script_string` SET `content_loc7`='Sin fin, buscó destruir mi voluntad para obligarme a servirle y vincularme a su espada…' WHERE `entry`=2000000530;
UPDATE `db_script_string` SET `content_loc7`='Al final, cuando la situación reclamó toda su atención, me dejó.' WHERE `entry`=2000000531;
UPDATE `db_script_string` SET `content_loc7`='El pensamiento al que me aferraba, Darion... El recuerdo que evitó que cediera...' WHERE `entry`=2000000532;
UPDATE `db_script_string` SET `content_loc7`='Fue tu sacrificio, hijo mío, que de nuevo me salvó del peligro eterno.' WHERE `entry`=2000000533;
UPDATE `db_script_string` SET `content_loc7`='Padre...' WHERE `entry`=2000000534;
UPDATE `db_script_string` SET `content_loc7`='Padre. Temía por tu... tu cordura.' WHERE `entry`=2000000535;
UPDATE `db_script_string` SET `content_loc7`='Padre... por ti, daría mi vida mil veces.' WHERE `entry`=2000000536;
UPDATE `db_script_string` SET `content_loc7`='¡%s olfatea el elixir y luego lo apura con avidez!' WHERE `entry`=2000000537;
UPDATE `db_script_string` SET `content_loc7`='Y gracias, $N. Sin tu ayuda, jamás habría encontrado el camino hasta los Renegados.' WHERE `entry`=2000000538;
UPDATE `db_script_string` SET `content_loc7`='Adiós, $Gamigo mío:amiga mía;.' WHERE `entry`=2000000539;
UPDATE `db_script_string` SET `content_loc7`='Cuanto tiempo, mi querido amigo Bethor.' WHERE `entry`=2000000540;
UPDATE `db_script_string` SET `content_loc7`='Si el tiempo lo permite tenemos que hablar con calma. Hay mucho que discutir.' WHERE `entry`=2000000541;
UPDATE `db_script_string` SET `content_loc7`='%s llama a un mensajero...' WHERE `entry`=2000000542;
UPDATE `db_script_string` SET `content_loc7`='%s lleva el mensaje a la boticaria Zamah.' WHERE `entry`=2000000544;
UPDATE `db_script_string` SET `content_loc7`='¡No espere! ¡Encárguelo ahora y reciba mi Gofrera Ultra-Cinética sin coste adicional!' WHERE `entry`=2000000545;
UPDATE `db_script_string` SET `content_loc7`='¡Pero este es el único edificio que sirve a Brogg! ¡Los goblins hacen los edificios muy pequeños!' WHERE `entry`=2000000546;
UPDATE `db_script_string` SET `content_loc7`='Creía que ya habíamos hablado de esto, Brogg. No puedes quedarte en este edificio. Claro que puede cobijarte, pero no queda sitio para nadie más.' WHERE `entry`=2000000547;
UPDATE `db_script_string` SET `content_loc7`='Sí, dijimos que podías quedarte aquí, ¡pero este edificio es nuestro cuartel general! ¡Todos nuestros manuales y anteproyectos están guardados aquí!' WHERE `entry`=2000000548;
UPDATE `db_script_string` SET `content_loc7`='¡Me atrevería a decir que hicieron a los ogros demasiado grandes! ¡Veo que no voy a convencerte de que te mudes, pero no creas que esto es permanente!' WHERE `entry`=2000000549;
UPDATE `db_script_string` SET `content_loc7`='¡Dijiste que Brogg podía quedarse! ¡Prometiste a Brogg que ayudarías a recuperar el montículo del clan Quebrantarrocas!' WHERE `entry`=2000000550;
UPDATE `db_script_string` SET `content_loc7`='En medio del Cataclismo, Ameth\'Aran quedó a merced de la voluntad del terror que subyugaba la tierra. Había pocas esperanzas de que sobreviviera.' WHERE `entry`=2000000551;
UPDATE `db_script_string` SET `content_loc7`='Athrikus vino a vernos. Nos dijo que podía protegernos del peligro. Nos lanzó un hechizo para protegernos del peligro.' WHERE `entry`=2000000552;
UPDATE `db_script_string` SET `content_loc7`='Cuando acabaron las sacudidas, se revelaron sus verdaderos motivos. Quedamos atrapados y él poco a poco fue absorbiendo nuestros poderes.' WHERE `entry`=2000000553;
UPDATE `db_script_string` SET `content_loc7`='Cientos fueron encarcelados por obra de este hechizo. Ahora solo unos cuantos permanecen en sus prisiones. Él hablaba a veces con nosotros.' WHERE `entry`=2000000554;
UPDATE `db_script_string` SET `content_loc7`='Le preocupaba que su poder se debilitara, que el brillo de sus valiosas gemas de almas desapareciera para siempre.' WHERE `entry`=2000000555;
UPDATE `db_script_string` SET `content_loc7`='Recibo más datos de la nada.' WHERE `entry`=2000000556;
UPDATE `db_script_string` SET `content_loc7`='Mmmm... ¿Dónde está mi caña de pescar? Oh, ahí está.' WHERE `entry`=2000000557;
UPDATE `db_script_string` SET `content_loc7`='Veamos si los peces pican...' WHERE `entry`=2000000558;
UPDATE `db_script_string` SET `content_loc7`='Mmmm... Creo que necesito preparar cebos. Iré a mirar en el vagón de mi padre.' WHERE `entry`=2000000559;
UPDATE `db_script_string` SET `content_loc7`='¡Desechos de jabaespín! Eso debería de servir. A los peces les encantan los jabaespines.' WHERE `entry`=2000000560;
UPDATE `db_script_string` SET `content_loc7`='¡He cogido uno! Voy a ver si alguno de los guardias tiene hambre. ¡Vamos, Colmillo!' WHERE `entry`=2000000561;
UPDATE `db_script_string` SET `content_loc7`='He pescado un pez... Si tienes hambre' WHERE `entry`=2000000562;
UPDATE `db_script_string` SET `content_loc7`='¡Muy bien hecho! ¡Traes honor a la Horda, chico, te lo agradecemos!' WHERE `entry`=2000000563;
UPDATE `db_script_string` SET `content_loc7`='¿Quiere algo más de comer, señor?' WHERE `entry`=2000000564;
UPDATE `db_script_string` SET `content_loc7`='¡Más pan, chico!' WHERE `entry`=2000000565;
UPDATE `db_script_string` SET `content_loc7`='Ahora mismo, señor.' WHERE `entry`=2000000566;
UPDATE `db_script_string` SET `content_loc7`='Aquí está su pan, señor.' WHERE `entry`=2000000567;
UPDATE `db_script_string` SET `content_loc7`='Aaaah... Muy bien. ¡Ahora, fuera!' WHERE `entry`=2000000568;
UPDATE `db_script_string` SET `content_loc7`='Como desees, señor.' WHERE `entry`=2000000569;
UPDATE `db_script_string` SET `content_loc7`='¿Puedo ir a buscar más piedras para mi collar, papá?' WHERE `entry`=2000000570;
UPDATE `db_script_string` SET `content_loc7`='Puedes, Okla. Pero no te alejes demasiado de mí.' WHERE `entry`=2000000571;
UPDATE `db_script_string` SET `content_loc7`='¡Dabu! ¡Tendré cuidado, papá!' WHERE `entry`=2000000572;
UPDATE `db_script_string` SET `content_loc7`='Ooohh... Esta piedra es bonita y me gusta mucho el color. A ver si puedo encontrar más iguales...' WHERE `entry`=2000000573;
UPDATE `db_script_string` SET `content_loc7`='Mmm... Esa piedra es bonita, pero demasiado pequeña.' WHERE `entry`=2000000574;
UPDATE `db_script_string` SET `content_loc7`='Seguro que a mi amigo Gruk le gustaría esta piedra para la lanza que está haciendo. Es muy afilada... Espero que le guste.' WHERE `entry`=2000000576;
UPDATE `db_script_string` SET `content_loc7`='Ya está... Con esto debería tener suficientes piedras para hacer un collar. De todas formas, ya debería volver a casa...' WHERE `entry`=2000000577;
UPDATE `db_script_string` SET `content_loc7`='¡Pagarás por este insulto, $C!' WHERE `entry`=2000000578;
UPDATE `db_script_string` SET `content_loc7`='La Cruzada Argenta te invita a que pongas a prueba tus habilidades en honorable combate contra otros héroes de Azeroth en el Torneo Argenta.' WHERE `entry`=2000000580;
UPDATE `db_script_string` SET `content_loc7`='Los campeones de las grandes naciones del mundo se han reunido en los Campos del Torneo Argenta al noreste de Corona de Hielo para enfrentarse a todos los contendientes.' WHERE `entry`=2000000581;
UPDATE `db_script_string` SET `content_loc7`='Llegas tarde, sobrestante.' WHERE `entry`=2000000582;
UPDATE `db_script_string` SET `content_loc7`='El destino no esperará. Tus artesanos han de incrementar la producción de gólems de piedra antes de que el anfitrión gigante de piedra llegue a Ulduar.' WHERE `entry`=2000000583;
UPDATE `db_script_string` SET `content_loc7`='Di a tus forjadores de runas que continúen transformando a tantos gigantes de piedra como puedan. Los que no se dobleguen deberán ser destruidos.' WHERE `entry`=2000000584;
UPDATE `db_script_string` SET `content_loc7`='Si los gigantes de piedra interfieren con nuestro trabajo en Ulduar, os haré responsables a ti y a tu señor feudal. No se debe permitir a los mortales que ayuden a los gigantes.' WHERE `entry`=2000000585;
UPDATE `db_script_string` SET `content_loc7`='Vuelve a tu trabajo, sobrestante. Asegúrate de transmitir a tus trabajadores la urgencia de sus tareas.' WHERE `entry`=2000000586;
UPDATE `db_script_string` SET `content_loc7`='Así que el Oráculo ha decidido ayudarte, $Gforastero:forastera;. Muy bien, te ofreceré mi conocimiento.' WHERE `entry`=2000000587;
UPDATE `db_script_string` SET `content_loc7`='Tu compañero busca al patriarca de los trozocuerno, Farunn, quien se mantiene apartado de sus hermanos.' WHERE `entry`=2000000588;
UPDATE `db_script_string` SET `content_loc7`='Vuelve con tu compañero. Dile que continúe y reclame su premio con Farunn.' WHERE `entry`=2000000590;
UPDATE `db_script_string` SET `content_loc7`='Cuando haya hecho eso, dile que deje esta tierra en paz. No pinta nada aquí.' WHERE `entry`=2000000591;
UPDATE `db_script_string` SET `content_loc7`='[Fúrbolg] Doy fe de los actos salvajes de nuestros adversarios, $N. Roban a nuestros jóvenes... nuestros débiles... Encarcelados aquí, están a merced de los ardides de Brazolanudo. ¡Libéralos, $GPrometido:Prometida;!' WHERE `entry`=2000000592;
UPDATE `db_script_string` SET `content_loc7`='Nunca he entendido qué es, pero hay algo muy extraño en las fuentes termales...' WHERE `entry`=2000000593;
UPDATE `db_script_string` SET `content_loc7`='%s sumerge la espada en las aguas poco profundas.' WHERE `entry`=2000000594;
UPDATE `db_script_string` SET `content_loc7`='He descubierto que si arrojo algo al agua mejora de forma increíble cuando lo vuelvo a sacar.' WHERE `entry`=2000000595;
UPDATE `db_script_string` SET `content_loc7`='Bien, sea lo que sea, ¡funciona bastante bien!' WHERE `entry`=2000000596;
UPDATE `db_script_string` SET `content_loc7`='Y no descansaré hasta llegar al final de este asunto... Así que si necesitas mi ayuda de nuevo, ya sabes dónde encontrarme.' WHERE `entry`=2000000597;
UPDATE `db_script_string` SET `content_loc7`='Por favor, sígueme.' WHERE `entry`=2000000598;
UPDATE `db_script_string` SET `content_loc7`='¿Eso son jacintos de invierno? ¿Para mí?' WHERE `entry`=2000000599;
UPDATE `db_script_string` SET `content_loc7`='Ha pasado mucho tiempo desde que un viajero trajo flores aquí.' WHERE `entry`=2000000600;
UPDATE `db_script_string` SET `content_loc7`='El lago ha estado muy solitario estos últimos años. Los viajeros dejaron de venir y el mal se apoderó de estas aguas.' WHERE `entry`=2000000601;
UPDATE `db_script_string` SET `content_loc7`='Tu don es un extraño favor, caminante. Por favor, toma esta hoja con mi gratitud. Hace mucho otro viajero la dejó aquí, pero a mí no me sirve de mucho.' WHERE `entry`=2000000602;
UPDATE `db_script_string` SET `content_loc7`='No me ha traído más que desgracias. Quizá encuentres a alguien que pueda contener su poder.' WHERE `entry`=2000000604;
UPDATE `db_script_string` SET `content_loc7`='¡Cría traidora! ¡Sironas te destruirá a ti y a tu pueblo!' WHERE `entry`=2000000606;
UPDATE `db_script_string` SET `content_loc7`='Mi hermano, Pezuña Tempestuosa, era mucho mejor guerrero que yo. Mientras yo estudiaba para ser cabecilla de nuestro clan, él se preparaba para una misión mucho mayor.' WHERE `entry`=2000000607;
UPDATE `db_script_string` SET `content_loc7`='¿Cómo puede estar él en peligro? Lo único que quería era darnos poder sobre los elementos que hacen que la vida sea dura para nosotros aquí.' WHERE `entry`=2000000608;
UPDATE `db_script_string` SET `content_loc7`='Buscaba un artefacto... un cuerno, creo. Dejó el pueblo para realizar un largo viaje en su busca.' WHERE `entry`=2000000609;
UPDATE `db_script_string` SET `content_loc7`='Sé que un temible enemigo le persiguió después de que consiguiera el cuerno, pero... no recuerdo qué pasó... después.' WHERE `entry`=2000000610;
UPDATE `db_script_string` SET `content_loc7`='Es como si mis recuerdos estuvieran cubiertos de niebla. Ni siquiera puedo recordar qué le pasó a mi hermano. ¿Por esto dices que está en peligro?' WHERE `entry`=2000000611;
UPDATE `db_script_string` SET `content_loc7`='Las alteraciones en la tumba... ellos tienen que estar involucrados. Mira alrededor, $N. ¿Los ves?' WHERE `entry`=2000000612;
UPDATE `db_script_string` SET `content_loc7`='Sí, es culpa suya, pero no tengo poder para detenerlos. ¿Avisarás a la gente de Tunka\'lo de que su pasado y sus ancestros están en peligro, $Gforastero:forastera;?' WHERE `entry`=2000000613;
UPDATE `db_script_string` SET `content_loc7`='El martillo de conocimiento está callado. Esto no es de la época de Pezuña Tempestuosa.' WHERE `entry`=2000000615;
UPDATE `db_script_string` SET `content_loc7`='Buena suerte... ¡la necesitarás!' WHERE `entry`=2000000616;
UPDATE `db_script_string` SET `content_loc7`='¡Que gane la mejor!' WHERE `entry`=2000000617;
UPDATE `db_script_string` SET `content_loc7`='¡No me derrotarás!' WHERE `entry`=2000000618;
UPDATE `db_script_string` SET `content_loc7`='¡No eres $Gdigno:digna; de Thorim!' WHERE `entry`=2000000619;
UPDATE `db_script_string` SET `content_loc7`='Vislumbrando muchas situaciones insignificantes en Corona de Hielo, te topas con algo interesante...' WHERE `entry`=2000000620;
UPDATE `db_script_string` SET `content_loc7`='El cultor está que echa chispas.' WHERE `entry`=2000000621;
UPDATE `db_script_string` SET `content_loc7`='Mi señora.' WHERE `entry`=2000000622;
UPDATE `db_script_string` SET `content_loc7`='Informa.' WHERE `entry`=2000000623;
UPDATE `db_script_string` SET `content_loc7`='Hay noticias de Jotunheim. Los vigías del sueño de esa zona creen que han encontrado algo importante.' WHERE `entry`=2000000624;
UPDATE `db_script_string` SET `content_loc7`='Describe a ese vrykul. ¿Qué aspecto tenía?' WHERE `entry`=2000000625;
UPDATE `db_script_string` SET `content_loc7`='¿Cómo, mi señora? Un vrykul, supongo. A mí no me lo mostraron. Desde La Cámara de las Sombras...' WHERE `entry`=2000000626;
UPDATE `db_script_string` SET `content_loc7`='No me interesan las excusas. ¿Te dieron un nombre?' WHERE `entry`=2000000627;
UPDATE `db_script_string` SET `content_loc7`='¿Un nombre? ¡Ah, claro, su nombre! Creo que se llamaba Iskalder.' WHERE `entry`=2000000628;
UPDATE `db_script_string` SET `content_loc7`='Vuelve a Jotunheim y diles que lo mantengan dormido hasta que yo llegue. Juzgaré a ese vrykul con mis propios ojos.' WHERE `entry`=2000000630;
UPDATE `db_script_string` SET `content_loc7`='Enseguida, mi señora.' WHERE `entry`=2000000631;
UPDATE `db_script_string` SET `content_loc7`='Morae... ¿Eres tú?' WHERE `entry`=2000000632;
UPDATE `db_script_string` SET `content_loc7`='No... No eres Morae, pero siento que os conocéis. Cuando la veas, dile que sobreviví al accidente para caer en manos de los infiltradores.' WHERE `entry`=2000000633;
UPDATE `db_script_string` SET `content_loc7`='Hazle saber... que mi último pensamiento fue para ella...' WHERE `entry`=2000000634;
UPDATE `db_script_string` SET `content_loc7`='¡Grek probar!' WHERE `entry`=2000000635;
UPDATE `db_script_string` SET `content_loc7`='¡Este aceite no vale para Grek! ¿Qué crees que es Grek? ¿Un debilucho con túnica?' WHERE `entry`=2000000636;
UPDATE `db_script_string` SET `content_loc7`='%s ríe.' WHERE `entry`=2000000637;
UPDATE `db_script_string` SET `content_loc7`='¿Te asusta saber que hay quienes son capaces de servir a la Legión con tal fervor y devoción que esperan la llamada actuando como ciudadanos ejemplares y sin descubrirse?' WHERE `entry`=2000000638;
UPDATE `db_script_string` SET `content_loc7`='Vivir con miedo, morir por voluntad de Kael\'thas... Es lo mismo.' WHERE `entry`=2000000639;
UPDATE `db_script_string` SET `content_loc7`='¡Y ahora, te rajo!' WHERE `entry`=2000000640;
UPDATE `db_script_string` SET `content_loc7`='¡Mueran!' WHERE `entry`=2000000641;
UPDATE `db_script_string` SET `content_loc7`='%s coge la bandera naga.' WHERE `entry`=2000000642;
UPDATE `db_script_string` SET `content_loc7`='¡No fracasaremos!' WHERE `entry`=2000000643;
UPDATE `db_script_string` SET `content_loc7`='%s coloca el cajón con las piezas el suelo.' WHERE `entry`=2000000644;
UPDATE `db_script_string` SET `content_loc7`='%s coge una pieza y la guarda en el bolsillo...' WHERE `entry`=2000000645;
UPDATE `db_script_string` SET `content_loc7`='Ya está, esto deberá servir...' WHERE `entry`=2000000646;
UPDATE `db_script_string` SET `content_loc7`='¿Anaia?... ¿Me engañan mis ojos? ¿Eres tú de verdad?' WHERE `entry`=2000000647;
UPDATE `db_script_string` SET `content_loc7`='Él ha sido cruel con nosotros, amor mío, pero ten por seguro que ahora sí permaneceremos unidos.' WHERE `entry`=2000000648;
UPDATE `db_script_string` SET `content_loc7`='El destino ha sido muy cruel al permitirnos volver a estar juntos solo tras miles de años de separación...' WHERE `entry`=2000000649;
UPDATE `db_script_string` SET `content_loc7`='¿Me odias, amor mío? Por haber tenido que destruir tu forma viviente y por liberar tu espíritu de tan triste cautiverio...' WHERE `entry`=2000000650;
UPDATE `db_script_string` SET `content_loc7`='No permitas que esto atormente tu corazón, amor mío. Me has liberado de la esclavitud y por ello te amo más aún.' WHERE `entry`=2000000651;
UPDATE `db_script_string` SET `content_loc7`='Desafortunadamente, esto también debe interrumpirse... Los lazos que me atan a este mundo se debilitan y me arrastran...' WHERE `entry`=2000000652;
UPDATE `db_script_string` SET `content_loc7`='¡No! Anaia... ¡Anaia! ¡No me abandones! Por favor...' WHERE `entry`=2000000653;
UPDATE `db_script_string` SET `content_loc7`='Adiós, Cerellean, hasta que volvamos a reunirnos...' WHERE `entry`=2000000654;
UPDATE `db_script_string` SET `content_loc7`='¿Cómo, amor mío? ¿Cómo encontraré fuerzas para enfrentarme a las edades del mundo sin ti a mi lado...?' WHERE `entry`=2000000656;
UPDATE `db_script_string` SET `content_loc7`='Salto de Fonroca se encuentra en las montañas al Este y Norte.' WHERE `entry`=2000000658;
UPDATE `db_script_string` SET `content_loc7`='¡Las escrituras son mías, hermano! ¡Pronto estarás fuera de mi camino para siempre!' WHERE `entry`=2000000659;
UPDATE `db_script_string` SET `content_loc7`='¡TROL CABREARME!' WHERE `entry`=2000000660;
UPDATE `db_script_string` SET `content_loc7`='¡Esto es la guerra!' WHERE `entry`=2000000661;
UPDATE `db_script_string` SET `content_loc7`='Por favor, $N, ven aquí. Ahora estoy en Zim\'Torga.' WHERE `entry`=2000000662;
UPDATE `db_script_string` SET `content_loc7`='Soy el heraldo de Mecazod. ¡Te libraré de la maldición!' WHERE `entry`=2000000663;
UPDATE `db_script_string` SET `content_loc7`='Tu intromisión está llegando a su fin. Mecazod levantará tu maldición cuando yo haya acabado contigo.' WHERE `entry`=2000000664;
UPDATE `db_script_string` SET `content_loc7`='¡ED-210 conectado!' WHERE `entry`=2000000665;
UPDATE `db_script_string` SET `content_loc7`='¡Twonky!' WHERE `entry`=2000000666;
UPDATE `db_script_string` SET `content_loc7`='¡Victoria o muerte! ¡Por la Horda!' WHERE `entry`=2000000669;
UPDATE `db_script_string` SET `content_loc7`='¡Honren a sus héroes! ¡En este día, han dado un gran golpe a uno de nuestros más odiados enemigos! ¡El falso Jefe de Guerra Rend Puño Negro ha caído!' WHERE `entry`=2000000670;
UPDATE `db_script_string` SET `content_loc7`='¡Báñate en mi poder! ¡Bebe de mi fuerza! ¡Combate por la gloria de la Horda!' WHERE `entry`=2000000671;
UPDATE `db_script_string` SET `content_loc7`='Actúa con rapidez, $N. Mi destino está en tus manos.' WHERE `entry`=2000000672;
UPDATE `db_script_string` SET `content_loc7`='¡¡¡Espera, $N!!! Parece que vamos a necesitar algo más de ayuda...' WHERE `entry`=2000000673;
UPDATE `db_script_string` SET `content_loc7`='%s comienza a bailar.' WHERE `entry`=2000000674;
UPDATE `db_script_string` SET `content_loc7`='¡Ja, ja, ja! ¡$N, vaya $Gcompañero:compañera; estás $Ghecho:hecha;!' WHERE `entry`=2000000675;
UPDATE `db_script_string` SET `content_loc7`='¿Ya liberaste a Drull? No sé dónde le han retenido.' WHERE `entry`=2000000676;
UPDATE `db_script_string` SET `content_loc7`='Haré que vayáis más lentos, ¡pero ningún humano de piel rosada me privará de libertad!' WHERE `entry`=2000000677;
UPDATE `db_script_string` SET `content_loc7`='No molestes a A\'dal con preguntas triviales, $R. La voluntad de este ser es lo único que evita que nuestros enemigos aplasten la ciudad.' WHERE `entry`=2000000678;
UPDATE `db_script_string` SET `content_loc7`='Aquí arriba, en la colina, $N.' WHERE `entry`=2000000679;
UPDATE `db_script_string` SET `content_loc7`='Déjame ayudarte con eso.' WHERE `entry`=2000000680;
UPDATE `db_script_string` SET `content_loc7`='Tómate esto, te ayudará.' WHERE `entry`=2000000681;
UPDATE `db_script_string` SET `content_loc7`='Ya verás cómo te curas.' WHERE `entry`=2000000682;
UPDATE `db_script_string` SET `content_loc7`='¿No os estáis viendo? ¡Es la excusa más patética para blandir la espada que he visto nunca!' WHERE `entry`=2000000685;
UPDATE `db_script_string` SET `content_loc7`='Estáis muertos, pero no enterrados. ¡Dad lo mejor que tenéis!' WHERE `entry`=2000000686;
UPDATE `db_script_string` SET `content_loc7`='Bueno, lo lamentarán, sacos de huesos. Veamos lo que tienen. Golpeen a su oponente y no paren hasta que yo lo ordene.' WHERE `entry`=2000000687;
UPDATE `db_script_string` SET `content_loc7`='Espero que esos elfos de sangre no crean que les estoy espiando.' WHERE `entry`=2000000688;
UPDATE `db_script_string` SET `content_loc7`='Si fuera un chico listo, vendería esta investigación a cambio de un asiento en el cohete. Me da la impresión de que vamos a tener que largarnos de aquí muy pronto.' WHERE `entry`=2000000689;
UPDATE `db_script_string` SET `content_loc7`='Si esto es cierto, no falta mucho. Vaya.' WHERE `entry`=2000000690;
UPDATE `db_script_string` SET `content_loc7`='Te he traído un regalo de bienvenida, Jarl.' WHERE `entry`=2000000692;
UPDATE `db_script_string` SET `content_loc7`='Vamos, ábrelo. Creo que te gustará.' WHERE `entry`=2000000693;
UPDATE `db_script_string` SET `content_loc7`='¿Jarl?' WHERE `entry`=2000000694;
UPDATE `db_script_string` SET `content_loc7`='A menos que sea algo que se pueda comer, no lo quiero.' WHERE `entry`=2000000695;
UPDATE `db_script_string` SET `content_loc7`='Jarl, no has sido tú mismo desde que llegué. ¿Qué está pasando?' WHERE `entry`=2000000696;
UPDATE `db_script_string` SET `content_loc7`='Nada. ¿Quieres ayudarme a preparar un estofado de anca de rana?' WHERE `entry`=2000000697;
UPDATE `db_script_string` SET `content_loc7`='¡He venido a visitarte, Jarl! ¡Y te encuentro viviendo en una cabaña vacía en medio de la nada balbuceando sobre ranas! ¡Eres un caso perdido!' WHERE `entry`=2000000698;
UPDATE `db_script_string` SET `content_loc7`='Edward. Tyler. Prepárense para su primer desafío.' WHERE `entry`=2000000699;
UPDATE `db_script_string` SET `content_loc7`='Lysta, invoca a los cautivos.' WHERE `entry`=2000000700;
UPDATE `db_script_string` SET `content_loc7`='Lysta, invoca a los cautivos no-muertos.' WHERE `entry`=2000000702;
UPDATE `db_script_string` SET `content_loc7`='Lysta, invoca... ¡a la abominación!' WHERE `entry`=2000000704;
UPDATE `db_script_string` SET `content_loc7`='Bien hecho, Edward y Tyler. Sus avances en la instrucción son bastante buenos. Pronto volveremos a probar su entereza.' WHERE `entry`=2000000705;
UPDATE `db_script_string` SET `content_loc7`='Gracias por salvarme, $N. ¡Mi padre se pondrá muy contento!' WHERE `entry`=2000000706;
UPDATE `db_script_string` SET `content_loc7`='¿Eres TÚ quien ha interrumpido mi cena? ¿Lo eres?' WHERE `entry`=2000000707;
UPDATE `db_script_string` SET `content_loc7`='¡Eres demasiado fea y flaca para ser una hembra yeti! Y ahora me estás enfadando...' WHERE `entry`=2000000708;
UPDATE `db_script_string` SET `content_loc7`='Gracias de nuevo, $N. Esperaré aquí hasta que sea seguro partir.' WHERE `entry`=2000000709;
UPDATE `db_script_string` SET `content_loc7`='¡El brebaje inservible cae al suelo y parece cobrar vida!' WHERE `entry`=2000000710;
UPDATE `db_script_string` SET `content_loc7`='¡Tiene vida!' WHERE `entry`=2000000711;
UPDATE `db_script_string` SET `content_loc7`='¡Mantén el pulso firme, insensato! ¡Esos componentes que estás derramando sobre el suelo son muy poco comunes!' WHERE `entry`=2000000713;
UPDATE `db_script_string` SET `content_loc7`='El brebaje de $N está arruinado. Hay que crear uno nuevo desde cero.' WHERE `entry`=2000000714;
UPDATE `db_script_string` SET `content_loc7`='¡Urtrak mata a ti!' WHERE `entry`=2000000715;
UPDATE `db_script_string` SET `content_loc7`='¡La venganza es nuestra! ¡Atacad hermanos!' WHERE `entry`=2000000716;
UPDATE `db_script_string` SET `content_loc7`='Vamos a ver cómo crecen estas semillas en la tierra que he preparado...' WHERE `entry`=2000000718;
UPDATE `db_script_string` SET `content_loc7`='Espero que esta planta eche raíces...' WHERE `entry`=2000000722;
UPDATE `db_script_string` SET `content_loc7`='%s comienza a manipular la Piedra de Relu sobre los dos fósiles.' WHERE `entry`=2000000723;
UPDATE `db_script_string` SET `content_loc7`='¿Quién ha invocado a Aman?' WHERE `entry`=2000000724;
UPDATE `db_script_string` SET `content_loc7`='Ah, veo que trabajas arduamente con las reliquias del pasado.' WHERE `entry`=2000000725;
UPDATE `db_script_string` SET `content_loc7`='Te advierto que incluso tus creadores pueden equivocarse.' WHERE `entry`=2000000726;
UPDATE `db_script_string` SET `content_loc7`='Si escarbas demasiado en tu pasado, tu futuro puede tener un fin repentino.' WHERE `entry`=2000000727;
UPDATE `db_script_string` SET `content_loc7`='¡Ah, esta sí que dará en el blanco! Creo que iré a por unas cuantas más... ¿puedes echar un vistazo a estos barriles, $N?' WHERE `entry`=2000000729;
UPDATE `db_script_string` SET `content_loc7`='¿Qué tal se te da vigilar los barriles...?' WHERE `entry`=2000000730;
UPDATE `db_script_string` SET `content_loc7`='Bueno, debo volver a mi trabajo. Me ha venido de perlas este pequeño descanso... y ¡qué bien sienta beber un poco de Cerveza del Trueno!' WHERE `entry`=2000000731;
UPDATE `db_script_string` SET `content_loc7`='%s se pone sus prendas de piel de lobo.' WHERE `entry`=2000000732;
UPDATE `db_script_string` SET `content_loc7`='%s aúlla en respuesta a la llamada de Malukaz.' WHERE `entry`=2000000733;
UPDATE `db_script_string` SET `content_loc7`='%s expresa su aprobación.' WHERE `entry`=2000000734;
UPDATE `db_script_string` SET `content_loc7`='Que nuestro vínculo no vuelva a romperse nunca, espíritu.' WHERE `entry`=2000000735;
UPDATE `db_script_string` SET `content_loc7`='¡Lo hemos conseguido! Thrall estaría orgulloso.' WHERE `entry`=2000000736;
UPDATE `db_script_string` SET `content_loc7`='¿Qué significa esto, Mor\'ghor?' WHERE `entry`=2000000737;
UPDATE `db_script_string` SET `content_loc7`='¡Silencio!' WHERE `entry`=2000000738;
UPDATE `db_script_string` SET `content_loc7`='Idiota charlatán. Bufón incomprensiblemente incompetente...' WHERE `entry`=2000000739;
UPDATE `db_script_string` SET `content_loc7`='¿ESTO es tu héroe?' WHERE `entry`=2000000740;
UPDATE `db_script_string` SET `content_loc7`='Te han engañado, imbécil.' WHERE `entry`=2000000741;
UPDATE `db_script_string` SET `content_loc7`='¡La... operación... entera... HA SIDO COMPROMETIDA!' WHERE `entry`=2000000742;
UPDATE `db_script_string` SET `content_loc7`='Espero ver el caparazón de ese insecto hecho pedazos en mi guarida dentro de una hora. Fracasa y tu destino será mucho peor que la muerte.' WHERE `entry`=2000000743;
UPDATE `db_script_string` SET `content_loc7`='¿Ya es seguro pasar por ese maldito chisme?' WHERE `entry`=2000000747;
UPDATE `db_script_string` SET `content_loc7`='En realidad, no.' WHERE `entry`=2000000748;
UPDATE `db_script_string` SET `content_loc7`='¿Puedes parar de preguntar eso?' WHERE `entry`=2000000749;
UPDATE `db_script_string` SET `content_loc7`='Está bien, parece seguro. ¡Entra ahí, ingeniero!' WHERE `entry`=2000000750;
UPDATE `db_script_string` SET `content_loc7`='¡A ver qué pasa!' WHERE `entry`=2000000751;
UPDATE `db_script_string` SET `content_loc7`='Esto dejará marca...' WHERE `entry`=2000000752;
UPDATE `db_script_string` SET `content_loc7`='%s se toca con sus manos para comprobar que todo está en su sitio.' WHERE `entry`=2000000753;
UPDATE `db_script_string` SET `content_loc7`='¡Lo conseguí! ¡Lo conseguí!' WHERE `entry`=2000000754;
UPDATE `db_script_string` SET `content_loc7`='%s desaprueba con la cabeza.' WHERE `entry`=2000000755;
UPDATE `db_script_string` SET `content_loc7`='No me gustaba mucho, de todas formas. Hablaba mucho y hacía poco.' WHERE `entry`=2000000756;
UPDATE `db_script_string` SET `content_loc7`='¡Que os vaya bien, estúpidos incompetentes! ¡Otro ingeniero vaporizado!' WHERE `entry`=2000000757;
UPDATE `db_script_string` SET `content_loc7`='¡No puedes evitar que expongamos la verdad acerca de la cobardía de Jaina!' WHERE `entry`=2000000758;
UPDATE `db_script_string` SET `content_loc7`='Si crees que puedes detenernos, te equivocas "colega".' WHERE `entry`=2000000759;
UPDATE `db_script_string` SET `content_loc7`='¡Esta gente conocerá al almirante Valiente como el autentico héroe que fue!' WHERE `entry`=2000000760;
UPDATE `db_script_string` SET `content_loc7`='Con un poquito de edición creativa son mucho más útiles...' WHERE `entry`=2000000761;
UPDATE `db_script_string` SET `content_loc7`='Sería una pena desaprovechar estos panfletos, ¿verdad?' WHERE `entry`=2000000762;
UPDATE `db_script_string` SET `content_loc7`='¿... crear un fondo para la compra de gomina? Me gusta mi pelo tal y como está, gracias.' WHERE `entry`=2000000763;
UPDATE `db_script_string` SET `content_loc7`='¿... como desertor, disfrutarás de actividades como nadar atado y cuidar perros? ¡Qué ridículo!' WHERE `entry`=2000000764;
UPDATE `db_script_string` SET `content_loc7`='Nunca me uniría a nada así. Mejor guardar esto. Puede ser de utilidad en el baño...' WHERE `entry`=2000000765;
UPDATE `db_script_string` SET `content_loc7`='¿... los desertores buscan un Theramore donde los ciudadanos puedan pasar el día haciendo malabarismos? ¿Qué es esta tontería?' WHERE `entry`=2000000766;
UPDATE `db_script_string` SET `content_loc7`='Esto es lo más divertido que he leído en mucho tiempo. ¡Me has alegrado el día!' WHERE `entry`=2000000767;
UPDATE `db_script_string` SET `content_loc7`='Esto... esto es una broma, ¿verdad?' WHERE `entry`=2000000768;
UPDATE `db_script_string` SET `content_loc7`='¡Que los espíritus te cuiden, $R!' WHERE `entry`=2000000770;
UPDATE `db_script_string` SET `content_loc7`='¡Muchas gracias, héroe!' WHERE `entry`=2000000771;
UPDATE `db_script_string` SET `content_loc7`='¡No me lo puedo creer! ¡Puedo irme!' WHERE `entry`=2000000772;
UPDATE `db_script_string` SET `content_loc7`='Gracias por rescatarme, ¡pero los demás aún están cautivos en los pisos de arriba!' WHERE `entry`=2000000774;
UPDATE `db_script_string` SET `content_loc7`='¡Están locos estos férreos! No hacen más que hablar de servir a su \'maestro Loken\' pero, por lo que yo sé, solo algunos reciben órdenes suyas.' WHERE `entry`=2000000775;
UPDATE `db_script_string` SET `content_loc7`='¡Gracias al cielo! ¡Me preocupaba que me fueran a obligar a luchar en su guerra contra los gigantes de piedra!' WHERE `entry`=2000000776;
UPDATE `db_script_string` SET `content_loc7`='¿Te atreves a retar a Drottinn Hrothgar, rey de los mares?' WHERE `entry`=2000000777;
UPDATE `db_script_string` SET `content_loc7`='Enseña una lección a esos cultores, $N. ¡Nadie se mete con el cruzado Kul y sale impune!' WHERE `entry`=2000000778;
UPDATE `db_script_string` SET `content_loc7`='¡Gracias a la Luz! ¡Jamás volveré a hacer algo tan insensato!' WHERE `entry`=2000000779;
UPDATE `db_script_string` SET `content_loc7`='Rescatemos a los demás aspirantes y volvamos al campamento. ¡Voy a necesitar un barril de cerveza entero después de esto!' WHERE `entry`=2000000780;
UPDATE `db_script_string` SET `content_loc7`='Unos segundos más y me hubieran atado a uno de esos altares para absorber mi alma. ¡Salgamos de aquí!' WHERE `entry`=2000000781;
UPDATE `db_script_string` SET `content_loc7`='Gracias, $C.' WHERE `entry`=2000000782;
UPDATE `db_script_string` SET `content_loc7`='¡Están matando a los prisioneros! De prisa, ayuda a los demás.' WHERE `entry`=2000000783;
UPDATE `db_script_string` SET `content_loc7`='¡Sabía que no debía haber venido al torneo!' WHERE `entry`=2000000784;
UPDATE `db_script_string` SET `content_loc7`='Pensé que no saldría de ahí con vida.' WHERE `entry`=2000000785;
UPDATE `db_script_string` SET `content_loc7`='¡$GEstúpido:Estúpida;, $C! ¡No puedes detener a los Kvaldir!' WHERE `entry`=2000000786;
UPDATE `db_script_string` SET `content_loc7`='Abandona este lugar.' WHERE `entry`=2000000792;
UPDATE `db_script_string` SET `content_loc7`='No podrás evitar que el Señor de la Escarcha entre en este mundo, mortal. El poder del cazamareas aplastará el de Ragnaros para siempre y tu tierra se convertirá en un paraíso helado.' WHERE `entry`=2000000797;
UPDATE `db_script_string` SET `content_loc7`='Gracias, $R. Me uniré a tu lucha contra los Tronaforjado.' WHERE `entry`=2000000798;
UPDATE `db_script_string` SET `content_loc7`='Imposible calcular. Fallo en la unidad. Directiva: apagar.' WHERE `entry`=2000000799;
UPDATE `db_script_string` SET `content_loc7`='¿Libre de nuevo? El trabajo del guardián Mimir aguarda.' WHERE `entry`=2000000800;
UPDATE `db_script_string` SET `content_loc7`='Nueva directiva: ayudar a derrotar a los enanos férreos.' WHERE `entry`=2000000801;
UPDATE `db_script_string` SET `content_loc7`='¡Estúpido insolente! ¿Pensaste en robar la sangre de Zelemar? ¡Pagarás con la tuya!' WHERE `entry`=2000000802;
UPDATE `db_script_string` SET `content_loc7`='¡Sacadle de ahí!' WHERE `entry`=2000000804;
UPDATE `db_script_string` SET `content_loc7`='Quizás Ograbisi use tu cabeza como pequeño sombrero cuando acabe contigo.' WHERE `entry`=2000000805;
UPDATE `db_script_string` SET `content_loc7`='¡En mi tierra te colgarían de un pie si abres la puerta de la celda de un interno!' WHERE `entry`=2000000806;
UPDATE `db_script_string` SET `content_loc7`='Ya he tenido bastante de tanto lloriqueo incesante, capitán. ¿Y de qué eres capitán tú? ¡Lo que hay que ver!' WHERE `entry`=2000000808;
UPDATE `db_script_string` SET `content_loc7`='¿Por qué no coges un martillo y empiezas a golpear esas estructuras en vez de preguntarnos cada cinco minutos?' WHERE `entry`=2000000809;
UPDATE `db_script_string` SET `content_loc7`='%s se encoge de hombros.' WHERE `entry`=2000000810;
UPDATE `db_script_string` SET `content_loc7`='Vaya. Otra vez a revisar el diseño.' WHERE `entry`=2000000811;
UPDATE `db_script_string` SET `content_loc7`='Solo me queda esperar que vea la Luz y, en su lugar, centre sus energías en restaurar la antes bella Quel\'Thalas.' WHERE `entry`=2000000816;
UPDATE `db_script_string` SET `content_loc7`='Canteros... err... Que avisen a Defias: El ancla oxidada se hundirá esta noche.' WHERE `entry`=2000000817;
UPDATE `db_script_string` SET `content_loc7`='¡Uf! Aquí está mi sombrero de la suerte. Lo había buscado por todas partes.' WHERE `entry`=2000000819;
UPDATE `db_script_string` SET `content_loc7`='No sé en qué estaba pensando. Mira que salir sin mi espada... Me la habría puesto si la hubiera visto aquí...' WHERE `entry`=2000000820;
UPDATE `db_script_string` SET `content_loc7`='No puedo seguir sin mi capa. Espero que esté aquí...' WHERE `entry`=2000000821;
UPDATE `db_script_string` SET `content_loc7`='¡Aquí está! Juraría que no estaba aquí la última vez que miré.' WHERE `entry`=2000000822;
UPDATE `db_script_string` SET `content_loc7`='No puedo dormir si no me cuentan un buen cuento. Ahora, seguro que duermo bien.' WHERE `entry`=2000000823;
UPDATE `db_script_string` SET `content_loc7`='¿Qué ha sido ese ruido? Parecía un rugido.' WHERE `entry`=2000000824;
UPDATE `db_script_string` SET `content_loc7`='¡¿Zarpadura?! Ahí mismo $Gamigo mío:amiga mía;... ¿dónde está tu maestro Volcor?' WHERE `entry`=2000000826;
UPDATE `db_script_string` SET `content_loc7`='¡Guau! Espera. Un momento...' WHERE `entry`=2000000828;
UPDATE `db_script_string` SET `content_loc7`='Ahí está... esto deberá servir. Ahora dime qué ha pasado, Zarpadura.' WHERE `entry`=2000000830;
UPDATE `db_script_string` SET `content_loc7`='Ya entiendo, $Gamigo mío:amiga mía;. Encontraré a alguien que ayude a tu maestro. Vuelve junto a él ahora, o al menos permanece cerca de él.' WHERE `entry`=2000000832;
UPDATE `db_script_string` SET `content_loc7`='Si dispones de tiempo, podrías ayudar a Zarpadura y a su maestro Volcor. Si te interesa, habla conmigo más adelante...' WHERE `entry`=2000000834;
UPDATE `db_script_string` SET `content_loc7`='Por favor, discúlpame mientras comienzo mis pruebas...' WHERE `entry`=2000000835;
UPDATE `db_script_string` SET `content_loc7`='Interesante...' WHERE `entry`=2000000836;
UPDATE `db_script_string` SET `content_loc7`='Pero, ¿qué es esto?' WHERE `entry`=2000000837;
UPDATE `db_script_string` SET `content_loc7`='¡Oh, vaya!' WHERE `entry`=2000000838;
UPDATE `db_script_string` SET `content_loc7`='Si la sangre no se me hubiera coagulado hace tiempo, estaría hirviendo de rabia ahora mismo...' WHERE `entry`=2000000839;
UPDATE `db_script_string` SET `content_loc7`='¡Saludos, Gezhe! Se han enviado diecisiete pedidos más al sector Hara\'samid. Los mercados de la veladrita pesada se han venido abajo, tal y como predijiste.' WHERE `entry`=2000000842;
UPDATE `db_script_string` SET `content_loc7`='Las exportaciones de Heraazi están subiendo. Ya hemos untado a los oficiales de aduanas que hacía falta y el contrabando empezará a llegar en cuestión de días.' WHERE `entry`=2000000843;
UPDATE `db_script_string` SET `content_loc7`='No me sorprende nada. Vuelve con alguna noticia de verdad, ¿de acuerdo?' WHERE `entry`=2000000844;
UPDATE `db_script_string` SET `content_loc7`='Lo que pensaba. Está bien. Puedes irte.' WHERE `entry`=2000000845;
UPDATE `db_script_string` SET `content_loc7`='Con tu venia, señor.' WHERE `entry`=2000000846;
UPDATE `db_script_string` SET `content_loc7`='Si estás aquí, significa que ya puedes empezar a estudiar la invocación de cohortes demoníacas para hacer lo que te plazca. Empezaremos por las criaturas más humildes que podrás invocar y continuaremos a partir de ahí. Comencemos.' WHERE `entry`=2000000847;
UPDATE `db_script_string` SET `content_loc7`='La criatura más fácil de invocar para ti es el diablillo. A estas alturas, ya deberías ser capaz de crear al esbirro, pero por razones de complejidad, empezaré por él.' WHERE `entry`=2000000848;
UPDATE `db_script_string` SET `content_loc7`='Esta pequeña bestia hedionda es el diablillo. Es pequeño y débil, lo que lo convierte en un ser prácticamente inútil como escudo de carne y hueso y su capacidad de destrucción es realmente mediocre. Esta criatura resulta más útil como apoyo de un grupo más grande.' WHERE `entry`=2000000849;
UPDATE `db_script_string` SET `content_loc7`='Ahora que has podido estudiar el diablillo, pasemos al siguiente esbirro que podrás invocar, el abisario.' WHERE `entry`=2000000850;
UPDATE `db_script_string` SET `content_loc7`='Este ente demoníaco se conoce como abisario. Si fuerza y resistencia son importantes, resultando ideal para la defensa. Envíalo al frente para atacar a tu enemigo y luego úsalo como escudo mientras utilizas tus encantamientos y habilidades para extinguir la vida de tu oponente.' WHERE `entry`=2000000851;
UPDATE `db_script_string` SET `content_loc7`='Merece la pena contemplarlo, por si no lo has visto antes. Se trata de una criatura realmente impresionante dentro como fuera del campo de batalla. Ahora, echemos un vistazo a lo que estoy seguro que todos ustedes, alumnos varones, han estado esperando. El súcubo.' WHERE `entry`=2000000852;
UPDATE `db_script_string` SET `content_loc7`='Muy bien. Además de la distracción evidente que un esbirro como este supondrá para sus enemigos masculinos, ella también tiene una capacidad de destrucción impresionante. No obstante, su frágil resistencia la hace casi inservible como escudo.' WHERE `entry`=2000000853;
UPDATE `db_script_string` SET `content_loc7`='Estudia mucho y quizás algún día podrás invocar a tu propio esbirro, pero ahora pasaremos a estudiar al manáfago.' WHERE `entry`=2000000854;
UPDATE `db_script_string` SET `content_loc7`='Lo que tenéis delante es un manáfago. Entre los talentos naturales de esta criatura está la capacidad de neutralizar encantamientos y otras habilidades, convirtiéndola en un ser inigualable a la hora de enfrentarse a un oponente con poderes mágicos.' WHERE `entry`=2000000855;
UPDATE `db_script_string` SET `content_loc7`='A la hora de enfrentarse a cualquier taumaturgo, esta bestia salvaje será tu mejor amigo. Veamos ahora algo ligeramente distinto. La siguiente criatura te ayudará en tus viajes y hará más agradables tus viajes futuros. Vamos a echar un vistazo a un corcel vil.' WHERE `entry`=2000000856;
UPDATE `db_script_string` SET `content_loc7`='Dudo que hayas tenido ocasión de ver una criatura semejante. Estos equinos demoníacos te permitirán ir más rápido, actuando como tu medio de transporte siempre y cuando puedas controlarlos. No obstante, son difíciles de controlar, así que asegúrate de que estés preparado antes de intentarlo.' WHERE `entry`=2000000857;
UPDATE `db_script_string` SET `content_loc7`='Aquí lo tenéis. Nuestra lección sobre las invocaciones ha llegado a su fin. En breve iniciaremos otra clase, así que si queréis poneros al día, os podéis quedar por aquí.' WHERE `entry`=2000000858;
UPDATE `db_script_string` SET `content_loc7`='El sacerdote se arrodilla y reza en silencio.' WHERE `entry`=2000000859;
UPDATE `db_script_string` SET `content_loc7`='Luz, dame fuerza.' WHERE `entry`=2000000860;
UPDATE `db_script_string` SET `content_loc7`='Luz, guía a los caídos de la falsedad a la verdad, de la oscuridad hacia la luz y de la muerte a la vida infinita.' WHERE `entry`=2000000861;
UPDATE `db_script_string` SET `content_loc7`='¡¡Nooooo!!' WHERE `entry`=2000000865;
UPDATE `db_script_string` SET `content_loc7`='Amigos... Os he fallado...' WHERE `entry`=2000000866;
UPDATE `db_script_string` SET `content_loc7`='No, amigo mío... Nosotros te hemos fallado. Te hemos fallado.' WHERE `entry`=2000000867;
UPDATE `db_script_string` SET `content_loc7`='El Dragonante Negro se materializó de la nada y nos embistió antes de que pudiéramos reaccionar.' WHERE `entry`=2000000870;
UPDATE `db_script_string` SET `content_loc7`='Nos masacraron con una eficacia fría, pero parecían más interesados en los huevos guardados aquí, en el Sagrario.' WHERE `entry`=2000000872;
UPDATE `db_script_string` SET `content_loc7`='En su asalto inicial, pude ver fugazmente a su verdadero líder, un temible dragón crepuscular.' WHERE `entry`=2000000874;
UPDATE `db_script_string` SET `content_loc7`='Desconozco sus verdaderos planes, héroes, pero sí sé esto: ¡no hay que permitirles que los lleven a cabo!' WHERE `entry`=2000000875;
UPDATE `db_script_string` SET `content_loc7`='Intrusos: su victoria será efímera. Soy el Comandante Varos Cloudstrider. Mis dracos controlan los cielos y protegen este conducto. ¡Me encargaré personalmente de que el Óculo no caiga en sus manos!' WHERE `entry`=2000000877;
UPDATE `db_script_string` SET `content_loc7`='El tramposo Señor de la magia Urom protege el tercer anillo. Aparecerá solo e indefenso, ¡pero que no te engañen las apariencias! Urom es un poderoso conjurador que dirige un grupo de criaturas fantasmales. Búscale arriba.' WHERE `entry`=2000000878;
UPDATE `db_script_string` SET `content_loc7`='El mayor de tus desafíos aún está por llegar. El Guardián-Ley Eregos es un dragón Azul de inmenso poder. Lo encontrarás sobrevolando el anillo superior.' WHERE `entry`=2000000879;
UPDATE `db_script_string` SET `content_loc7`='Solo aquellos que se alimentan de almas perdidas conocen el hambre.' WHERE `entry`=2000000880;
UPDATE `db_script_string` SET `content_loc7`='Contempla el vacío. Esa es la perpetuidad en la que moran.' WHERE `entry`=2000000881;
UPDATE `db_script_string` SET `content_loc7`='Se beben tu temor: es la sangre de la vida.' WHERE `entry`=2000000882;
UPDATE `db_script_string` SET `content_loc7`='Son el susurro del escalofriante viento.' WHERE `entry`=2000000883;
UPDATE `db_script_string` SET `content_loc7`='No mueren, no viven. Están fuera del ciclo.' WHERE `entry`=2000000884;
UPDATE `db_script_string` SET `content_loc7`='Donde uno cae, muchos podrán ocupar su puesto.' WHERE `entry`=2000000885;
UPDATE `db_script_string` SET `content_loc7`='Volveremos a encontrarnos... Algún día.' WHERE `entry`=2000000888;
UPDATE `db_script_string` SET `content_loc7`='¡La encarnación de la muerte ha sido liberada una vez más sobre el mundo! Teron Sanguino ha vuelto...' WHERE `entry`=2000000889;
UPDATE `db_script_string` SET `content_loc7`='No estaré mucho tiempo en esta celda. Tendré la cabeza de Illidan de una forma u otra.' WHERE `entry`=2000000891;
UPDATE `db_script_string` SET `content_loc7`='¡Escóndete! ¡Escóndete antes de que te vean! ¡Arriba!' WHERE `entry`=2000000892;
UPDATE `db_script_string` SET `content_loc7`='El momento de atacar se acerca. Acabad con Icarius.' WHERE `entry`=2000000893;
UPDATE `db_script_string` SET `content_loc7`='¡Alto!' WHERE `entry`=2000000894;
UPDATE `db_script_string` SET `content_loc7`='¿Qué sucede, mi señor?' WHERE `entry`=2000000895;
UPDATE `db_script_string` SET `content_loc7`='¡Zarath, debes volver al Templo Oscuro de inmediato! Pa... parece que he extraviado las órdenes de Lord Illidan. ¡Rápido!' WHERE `entry`=2000000896;
UPDATE `db_script_string` SET `content_loc7`='Mi señor, sin duda no esperas que te deje desatendido. Lord Illidan me cortaría la cabeza si te ocurriera algo.' WHERE `entry`=2000000897;
UPDATE `db_script_string` SET `content_loc7`='Zarath, soy perfectamente capaz de subir este camino hasta Punta Eclipse. ¡Si no entregamos la misiva, Lord Illidan nos cortará la cabeza a los dos! ¡Puedes marcharte!' WHERE `entry`=2000000898;
UPDATE `db_script_string` SET `content_loc7`='Como desee, mi señor.' WHERE `entry`=2000000899;
UPDATE `db_script_string` SET `content_loc7`='Ah, dulce cardo de sangre... Probablemente olvidado por uno de esos mugrientos adictos de Punta Eclipse.' WHERE `entry`=2000000900;
UPDATE `db_script_string` SET `content_loc7`='Su pérdida es, sin duda, mi ganancia...' WHERE `entry`=2000000901;
UPDATE `db_script_string` SET `content_loc7`='%s se mete el fardo de cardos de sangre en la toga.' WHERE `entry`=2000000902;
UPDATE `db_script_string` SET `content_loc7`='Atrás, $N. Los espíritus pueden atacar.' WHERE `entry`=2000000903;
UPDATE `db_script_string` SET `content_loc7`='¿Por qué nos has capturado?' WHERE `entry`=2000000904;
UPDATE `db_script_string` SET `content_loc7`='¿Qué esperas obtener de esto?' WHERE `entry`=2000000905;
UPDATE `db_script_string` SET `content_loc7`='%s se inclina.' WHERE `entry`=2000000906;
UPDATE `db_script_string` SET `content_loc7`='Queremos ayudarte, espíritu. Buscamos tu sabiduría.' WHERE `entry`=2000000907;
UPDATE `db_script_string` SET `content_loc7`='¿Sabiduría? Tu pueblo fue el que provocó la terrible ira de Cyrukh.' WHERE `entry`=2000000908;
UPDATE `db_script_string` SET `content_loc7`='¿Por qué deberíamos ayudarte? El mundo está arrasado. Destruido.' WHERE `entry`=2000000909;
UPDATE `db_script_string` SET `content_loc7`='%s asiente.' WHERE `entry`=2000000910;
UPDATE `db_script_string` SET `content_loc7`='Tienes razón, espíritu. Mi pueblo os hizo esto. Déjanos enmendar nuestros errores.' WHERE `entry`=2000000911;
UPDATE `db_script_string` SET `content_loc7`='Solo dinos esto: ¿qué o quién es Cyrukh?' WHERE `entry`=2000000912;
UPDATE `db_script_string` SET `content_loc7`='¡Insensato! ¡Cyrukh, el señor de Fuego! Cuando se rompió el vínculo, Cyrukh despertó. Son las lágrimas de Cyrukh las que llenan este mundo de roca fundida.' WHERE `entry`=2000000913;
UPDATE `db_script_string` SET `content_loc7`='El... ¿El Señor del Fuego? ¿Despertó a un señor elemental? Claro... ¡Maldito Gul\'dan!' WHERE `entry`=2000000914;
UPDATE `db_script_string` SET `content_loc7`='Ahora ha perdido la razón. Se volvió loco durante la rotura. No hay descanso para los elementos.' WHERE `entry`=2000000915;
UPDATE `db_script_string` SET `content_loc7`='Tiene que haber una forma.' WHERE `entry`=2000000916;
UPDATE `db_script_string` SET `content_loc7`='Sí, una forma. Destruir a Cyrukh. Destruir a un señor elemental.' WHERE `entry`=2000000917;
UPDATE `db_script_string` SET `content_loc7`='No dudes de mis camaradas ni de mí, espíritu. Dinos cómo invocar al señor elemental. Mis aliados y yo lo destruiremos.' WHERE `entry`=2000000918;
UPDATE `db_script_string` SET `content_loc7`='Debéis pronunciar las palabras...' WHERE `entry`=2000000919;
UPDATE `db_script_string` SET `content_loc7`='Sí... Sí..., las palabras. Las palabras de la maldición. Las palabras pronunciadas por el oscuro, tu maestro.' WHERE `entry`=2000000920;
UPDATE `db_script_string` SET `content_loc7`='Sí... Gul\'dan...' WHERE `entry`=2000000921;
UPDATE `db_script_string` SET `content_loc7`='Encontraremos esas palabras. Invocaremos a tu maestro y lo destruiremos.' WHERE `entry`=2000000922;
UPDATE `db_script_string` SET `content_loc7`='Hablas. Solo hablas... Ahora libéranos, mortal.' WHERE `entry`=2000000923;
UPDATE `db_script_string` SET `content_loc7`='Ahora, a recuperar las palabras perdidas.' WHERE `entry`=2000000924;
UPDATE `db_script_string` SET `content_loc7`='Estoy harto de pedir las cosas con cortesía, hombre pez.' WHERE `entry`=2000000925;
UPDATE `db_script_string` SET `content_loc7`='¡¿Dónde está la Clave de Condenación?! Sé que un tercio está en este pueblo.' WHERE `entry`=2000000926;
UPDATE `db_script_string` SET `content_loc7`='¿De qué me hablasss, piel ssuave?' WHERE `entry`=2000000927;
UPDATE `db_script_string` SET `content_loc7`='Los nagasss no tienen ninguna cccclave...' WHERE `entry`=2000000928;
UPDATE `db_script_string` SET `content_loc7`='No... Claro que no. ¿En qué estaría pensando? ¿Para qué quieren ustedes un tercio de la Clave de Condenación?' WHERE `entry`=2000000929;
UPDATE `db_script_string` SET `content_loc7`='Qué tonto. Quizás debería soltarte y dejarte marchar...' WHERE `entry`=2000000930;
UPDATE `db_script_string` SET `content_loc7`='Sssí..., sssí... Déjame marcharrr...' WHERE `entry`=2000000931;
UPDATE `db_script_string` SET `content_loc7`='¡¿DE VERDAD CREES QUE SOY TAN ESTÚPIDO, NAGA?! ¡SUFRE!' WHERE `entry`=2000000932;
UPDATE `db_script_string` SET `content_loc7`='%s escupe sobre el naga Cicatriz Espiral.' WHERE `entry`=2000000933;
UPDATE `db_script_string` SET `content_loc7`='¡LEVÁNTATE, BASURA INMUNDA! ¡LEVÁNTATE ANTES DE QUE TE MATE!' WHERE `entry`=2000000934;
UPDATE `db_script_string` SET `content_loc7`='%s recupera la conciencia.' WHERE `entry`=2000000935;
UPDATE `db_script_string` SET `content_loc7`='¡No vuelvas a pegarle al comandante Cicatriz Essspiral, orco! ¡Te lo diré! La clave... essstá essscondido en el Alto Cicatriz Espiral. Los nagasss de allí lo guardan bajo llave...' WHERE `entry`=2000000937;
UPDATE `db_script_string` SET `content_loc7`='¡CICATRIZ ESSSPIRAL!' WHERE `entry`=2000000938;
UPDATE `db_script_string` SET `content_loc7`='¿Qué es lo que quieres de mí, chamán?' WHERE `entry`=2000000939;
UPDATE `db_script_string` SET `content_loc7`='¿Acaso no tienes ojos? ¿No has visto a los demonios en nuestro mismo umbral? ¡No puedo prescindir de ningún soldado!' WHERE `entry`=2000000940;
UPDATE `db_script_string` SET `content_loc7`='Reza a tus dioses, chamán. Quizás vengan en tu ayuda. Sin embargo, la Horda no puede...' WHERE `entry`=2000000941;
UPDATE `db_script_string` SET `content_loc7`='Todo está dispuesto, belisario.' WHERE `entry`=2000000942;
UPDATE `db_script_string` SET `content_loc7`='El señor de fatalidad Kazzak estará satisfecho. Vas a aumentar el ritmo de tus ataques. Destruid a los orcos y los bastiones de los enanos se precipitarán.' WHERE `entry`=2000000943;
UPDATE `db_script_string` SET `content_loc7`='Belisario, eso requerirá el uso de todos los infernales del bastión. Puede que nos deje vulnerables ante un contraataque.' WHERE `entry`=2000000944;
UPDATE `db_script_string` SET `content_loc7`='No te preocupes por eso. He aumentado la producción en la Forja Muerta. Tendrás a todos los infernales que quieras para que sigan tus órdenes. No fracases, Jovaan.' WHERE `entry`=2000000945;
UPDATE `db_script_string` SET `content_loc7`='Se hará como dices, belisario. Una última pregunta, si se me permite...' WHERE `entry`=2000000946;
UPDATE `db_script_string` SET `content_loc7`='¿Pero?' WHERE `entry`=2000000947;
UPDATE `db_script_string` SET `content_loc7`='¿Qué hay en la caja?' WHERE `entry`=2000000948;
UPDATE `db_script_string` SET `content_loc7`='¿Caja? No te envié una caja, Jovaan. ¿No tienes cosas más importantes de las que preocuparte? ¡Ve a ocuparte de ellas!' WHERE `entry`=2000000949;
UPDATE `db_script_string` SET `content_loc7`='¡Si quieres la llave tendrás que cogerme!' WHERE `entry`=2000000950;
UPDATE `db_script_string` SET `content_loc7`='¡Cógeme si puedes! ¡Corro tan rápido como el viento!' WHERE `entry`=2000000951;
UPDATE `db_script_string` SET `content_loc7`='¿Por qué ibas a querer hacerme daño? ¡Vamos! ¡Podemos ser amigos!' WHERE `entry`=2000000952;
UPDATE `db_script_string` SET `content_loc7`='¿MORIR? ¡Estás haciendo llorar a Pusillín!' WHERE `entry`=2000000953;
UPDATE `db_script_string` SET `content_loc7`='¡Saluda a mis amiguitos!' WHERE `entry`=2000000954;
UPDATE `db_script_string` SET `content_loc7`='¡Vale, allá voy!' WHERE `entry`=2000000956;
UPDATE `db_script_string` SET `content_loc7`='¿Después de todo lo que hemos pasado juntos? ¡Si al fin y al cabo no me gustabas!' WHERE `entry`=2000000958;
UPDATE `db_script_string` SET `content_loc7`='¡Yo me largo!' WHERE `entry`=2000000959;
UPDATE `db_script_string` SET `content_loc7`='¡Venga, en marcha!' WHERE `entry`=2000000961;
UPDATE `db_script_string` SET `content_loc7`='¡La piedra de hielo se ha derretido!' WHERE `entry`=2000000962;
UPDATE `db_script_string` SET `content_loc7`='¡Ahune, tu fuerza ya no aumenta!' WHERE `entry`=2000000963;
UPDATE `db_script_string` SET `content_loc7`='¡Tu reino helado no tendrá lugar!' WHERE `entry`=2000000964;
UPDATE `db_script_string` SET `content_loc7`='¡Se está muriendo! La segunda parte de la Clave de Condenación es nuestra. Yo...' WHERE `entry`=2000000965;
UPDATE `db_script_string` SET `content_loc7`='Me desvanezco... Vuelve a Ar\'tor... Vuel... ve... a...' WHERE `entry`=2000000966;
UPDATE `db_script_string` SET `content_loc7`='Una voz tenebrosa y profunda surge de %s...' WHERE `entry`=2000000967;
UPDATE `db_script_string` SET `content_loc7`='¡Silencio! ¡Ahora llega la destrucción!' WHERE `entry`=2000000970;
UPDATE `db_script_string` SET `content_loc7`='¡Contempla el innegable poder de nuestro oscuro amo!' WHERE `entry`=2000000971;
UPDATE `db_script_string` SET `content_loc7`='¡Con este don, arrasaré la tierra para recrearla!' WHERE `entry`=2000000972;
UPDATE `db_script_string` SET `content_loc7`='¡Mira! ¡Mira cómo se abre el suelo ante nosotros! ¡Mira cómo fluye la energía! Alimentará a nuestro ejército...' WHERE `entry`=2000000973;
UPDATE `db_script_string` SET `content_loc7`='¡Nunca nos faltará poder! He asegurado nuestro futuro. ¡Someteos a mi gloria!' WHERE `entry`=2000000974;
UPDATE `db_script_string` SET `content_loc7`='Ya... está... hecho...' WHERE `entry`=2000000975;
UPDATE `db_script_string` SET `content_loc7`='¡Parte la tierra, Gul\'dan! ¡Corta el vínculo!' WHERE `entry`=2000000976;
UPDATE `db_script_string` SET `content_loc7`='¡No lo hagas, Gul\'dan! ¡Los elementos no nos perdonarán nunca!' WHERE `entry`=2000000977;
UPDATE `db_script_string` SET `content_loc7`='¡Nos han abandonado! ¡No olvides!' WHERE `entry`=2000000979;
UPDATE `db_script_string` SET `content_loc7`='¡Tomaremos por la fuerza lo que la tierra nos niegue!' WHERE `entry`=2000000980;
UPDATE `db_script_string` SET `content_loc7`='¡Para! ¡Detente de una vez!' WHERE `entry`=2000000981;
UPDATE `db_script_string` SET `content_loc7`='Nos has condenado a todos... Estamos perdidos.' WHERE `entry`=2000000982;
UPDATE `db_script_string` SET `content_loc7`='¡A tus órdenes!' WHERE `entry`=2000000983;
UPDATE `db_script_string` SET `content_loc7`='Espera aquí, $N. El robot espía hará salir a Lescovar lo antes posible. ¡Prepárate! Ataca solo después de escuchar su conversación.' WHERE `entry`=2000000984;
UPDATE `db_script_string` SET `content_loc7`='Que tengáis los dos un buen día. Me gustaría hablar con el señor Lescovar.' WHERE `entry`=2000000985;
UPDATE `db_script_string` SET `content_loc7`='Claro. Él te espera en la biblioteca.' WHERE `entry`=2000000986;
UPDATE `db_script_string` SET `content_loc7`='Gracias. Que la Luz os acompañe a los dos.' WHERE `entry`=2000000987;
UPDATE `db_script_string` SET `content_loc7`='Señor, su invitado ha llegado. Le está esperando.' WHERE `entry`=2000000988;
UPDATE `db_script_string` SET `content_loc7`='Ah, muchas gracias. Te acompaño hasta la biblioteca mientras me ocupo de este asuntillo.' WHERE `entry`=2000000989;
UPDATE `db_script_string` SET `content_loc7`='He de meditar, dejadme a solas.' WHERE `entry`=2000000991;
UPDATE `db_script_string` SET `content_loc7`='¡Enseguida!' WHERE `entry`=2000000992;
UPDATE `db_script_string` SET `content_loc7`='%s espera hasta perder de vista a los guardias.' WHERE `entry`=2000000993;
UPDATE `db_script_string` SET `content_loc7`='Aquí estás. ¿Traes noticias de los Páramos de Poniente?' WHERE `entry`=2000000994;
UPDATE `db_script_string` SET `content_loc7`='VanCleef manda decir que los planes siguen en pie. Sin embargo, ha oído rumores de que alguien anda husmeando por ahí.' WHERE `entry`=2000000995;
UPDATE `db_script_string` SET `content_loc7`='Mmm, podría ser ese entrometido de Shaw. Veré lo que puedo descubrir. Y ahora, vete. Me pondré en contacto contigo pronto.' WHERE `entry`=2000000996;
UPDATE `db_script_string` SET `content_loc7`='Muy bien. Entonces regresaré.' WHERE `entry`=2000000997;
UPDATE `db_script_string` SET `content_loc7`='¡Eso es! ¡Eso es lo que estabas esperando! ¡MÁTALOS!' WHERE `entry`=2000000998;
UPDATE `db_script_string` SET `content_loc7`='El rey debía llegar aquí en barco, en secreto. Pero el barco nunca llegó y el rey Wrynn desapareció.' WHERE `entry`=2000001001;
UPDATE `db_script_string` SET `content_loc7`='Tu ayuda nos permitió relacionar a los Defias con la cabeza pensante que estaba detrás de la abducción, Onyxia, quien se hacía pasar por un miembro de la corte real.' WHERE `entry`=2000001002;
UPDATE `db_script_string` SET `content_loc7`='Mientras tanto, el rey logró escapar de sus captores y regresó a Ventormenta para encargarse de Onyxia.' WHERE `entry`=2000001003;
UPDATE `db_script_string` SET `content_loc7`='Gracias, $N.' WHERE `entry`=2000001008;
UPDATE `db_script_string` SET `content_loc7`='Tal vez debería explicar…' WHERE `entry`=2000001014;
UPDATE `db_script_string` SET `content_loc7`='¡Ahora!' WHERE `entry`=2000001021;
UPDATE `db_script_string` SET `content_loc7`='Lamentable mortal, ¡Onyxia no responde ante nadie!' WHERE `entry`=2000001026;
UPDATE `db_script_string` SET `content_loc7`='Tu patético reto no ha pasado desapercibido. Disfrutaré jugando contigo antes de que mueras.' WHERE `entry`=2000001027;
UPDATE `db_script_string` SET `content_loc7`='¡Gracias! Mejor no pensar en lo que esos soldados me habrían hecho.' WHERE `entry`=2000001028;
UPDATE `db_script_string` SET `content_loc7`='Podemos pasar de largo sin que nos vean. Mientras podamos abatir el ensamblaje frente a la puerta, debiéramos ser capaces de entrar.' WHERE `entry`=2000001029;
UPDATE `db_script_string` SET `content_loc7`='Tossshtando trolesssshhh plagadossshh...' WHERE `entry`=2000001031;
UPDATE `db_script_string` SET `content_loc7`='¡Eso es! Todo lo que tengo... ¿Qué te parece?' WHERE `entry`=2000001034;
UPDATE `db_script_string` SET `content_loc7`='Esto no está nada bien, $N. Es la historia que me contó la domadora del viento Barah, Pezuña Tempestuosa era un héroe, no un villano.' WHERE `entry`=2000001042;
UPDATE `db_script_string` SET `content_loc7`='El Viento del Norte es el ancestral enemigo de los taunka, un soberano entre los elementos y la fuerza que arrebata la vida a este antiguo pueblo.' WHERE `entry`=2000001043;
UPDATE `db_script_string` SET `content_loc7`='Preferirían morir antes que inclinarse ante el Viento del Norte. Algo va mal aquí.' WHERE `entry`=2000001044;
UPDATE `db_script_string` SET `content_loc7`='El martillo de conocimiento...' WHERE `entry`=2000001045;
UPDATE `db_script_string` SET `content_loc7`='Esto confirma nuestras sospechas. Los hilos del tiempo han sido cortados y vueltos a tejer con un patrón incorrecto. Pero... ¿cómo?' WHERE `entry`=2000001046;
UPDATE `db_script_string` SET `content_loc7`='¡AGH! ¡Vaya, me he atrapado yo sola! Es pegajoso. ¡MI PELO!' WHERE `entry`=2000001048;
UPDATE `db_script_string` SET `content_loc7`='Parar ...' WHERE `entry`=2000001060;
UPDATE `db_script_string` SET `content_loc7`='Es solo...' WHERE `entry`=2000001061;
UPDATE `db_script_string` SET `content_loc7`='Guardan sus armas en cajas con este aspecto. Búscalas cerca de los pabellones.' WHERE `entry`=2000001065;
UPDATE `db_script_string` SET `content_loc7`='Solo voy a grabar las runas que creo que te proporcionarán el control de un gigante.' WHERE `entry`=2000001066;
UPDATE `db_script_string` SET `content_loc7`='Cuanto más esperen, menos futuro tendrán para que se los prediga.' WHERE `entry`=2000001067;
UPDATE `db_script_string` SET `content_loc7`='Ahí está, la runa de mando... espero. No tan perversa como su prima enana férrea, pero funcional.' WHERE `entry`=2000001068;
UPDATE `db_script_string` SET `content_loc7`='¡Hurra!' WHERE `entry`=2000001090;
UPDATE `db_script_string` SET `content_loc7`='¡GLORIA PARA LA ALIANZA!' WHERE `entry`=2000001094;
UPDATE `db_script_string` SET `content_loc7`='¿¡Escabullirnos!? ¿Qué crees que somos, marmotas?' WHERE `entry`=2000001100;
UPDATE `db_script_string` SET `content_loc7`='Estamos dando caza a un dios antiguo, Brann.' WHERE `entry`=2000001101;
UPDATE `db_script_string` SET `content_loc7`='Bien. Si nuestros aliados son los que van a ensuciarse las manos, dejaremos que sean ellos quienes decidan cómo proceder.' WHERE `entry`=2000001102;
UPDATE `db_script_string` SET `content_loc7`='¡¿Qué... Qué has hecho, $N?! ¡Brann! ¡Braaaaaannnn!' WHERE `entry`=2000001103;
UPDATE `db_script_string` SET `content_loc7`='¡Brann! ¡$N acaba de activar el sistema de defensa orbital! ¡Si no nos largamos pronto de aquí, estamos fritos!' WHERE `entry`=2000001104;
UPDATE `db_script_string` SET `content_loc7`='¡Magos del Kirin Tor, cuando Brann dé la orden, abran el escudo! ¡Defiendan esta plataforma y nuestros aliados con sus vidas! ¡Por Dalaran! ¡Por Dalaran!' WHERE `entry`=2000001108;
UPDATE `db_script_string` SET `content_loc7`='Nuestros aliados están preparados. ¡Desactiven el escudo y abran paso!' WHERE `entry`=2000001109;
UPDATE `db_script_string` SET `content_loc7`='¡Han vencido al Consejo Férreo y abierto el Archivum! ¡Muy bien, muchachos!' WHERE `entry`=2000001110;
UPDATE `db_script_string` SET `content_loc7`='¡Lo logramos! Llegamos antes que Algalon. Tal vez podamos alterar el sistema para interferir en su análisis…' WHERE `entry`=2000001111;
UPDATE `db_script_string` SET `content_loc7`='Volveré al Archivum y trataré de interferir su señal. Tal vez nos de algo de tiempo mientras ustedes se ocupan de él.' WHERE `entry`=2000001112;
