UPDATE `trans_words` SET `content_loc10`='Guarding the bank and treasures of the Horde is far more satisfying than dispensing coins and trade goods from inside a cage of iron.$B$BI would not exchange places with one of those quill-pushing goblins for a moment.' WHERE `word_id` = 202152; -- locales_npc_text - Text0_1 : entry=17087
UPDATE `trans_words` SET `content_loc10`='H\'thon, the column of darkness stretching beyond sight. H\'thon marwol qualar: the infinite grasp of night.' WHERE `word_id` = 201155; -- locales_npc_text - Text7_0 : entry=15711
UPDATE `trans_words` SET `content_loc10`='Hah!  Tirth\'s been looking for him for days, but he doesn\'t want to be found. He\'s chicken!' WHERE `word_id` = 192082; -- locales_npc_text - Text1_0 : entry=779
