UPDATE `trans_words` SET `content_loc10`='Hail, $C!  Welcome to the celebration!' WHERE `word_id` = 196992; -- locales_npc_text - Text3_0 : entry=8134
UPDATE `trans_words` SET `content_loc10`='Hail, $C. I trust you are here to inquire on the Ahn\'Qiraj war effort. We are going to need a great deal of assistance.$B$BThe war effort is a united effort between the Horde and Alliance to prepare for the impending war against the Silithid and their masters in Ahn\'Qiraj. In Orgrimmar\'s Valley of Spirits, we are collecting a vast number of goods to create war materiel, and these gathering tasks are what we need your help with.$B$BCan I convince you to speak with our commander about helping out?' WHERE `word_id` = 194988; -- locales_npc_text - Text0_1 : entry=8106
UPDATE `trans_words` SET `content_loc10`='Hail, $C. The Ironforge Brigade is stationed here to keep an eye on Hive\'Zora. The Cenarion Circle druids have been monitoring silithid movement and they\'re anticipating something major in this area.' WHERE `word_id` = 194886; -- locales_npc_text - Text0_1 : entry=7874
UPDATE `trans_words` SET `content_loc10`='Hail, $N!  It is good you came to see me. We are prepared to launch a ground assuault through the Field of Strife!  Once Warmaster Garrick gets his orders, he and his reavers will cut a swath through the Alliance!$B$BDo you want to deliver those orders, $N?  Garrick is waiting in the Field of Strife...' WHERE `word_id` = 194232; -- locales_npc_text - Text0_1 : entry=6433
UPDATE `trans_words` SET `content_loc10`='Hail, champion. The tale of your deeds will be told throughout the ages. Perhaps one day, a statue of you will stand amongst the heroes of Stormwind.' WHERE `word_id` = 195483; -- locales_npc_text - Text0_1 : entry=9066
UPDATE `trans_words` SET `content_loc10`='Hail, traveler! What brings you to my doorstep?' WHERE `word_id` = 193736; -- locales_npc_text - Text0_1 : entry=5181
UPDATE `trans_words` SET `content_loc10`='Halbin Frosthammer, from the Wildhammer clan, can be found in Thunderbrew Distillery. He can help teach ya the ways of the shaman.' WHERE `word_id` = 202653; -- locales_npc_text - Text0_1 : entry=17748
UPDATE `trans_words` SET `content_loc10`='Hallow\'s End is upon us, $C. I hope you are enjoying the festivities.' WHERE `word_id` = 202685; -- locales_npc_text - Text0_1 : entry=17849
UPDATE `trans_words` SET `content_loc10`='Happy Hallow\'s End!  Do you like my mask?  Very scary!' WHERE `word_id` = 194837; -- locales_npc_text - Text0_1 : entry=7805
UPDATE `trans_words` SET `content_loc10`='Happy Hallow\'s End!  How do you like my mask?  Very scary!' WHERE `word_id` = 194838; -- locales_npc_text - Text0_1 : entry=7806
UPDATE `trans_words` SET `content_loc10`='Harrison Jones teaches Archaeology in Stormwind Keep\'s Royal Library when not out in the field.' WHERE `word_id` = 201808; -- locales_npc_text - Text0_1 : entry=16628
UPDATE `trans_words` SET `content_loc10`='Has the Prince recanted the tale of the Ashbringer? It is our most favorite of his stories. His favorite is the followup to the Ashbringer. Of course, nobody has ever been able to get that story out of him.' WHERE `word_id` = 194333; -- locales_npc_text - Text5_0 : entry=6911
UPDATE `trans_words` SET `content_loc10`='Has the sun risen already?  There are just never enough hours in the night!  Well, Saelienne will take good care of you. You can find her tending to her guests over in the Craftsmen\'s Terrace in northeastern Darnassus.' WHERE `word_id` = 192732; -- locales_npc_text - Text1_0 : entry=3020
UPDATE `trans_words` SET `content_loc10`='Have a drink, $C. It might be your last!' WHERE `word_id` = 192576; -- locales_npc_text - Text1_0 : entry=2636
UPDATE `trans_words` SET `content_loc10`='Have a wondrous Feast of Winter Veil!  Start it off right with treats from Smokywood Pastures!' WHERE `word_id` = 194170; -- locales_npc_text - Text3_0 : entry=6194
UPDATE `trans_words` SET `content_loc10`='Have ye any news from outside?  I hear the trolls have been actin\' up too.' WHERE `word_id` = 200765; -- locales_npc_text - Text3_0 : entry=15140
UPDATE `trans_words` SET `content_loc10`='Have you any idea what I go through to bring these incomparable valuables to sell?  Come see them all for yourself!' WHERE `word_id` = 194589; -- locales_npc_text - Text3_1 : entry=7389
UPDATE `trans_words` SET `content_loc10`='Have you been to Ashenvale lately? How are things?' WHERE `word_id` = 201324; -- locales_npc_text - Text0_1 : entry=15966
UPDATE `trans_words` SET `content_loc10`='Have you come for training, hunter?' WHERE `word_id` = 202311; -- locales_npc_text - Text0_1 : entry=17266
UPDATE `trans_words` SET `content_loc10`='Have you come here for instruction in the art of making potions? You were mistaken if you thought that I would offer such a privilege to you, unearned. Go, and do not return to me until you have greater skill.' WHERE `word_id` = 193667; -- locales_npc_text - Text0_1 : entry=5105
UPDATE `trans_words` SET `content_loc10`='Have you come to help overthrow Mor\'ghor?' WHERE `word_id` = 201676; -- locales_npc_text - Text1_0 : entry=16470
UPDATE `trans_words` SET `content_loc10`='Have you come to hurt me?  I have nothing to do with the war, $R.$B$BI\'m just a sickly scholar, please leave me alone!' WHERE `word_id` = 196516; -- locales_npc_text - Text0_1 : entry=10930
UPDATE `trans_words` SET `content_loc10`='Have you come to join the hunt, $N?' WHERE `word_id` = 200957; -- locales_npc_text - Text0_1 : entry=15460
UPDATE `trans_words` SET `content_loc10`='Have you come to observe?  Would you like a coconut?' WHERE `word_id` = 200880; -- locales_npc_text - Text3_0 : entry=15348
UPDATE `trans_words` SET `content_loc10`='Have you ever tried Thunderbolt Light? Great stuff!' WHERE `word_id` = 277155; -- locales_npc_text - Text2_1 : entry=2637
UPDATE `trans_words` SET `content_loc10`='Have you recovered the final piece?' WHERE `word_id` = 201816; -- locales_npc_text - Text3_0 : entry=16635
UPDATE `trans_words` SET `content_loc10`='Have you seen all the wonderful things here in Ungoro Crater, $N? It\'s simply amazing. Not as amazing as some of the things I can make with my skill at engineering, but for it being all natural here... it\'s not too bad.$B$BMy uncle Remtravel loves old stuff like this place. I\'m following in his footsteps kinda--I want to discover new things from lost cultures too!' WHERE `word_id` = 192622; -- locales_npc_text - Text0_1 : entry=2734
UPDATE `trans_words` SET `content_loc10`='Have you seen the befouled water elementals in our lake, $N? It is a sorry sight, but I do not know where they are coming from.$B$BYour name has been mentioned in conversations past, perhaps you could help me with this problem.$B$BThe first thing we must learn is who is in control of these elementals.' WHERE `word_id` = 192800; -- locales_npc_text - Text0_1 : entry=3233
UPDATE `trans_words` SET `content_loc10`='Have you seen the damaged sentries that wander the island?  A few modifications to the crystals that control their behavior will allow us to gain control of them and use them to secure tactical locations.$B$BThe Sun\'s Reach Sanctum will be ours in no time. We are $3244w percent done with this goal.' WHERE `word_id` = 196741; -- locales_npc_text - Text0_1 : entry=12240
UPDATE `trans_words` SET `content_loc10`='Have you seen the Headless Horseman?  If he comes here then I\'ll fight him and steal his pumpkin!' WHERE `word_id` = 196657; -- locales_npc_text - Text1_0 : entry=11477
UPDATE `trans_words` SET `content_loc10`='Have you seen the Headless Horseman?  The matron says if I look at him, then he\'ll turn me into a pumpkin!' WHERE `word_id` = 196642; -- locales_npc_text - Text1_0 : entry=11359
UPDATE `trans_words` SET `content_loc10`='Have you seen Watcher Backus? So brave...$B$BI think he\'s the toughest of all the Night Watch. I wonder if he even notices I\'m around....' WHERE `word_id` = 193062; -- locales_npc_text - Text0_1 : entry=3868
UPDATE `trans_words` SET `content_loc10`='Have you seen what prices are going up here? Horror!' WHERE `word_id` = 280301; -- locales_npc_text - Text1_0 : entry=17850
UPDATE `trans_words` SET `content_loc10`='Have you spoken to either of my acolytes inside the tower yet, $N? We cannot complete your orb until you\'ve done one of their tasks. Speak to them now, if you\'re ready.' WHERE `word_id` = 192984; -- locales_npc_text - Text0_1 : entry=3594
UPDATE `trans_words` SET `content_loc10`='Have you tried our bread? Baker Masterson must be the most gifted chef to have ever lived!' WHERE `word_id` = 193231; -- locales_npc_text - Text1_0 : entry=4118
UPDATE `trans_words` SET `content_loc10`='Having a bad day? Try some cherry pie.' WHERE `word_id` = 192482; -- locales_npc_text - Text3_0 : entry=2355
UPDATE `trans_words` SET `content_loc10`='Having a good time?' WHERE `word_id` = 7488; -- locales_npc_text - Text1_0 : entry=7382
UPDATE `trans_words` SET `content_loc10`='Hawkstriders are noble creatures. Make sure to always treat yours well; there are fewer things more dangerous than an angry hawkstrider.' WHERE `word_id` = 195962; -- locales_npc_text - Text0_1 : entry=10011
UPDATE `trans_words` SET `content_loc10`='He just came stumbing out of the barracks.$B$BI screamed for help, but the guards wouldn\'t even look at me!' WHERE `word_id` = 202307; -- locales_npc_text - Text0_1 : entry=17258
UPDATE `trans_words` SET `content_loc10`='He... hello, $N. Sorry, I\'m recovering from my wounds.$B$BThose Scarlet Crusaders are tougher than I had given them credit for. They almost did me in.$B$BYou should be careful too. I\'d hate to see them get the jump on you.' WHERE `word_id` = 193396; -- locales_npc_text - Text0_1 : entry=4355
UPDATE `trans_words` SET `content_loc10`='Head to the Apothecarium\'s inner ring. Martha Alliestar is there, and she\'ll instruct you in herbalism.' WHERE `word_id` = 199732; -- locales_npc_text - Text1_0 : entry=14912
UPDATE `trans_words` SET `content_loc10`='Head to the central ring; you\'ll find auctioneers stationed there.' WHERE `word_id` = 199709; -- locales_npc_text - Text0_1 : entry=14900
UPDATE `trans_words` SET `content_loc10`='Head to the War Quarter. The priests are there on the outer ring making sure no limb remains unattached for long.' WHERE `word_id` = 199747; -- locales_npc_text - Text1_0 : entry=14921
UPDATE `trans_words` SET `content_loc10`='Heading down to Tranquillien today, $C, or is there something else that I can do for you?' WHERE `word_id` = 198418; -- locales_npc_text - Text0_1 : entry=8413
UPDATE `trans_words` SET `content_loc10`='Heading to Outland? Visit Vixton Pinchwhistle in Netherstorm\'s Area 52. As the Steamwheedle Fighting Circuit\'s official Arena Vendor, he has all sorts of incredible items for sale to those with enough Arena Points.' WHERE `word_id` = 195964; -- locales_npc_text - Text0_1 : entry=10018
UPDATE `trans_words` SET `content_loc10`='Healing is for those who cannot endure pain. However if you must, seek out the Spirit Lodge on the lake in the Valley of Spirits, up and above the Valley of Strength to the west.' WHERE `word_id` = 192544; -- locales_npc_text - Text1_0 : entry=2561
UPDATE `trans_words` SET `content_loc10`='Hee hee hee! Dis gunna be some fun, mon!' WHERE `word_id` = 196856; -- locales_npc_text - Text3_0 : entry=12622
UPDATE `trans_words` SET `content_loc10`='Heh, bombs, us like bombs. Graham Van Talen show you how to make. He in outer ring of Rogues\' Quarter.' WHERE `word_id` = 192936; -- locales_npc_text - Text1_0 : entry=3532
UPDATE `trans_words` SET `content_loc10`='Hellfire Peninsula is a shattered realm - ruthless and unforgiving to its inhabitants. Be sure to repair and stock up on supplies before venturing out.' WHERE `word_id` = 195852; -- locales_npc_text - Text0_1 : entry=9775
UPDATE `trans_words` SET `content_loc10`='Hello $C, and welcome to the Darkmoon Faire.$B$BThere is so much to do here at the faire that it can be a little overwhelming for our first time visitors. That\'s why I\'m here to help.$B$BDo you have any questions?' WHERE `word_id` = 203026; -- locales_npc_text - Text0_1 : entry=18372
UPDATE `trans_words` SET `content_loc10`='Hello $R, it is a pleasure to make your acquaintance.' WHERE `word_id` = 192101; -- locales_npc_text - Text0_1 : entry=838
UPDATE `trans_words` SET `content_loc10`='Hello and welcome to the Darkmoon Faire Staging Area.$B$BThe portal here will take you to the Darkmoon Faire.$B$BBut if you have any questions before you go, I can answer them.' WHERE `word_id` = 203018; -- locales_npc_text - Text0_1 : entry=18362
UPDATE `trans_words` SET `content_loc10`='Hello friend.' WHERE `word_id` = 199993; -- locales_npc_text - Text0_1 : entry=7381
UPDATE `trans_words` SET `content_loc10`='Hello once again, $N. I sense that the flow of time remains threatened by forces that are quite powerful indeed. Your continued assistance is always welcomed by the bronze dragonflight.$B$BWhile the past is set in its ways, the future is always changing. The decisions you make in the here and now help guide that change.' WHERE `word_id` = 192977; -- locales_npc_text - Text0_1 : entry=3585
UPDATE `trans_words` SET `content_loc10`='Hello there!  Fizzcrank\'s pilots are the best of the best. We stay that way through constant training. That includes parachute training. You never know when you will need to use your chute, but you do know that one day you\'ll have to use it.$B$BSo what we have here is a teleporter to the top of this spire. Once you get up there, the only way for a real soldier to get back down is with a parachute. We\'ve got another trooper stationed up top ready with the parachutes.$B$BIf you feel like trying it out, $C, I\'ll clear you for teleporter use. Then just step onto the teleporter.' WHERE `word_id` = 199124; -- locales_npc_text - Text0_1 : entry=12658
UPDATE `trans_words` SET `content_loc10`='Hello there! Care to buy some fishing supplies, or perhaps take a lesson or two?' WHERE `word_id` = 197537; -- locales_npc_text - Text0_1 : entry=13591
UPDATE `trans_words` SET `content_loc10`='Hello there! My name\'s Jenna! And you?' WHERE `word_id` = 193681; -- locales_npc_text - Text0_1 : entry=5121
UPDATE `trans_words` SET `content_loc10`='Hello there! Plants are my speciality. Do you have an inquiry about the local flora?' WHERE `word_id` = 202885; -- locales_npc_text - Text2_1 : entry=18165
UPDATE `trans_words` SET `content_loc10`='Hello there! You\'re really growing on me.' WHERE `word_id` = 202771; -- locales_npc_text - Text2_1 : entry=18025
UPDATE `trans_words` SET `content_loc10`='Hello there, $C!' WHERE `word_id` = 201899; -- locales_npc_text - Text0_1 : entry=16758
UPDATE `trans_words` SET `content_loc10`='Hello there, $N. I am known as Chronormu, but you may call me Chromie. I am comfortable with such familiarity.$B$BNow - how did I know your name, you ask?  I am one of the bronze dragonflight, forever attuned to the ebb and flow of time. You could say that we have met before. To that end, we shall also meet again.$B$BI hope that my choice of visual demeanor does not disturb you; while I might be charged to be a sentry of sorrowful places, I choose not to drown in the sorrow of the locale.' WHERE `word_id` = 192976; -- locales_npc_text - Text0_1 : entry=3584
UPDATE `trans_words` SET `content_loc10`='Hello there, my name is Petra. To my right is my kodo, Dadanga. We\'re with Marshal Expeditions - been so for a few years now, ever since Dadanga was a babe.$B$BIf you have any lit torches or any open sources of flame, I\'d like to ask you to keep them stashed if you could. Dadanga dislikes smoke.' WHERE `word_id` = 192775; -- locales_npc_text - Text0_1 : entry=3093
UPDATE `trans_words` SET `content_loc10`='Hello young $G Master:Mistress;. I am Eva Sarkhoff and this is my husband, Lucien. It is a pleasure to make your acquaintance.' WHERE `word_id` = 193141; -- locales_npc_text - Text0_1 : entry=4041
UPDATE `trans_words` SET `content_loc10`='Hello!  So good to meet you!' WHERE `word_id` = 195348; -- locales_npc_text - Text0_1 : entry=8787
UPDATE `trans_words` SET `content_loc10`='Hello! What brings you to Winterspring? These cold and vast lands may seem dismal, but this is a place of many secrets...' WHERE `word_id` = 193464; -- locales_npc_text - Text0_1 : entry=4634
UPDATE `trans_words` SET `content_loc10`='Hello, $C!  Welcome to the races at the Shimmering Flats!' WHERE `word_id` = 192157; -- locales_npc_text - Text1_0 : entry=932
UPDATE `trans_words` SET `content_loc10`='Hello, $C!$B$BThese poor creatures are giving everything they have to fight the minions of the Twilight\'s Hammer in this dark place.$B$BI\'ve done my best to revive those that have fallen.$B$BIf you need an additional mount, these birds are ready to take wing again. Treat them well!' WHERE `word_id` = 201296; -- locales_npc_text - Text0_1 : entry=15922
UPDATE `trans_words` SET `content_loc10`='Hello, $C, good of you to show up. Allow me to be succinct; we are gathering supplies for the Ahn\'Qiraj war effort. We wouldn\'t want to have more casualties than necessary, now would we? I have the responsibility of coordinating the collection of $2086w mageweave bandages. To date we have garnered $2085w. Care to assist?' WHERE `word_id` = 194909; -- locales_npc_text - Text0_1 : entry=7920
UPDATE `trans_words` SET `content_loc10`='Hello, $C. Care to purchase something?' WHERE `word_id` = 201721; -- locales_npc_text - Text0_1 : entry=16547
UPDATE `trans_words` SET `content_loc10`='Hello, $C. From what I\'ve been told, everything\'s been collected, and all of the materiel for the war is being shipped over to Silithus as we speak. It should all be there in less than $2113w days. Once that\'s done, and the gong has been rung with the Scepter of the Shifting Sands, the war will really begin.' WHERE `word_id` = 194934; -- locales_npc_text - Text0_1 : entry=7962
UPDATE `trans_words` SET `content_loc10`='Hello, $C. I am collecting linen bandages here for the Ahn\'Qiraj war effort; an effort that will put us all to the test, but strengthen us for the war to come. I can assure you that any bandages that you choose to donate will be put to good use in helping to save the lives of our soldiers when the time comes. I am in need of $2077w, and to this point have managed to collect $2076w. Will you assist us?' WHERE `word_id` = 194870; -- locales_npc_text - Text0_1 : entry=7847
UPDATE `trans_words` SET `content_loc10`='Hello, $C. I am Jessir Moonbow. I have come from Darnassus seeking two friends who came looking for signs of the Shadow Council in Felwood.$B$BIt seems those vile creatures who claim responsibility for much of the orcish race\'s suffering seeks to regain some of its power.$B$BMy two friends went looking in Jaedenar to prove one way or the other what was happening.$B$BI expected them to be here when I arrived, but now they are days late.' WHERE `word_id` = 193059; -- locales_npc_text - Text0_1 : entry=3864
UPDATE `trans_words` SET `content_loc10`='Hello, $C. If you haven\'t already, you really should speak with Farstrider Solanna.' WHERE `word_id` = 195280; -- locales_npc_text - Text0_1 : entry=8638
UPDATE `trans_words` SET `content_loc10`='Hello, $C. Please excuse me as I am having a conversation with Rimblat. He believes that even this land is not beyond saving.' WHERE `word_id` = 195123; -- locales_npc_text - Text0_1 : entry=8358
UPDATE `trans_words` SET `content_loc10`='Hello, $C. These are troubling times indeed if this is what is to become of our beloved Quel\'Thalas.' WHERE `word_id` = 195073; -- locales_npc_text - Text0_1 : entry=8228
UPDATE `trans_words` SET `content_loc10`='Hello, $N. Are you helping with the invasion? A skilled $C such as yourself will be a valuable addition to our force.' WHERE `word_id` = 202882; -- locales_npc_text - Text0_1 : entry=18158
UPDATE `trans_words` SET `content_loc10`='Hello, $N. Need to quickly get to Blackwind Landing, our base in the Skethyl Mountains?  I\'ve got a pretty little nether ray ready.$B$BYou want to go now?  You just say the word, $G big guy : bunny;!' WHERE `word_id` = 198927; -- locales_npc_text - Text0_1 : entry=10978
UPDATE `trans_words` SET `content_loc10`='Hello, $N.$B$BNow that I\'ve finished creating my anvil I can begin outfitting our troops. It\'s an important step, but there\'s still much to be done.$B$BMore importantly for you, there are new things that I can create, which you may be interested in.' WHERE `word_id` = 196757; -- locales_npc_text - Text0_1 : entry=12286
UPDATE `trans_words` SET `content_loc10`='Hello, $R. I\'m sorry that you had to see our sacred land for the first time in such a state.' WHERE `word_id` = 197320; -- locales_npc_text - Text0_1 : entry=12727
UPDATE `trans_words` SET `content_loc10`='Hello, $R.$B$BLet\'s get busy! No sense waiting around for supplies that may never arrive.$B$BI need my anvil in order to outfit our troops properly. Right now It\'s $3228w percent complete.$B$BAnd when the anvil is completed, there will be many more powerful items that I will be able to smith for you.' WHERE `word_id` = 196756; -- locales_npc_text - Text0_1 : entry=12285
UPDATE `trans_words` SET `content_loc10`='Hello, are you here to learn about the conflict in Bloodmyst?' WHERE `word_id` = 195545; -- locales_npc_text - Text1_1 : entry=9172
UPDATE `trans_words` SET `content_loc10`='Hello, dear. I am Geyah, matron of the Mag\'har.$B$B<Greatmother Geyah coughs.>$B$BIf you are here, it would be safe to assume that the portal has opened. Most of the orcs here were too young or too sick to remember the Dark Portal.$B$B<Greatmother Geyah points to the orcs around her.>$B$BMany of them were orphans or would soon become orphans. Aye, Blackhand would have it no other way. Keep the sick from the healthy. Only the strong would survive... Or so they say.' WHERE `word_id` = 195742; -- locales_npc_text - Text0_1 : entry=9508
UPDATE `trans_words` SET `content_loc10`='Hello, how are you feeling today, $C? Well, I hope. You may be wondering what is going on here. There\'s a war at Ahn\'Qiraj that is being planned and prepared for. We and the Horde are preparing as best we can for it.$B$BTo that end, the Alliance has asked me to supervise the collection of silk bandages here. To date we\'ve managed to put together $2082w out of $2083w needed. Are you thinking about helping out?' WHERE `word_id` = 194872; -- locales_npc_text - Text0_1 : entry=7851
UPDATE `trans_words` SET `content_loc10`='Hello, my child.' WHERE `word_id` = 195448; -- locales_npc_text - Text0_1 : entry=9009
UPDATE `trans_words` SET `content_loc10`='Hello, peach - good to have your company!' WHERE `word_id` = 197573; -- locales_npc_text - Text0_1 : entry=13792
UPDATE `trans_words` SET `content_loc10`='Hello, what business brings you here?$B$BThis is an orphanage for the children of Outland who have lost their parents and have no one else to look after them.$B$BPerhaps you are here because you are considering adoption?' WHERE `word_id` = 198711; -- locales_npc_text - Text0_1 : entry=9854
UPDATE `trans_words` SET `content_loc10`='Hello, young $C. Are you here to bring me a batch of peacebloom? We are collecting them to make potions and elixirs for the Ahn\'Qiraj war effort. Though tragic that we are forced into this war, the results will be even more terrible if we do not prepare properly.$B$BThe Horde has tasked me with the collection of $2020w peacebloom, and so far we\'ve managed to put together $2021w. If you have it within your ability, please gather up all that you can and return them to me here.' WHERE `word_id` = 194889; -- locales_npc_text - Text0_1 : entry=7878
UPDATE `trans_words` SET `content_loc10`='Hello, young $R.' WHERE `word_id` = 195741; -- locales_npc_text - Text0_1 : entry=9507
UPDATE `trans_words` SET `content_loc10`='Hello. What brings you to Chillwind Camp today?' WHERE `word_id` = 195384; -- locales_npc_text - Text0_1 : entry=8846
UPDATE `trans_words` SET `content_loc10`='Hendel is in our custody now, $N, thanks to you.$B$BAlthough the questioning goes slowly, I assure you, he will tell us everything he knows.$B$BFor now, please be patient. I will contact you immediately if we have need of your assistance.$B$BUntil then, please, enjoy Theramore and I invite you to stay for as long as you like within our city\'s walls as a friend of the Alliance.' WHERE `word_id` = 192792; -- locales_npc_text - Text0_1 : entry=3158
UPDATE `trans_words` SET `content_loc10`='Herbalism requires a gentle touch that few such as Malorne can teach. Seek him out east of the Inn in Dolanaar.' WHERE `word_id` = 193388; -- locales_npc_text - Text1_0 : entry=4334
UPDATE `trans_words` SET `content_loc10`='Here for a ride, $R?$B$BOr, is something else bothering you?' WHERE `word_id` = 197380; -- locales_npc_text - Text0_1 : entry=12937
UPDATE `trans_words` SET `content_loc10`='Here in Outland, our forces fight against the Horde to control the Eye of the Storm. It is dangerous, fighting amidst the raging energies of the Nether. Do you have what it takes to join us?' WHERE `word_id` = 200131; -- locales_npc_text - Text0_1 : entry=10558
UPDATE `trans_words` SET `content_loc10`='Here lie the remains of Weeder Greenthumb, one of Watcher Jhang\'s druid companions. By all appearances he was killed quick and clean while trying to make his escape from the Slave Pens.' WHERE `word_id` = 195515; -- locales_npc_text - Text0_1 : entry=9118
UPDATE `trans_words` SET `content_loc10`='Here lies the corpse of one of the Steeljaw\'s caravan guards.' WHERE `word_id` = 199075; -- locales_npc_text - Text0_1 : entry=12388
UPDATE `trans_words` SET `content_loc10`='Here lies the corpse of one of the Steeljaw\'s caravan workers.' WHERE `word_id` = 199076; -- locales_npc_text - Text0_1 : entry=12389
UPDATE `trans_words` SET `content_loc10`='Here to learn about potions? I\'ve got plenty to teach you. I might be dead, but I haven\'t lost my brains. Yet.' WHERE `word_id` = 193619; -- locales_npc_text - Text0_1 : entry=5044
UPDATE `trans_words` SET `content_loc10`='Here we practice fire fighting techniques. Power to the Forsaken!' WHERE `word_id` = 197164; -- locales_npc_text - Text0_1 : entry=12108
UPDATE `trans_words` SET `content_loc10`='Here yous go. My best pair and I\'m giving them to ya cheap!$B$BYous gonna look sharp in them!' WHERE `word_id` = 200548; -- locales_npc_text - Text0_1 : entry=14705
UPDATE `trans_words` SET `content_loc10`='Here\'s to hoping not all of Outland is as barren as this region. This is quite a change from the green grasslands and forests of Eversong.' WHERE `word_id` = 195293; -- locales_npc_text - Text0_1 : entry=8674
UPDATE `trans_words` SET `content_loc10`='Heroes of the Brood? C\'Thun must be banished! We can assist you, mortal!' WHERE `word_id` = 194966; -- locales_npc_text - Text0_1 : entry=8079
UPDATE `trans_words` SET `content_loc10`='Hey $C, if you\'ve lost a key I might be able to help you out.' WHERE `word_id` = 199335; -- locales_npc_text - Text0_1 : entry=13572
