UPDATE `trans_words` SET `content_loc10`='Looks like I picked the wrong day to stop killin\' fel orcs.' WHERE `word_id` = 201681; -- locales_npc_text - Text0_1 : entry=16471
UPDATE `trans_words` SET `content_loc10`='Looks like the last of the goods we\'ve collected are shipping out to Silithus over the next $2113w days. And once that\'s done, someone will have to come along and ring the gong on the Scarab Dais, outside of Ahn\'Qiraj, to open the gates. That\'s when our bandages will come in handy.' WHERE `word_id` = 194943; -- locales_npc_text - Text0_1 : entry=7991
UPDATE `trans_words` SET `content_loc10`='Looks like they found their way back home. I\'m relieved.$B$BYou came back, too!  Are you interested in joining us?' WHERE `word_id` = 202041; -- locales_npc_text - Text0_1 : entry=16945
UPDATE `trans_words` SET `content_loc10`='Looks like this is it, boss-$G man : lady;. It\'s been a real honor. I\'m sure we\'ll see each other again real soon.' WHERE `word_id` = 201147; -- locales_npc_text - Text0_1 : entry=15701
UPDATE `trans_words` SET `content_loc10`='Looks like we\'re in a pretty tight jam. Think you can get us out of here?' WHERE `word_id` = 200659; -- locales_npc_text - Text3_0 : entry=14879
