UPDATE `trans_words` SET `content_loc10`='Lorna said a truly brave adventurer helped rescue many of the horses near her family\'s orchard.$B$BI offered to take care of those that made it across the sea with us. I learned how from my mother and father, Light bless them!$B$BWere you interested in a fine Gilnean steed...?' WHERE `word_id` = 280427; -- locales_npc_text - Text0_1 : entry=18430
UPDATE `trans_words` SET `content_loc10`='Lorokeem, grand master alchemist and Skettis outcast, lives among the other arakkoa in the northwest portion of the lower city.' WHERE `word_id` = 198808; -- locales_npc_text - Text0_1 : entry=10392
UPDATE `trans_words` SET `content_loc10`='Lot\'s to be done around here, $R. Are you ready to help?' WHERE `word_id` = 202453; -- locales_npc_text - Text0_1 : entry=17461
UPDATE `trans_words` SET `content_loc10`='Lotan is where weapons and armor are. A warrior is always next to his sword.' WHERE `word_id` = 280289; -- locales_npc_text - Text0_1 : entry=17755
UPDATE `trans_words` SET `content_loc10`='Lyria\'s the one you should talk to if you need some training. She can usually be found out behind the smithy waiting for some armor or weapon to be finished.' WHERE `word_id` = 193282; -- locales_npc_text - Text1_0 : entry=4271
UPDATE `trans_words` SET `content_loc10`='Lysheana can offer you the proper documents, she resides in the Craftsmen\'s Terrace in northeasten Darnassus. May your fellowship bring glory to the Night Elves!' WHERE `word_id` = 192730; -- locales_npc_text - Text1_0 : entry=3019
UPDATE `trans_words` SET `content_loc10`='Maces are a specialty of Buliwyf\'s! He\'s around this shop somewhere, grousing. If his training techniques aren\'t your style, I hear the draenei weapon master in the Exodar, Handiir, knows his way around the weapon too! He\'s up on the Trader\'s Tier there.' WHERE `word_id` = 198888; -- locales_npc_text - Text0_1 : entry=10766
UPDATE `trans_words` SET `content_loc10`='Mages? They\'re in the Magic Quarter, in the central building. Thought you\'d be smart enough to figure that out.' WHERE `word_id` = 199739; -- locales_npc_text - Text0_1 : entry=14917
UPDATE `trans_words` SET `content_loc10`='Mailbox? You want to head to the inn in the center of the city\'s upper ring. It\'s right there.' WHERE `word_id` = 199742; -- locales_npc_text - Text1_0 : entry=14918
UPDATE `trans_words` SET `content_loc10`='Makaru can be found day and night at Red Canyon Mining in the Valley of Honor, just off the lake.' WHERE `word_id` = 192522; -- locales_npc_text - Text1_0 : entry=2515
