UPDATE `trans_words` SET `content_loc10`='Marooned! Blasted to bits by the Alliance and stranded on some island in the middle of who-knows-where?!$B$BBusiness as usual, right, $N?' WHERE `word_id` = 200591; -- locales_npc_text - Text0_1 : entry=14789
