UPDATE `trans_words` SET `content_loc10`='May Elune walk with you, $C. The Temple of the Moon has come to help the troubled citizens of Auberdine.' WHERE `word_id` = 194086; -- locales_npc_text - Text0_1 : entry=5943
UPDATE `trans_words` SET `content_loc10`='May I suggest that you save us from the pirates, my dear $N?!' WHERE `word_id` = 202263; -- locales_npc_text - Text0_1 : entry=17214
UPDATE `trans_words` SET `content_loc10`='May Mother Earth protect us.' WHERE `word_id` = 280064; -- locales_npc_text - Text3_0 : entry=15837
UPDATE `trans_words` SET `content_loc10`='May the blessings of the night be yours, friend!  I am Illiyana Moonblaze, and I am the main supplier for the Silverwing Sentinels here in Ashenvale. As you improve your standing with the Sentinels, I will make available to you a fine selection of goods that you can use inside Warsong Gulch. Should you find yourself in need of item repair, I also provide those services.' WHERE `word_id` = 194532; -- locales_npc_text - Text0_1 : entry=7295
UPDATE `trans_words` SET `content_loc10`='May the Earth Mother watch over you, $N. Thank you for your help.' WHERE `word_id` = 201142; -- locales_npc_text - Text0_1 : entry=15697
