UPDATE `trans_words` SET `content_loc10`='What a fine day! It inspires me to create a new design.' WHERE `word_id` = 193962; -- locales_npc_text - Text0_1 : entry=5540
UPDATE `trans_words` SET `content_loc10`='What a mess! How am I going to spin this?$B$BAnd really... goblin zombies?!!' WHERE `word_id` = 201114; -- locales_npc_text - Text0_1 : entry=15678
UPDATE `trans_words` SET `content_loc10`='What a wonderful day to be alive! Look at the beauty that surrounds you, friend. Enjoy what life has to give!' WHERE `word_id` = 194434; -- locales_npc_text - Text0_1 : entry=7041
UPDATE `trans_words` SET `content_loc10`='What an unusual look! How many opportunities he has! I need to devote myself to the study of local flora and fauna.' WHERE `word_id` = 280353; -- locales_npc_text - Text1_0 : entry=18124
UPDATE `trans_words` SET `content_loc10`='What are the Wardens? We are the hum of a spinning blade in the moment before death.' WHERE `word_id` = 202859; -- locales_npc_text - Text2_1 : entry=18143
UPDATE `trans_words` SET `content_loc10`='What are the Wardens? We are the precision slice of a killing blow.' WHERE `word_id` = 202858; -- locales_npc_text - Text1_1 : entry=18143
