UPDATE `trans_words` SET `content_loc10`='What are we calling you things these days? Brutes? Hobgoblins?$B$BI can\'t keep it straight. Get out of here.' WHERE `word_id` = 202512; -- locales_npc_text - Text1_1 : entry=17550
UPDATE `trans_words` SET `content_loc10`='What are you doing all the way back here? You need to get back to Stratholme immediately!$B$BShall I teleport you to Stratholme?' WHERE `word_id` = 295546; -- locales_npc_text - Text0_1 : entry=13470
UPDATE `trans_words` SET `content_loc10`='What are you doing here?  Get out of here!' WHERE `word_id` = 200979; -- locales_npc_text - Text0_1 : entry=15487
