UPDATE `trans_words` SET `content_loc10`='What are you doing out here? You should be inside fighting off the Blue Dragonflight!' WHERE `word_id` = 199603; -- locales_npc_text - Text0_1 : entry=14271
UPDATE `trans_words` SET `content_loc10`='What are you doing over there?' WHERE `word_id` = 279173; -- locales_npc_text - Text2_1 : entry=13047
