UPDATE `trans_words` SET `content_loc10`='What can I do for you, $R?' WHERE `word_id` = 269946; -- locales_npc_text - Text0_1 : entry=13426
UPDATE `trans_words` SET `content_loc10`='What could you possibly need a bank for?  Very well, you\'ll need to go through the Ruins of Lordaeron to the south of Brill. In the back are a set of elevators leading down into the Undercity. When you get there stop any of those lumbering Abominations they like to call an Undercity Guardian and ask it for better directions.' WHERE `word_id` = 193188; -- locales_npc_text - Text1_0 : entry=4074
UPDATE `trans_words` SET `content_loc10`='What d\'ye want, $C?' WHERE `word_id` = 200754; -- locales_npc_text - Text0_1 : entry=15131
UPDATE `trans_words` SET `content_loc10`='What did Garrosh tell you, $N?' WHERE `word_id` = 199830; -- locales_npc_text - Text0_1 : entry=9819
UPDATE `trans_words` SET `content_loc10`='What do I care if these she-wolves and the frost giants kill each other every day?$B$BNo, no... I want Thorim to break out of his stupor and rally all his allies to retake Ulduar.$B$BThe world will be torn asunder if he fails to accomplish this!$B$BAnd of course, I\'ll reward you appropriately.' WHERE `word_id` = 197567; -- locales_npc_text - Text0_1 : entry=13780
