UPDATE `trans_words` SET `content_loc10`='You will find the Exodar\'s skinning trainers in the Trader\'s Tier with the leatherworkers. May the Light bless you.' WHERE `word_id` = 198667; -- locales_npc_text - Text0_1 : entry=9557
UPDATE `trans_words` SET `content_loc10`='You will find the Sunwalker\'s Lodge on Hunter\'s Rise.' WHERE `word_id` = 202357; -- locales_npc_text - Text0_1 : entry=17324
UPDATE `trans_words` SET `content_loc10`='You will find the the battlemasters gathered at the Warrior\'s Terrace.' WHERE `word_id` = 196065; -- locales_npc_text - Text0_1 : entry=10217
UPDATE `trans_words` SET `content_loc10`='You will need to find Nat Pagle in Dustwallow Marsh to get any better than you are currently.' WHERE `word_id` = 194375; -- locales_npc_text - Text0_1 : entry=6959
UPDATE `trans_words` SET `content_loc10`='You will not be disappointed, traveler. Tailoring is a joyous endeavor.' WHERE `word_id` = 193919; -- locales_npc_text - Text0_1 : entry=5439
UPDATE `trans_words` SET `content_loc10`='You will speak when spoken to.' WHERE `word_id` = 201832; -- locales_npc_text - Text0_1 : entry=16667
UPDATE `trans_words` SET `content_loc10`='You wish to aid The Defilers in their fight against The League of Arathor in Arathi Basin?  Very well, you should ask Martin Lindsey for his assistance then. Look for him in the Pools of Vision.' WHERE `word_id` = 194697; -- locales_npc_text - Text1_0 : entry=7648
UPDATE `trans_words` SET `content_loc10`='You wish to help the Forsaken with their fight against the humans in Arathi Basin?  Then go to the Hall of the Brave and speak with Deze Snowbane.' WHERE `word_id` = 194692; -- locales_npc_text - Text1_0 : entry=7644
UPDATE `trans_words` SET `content_loc10`='You won\'t find a finer firearm anywhere else in Orgrimmar.' WHERE `word_id` = 196161; -- locales_npc_text - Text0_1 : entry=16901
UPDATE `trans_words` SET `content_loc10`='You wouldn\'t believe some of the rabble that we\'ve had come through here lately. Most are more trouble than the supposed help that they provide. Nothing at all like you.$B$BYou sure you don\'t want to sign up with the Farstriders?' WHERE `word_id` = 195259; -- locales_npc_text - Text0_1 : entry=8572
UPDATE `trans_words` SET `content_loc10`='You!  $N!  What are you doing wandering around the village like this?  There are Alliance foes in Alterac!  We must drive them out!$B$BOur supplies are too low to launch a large ground assault, so speak with our quatermaster. He\'ll tell you how you can increase our supply base.' WHERE `word_id` = 194234; -- locales_npc_text - Text0_1 : entry=6437
UPDATE `trans_words` SET `content_loc10`='You! COME! Drink, feast, rest for the next battle.$B$BBut do not fight! Or I will feed you to my wolves.' WHERE `word_id` = 199536; -- locales_npc_text - Text0_1 : entry=14077
UPDATE `trans_words` SET `content_loc10`='You$G owe: must; go to the Thousand Needles and provide assistance. You are welcome!' WHERE `word_id` = 280063; -- locales_npc_text - Text2_1 : entry=15837
UPDATE `trans_words` SET `content_loc10`='You\'d best scram, $N. I do not want Jonah\'s death to be linked back to me.$B$BIt\'ll be hard enough to establish myself as leader of this lot without them knowing I had their previous skipper killed.' WHERE `word_id` = 200224; -- locales_npc_text - Text0_1 : entry=12187
UPDATE `trans_words` SET `content_loc10`='You\'d better have good reason to approach me, minnow!' WHERE `word_id` = 200617; -- locales_npc_text - Text1_1 : entry=14827
UPDATE `trans_words` SET `content_loc10`='You\'d better not be causing trouble, $N!' WHERE `word_id` = 195239; -- locales_npc_text - Text0_1 : entry=8537
UPDATE `trans_words` SET `content_loc10`='You\'ll be purchasing no hawkstrider here until you\'ve proven your worth and dedication to our people.$B$BDo not return until you are considered exalted among the blood elves. Away with you!' WHERE `word_id` = 288857; -- locales_npc_text - Text0_1 : entry=10305
UPDATE `trans_words` SET `content_loc10`='You\'ll find Carolai Anise trying to blow up her house at the northwest end of town. Why don\'t you go see if you can help her?' WHERE `word_id` = 193204; -- locales_npc_text - Text1_0 : entry=4082
UPDATE `trans_words` SET `content_loc10`='You\'ll find Elder Grimtotem at the Lariss Pavilion in Feralas.' WHERE `word_id` = 196628; -- locales_npc_text - Text0_1 : entry=8192
UPDATE `trans_words` SET `content_loc10`='You\'ll find Elder Northal inside the Transitus Shield in Coldarra.' WHERE `word_id` = 199405; -- locales_npc_text - Text0_1 : entry=13809
UPDATE `trans_words` SET `content_loc10`='You\'ll find Elder Whurain at Camp Oneqwah in Grizzly Hills.' WHERE `word_id` = 200311; -- locales_npc_text - Text0_1 : entry=13818
UPDATE `trans_words` SET `content_loc10`='You\'ll find Horace Alder pacing about above the ground level of Lady Proudmoore\'s tower.' WHERE `word_id` = 202818; -- locales_npc_text - Text0_1 : entry=18088
UPDATE `trans_words` SET `content_loc10`='You\'ll find it in the northernmost part of the Dwarven District, near the Northern Landing.' WHERE `word_id` = 201782; -- locales_npc_text - Text0_1 : entry=16602
UPDATE `trans_words` SET `content_loc10`='You\'ll find Krendle Bigpockets near the zeppelin\'s perch on the western edge of Spirit Rise. I\'m not fond of the goblin contraption flying through here... but it will take you to Orgrimmar if you can stand it.' WHERE `word_id` = 199680; -- locales_npc_text - Text0_1 : entry=14658
UPDATE `trans_words` SET `content_loc10`='You\'ll find reagants and more at the Sisters Sorcerous in western Dalaran.$B$BJust mind the Mooreheads\' black cat...' WHERE `word_id` = 199549; -- locales_npc_text - Text0_1 : entry=14104
UPDATE `trans_words` SET `content_loc10`='You\'ll find that goblin and his toy shop in the Valley of Honor.' WHERE `word_id` = 192597; -- locales_npc_text - Text1_0 : entry=2653
UPDATE `trans_words` SET `content_loc10`='You\'ll find the city\'s Flight Master at Krasus\' Landing in eastern Dalaran.' WHERE `word_id` = 199496; -- locales_npc_text - Text0_1 : entry=14004
UPDATE `trans_words` SET `content_loc10`='You\'ll find the warlocks in the Magic Quarter\'s main building, but I better not hear of any trouble that comes of this.' WHERE `word_id` = 199757; -- locales_npc_text - Text1_0 : entry=14926
UPDATE `trans_words` SET `content_loc10`='You\'ll find them in the Apothecarium. Names are Doctor Herbert Halsey and Doctor Marsh. Just watch your back.' WHERE `word_id` = 199707; -- locales_npc_text - Text0_1 : entry=14899
UPDATE `trans_words` SET `content_loc10`='You\'ll find what you are looking for at One More Glass in northern Dalaran.' WHERE `word_id` = 199541; -- locales_npc_text - Text0_1 : entry=14095
UPDATE `trans_words` SET `content_loc10`='You\'ll get nothing from me, $R!' WHERE `word_id` = 201336; -- locales_npc_text - Text0_1 : entry=15982
UPDATE `trans_words` SET `content_loc10`='You\'ll have to give me a moment, $C, the naga are attacking us right now. I\'ll speak to you as soon as the attack is done!' WHERE `word_id` = 192769; -- locales_npc_text - Text0_1 : entry=3073
UPDATE `trans_words` SET `content_loc10`='You\'ll need to prove your worthiness before I go giving away my secrets! I\'ve got just the test for you...' WHERE `word_id` = 193713; -- locales_npc_text - Text0_1 : entry=5154
UPDATE `trans_words` SET `content_loc10`='You\'ll want to find Adam Hossack. He\'s got an office in the southern Mage Quarter along the inner ring.$B$BIf he\'s not out exploring, he\'ll likely be there.' WHERE `word_id` = 202264; -- locales_npc_text - Text0_1 : entry=17216
UPDATE `trans_words` SET `content_loc10`='You\'ll want to find Sansha MacVince, just outside Theramore\'s stables.' WHERE `word_id` = 202837; -- locales_npc_text - Text0_1 : entry=18107
UPDATE `trans_words` SET `content_loc10`='You\'ll want to learn from Zamja. Head to Borstan\'s Firepit on the upper level of The Drag by taking the stairs on the left as you enter from the Valley of Strength.' WHERE `word_id` = 192510; -- locales_npc_text - Text1_0 : entry=2500
UPDATE `trans_words` SET `content_loc10`='You\'ll want to look in the Undercity. In the old Ruins of Lordaeron, south of here, you\'ll find an elevator down. Once you\'re there, ask an Undercity Guardian for further directions.' WHERE `word_id` = 199767; -- locales_npc_text - Text0_1 : entry=15005
UPDATE `trans_words` SET `content_loc10`='You\'ll want to visit Simply Enchanting, just outside the Magus Commerce Exchange in northwest Dalaran.' WHERE `word_id` = 199483; -- locales_npc_text - Text0_1 : entry=13990
UPDATE `trans_words` SET `content_loc10`='You\'re a fool that should heed my warnings. No one is invulnerable. You would be wise to remember that bit of advice.' WHERE `word_id` = 192387; -- locales_npc_text - Text0_1 : entry=1837
UPDATE `trans_words` SET `content_loc10`='You\'re a hunter?  Well anyway, you\'ll find Thotar hiding from the sun inside the burrow at the southwest corner of town.' WHERE `word_id` = 193096; -- locales_npc_text - Text1_0 : entry=4013
UPDATE `trans_words` SET `content_loc10`='You\'re a leatherworker by trade, aren\'t you?  It\'s that faint smell of cured hides, I\'d recognize it anywhere.$B$BMy father used to talk about a famous artisan who lived in Southwind Village centuries ago. It\'s a shame the place is in ruins now.' WHERE `word_id` = 194848; -- locales_npc_text - Text0_1 : entry=7820
UPDATE `trans_words` SET `content_loc10`='You\'re a true $G hero : heroine;, $N! The poster-$G boy : girl; for the Bilgewater Cartel.$B$BOur stock is going to skyrocket now and I\'m going to get a raise!' WHERE `word_id` = 201107; -- locales_npc_text - Text0_1 : entry=15673
UPDATE `trans_words` SET `content_loc10`='You\'re always welcome here, $N. Thanks for the laugh -- and don\'t worry about Daryl, he had one coming!' WHERE `word_id` = 192922; -- locales_npc_text - Text0_1 : entry=3525
UPDATE `trans_words` SET `content_loc10`='You\'re big and strong. But you need a bath. Go jump in the pool.' WHERE `word_id` = 202513; -- locales_npc_text - Text2_1 : entry=17550
UPDATE `trans_words` SET `content_loc10`='You\'re definitely strong, $R, but do you have what it takes to become the Warmaul champion?' WHERE `word_id` = 195687; -- locales_npc_text - Text0_1 : entry=9394
UPDATE `trans_words` SET `content_loc10`='You\'re going to pull off a stunt on Jack in front of his boys?$B$BThat takes a good amount of... stupidity. Well, it\'s going to cost you. I\'m not going to risk my life for anything less than a gold. And you best wait til I\'m out of the way before you make a move.' WHERE `word_id` = 197177; -- locales_npc_text - Text0_1 : entry=12180
UPDATE `trans_words` SET `content_loc10`='You\'re here to help with the heavy leather for the Ahn\'Qiraj war effort, are you, $C? Good, good. As you can see I\'ve collected $2066w, but we\'re going to need $2067w in total. That\'s a lot of skinning my friend, so I hope that your knife is sharp. The leather will go to all sorts of uses for making our war materiel and ensuring our success.' WHERE `word_id` = 194903; -- locales_npc_text - Text0_1 : entry=7909
UPDATE `trans_words` SET `content_loc10`='You\'re into leather, eh?  Sounds like you should talk to Simon Tanner over in Old Town. He runs a shop there called the Protective Hide.' WHERE `word_id` = 192147; -- locales_npc_text - Text1_0 : entry=925
UPDATE `trans_words` SET `content_loc10`='You\'re just in time, $C. Do you feel the ground shaking beneath you?$B$BSomething approaches from the hive. Take a place near the grunts, $N. I\'m counting on you!' WHERE `word_id` = 194955; -- locales_npc_text - Text0_1 : entry=8060
UPDATE `trans_words` SET `content_loc10`='You\'re looking for Lau\'Tiki. You\'ll find him on Darkspear Strand out beyond Sen\'jin Village to the south.' WHERE `word_id` = 193122; -- locales_npc_text - Text1_0 : entry=4026
UPDATE `trans_words` SET `content_loc10`='You\'re looking for the Cantrips and Crows Tavern in the western sewers.' WHERE `word_id` = 199509; -- locales_npc_text - Text0_1 : entry=14021
UPDATE `trans_words` SET `content_loc10`='You\'re looking for The Filthy Animal in the Horde quarter of Sunreaver\'s Sanctuary, northeastern Dalaran.$B$BI am told it is a rather rough place, but with an excellent cooking trainer in its kitchen.' WHERE `word_id` = 199486; -- locales_npc_text - Text0_1 : entry=13993
UPDATE `trans_words` SET `content_loc10`='You\'re looking very handsome today, Martek.' WHERE `word_id` = 202200; -- locales_npc_text - Text4_1 : entry=17143
UPDATE `trans_words` SET `content_loc10`='You\'re no Scourge. What you doin\' here, mon?' WHERE `word_id` = 197488; -- locales_npc_text - Text1_0 : entry=13390
UPDATE `trans_words` SET `content_loc10`='You\'re not a druid!' WHERE `word_id` = 193535; -- locales_npc_text - Text0_1 : entry=4913
UPDATE `trans_words` SET `content_loc10`='You\'re not from Twilight\'s Hammer. What is happening out there?' WHERE `word_id` = 201425; -- locales_npc_text - Text3_0 : entry=16078
UPDATE `trans_words` SET `content_loc10`='You\'re not quite in my league, honey.' WHERE `word_id` = 202504; -- locales_npc_text - Text2_1 : entry=17549
UPDATE `trans_words` SET `content_loc10`='You\'re sweet for asking, $C, but we have all of the light leather that we need. May be that one of the others in charge of collecting around here is in need of your assistance though?' WHERE `word_id` = 194865; -- locales_npc_text - Text0_1 : entry=7842
UPDATE `trans_words` SET `content_loc10`='You\'re the best, $N - thanks for going trick-or-treating for me!' WHERE `word_id` = 194840; -- locales_npc_text - Text0_1 : entry=7809
UPDATE `trans_words` SET `content_loc10`='You\'re welcome to stay at the Farstrider Enclave for as long as you\'d like, $C. We welcome any who would aid us in our endeavors in the Ghostlands.' WHERE `word_id` = 195205; -- locales_npc_text - Text0_1 : entry=8487
UPDATE `trans_words` SET `content_loc10`='You\'ve already done me a great service, $N. For that I thank you.$B$BThe least I can do now is tell you what brought me here: My long time friend Trey Lightforge and I came to Jaedenar to investigate the Shadow Council when they caught us unprepared. They took Trey deeper into Shadow Hold and put me in here after taking all of our equipment... I can still hear his screams from below, $N. They were... inhuman. I don\'t know what they\'ve done to him, but they will pay for it.' WHERE `word_id` = 193227; -- locales_npc_text - Text0_1 : entry=4113
UPDATE `trans_words` SET `content_loc10`='You\'ve caught me at a critical time in my research!  No worries, you\'re welcome to join me in quiet contemplation... emphasis on "quiet", if you don\'t mind though.' WHERE `word_id` = 194823; -- locales_npc_text - Text1_0 : entry=15296
UPDATE `trans_words` SET `content_loc10`='You\'ve come further than many, $N. Respect the elements around you and you shall fare far greater than most.' WHERE `word_id` = 192393; -- locales_npc_text - Text0_1 : entry=1916
UPDATE `trans_words` SET `content_loc10`='You\'ve got a miscievious look in your eye. What have you been up to, babe?$B$BWell?' WHERE `word_id` = 200527; -- locales_npc_text - Text7_1 : entry=14681
UPDATE `trans_words` SET `content_loc10`='You\'ve got the hang of it. I can\'t teach you any more, but talk to Arthur Moore, a Forsaken who works in the Rogues\' Quarter of the Undercity. Tell him I sent you.' WHERE `word_id` = 193822; -- locales_npc_text - Text0_1 : entry=5283
UPDATE `trans_words` SET `content_loc10`='You\'ve got the moves, $N. Use them!' WHERE `word_id` = 200891; -- locales_npc_text - Text0_1 : entry=15365
UPDATE `trans_words` SET `content_loc10`='You\'ve heard about the Tram have you?  What a ride that thing is!  You\'ll find it in the Dwarven District towards the back. Oh, and be sure to keep your arms and legs inside the tram while the tram is in motion.' WHERE `word_id` = 193041; -- locales_npc_text - Text1_0 : entry=3813
UPDATE `trans_words` SET `content_loc10`='You\'ve managed to walk right into my trap!' WHERE `word_id` = 202561; -- locales_npc_text - Text0_1 : entry=17590
UPDATE `trans_words` SET `content_loc10`='Younger races love the Winter Veil festival. The celebration center is located in the bank of Ironforge.' WHERE `word_id` = 279801; -- locales_npc_text - Text0_1 : entry=9690
UPDATE `trans_words` SET `content_loc10`='Your aid is required, $N. Help us stop the brutish Horde!' WHERE `word_id` = 201311; -- locales_npc_text - Text0_1 : entry=15946
UPDATE `trans_words` SET `content_loc10`='Your assistance fighting the Burning Legion would be most appreciated. Lady Jaina Proudmoore could use your help on the front lines.' WHERE `word_id` = 195694; -- locales_npc_text - Text0_1 : entry=9408
UPDATE `trans_words` SET `content_loc10`='Your cities will fall, $R. You\'ll perish in the darkness of the new regime!' WHERE `word_id` = 201338; -- locales_npc_text - Text2_1 : entry=15982
UPDATE `trans_words` SET `content_loc10`='Your deeds shall live on forever in the memories of every blue dragon, $N.' WHERE `word_id` = 202798; -- locales_npc_text - Text0_1 : entry=18043
UPDATE `trans_words` SET `content_loc10`='Your education is nearly complete. One final task is all that stands between you and the culmination of all your efforts.' WHERE `word_id` = 193801; -- locales_npc_text - Text0_1 : entry=5255
UPDATE `trans_words` SET `content_loc10`='Your education is progressing well. You should be proud of your accomplishments.' WHERE `word_id` = 193661; -- locales_npc_text - Text0_1 : entry=5098
UPDATE `trans_words` SET `content_loc10`='Your efforts here shall not be forgotten, $N! I shall make certain of that.' WHERE `word_id` = 200623; -- locales_npc_text - Text0_1 : entry=14834
UPDATE `trans_words` SET `content_loc10`='Your errand must mean much, $C, to draw you so deep into the marsh...' WHERE `word_id` = 192981; -- locales_npc_text - Text0_1 : entry=3590
UPDATE `trans_words` SET `content_loc10`='Your help was invaluable, my friend.$B$BI can assure you that the Cenarion Expedition will do everything in its power to counter any future tainted crystals dropped from the heavens by Illidan.' WHERE `word_id` = 196023; -- locales_npc_text - Text0_1 : entry=10109
UPDATE `trans_words` SET `content_loc10`='Your interruption is costing me valuable time, this had better be good.' WHERE `word_id` = 192856; -- locales_npc_text - Text0_1 : entry=3418
UPDATE `trans_words` SET `content_loc10`='Your knowledge is great, traveler. You must learn from one who is wiser than I. Many tales have been told of the wisdom of Josef Gregorian. You may find him in Undercity.' WHERE `word_id` = 193967; -- locales_npc_text - Text0_1 : entry=5546
UPDATE `trans_words` SET `content_loc10`='Your name seems to be on everyone\'s lips around the outpost. Don\'t let it get to your head; I know that you can do better!$B$BI\'ll be watching you, rookie.' WHERE `word_id` = 196546; -- locales_npc_text - Text0_1 : entry=10994
UPDATE `trans_words` SET `content_loc10`='Your potion-making skills are beyond mine. Seek out the undead Doctor Herbert Halsey in the Apothecarium of the Undercity. He has the skill to train you further, and you can witness firsthand his people\'s plight.' WHERE `word_id` = 193649; -- locales_npc_text - Text0_1 : entry=5083
UPDATE `trans_words` SET `content_loc10`='Your search is almost completed. Seek your truth in the entrance room of Thrall\'s Fortress in the Valley of Wisdom.' WHERE `word_id` = 192546; -- locales_npc_text - Text1_0 : entry=2562
UPDATE `trans_words` SET `content_loc10`='Your skill at brewing potions needs to be greater before you can become one of my students. In the meantime, perhaps Sylvanna Forestmoon or Milla Fairancora can help you, just over there.' WHERE `word_id` = 193660; -- locales_npc_text - Text0_1 : entry=5097
UPDATE `trans_words` SET `content_loc10`='Your skill displays an enduring affection for our art, but I would see further evidence of your commitment. I shall charge you with a task. Fulfill it, and I will be content.' WHERE `word_id` = 193668; -- locales_npc_text - Text0_1 : entry=5106
UPDATE `trans_words` SET `content_loc10`='Your skills are beyond my reach. It is time for you to face new challenges. Taladan will help you prepare. He is standing by the table there.' WHERE `word_id` = 193743; -- locales_npc_text - Text0_1 : entry=5188
UPDATE `trans_words` SET `content_loc10`='Your skills are truly becoming extraordinary. Now, let us continue your training...' WHERE `word_id` = 193795; -- locales_npc_text - Text0_1 : entry=5247
UPDATE `trans_words` SET `content_loc10`='Your talents excel! You are truly inspired. To continue your pursuit of knowledge, speak with Georgio Bolero in Stormwind. He is held in high regard, and will be able to further your instruction.' WHERE `word_id` = 193963; -- locales_npc_text - Text0_1 : entry=5541
UPDATE `trans_words` SET `content_loc10`='Your time is coming to an end.' WHERE `word_id` = 202133; -- locales_npc_text - Text0_1 : entry=17057
UPDATE `trans_words` SET `content_loc10`='Your voice is hoarse from yelling huh?  Find the Orgrimmar Auction House in the Valley of Strength.' WHERE `word_id` = 192772; -- locales_npc_text - Text1_0 : entry=3075
UPDATE `trans_words` SET `content_loc10`='Zalazane\'s mindless servants were killin\' all de beasts o\' de isles, burnin\' up de trees and plants too. Gonk needed us ta save de life on de isles. But since we not be able ta draw upon de power o\' de loa wit Zalazane in power, Gonk be showin\' us a new way ta connect wit ALL de spirits o\' nature, ta work WIT de spirits, not just be servin\' a single loa at a time. $B$BIt be difficult at first, ya, mon, but Gonk be showin\' us how ta also reach inta de Emerald Dream ta speak wit de spirits and learn from dem directly!' WHERE `word_id` = 202585; -- locales_npc_text - Text0_1 : entry=17633
UPDATE `trans_words` SET `content_loc10`='Zamja be de best cook in Orgrimmar, mon.$B$BYou can find her just south of de valley\'s central building. Batamsi sells de food Zamja makes, too, if ya just be hungry.' WHERE `word_id` = 201945; -- locales_npc_text - Text0_1 : entry=16807
UPDATE `trans_words` SET `content_loc10`='Zim\'Torga is able to provide us with a certain level of protection, but she has been greatly weakened by the presence of the Scourge and the actions of the Drakkari.$B$BIt falls to me and Ahunae to see to the rest of our defenses.' WHERE `word_id` = 197445; -- locales_npc_text - Text0_1 : entry=13180
UPDATE `trans_words` SET `content_loc10`='Zippik would drive us straight into the Bluff if I wasn\'t here. Moron.' WHERE `word_id` = 197748; -- locales_npc_text - Text0_1 : entry=14614
UPDATE `trans_words` SET `content_loc10`='Zombies, goblin or not, are an abomination in the eyes of the Light. Besides, there\'s no profit in them.$B$BNot yet anyway.' WHERE `word_id` = 202625; -- locales_npc_text - Text0_1 : entry=17698
UPDATE `trans_words` SET `content_loc10`='Zul\'Drak belongs to da Drakkari, mon. Always will!' WHERE `word_id` = 197492; -- locales_npc_text - Text1_0 : entry=13391
UPDATE `trans_words` SET `content_loc10`='Zul\'Farrak in Tanaris Desert is Elder Wildmane\'s home.' WHERE `word_id` = 196312; -- locales_npc_text - Text0_1 : entry=8198

