UPDATE `locales_achievement_reward` SET `subject_loc2`='Horde War Wolf',`text_loc2`='How many Alliance heads have fallen into the dust by your hand? How many Victories won, enemies crushed, battefields(sic) taken?$B$BThe eyes of Hellscream have been upon you, and they are pleased. Ride on proudly, for the Horde!$B$B--Warchief Garrosh Hellscream' WHERE `entry`=5325;
