UPDATE `trans_words` SET `content_loc3`='Ambermill Mage slain' WHERE `word_id` = 184770; -- locales_quest - ObjectiveText1 : entry=27513
UPDATE `trans_words` SET `content_loc3`='Amberwind was making use of an Arcane Construct? Hah! How delightfully inexperienced. It\'s like watching children play with firearms. Let\'s take a look at that journal, $N. Hold it up to the scrying stone... keep it still ...' WHERE `word_id` = 174339; -- locales_quest - offerrewardtext : entry=14428
