UPDATE `trans_words` SET `content_loc3`='And good riddance! Thandol Span\'s safe thanks to you, $N, and hopefully Calamoth is down for good this time.' WHERE `word_id` = 180000; -- locales_quest - offerrewardtext : entry=26128
UPDATE `trans_words` SET `content_loc3`='And here I thought that today couldn\'t get any better. I enjoy being wrong, occasionally.' WHERE `word_id` = 184686; -- locales_quest - offerrewardtext : entry=27493
