UPDATE `trans_words` SET `content_loc3`='I knew you had it in you, boss-man. You know what they say: You can take the $R out of the islands, but you can\'t take the murderous tendencies out of $N.' WHERE `word_id` = 181235; -- locales_quest - offerrewardtext : entry=26489
