UPDATE `trans_words` SET `content_loc3`='Izzy\'s mind freed' WHERE `word_id` = 176426; -- locales_quest - ObjectiveText3 : entry=25122
UPDATE `trans_words` SET `content_loc3`='Jadefire Glen satyr slain' WHERE `word_id` = 186545; -- locales_quest - ObjectiveText1 : entry=27997
UPDATE `trans_words` SET `content_loc3`='Jadefire Rogue or Shadowstalker slain' WHERE `word_id` = 186899; -- locales_quest - ObjectiveText1 : entry=28102
