UPDATE `trans_words` SET `content_loc3`='Thank the Makers! It\'s downright pristine. Those orcs couldn\'t figure out the tap-lock, it seems...and couldn\'t hack through the mastercraft work of Ironforge\'s keg-crafters with their crude weapons!' WHERE `word_id` = 177328; -- locales_quest - offerrewardtext : entry=25395
UPDATE `trans_words` SET `content_loc3`='Thank the spirits you\'re here! We must summon Ahune quickly before it\'s too late...' WHERE `word_id` = 274225; -- locales_quest - offerrewardtext : entry=11955
