UPDATE `trans_words` SET `content_loc3`='This is just the beginning, $N. If Deathwing\'s minions can bury entire cities, we have little chance of defeating them through conventional means.' WHERE `word_id` = 185173; -- locales_quest - offerrewardtext : entry=27628
