UPDATE `trans_words` SET `content_loc3`='Well well well!  Someone who goes and gets things done!  Here\'s your cut, chief. I\'ll be seein\' you around.' WHERE `word_id` = 184531; -- locales_quest - offerrewardtext : entry=27458
