UPDATE `trans_words` SET `content_loc3`='You bring word of victory, just as I expected!  Any triumph in Tol Barad is in turn a triumph for the Horde. Have no doubt, your efforts here will be rewarded.' WHERE `word_id` = 189511; -- locales_quest - offerrewardtext : entry=28884
UPDATE `trans_words` SET `content_loc3`='You brought me my treasure!  What a sweetheart!' WHERE `word_id` = 176612; -- locales_quest - offerrewardtext : entry=25176
