UPDATE `trans_words` SET `content_loc3`='You were sent from Agmar\'s Hammer? It\'s about time!$B$BNo, the help is not for me. I simply have no one else to spare, but you\'ll do.' WHERE `word_id` = 159897; -- locales_quest - offerrewardtext : entry=12488
