UPDATE `trans_words` SET `content_loc3`='You\'ve done well, $N. This will bring us a step closer to shifting Illidan\'s focus away from us and from...$B$B<Akama looks in Maiev Shadowsong\'s direction.>$B$BMore work remains to be done, $N.' WHERE `word_id` = 153023; -- locales_quest - offerrewardtext : entry=10946
UPDATE `trans_words` SET `content_loc3`='You\'ve done well, $N. Unfortunately the battle is far from over.' WHERE `word_id` = 172756; -- locales_quest - offerrewardtext : entry=13900
UPDATE `trans_words` SET `content_loc3`='You\'ve done well, $N. We cannot allow the Twilight\'s Hammer spies this close to the world tree!' WHERE `word_id` = 177236; -- locales_quest - offerrewardtext : entry=25370
UPDATE `trans_words` SET `content_loc3`='You\'ve done well, $N. We will use this to draw out the Twilight matriarch.' WHERE `word_id` = 177839; -- locales_quest - offerrewardtext : entry=25552
UPDATE `trans_words` SET `content_loc3`='You\'ve done well, $N. We\'re in the process of preparing the attack now.' WHERE `word_id` = 187506; -- locales_quest - offerrewardtext : entry=28248
UPDATE `trans_words` SET `content_loc3`='You\'ve done well, $N. With our arcane sanctums not operating at full capacity, these power cores will go a long way in supplementing our needs.' WHERE `word_id` = 146005; -- locales_quest - offerrewardtext : entry=8472
