UPDATE `trans_words` SET `content_loc3`='You\'ve proved your worth. $R. Up to a certain point. at least.$B$BThe favor of Nozdormu\'s prime mate is not obtained quickly.' WHERE `word_id` = 151156; -- locales_quest - offerrewardtext : entry=10467
UPDATE `trans_words` SET `content_loc3`='You\'ve proven a capable student. When it is time to resume your training, return to me and I will show you other techniques.' WHERE `word_id` = 182876; -- locales_quest - offerrewardtext : entry=26945
UPDATE `trans_words` SET `content_loc3`='You\'ve proven a great ally. I would have never expected such upon our first meeting.' WHERE `word_id` = 187272; -- locales_quest - offerrewardtext : entry=28191
