UPDATE db_script_string SET content_loc3='Wait a second. Grain isn\'t supposed to smell like THAT! I better go find a guard.' WHERE entry=2000001137; /* content: Wait a second. Grain isn\'t supposed to smell like THAT! I better go find a guard. */
UPDATE db_script_string SET content_loc3='You don\'t mind me checking out your merchandise for signs of tampering, do you?' WHERE entry=2000001138; /* content: You don\'t mind me checking out your merchandise for signs of tampering, do you? */
UPDATE db_script_string SET content_loc3='No, Sergeant.' WHERE entry=2000001139; /* content: No, Sergeant. */
