UPDATE db_script_string SET content_loc3='You have to believe me, I\'m innocent!' WHERE entry=2000001143; /* content: You have to believe me, I\'m innocent! */
UPDATE db_script_string SET content_loc3='We\'ll see about that, Perelli. We\'ll see about that.' WHERE entry=2000001144; /* content: We\'ll see about that, Perelli. We\'ll see about that. */
UPDATE db_script_string SET content_loc3='Martha, I\'m out of flour for bread. You wouldn\'t happen to have any grain from that recent shipment, would you?' WHERE entry=2000001145; /* content: Martha, I\'m out of flour for bread. You wouldn\'t happen to have any grain from that recent shipment, would you? */
