UPDATE db_script_string SET content_loc3='Martha, something\'s wrong with this grain! Some of the Prince\'s soldiers were looking for this. I\'m going to go look for one.' WHERE entry=2000001148; /* content: Martha, something\'s wrong with this grain! Some of the Prince\'s soldiers were looking for this. I\'m going to go look for one. */
UPDATE db_script_string SET content_loc3='Oh, dear.' WHERE entry=2000001149; /* content: Oh, dear. */
UPDATE db_script_string SET content_loc3='Oh, my.' WHERE entry=2000001150; /* content: Oh, my. */
UPDATE db_script_string SET content_loc3='Looks like a storm\'s coming in, Scruffy...' WHERE entry=2000001151; /* content: Looks like a storm\'s coming in, Scruffy... */
UPDATE db_script_string SET content_loc3='%s begins to growl...' WHERE entry=2000001152; /* content: %s begins to growl... */
UPDATE db_script_string SET content_loc3='What\'s wrong, pal?' WHERE entry=2000001153; /* content: What\'s wrong, pal? */
UPDATE db_script_string SET content_loc3='What did you find, boy?' WHERE entry=2000001154; /* content: What did you find, boy? */
UPDATE db_script_string SET content_loc3='This is no good, Scruffy. Stay here and guard the house, I need to go find a soldier.' WHERE entry=2000001155; /* content: This is no good, Scruffy. Stay here and guard the house, I need to go find a soldier. */
UPDATE db_script_string SET content_loc3='This grain shipment has been nothing but trouble!' WHERE entry=2000001156; /* content: This grain shipment has been nothing but trouble! */
UPDATE db_script_string SET content_loc3='I\'m going to lose my on time bonus because of this!' WHERE entry=2000001157; /* content: I\'m going to lose my on time bonus because of this! */
UPDATE db_script_string SET content_loc3='I wasn\'t even supposed to be here today!' WHERE entry=2000001158; /* content: I wasn\'t even supposed to be here today! */
UPDATE db_script_string SET content_loc3='Well, guess I should load everything back into the cart.' WHERE entry=2000001159; /* content: Well, guess I should load everything back into the cart. */
UPDATE db_script_string SET content_loc3='Oh, come on! My cart broke, my horse lost a shoe, and now the cargo goes bad!' WHERE entry=2000001160; /* content: Oh, come on! My cart broke, my horse lost a shoe, and now the cargo goes bad! */
UPDATE db_script_string SET content_loc3='I guess I\'ll go find the athorities. If I\'m lucky they\'ll tell me it\'s the plague and that we\'re all going to die.' WHERE entry=2000001161; /* content: I guess I\'ll go find the athorities. If I\'m lucky they\'ll tell me it\'s the plague and that we\'re all going to die. */
UPDATE db_script_string SET content_loc3='You\'ve freed me!  The winds speak to my people once again and grant us their strength.  I thank you, stranger.' WHERE entry=2000001200; /* content: You\'ve freed me!  The winds speak to my people once again and grant us their strength.  I thank you, stranger. */
UPDATE db_script_string SET content_loc3='I am free! Spirit of the water back to my people. It will bring us the wisdom that is necessary for survival in this harsh land. I am in your duty, $ N.' WHERE entry=2000001201; /* content: I am free! Spirit of the water back to my people. It will bring us the wisdom that is necessary for survival in this harsh land. I am in your duty, $ N. */
UPDATE db_script_string SET content_loc3='I am free! I ask the spirit of fire back to us so that my people have found the courage to fight with the owners! Thank you, $ R.' WHERE entry=2000001202; /* content: I am free! I ask the spirit of fire back to us so that my people have found the courage to fight with the owners! Thank you, $ R. */
UPDATE db_script_string SET content_loc3='ACK! That\'s the worst thing I\'ve ever tasted! I wouldn\'t let my ram drink that!' WHERE entry=2000001203; /* content: ACK! That\'s the worst thing I\'ve ever tasted! I wouldn\'t let my ram drink that! */
UPDATE db_script_string SET content_loc3='It\'s good that I managed to make copies of the Felsworn Gas Mask...' WHERE entry=2000001204; /* content: It\'s good that I managed to make copies of the Felsworn Gas Mask... */
UPDATE db_script_string SET content_loc3='OK, $N, begin. Fingers crossed arms, legs, eyes, and that you are there you can still cross!...' WHERE entry=2000001209; /* content: OK, $N, begin. Fingers crossed arms, legs, eyes, and that you are there you can still cross!... */
UPDATE db_script_string SET content_loc3='Oh, no ... not worked! Somehow I feel ... easier.' WHERE entry=2000001210; /* content: Oh, no ... not worked! Somehow I feel ... easier. */
UPDATE db_script_string SET content_loc3='Excellent, $N, here she is: masking orc Warsong clan, with power of illusion and supplemented by a premonition!' WHERE entry=2000001211; /* content: Excellent, $N, here she is: masking orc Warsong clan, with power of illusion and supplemented by a premonition! */
UPDATE db_script_string SET content_loc3='With the return of the world starts looking for answers!' WHERE entry=2000001212; /* content: With the return of the world starts looking for answers! */
UPDATE db_script_string SET content_loc3='Do not go too far down the corridor, $N. Stay with the captain and his men ... dead you have to anything.' WHERE entry=2000001213; /* content: Do not go too far down the corridor, $N. Stay with the captain and his men ... dead you have to anything. */
UPDATE db_script_string SET content_loc3='Perfect.  As long as you don\'t go hacking and slashing your way through, they\'ll never know the difference.' WHERE entry=2000001214; /* content: Perfect.  As long as you don\'t go hacking and slashing your way through, they\'ll never know the difference. */
UPDATE db_script_string SET content_loc3='He wants me to do WHAT?' WHERE entry=2000001215; /* content: He wants me to do WHAT? */
UPDATE db_script_string SET content_loc3='Well, he will not stingy with the drink, if he really wants to see my people abandoned their studies and started working on the military!' WHERE entry=2000001216; /* content: Well, he will not stingy with the drink, if he really wants to see my people abandoned their studies and started working on the military! */
UPDATE db_script_string SET content_loc3='read the latest entry in the Brann journal.' WHERE entry=2000001217; /* content: read the latest entry in the Brann journal. */
UPDATE db_script_string SET content_loc3='Unbelievable! It says that Brann discovered a mysterious slab, covered with runes, in the depths of Thor Modan!' WHERE entry=2000001218; /* content: Unbelievable! It says that Brann discovered a mysterious slab, covered with runes, in the depths of Thor Modan! */
UPDATE db_script_string SET content_loc3='Brann writes that he worked on a guide for those runic symbols, when attacked by the giants with their allies.' WHERE entry=2000001219; /* content: Brann writes that he worked on a guide for those runic symbols, when attacked by the giants with their allies. */
UPDATE db_script_string SET content_loc3='He deciphers aloud part of the inscriptions on rune plate, but it seems that he wrote his translation.' WHERE entry=2000001220; /* content: He deciphers aloud part of the inscriptions on rune plate, but it seems that he wrote his translation. */
UPDATE db_script_string SET content_loc3='By the beard of Magni, we need to recreate the key, which he used to read the words on the stove. We could go right on his heels!' WHERE entry=2000001221; /* content: By the beard of Magni, we need to recreate the key, which he used to read the words on the stove. We could go right on his heels! */
UPDATE db_script_string SET content_loc3='Finally we have the parts to repair the Spirit of Gnomeregan.' WHERE entry=2000001222; /* content: Finally we have the parts to repair the Spirit of Gnomeregan. */
UPDATE db_script_string SET content_loc3='Well, a little tweak here, a little set up here ...' WHERE entry=2000001223; /* content: Well, a little tweak here, a little set up here ... */
UPDATE db_script_string SET content_loc3='And you - Spirit Gnomregan!' WHERE entry=2000001224; /* content: And you - Spirit Gnomregan! */
UPDATE db_script_string SET content_loc3='You can use it, but do not complain: after repair of the seats can stick teeth of a saw or something like that. What I had - all went to work.' WHERE entry=2000001225; /* content: You can use it, but do not complain: after repair of the seats can stick teeth of a saw or something like that. What I had - all went to work. */
UPDATE db_script_string SET content_loc3='It\'s those blasted iron dwarves again! Somehow, they found him before we did. ' WHERE entry=2000001227; /* content: It\'s those blasted iron dwarves again! Somehow, they found him before we did. */
UPDATE db_script_string SET content_loc3='They\'re not going to give up until they find him. We can\'t allow that to happen, $N, and Brann knows it.' WHERE entry=2000001228; /* content: They\'re not going to give up until they find him. We can\'t allow that to happen, $N, and Brann knows it. */
UPDATE db_script_string SET content_loc3='There are some rather cryptic instructions in this note. Brann must\'ve had some kind of backup plan, but he can\'t keep evading capture forever...' WHERE entry=2000001229; /* content: There are some rather cryptic instructions in this note. Brann must\'ve had some kind of backup plan, but he can\'t keep evading capture forever... */
UPDATE db_script_string SET content_loc3='It\'s up to you to find him before the irons do!' WHERE entry=2000001230; /* content: It\'s up to you to find him before the irons do! */
UPDATE db_script_string SET content_loc3='Hey, thanks.' WHERE entry=2000001231; /* content: Hey, thanks. */
UPDATE db_script_string SET content_loc3='%s reads the leaflet.' WHERE entry=2000001232; /* content: %s reads the leaflet. */
UPDATE db_script_string SET content_loc3='...receive 50 percent of the clothes deserters? What is this rubbish?' WHERE entry=2000001233; /* content: ...receive 50 percent of the clothes deserters? What is this rubbish? */
UPDATE db_script_string SET content_loc3='What a crazy crowd! You really believe in this nonsense?' WHERE entry=2000001234; /* content: What a crazy crowd! You really believe in this nonsense? */
UPDATE db_script_string SET content_loc3='Look around, $N. Maybe you\'ll see something that I missed.' WHERE entry=2000001235; /* content: Look around, $N. Maybe you\'ll see something that I missed. */
UPDATE db_script_string SET content_loc3='%s points to a pile of books...' WHERE entry=2000001236; /* content: %s points to a pile of books... */
UPDATE db_script_string SET content_loc3='A stone dam was saved! The triple "Hurray!" you, $N!' WHERE entry=2000001237; /* content: A stone dam was saved! The triple "Hurray!" you, $N! */
UPDATE db_script_string SET content_loc3='$N, look, please, Vylestem Vines, which I brought from the caves, so you\'d know what to look for...' WHERE entry=2000001238; /* content: $N, look, please, Vylestem Vines, which I brought from the caves, so you\'d know what to look for... */
UPDATE db_script_string SET content_loc3='About, the spirit of a tiger, $N appeals to you! Have put on us the force and help us to find answers to our questions!' WHERE entry=2000001239; /* content: About, the spirit of a tiger, $N appeals to you! Have put on us the force and help us to find answers to our questions! */
UPDATE db_script_string SET content_loc3='We\'d do well not to be reckless. There\'s still much to learn about this land and about the enemy.' WHERE entry=2000005245; /* content: We\'d do well not to be reckless. There\'s still much to learn about this land and about the enemy. */
UPDATE db_script_string SET content_loc3='Prudence should not mean granting our enemy unnecessary advantages.' WHERE entry=2000005246; /* content: Prudence should not mean granting our enemy unnecessary advantages. */
UPDATE db_script_string SET content_loc3='We will act when we know what we need, old friend. The druids have proven wise and capable of making their own decisions.' WHERE entry=2000005247; /* content: We will act when we know what we need, old friend. The druids have proven wise and capable of making their own decisions. */
UPDATE db_script_string SET content_loc3='All my bags are packed, I\'m ready to go.' WHERE entry=2000005248; /* content: All my bags are packed, I\'m ready to go. */
UPDATE db_script_string SET content_loc3='The ship\'s not ready yet. We still need to calibrate the fuse length to make sure that it doesn\'t burn out and leave you up there alone.' WHERE entry=2000005249; /* content: The ship\'s not ready yet. We still need to calibrate the fuse length to make sure that it doesn\'t burn out and leave you up there alone. */
UPDATE db_script_string SET content_loc3='You need to do what? All this science. I don\'t understand... Look, this is just my job, five days a week.' WHERE entry=2000005250; /* content: You need to do what? All this science. I don\'t understand... Look, this is just my job, five days a week. */
UPDATE db_script_string SET content_loc3='Essentially, it\'s going to be a long, long time till we are ready to launch. Maybe you should just head back home to your family.' WHERE entry=2000005251; /* content: Essentially, it\'s going to be a long, long time till we are ready to launch. Maybe you should just head back home to your family. */
UPDATE db_script_string SET content_loc3='I can\'t. I\'m not the man they think I am at home. Besides, I didn\'t bring them out here.' WHERE entry=2000005252; /* content: I can\'t. I\'m not the man they think I am at home. Besides, I didn\'t bring them out here. */
UPDATE db_script_string SET content_loc3='Why not? A family can give you strength.' WHERE entry=2000005253; /* content: Why not? A family can give you strength. */
UPDATE db_script_string SET content_loc3='This isn\'t the kind of place to raise your kids. It\'s cold, and there\'d be no one to raise them.' WHERE entry=2000005254; /* content: This isn\'t the kind of place to raise your kids. It\'s cold, and there\'d be no one to raise them. */
UPDATE db_script_string SET content_loc3='Couldn\'t you raise them?' WHERE entry=2000005255; /* content: Couldn\'t you raise them? */
UPDATE db_script_string SET content_loc3='Oh no, no, no... I\'m a rocket man.' WHERE entry=2000005256; /* content: Oh no, no, no... I\'m a rocket man. */
UPDATE db_script_string SET content_loc3='Well, it\'s like I said; it\'s going to be a while before we get this ship ready for flight. We\'ll keep you informed.' WHERE entry=2000005257; /* content: Well, it\'s like I said; it\'s going to be a while before we get this ship ready for flight. We\'ll keep you informed. */
UPDATE db_script_string SET content_loc3='With all due respect, A\'dal sent us here to investigate Kael\'s activities, not mount a full-scaled attack.' WHERE entry=2000005271; /* content: With all due respect, A\'dal sent us here to investigate Kael\'s activities, not mount a full-scaled attack. */
UPDATE db_script_string SET content_loc3='Look, sweetheart. No one\'s talking about storming Tempest Keep ourselves, but when we see a target of opportunity we have to take it.' WHERE entry=2000005272; /* content: Look, sweetheart. No one\'s talking about storming Tempest Keep ourselves, but when we see a target of opportunity we have to take it. */
UPDATE db_script_string SET content_loc3='Hold your tongue, knave! Do not dare use that tone with a priestess of the Light!' WHERE entry=2000005273; /* content: Hold your tongue, knave! Do not dare use that tone with a priestess of the Light! */
UPDATE db_script_string SET content_loc3='I\'m surrounded by idiots.' WHERE entry=2000005274; /* content: I\'m surrounded by idiots. */
UPDATE db_script_string SET content_loc3='The magistrix will be happy to know I\'m fluent in Thalassian. Your courtesy is not lost on me.' WHERE entry=2000005275; /* content: The magistrix will be happy to know I\'m fluent in Thalassian. Your courtesy is not lost on me. */
UPDATE db_script_string SET content_loc3='Back to the topic... we should focus our attention on these manaforges. We need to learn what the enemy is doing with them and why.' WHERE entry=2000005276; /* content: Back to the topic... we should focus our attention on these manaforges. We need to learn what the enemy is doing with them and why. */
UPDATE db_script_string SET content_loc3='We\'ve told you all there is to know! Do you forget that many of us used to be trusted followers of Kael\'thas?' WHERE entry=2000005277; /* content: We\'ve told you all there is to know! Do you forget that many of us used to be trusted followers of Kael\'thas? */
UPDATE db_script_string SET content_loc3='...and just look at where that got us!' WHERE entry=2000005278; /* content: ...and just look at where that got us! */
UPDATE db_script_string SET content_loc3='Even you must admit that your information from defectors is outdated and unreliable. Let us make calculated attacks on these manaforges and gather what information we can.' WHERE entry=2000005279; /* content: Even you must admit that your information from defectors is outdated and unreliable. Let us make calculated attacks on these manaforges and gather what information we can. */
UPDATE db_script_string SET content_loc3='Finally some talk of action from the big guy!' WHERE entry=2000005280; /* content: Finally some talk of action from the big guy! */

