UPDATE db_script_string SET content_loc9='The Lich King tormented me without end, Darion.' WHERE entry=2000000529; /* content: The Lich King tormented me without end, Darion. */
UPDATE db_script_string SET content_loc9='Endlessly, he sought to break my will, to force me to serve him, to bind me to his blade...' WHERE entry=2000000530; /* content: Endlessly, he sought to break my will, to force me to serve him, to bind me to his blade... */
UPDATE db_script_string SET content_loc9='Finally, when events demanded his full attention, he left me.' WHERE entry=2000000531; /* content: Finally, when events demanded his full attention, he left me. */
UPDATE db_script_string SET content_loc9='The one memory, I clung to, Darion... The one thought that kept me from giving in...' WHERE entry=2000000532; /* content: The one memory, I clung to, Darion... The one thought that kept me from giving in... */
UPDATE db_script_string SET content_loc9='It was your sacrifice, my son, that again saved me from eternal peril.' WHERE entry=2000000533; /* content: It was your sacrifice, my son, that again saved me from eternal peril. */
UPDATE db_script_string SET content_loc9='Father...' WHERE entry=2000000534; /* content: Father... */
UPDATE db_script_string SET content_loc9='Father. I feared for your... your sanity.' WHERE entry=2000000535; /* content: Father. I feared for your... your sanity. */
UPDATE db_script_string SET content_loc9='Father... For you, I would give my life a thousand times.' WHERE entry=2000000536; /* content: Father... For you, I would give my life a thousand times. */
UPDATE db_script_string SET content_loc9='%s sniffs the elixir then eagerly digs in!' WHERE entry=2000000537; /* content: %s sniffs the elixir then eagerly digs in! */
UPDATE db_script_string SET content_loc9='And thank you, $N.  Without your aid I may never have found my way to the Forsaken.' WHERE entry=2000000538; /* content: And thank you, $N.  Without your aid I may never have found my way to the Forsaken. */
UPDATE db_script_string SET content_loc9='Farewell, my friend.' WHERE entry=2000000539; /* content: Farewell, my friend. */
UPDATE db_script_string SET content_loc9='It has been a long time, Bethor, my friend.' WHERE entry=2000000540; /* content: It has been a long time, Bethor, my friend. */
UPDATE db_script_string SET content_loc9='When time permits, we must speak at length.  For we have much to discuss.' WHERE entry=2000000541; /* content: When time permits, we must speak at length.  For we have much to discuss. */
UPDATE db_script_string SET content_loc9='%s summons a messenger...' WHERE entry=2000000542; /* content: %s summons a messenger... */
UPDATE db_script_string SET content_loc9='Go, my servant. Send word of Stonetalon to Apothecary Zamah.' WHERE entry=2000000543; /* content: Go, my servant. Send word of Stonetalon to Apothecary Zamah. */
UPDATE db_script_string SET content_loc9='%s gives message to Apothecary Zamah.' WHERE entry=2000000544; /* content: %s gives message to Apothecary Zamah. */
UPDATE db_script_string SET content_loc9='Apothecary Zamah smiles.' WHERE entry=2000000545; /* content: Apothecary Zamah smiles. */
UPDATE db_script_string SET content_loc9='But this the only building that fit Brogg! Goblins make buildings too small!' WHERE entry=2000000546; /* content: But this the only building that fit Brogg! Goblins make buildings too small! */
UPDATE db_script_string SET content_loc9='I thought we talked about this, Brogg. You can\'t stay in this building. Sure, it can house you, but there\'s no room for anyone else!' WHERE entry=2000000547; /* content: I thought we talked about this, Brogg. You can\'t stay in this building. Sure, it can house you, but there\'s no room for anyone else! */
UPDATE db_script_string SET content_loc9='Yes, we said you can stay here, but this building is our headquarters! All of our manuals and blueprints are stored here!' WHERE entry=2000000548; /* content: Yes, we said you can stay here, but this building is our headquarters! All of our manuals and blueprints are stored here! */
UPDATE db_script_string SET content_loc9='I\'m inclined to say they made ogres too big! I can see I\'m not going to convince you to move now, but don\'t think this is permanent!' WHERE entry=2000000549; /* content: I\'m inclined to say they made ogres too big! I can see I\'m not going to convince you to move now, but don\'t think this is permanent! */
UPDATE db_script_string SET content_loc9='You said Brogg could stay here! You promised Brogg you\'d help regain Stonemaul clan mound!' WHERE entry=2000000550; /* content: You said Brogg could stay here! You promised Brogg you\'d help regain Stonemaul clan mound! */
UPDATE db_script_string SET content_loc9='In the throes of the Sundering, Ameth\'Aran was at the whim of the terror that gripped the land. There was little hope for survival.' WHERE entry=2000000551; /* content: In the throes of the Sundering, Ameth\'Aran was at the whim of the terror that gripped the land. There was little hope for survival. */
UPDATE db_script_string SET content_loc9='Athrikus came to us. He told us that he could save us from harm. He cast a spell upon us to protect us from harm.' WHERE entry=2000000552; /* content: Athrikus came to us. He told us that he could save us from harm. He cast a spell upon us to protect us from harm. */
UPDATE db_script_string SET content_loc9='When the shaking stopped, his true motives were revealed. We were trapped, and he was slowly draining our powers.' WHERE entry=2000000553; /* content: When the shaking stopped, his true motives were revealed. We were trapped, and he was slowly draining our powers. */
UPDATE db_script_string SET content_loc9='There were hundreds imprisoned by his spell. Now only a few remain in their prisons. He would speak to us sometimes.' WHERE entry=2000000554; /* content: There were hundreds imprisoned by his spell. Now only a few remain in their prisons. He would speak to us sometimes. */
UPDATE db_script_string SET content_loc9='He worried that his power weakened, that soon even we last of his precious soulgems would fade and die.' WHERE entry=2000000555; /* content: He worried that his power weakened, that soon even we last of his precious soulgems would fade and die. */
UPDATE db_script_string SET content_loc9='His lieutenant, Ilkurd Magthrull possesses a tome which might indicate the location of the remaining soulgems.' WHERE entry=2000000556; /* content: His lieutenant, Ilkurd Magthrull possesses a tome which might indicate the location of the remaining soulgems. */
UPDATE db_script_string SET content_loc9='Hmmm... Where is my fishing hook? Oh, there it is.' WHERE entry=2000000557; /* content: Hmmm... Where is my fishing hook? Oh, there it is. */
UPDATE db_script_string SET content_loc9='Let\'s see if the fish are biting.' WHERE entry=2000000558; /* content: Let\'s see if the fish are biting. */
UPDATE db_script_string SET content_loc9='Hmmm... I think I need better bait. I\'ll check my father\'s wagon.' WHERE entry=2000000559; /* content: Hmmm... I think I need better bait. I\'ll check my father\'s wagon. */
UPDATE db_script_string SET content_loc9='Quillboar scraps! These should do the trick. Fish love Quillboar.' WHERE entry=2000000560; /* content: Quillboar scraps! These should do the trick. Fish love Quillboar. */
UPDATE db_script_string SET content_loc9='Caught one! I\'ll see if any of the guards are hungry. Come on Fang!' WHERE entry=2000000561; /* content: Caught one! I\'ll see if any of the guards are hungry. Come on Fang! */
UPDATE db_script_string SET content_loc9='I caught a fish if you are hungry...' WHERE entry=2000000562; /* content: I caught a fish if you are hungry... */
UPDATE db_script_string SET content_loc9='A nice catch it is at that! You bring honor to the Horde boy, We thank you!' WHERE entry=2000000563; /* content: A nice catch it is at that! You bring honor to the Horde boy, We thank you! */
UPDATE db_script_string SET content_loc9='Would you like something more to eat sir?' WHERE entry=2000000564; /* content: Would you like something more to eat sir? */
UPDATE db_script_string SET content_loc9='More bread boy!' WHERE entry=2000000565; /* content: More bread boy! */
UPDATE db_script_string SET content_loc9='Right away sir.' WHERE entry=2000000566; /* content: Right away sir. */
UPDATE db_script_string SET content_loc9='Here is your bread sir.' WHERE entry=2000000567; /* content: Here is your bread sir. */
UPDATE db_script_string SET content_loc9='Aaahh... Very good. Now scat!' WHERE entry=2000000568; /* content: Aaahh... Very good. Now scat! */
UPDATE db_script_string SET content_loc9='As you wish sir.' WHERE entry=2000000569; /* content: As you wish sir. */
UPDATE db_script_string SET content_loc9='Can I go collect more rocks for my necklace, Papa?' WHERE entry=2000000570; /* content: Can I go collect more rocks for my necklace, Papa? */
UPDATE db_script_string SET content_loc9='Yes you may, Okla. But do not stray too far from home.' WHERE entry=2000000571; /* content: Yes you may, Okla. But do not stray too far from home. */
UPDATE db_script_string SET content_loc9='Dabu! I will be careful Papa!' WHERE entry=2000000572; /* content: Dabu! I will be careful Papa! */
UPDATE db_script_string SET content_loc9='Ooohh... This rock is nice and colorful. Let\'s see if I can find more like it...' WHERE entry=2000000573; /* content: Ooohh... This rock is nice and colorful. Let\'s see if I can find more like it... */
UPDATE db_script_string SET content_loc9='Hmm... That rock is nice, but too small.' WHERE entry=2000000574; /* content: Hmm... That rock is nice, but too small. */
UPDATE db_script_string SET content_loc9='This is a shiney rock. I\'ll put it in my bucket. Ok, I just need a few more...' WHERE entry=2000000575; /* content: This is a shiney rock. I\'ll put it in my bucket. Ok, I just need a few more... */
UPDATE db_script_string SET content_loc9='I bet my friend Gruk would like this rock for the spear he is making! It\'s very sharp... I hope he likes it.' WHERE entry=2000000576; /* content: I bet my friend Gruk would like this rock for the spear he is making! It\'s very sharp... I hope he likes it. */
UPDATE db_script_string SET content_loc9='There... That should be enough rocks to make a necklace. I should probably get home now anyway.' WHERE entry=2000000577; /* content: There... That should be enough rocks to make a necklace. I should probably get home now anyway. */
UPDATE db_script_string SET content_loc9='You\'ll pay for this insult, $R!' WHERE entry=2000000578; /* content: You\'ll pay for this insult, $R! */
UPDATE db_script_string SET content_loc9='Your body is mine! Our fates are forever intertwined!' WHERE entry=2000000579; /* content: Your body is mine! Our fates are forever intertwined! */
UPDATE db_script_string SET content_loc9='The Argent Crusade invites you to test your skills in honorable combat against other heroes of Azeroth at the Argent Tournament.' WHERE entry=2000000580; /* content: The Argent Crusade invites you to test your skills in honorable combat against other heroes of Azeroth at the Argent Tournament. */
UPDATE db_script_string SET content_loc9='The champions of the world\'s great nations have gathered at the Argent Tournament Grounds in northeastern Icecrown to face all challengers.' WHERE entry=2000000581; /* content: The champions of the world\'s great nations have gathered at the Argent Tournament Grounds in northeastern Icecrown to face all challengers. */
UPDATE db_script_string SET content_loc9='You\'re late, overseer.' WHERE entry=2000000582; /* content: You\'re late, overseer. */
UPDATE db_script_string SET content_loc9='Destiny will not wait. Your craftsmen must increase their production of the war golems before the stone giant leader and his army reach Ulduar.' WHERE entry=2000000583; /* content: Destiny will not wait. Your craftsmen must increase their production of the war golems before the stone giant leader and his army reach Ulduar. */
UPDATE db_script_string SET content_loc9='Tell your rune-smiths to continue converting what stone giants you can. Those that will not submit must be destroyed.' WHERE entry=2000000584; /* content: Tell your rune-smiths to continue converting what stone giants you can. Those that will not submit must be destroyed. */
UPDATE db_script_string SET content_loc9='If the stone giants interfere with our work at Ulduar, I will hold you and your thane responsible. Mortals must not be allowed to come to the aid of the giants.' WHERE entry=2000000585; /* content: If the stone giants interfere with our work at Ulduar, I will hold you and your thane responsible. Mortals must not be allowed to come to the aid of the giants. */
UPDATE db_script_string SET content_loc9='Return to your duties, overseer. Be certain to impress upon your workers the urgency of their tasks.' WHERE entry=2000000586; /* content: Return to your duties, overseer. Be certain to impress upon your workers the urgency of their tasks. */
UPDATE db_script_string SET content_loc9='So the oracle has decided to aid you, outlander. Very well, I will offer you what i know.' WHERE entry=2000000587; /* content: So the oracle has decided to aid you, outlander. Very well, I will offer you what i know. */
UPDATE db_script_string SET content_loc9='Your companion seeks the shardhorn patriarch Farunn, who holds himself aloof from his brothers.' WHERE entry=2000000588; /* content: Your companion seeks the shardhorn patriarch Farunn, who holds himself aloof from his brothers. */
UPDATE db_script_string SET content_loc9='Farunn walks the lands north of what your people call the Bittertide Falls. You will find him alone, but you will see her requires no defenders.' WHERE entry=2000000589; /* content: Farunn walks the lands north of what your people call the Bittertide Falls. You will find him alone, but you will see her requires no defenders. */
UPDATE db_script_string SET content_loc9='Return to your companion. Tell him to go forth and claim his prize in Farunn.' WHERE entry=2000000590; /* content: Return to your companion. Tell him to go forth and claim his prize in Farunn. */
UPDATE db_script_string SET content_loc9='Once he has done so, tell him to leave this land in peace. He does not belong here.' WHERE entry=2000000591; /* content: Once he has done so, tell him to leave this land in peace. He does not belong here. */
UPDATE db_script_string SET content_loc9='[Furbolg] Bear witness to the savage acts of our adversaries, $N. They steal our young... our weak... Imprisoned here, they are left to the devices of the Bristlelimb. Set them free, Promised One!' WHERE entry=2000000592; /* content: [Furbolg] Bear witness to the savage acts of our adversaries, $N. They steal our young... our weak... Imprisoned here, they are left to the devices of the Bristlelimb. Set them free, Promised One! */
UPDATE db_script_string SET content_loc9='I haven\'t really figured out what it is, but there\'s something strange about the hot springs...' WHERE entry=2000000593; /* content: I haven\'t really figured out what it is, but there\'s something strange about the hot springs... */
UPDATE db_script_string SET content_loc9='%s tosses the sword into the shallow pool.' WHERE entry=2000000594; /* content: %s tosses the sword into the shallow pool. */
UPDATE db_script_string SET content_loc9='I\'ve found that when I throw something into this water, it has improved incredibly when I retrieve it.' WHERE entry=2000000595; /* content: I\'ve found that when I throw something into this water, it has improved incredibly when I retrieve it. */
UPDATE db_script_string SET content_loc9='Well, whatever it is, it works quite well!' WHERE entry=2000000596; /* content: Well, whatever it is, it works quite well! */
UPDATE db_script_string SET content_loc9='And I am determined to find out more... So if you need my help again, you know where I\'ll be.' WHERE entry=2000000597; /* content: And I am determined to find out more... So if you need my help again, you know where I\'ll be. */
UPDATE db_script_string SET content_loc9='Please, follow me.' WHERE entry=2000000598; /* content: Please, follow me. */
UPDATE db_script_string SET content_loc9='Are those winter hyacinths? For me?' WHERE entry=2000000599; /* content: Are those winter hyacinths? For me? */
UPDATE db_script_string SET content_loc9='It\'s been so long since a traveler has come here bearing flowers.' WHERE entry=2000000600; /* content: It\'s been so long since a traveler has come here bearing flowers. */
UPDATE db_script_string SET content_loc9='The lake has been too lonely these past years. The travelers stopped coming and evil came to these waters.' WHERE entry=2000000601; /* content: The lake has been too lonely these past years. The travelers stopped coming and evil came to these waters. */
UPDATE db_script_string SET content_loc9='Your gift is a rare kindness, traveler. Please take this blade with my gratitude. Long ago, another traveler left it here, but I have little use for it.' WHERE entry=2000000602; /* content: Your gift is a rare kindness, traveler. Please take this blade with my gratitude. Long ago, another traveler left it here, but I have little use for it. */
UPDATE db_script_string SET content_loc9='Thank you, mortal, for freeing me from this curse. I beg you, take this blade' WHERE entry=2000000603; /* content: Thank you, mortal, for freeing me from this curse. I beg you, take this blade */
UPDATE db_script_string SET content_loc9='It has brought me naught but ill. Mayhap you can find someone who will contain its power.' WHERE entry=2000000604; /* content: It has brought me naught but ill. Mayhap you can find someone who will contain its power. */
UPDATE db_script_string SET content_loc9='As soon as the neutralizing agent is mixed with the waters of the lake, water, contaminated crystal, seems to have cleared.' WHERE entry=2000000605; /* content: As soon as the neutralizing agent is mixed with the waters of the lake, water, contaminated crystal, seems to have cleared. */
UPDATE db_script_string SET content_loc9='Treacherous whelp! Sironas will destroy you and your people!' WHERE entry=2000000606; /* content: Treacherous whelp! Sironas will destroy you and your people! */
UPDATE db_script_string SET content_loc9='My brother, Stormhoof, was a far greater warrior than I. While I trained to be the chieftain of our clan, he prepared for a larger quest.' WHERE entry=2000000607; /* content: My brother, Stormhoof, was a far greater warrior than I. While I trained to be the chieftain of our clan, he prepared for a larger quest. */
UPDATE db_script_string SET content_loc9='How could he be in danger? All he wanted was to give us power over the elements that made life harsh for us here.' WHERE entry=2000000608; /* content: How could he be in danger? All he wanted was to give us power over the elements that made life harsh for us here. */
UPDATE db_script_string SET content_loc9='He sought an artifact... a horn, I think. He left the village on a long journey in search of it.' WHERE entry=2000000609; /* content: He sought an artifact... a horn, I think. He left the village on a long journey in search of it. */
UPDATE db_script_string SET content_loc9='I know that a terrible enemy pursued him after he won the horn, but I... I don\'t recall... what happened... next.' WHERE entry=2000000610; /* content: I know that a terrible enemy pursued him after he won the horn, but I... I don\'t recall... what happened... next. */
UPDATE db_script_string SET content_loc9='It is as though my memories are shrouded in mist. I cannot even recall what became of my brother. Is this how you mean that he is in danger?' WHERE entry=2000000611; /* content: It is as though my memories are shrouded in mist. I cannot even recall what became of my brother. Is this how you mean that he is in danger? */
UPDATE db_script_string SET content_loc9='The disturbances in the tomb, they must be involved. Look around you, $N. Do you see them?' WHERE entry=2000000612; /* content: The disturbances in the tomb, they must be involved. Look around you, $N. Do you see them? */
UPDATE db_script_string SET content_loc9='Yes, they are at fault, but I am powerless to stop them. Will you warn the people of Tunka\'lo, stranger, that their past and their ancestors are in danger?' WHERE entry=2000000613; /* content: Yes, they are at fault, but I am powerless to stop them. Will you warn the people of Tunka\'lo, stranger, that their past and their ancestors are in danger? */
UPDATE db_script_string SET content_loc9='The Lorehammer ist reacting. This must be from Stormhoof\'s time.' WHERE entry=2000000614; /* content: The Lorehammer ist reacting. This must be from Stormhoof\'s time. */
UPDATE db_script_string SET content_loc9='The Lorehammer is silent. This isn\'t from Stormhoof\'s time.' WHERE entry=2000000615; /* content: The Lorehammer is silent. This isn\'t from Stormhoof\'s time. */
UPDATE db_script_string SET content_loc9='Good luck... You\'ll need it!' WHERE entry=2000000616; /* content: Good luck... You\'ll need it! */
UPDATE db_script_string SET content_loc9='May the best win!' WHERE entry=2000000617; /* content: May the best win! */
UPDATE db_script_string SET content_loc9='You will not defeat me!' WHERE entry=2000000618; /* content: You will not defeat me! */
UPDATE db_script_string SET content_loc9='You\'re not worthy of Thorim!' WHERE entry=2000000619; /* content: You\'re not worthy of Thorim! */
UPDATE db_script_string SET content_loc9='Scrying upon many insignificant situations within Icecrown,$B you stumble upon something interesting...' WHERE entry=2000000620; /* content: Scrying upon many insignificant situations within Icecrown,$B you stumble upon something interesting... */
UPDATE db_script_string SET content_loc9='The cultist practically sputters.' WHERE entry=2000000621; /* content: The cultist practically sputters. */
UPDATE db_script_string SET content_loc9='My lady.' WHERE entry=2000000622; /* content: My lady. */
UPDATE db_script_string SET content_loc9='Report.' WHERE entry=2000000623; /* content: Report. */
UPDATE db_script_string SET content_loc9='There is word from Jotunheim. The sleep-watchers there believe that they have found someone of significance.' WHERE entry=2000000624; /* content: There is word from Jotunheim. The sleep-watchers there believe that they have found someone of significance. */
UPDATE db_script_string SET content_loc9='Describe this vrykul. What does he look like?' WHERE entry=2000000625; /* content: Describe this vrykul. What does he look like? */
UPDATE db_script_string SET content_loc9='Look like, my lady? A vrykul, I suppose. They did not actually show him to me. Ever since The Shadow Vault....' WHERE entry=2000000626; /* content: Look like, my lady? A vrykul, I suppose. They did not actually show him to me. Ever since The Shadow Vault.... */
UPDATE db_script_string SET content_loc9='I am not interested in excuses. Perhaps they gave you a name?' WHERE entry=2000000627; /* content: I am not interested in excuses. Perhaps they gave you a name? */
UPDATE db_script_string SET content_loc9='A name? Oh, yes, the name! I believe it was Iskalder.' WHERE entry=2000000628; /* content: A name? Oh, yes, the name! I believe it was Iskalder. */
UPDATE db_script_string SET content_loc9='Iskalder? You fool! Have you no idea who that is? He\'s only the greatest vrykul warrior who ever lived!' WHERE entry=2000000629; /* content: Iskalder? You fool! Have you no idea who that is? He\'s only the greatest vrykul warrior who ever lived! */
UPDATE db_script_string SET content_loc9='Return to Jotunheim and tell them to keep him asleep until I arrive. I will judge this vrykul with my own eyes.' WHERE entry=2000000630; /* content: Return to Jotunheim and tell them to keep him asleep until I arrive. I will judge this vrykul with my own eyes. */
UPDATE db_script_string SET content_loc9='Right away, my lady.' WHERE entry=2000000631; /* content: Right away, my lady. */
UPDATE db_script_string SET content_loc9='Morae... Is that you?' WHERE entry=2000000632; /* content: Morae... Is that you? */
UPDATE db_script_string SET content_loc9='No... you\'re not Morae, but I sense that you have met. When you see her, tell her that I survived the crash, only to be done in by the infiltrators.' WHERE entry=2000000633; /* content: No... you\'re not Morae, but I sense that you have met. When you see her, tell her that I survived the crash, only to be done in by the infiltrators. */
UPDATE db_script_string SET content_loc9='Let her know... my last thought was of her...' WHERE entry=2000000634; /* content: Let her know... my last thought was of her... */
UPDATE db_script_string SET content_loc9='Grek try!' WHERE entry=2000000635; /* content: Grek try! */
UPDATE db_script_string SET content_loc9='This oil no good for Grek! What Grek look like to you, some weakling in robes?' WHERE entry=2000000636; /* content: This oil no good for Grek! What Grek look like to you, some weakling in robes? */
UPDATE db_script_string SET content_loc9='%s laughs.' WHERE entry=2000000637; /* content: %s laughs. */
UPDATE db_script_string SET content_loc9='Does it frighten you to know that there are those that would serve the Legion with such devotion as to remain unwavering citizens of your pointless civilizations until called upon?' WHERE entry=2000000638; /* content: Does it frighten you to know that there are those that would serve the Legion with such devotion as to remain unwavering citizens of your pointless civilizations until called upon? */
UPDATE db_script_string SET content_loc9='Live in fear, die by the will of Kael\'thas... It\'s all the same.' WHERE entry=2000000639; /* content: Live in fear, die by the will of Kael\'thas... It\'s all the same. */
UPDATE db_script_string SET content_loc9='And now, I cut you!' WHERE entry=2000000640; /* content: And now, I cut you! */
UPDATE db_script_string SET content_loc9='DIE!' WHERE entry=2000000641; /* content: DIE! */
UPDATE db_script_string SET content_loc9='%s picks up the naga flag.' WHERE entry=2000000642; /* content: %s picks up the naga flag. */
UPDATE db_script_string SET content_loc9='We will not fail!' WHERE entry=2000000643; /* content: We will not fail! */
UPDATE db_script_string SET content_loc9='%s places the crate of parts on the ground.' WHERE entry=2000000644; /* content: %s places the crate of parts on the ground. */
UPDATE db_script_string SET content_loc9='%s grabs a part and puts it in his pocket...' WHERE entry=2000000645; /* content: %s grabs a part and puts it in his pocket... */
UPDATE db_script_string SET content_loc9='There, that should do it...' WHERE entry=2000000646; /* content: There, that should do it... */
UPDATE db_script_string SET content_loc9='Anaya...? Do my eyes deceive me? Is it really you?' WHERE entry=2000000647; /* content: Anaya...? Do my eyes deceive me? Is it really you? */
UPDATE db_script_string SET content_loc9='The ages have been cruel to you and I, my love, but be assured, it is, and at long last we are reunited.' WHERE entry=2000000648; /* content: The ages have been cruel to you and I, my love, but be assured, it is, and at long last we are reunited. */
UPDATE db_script_string SET content_loc9='That the fates should be so cruel as to permit us only this after a thousand years apart...' WHERE entry=2000000649; /* content: That the fates should be so cruel as to permit us only this after a thousand years apart... */
UPDATE db_script_string SET content_loc9='Do you hate me, my love? That I was forced to destroy your living form, that your spirit be released from unhappy bondage.' WHERE entry=2000000650; /* content: Do you hate me, my love? That I was forced to destroy your living form, that your spirit be released from unhappy bondage. */
UPDATE db_script_string SET content_loc9='Let it not trouble your heart, beloved. You have freed me from slavery, and for that I love you all the more.' WHERE entry=2000000651; /* content: Let it not trouble your heart, beloved. You have freed me from slavery, and for that I love you all the more. */
UPDATE db_script_string SET content_loc9='Sadly, even this must be cut short... The ties that bind me to this world weaken, and pull me away...' WHERE entry=2000000652; /* content: Sadly, even this must be cut short... The ties that bind me to this world weaken, and pull me away... */
UPDATE db_script_string SET content_loc9='No! Anaya... Anaya! Don\'t leave me! Please...' WHERE entry=2000000653; /* content: No! Anaya... Anaya! Don\'t leave me! Please... */
UPDATE db_script_string SET content_loc9='Farewell, Cerellean, until we are joined once again...' WHERE entry=2000000654; /* content: Farewell, Cerellean, until we are joined once again... */
UPDATE db_script_string SET content_loc9='Anaya\'s soft voice trails away into the mists. "Know that I love you always..."' WHERE entry=2000000655; /* content: Anaya\'s soft voice trails away into the mists. "Know that I love you always..." */
