UPDATE db_script_string SET content_loc9='Free again? Keeper Mimir\'s work awaits.' WHERE entry=2000000800; /* content: Free again? Keeper Mimir\'s work awaits. */
UPDATE db_script_string SET content_loc9='New directive: assist in the defeat of the iron dwarves.' WHERE entry=2000000801; /* content: New directive: assist in the defeat of the iron dwarves. */
UPDATE db_script_string SET content_loc9='Insolent fool! You thought to steal Zelemar\'s blood? You shall pay with your own!' WHERE entry=2000000802; /* content: Insolent fool! You thought to steal Zelemar\'s blood? You shall pay with your own! */
UPDATE db_script_string SET content_loc9='Fools. Obsidion! Rise, and destroy the interlopers!' WHERE entry=2000000803; /* content: Fools. Obsidion! Rise, and destroy the interlopers! */
UPDATE db_script_string SET content_loc9='Get him out of there!' WHERE entry=2000000804; /* content: Get him out of there! */
UPDATE db_script_string SET content_loc9='Perhaps Ograbisi will use your head as a tiny little hat when I\'m through with you.' WHERE entry=2000000805; /* content: Perhaps Ograbisi will use your head as a tiny little hat when I\'m through with you. */
UPDATE db_script_string SET content_loc9='Where I come from, you get shanked for opening another inmate\'s cell door!' WHERE entry=2000000806; /* content: Where I come from, you get shanked for opening another inmate\'s cell door! */
UPDATE db_script_string SET content_loc9='Ograbisi needs new hat.' WHERE entry=2000000807; /* content: Ograbisi needs new hat. */
UPDATE db_script_string SET content_loc9='I\'ve had just about enough of your incessant whining, captain. What are you even a captain of? I mean, The nerve!' WHERE entry=2000000808; /* content: I\'ve had just about enough of your incessant whining, captain. What are you even a captain of? I mean, The nerve! */
UPDATE db_script_string SET content_loc9='Why don\'t you pick up a hammer and start banging on these structures instead of asking us if i\'ts done every 5 minutes?' WHERE entry=2000000809; /* content: Why don\'t you pick up a hammer and start banging on these structures instead of asking us if i\'ts done every 5 minutes? */
UPDATE db_script_string SET content_loc9='%s shrugs.' WHERE entry=2000000810; /* content: %s shrugs. */
UPDATE db_script_string SET content_loc9='Oh well, back to the drawing board.' WHERE entry=2000000811; /* content: Oh well, back to the drawing board. */
UPDATE db_script_string SET content_loc9='Damnit. How are we ever going to make it through their security measures?' WHERE entry=2000000812; /* content: Damnit. How are we ever going to make it through their security measures? */
UPDATE db_script_string SET content_loc9='Why do you do this? Did I somehow wrong you in life?' WHERE entry=2000000813; /* content: Why do you do this? Did I somehow wrong you in life? */
UPDATE db_script_string SET content_loc9='Ah, I see it now in your mind. This is the work of one of my former students... Mehlar Dawnblade. It is sad to know that his heart has turned so dark.' WHERE entry=2000000814; /* content: Ah, I see it now in your mind. This is the work of one of my former students... Mehlar Dawnblade. It is sad to know that his heart has turned so dark. */
UPDATE db_script_string SET content_loc9='Return to him. Return to Mehlor and tell him that I forgive him and that I understand why he believes what he does.' WHERE entry=2000000815; /* content: Return to him. Return to Mehlor and tell him that I forgive him and that I understand why he believes what he does. */
UPDATE db_script_string SET content_loc9='I can only hope that he will see the Light and instead turn his energies to restoring once-beautiful Quel\'Thalas.' WHERE entry=2000000816; /* content: I can only hope that he will see the Light and instead turn his energies to restoring once-beautiful Quel\'Thalas. */
UPDATE db_script_string SET content_loc9='Stonemasons. . .errr. . Defias be warned: The rusty anchor sinks tonight.' WHERE entry=2000000817; /* content: Stonemasons. . .errr. . Defias be warned: The rusty anchor sinks tonight. */
UPDATE db_script_string SET content_loc9='The boss wants all hands on high alert. The rusty anchor sinks tonight.' WHERE entry=2000000818; /* content: The boss wants all hands on high alert. The rusty anchor sinks tonight. */
UPDATE db_script_string SET content_loc9='Phew! There\'s my lucky hat. I\'ve been looking for it everywhere.' WHERE entry=2000000819; /* content: Phew! There\'s my lucky hat. I\'ve been looking for it everywhere. */
UPDATE db_script_string SET content_loc9='I don\'t know what I was thinking, going out without my sword. I would\'ve put it on if I\'d seen it here...' WHERE entry=2000000820; /* content: I don\'t know what I was thinking, going out without my sword. I would\'ve put it on if I\'d seen it here... */
UPDATE db_script_string SET content_loc9='I can\'t possibly go out without my cloak. I hope it\'s in here...' WHERE entry=2000000821; /* content: I can\'t possibly go out without my cloak. I hope it\'s in here... */
UPDATE db_script_string SET content_loc9='There it is! I could\'ve sworn it wasn\'t here last time I checked...' WHERE entry=2000000822; /* content: There it is! I could\'ve sworn it wasn\'t here last time I checked... */
UPDATE db_script_string SET content_loc9='I can\'t sleep without a good bedtime story. Now I\'m certain to rest well.' WHERE entry=2000000823; /* content: I can\'t sleep without a good bedtime story. Now I\'m certain to rest well. */
UPDATE db_script_string SET content_loc9='What was that noise? It sounded like a roar.' WHERE entry=2000000824; /* content: What was that noise? It sounded like a roar. */
UPDATE db_script_string SET content_loc9='Grimclaw roars at Terenthis to get his attention.' WHERE entry=2000000825; /* content: Grimclaw roars at Terenthis to get his attention. */
UPDATE db_script_string SET content_loc9='Grimclaw?! Easy there, my friend... where is your master Volcor?' WHERE entry=2000000826; /* content: Grimclaw?! Easy there, my friend... where is your master Volcor? */
UPDATE db_script_string SET content_loc9='Grimclaw begins to moan and roar at Terenthis while stomping his paws on the ground.' WHERE entry=2000000827; /* content: Grimclaw begins to moan and roar at Terenthis while stomping his paws on the ground. */
UPDATE db_script_string SET content_loc9='Whoa, whoa there, my friend. One moment...' WHERE entry=2000000828; /* content: Whoa, whoa there, my friend. One moment... */
UPDATE db_script_string SET content_loc9='Terenthis begins to cast a spell on Grimclaw.' WHERE entry=2000000829; /* content: Terenthis begins to cast a spell on Grimclaw. */
UPDATE db_script_string SET content_loc9='There... that should help. Now, tell me what\'s happened, Grimclaw.' WHERE entry=2000000830; /* content: There... that should help. Now, tell me what\'s happened, Grimclaw. */
UPDATE db_script_string SET content_loc9='Grimclaw roars at Terenthis more, but this time the druid seems to understand the bear.' WHERE entry=2000000831; /* content: Grimclaw roars at Terenthis more, but this time the druid seems to understand the bear. */
UPDATE db_script_string SET content_loc9='I understand, my friend. I shall find someone to help your master. Go back to him now, or at least stay close.' WHERE entry=2000000832; /* content: I understand, my friend. I shall find someone to help your master. Go back to him now, or at least stay close. */
UPDATE db_script_string SET content_loc9='Grimclaw roars in acknowledgement at Terenthis.' WHERE entry=2000000833; /* content: Grimclaw roars in acknowledgement at Terenthis. */
UPDATE db_script_string SET content_loc9='If you have the time, Grimclaw and his master Volcor could use your help. If you\'re interested, speak with me further...' WHERE entry=2000000834; /* content: If you have the time, Grimclaw and his master Volcor could use your help. If you\'re interested, speak with me further... */
UPDATE db_script_string SET content_loc9='Please excuse me while I begin my tests...' WHERE entry=2000000835; /* content: Please excuse me while I begin my tests... */
UPDATE db_script_string SET content_loc9='Interesting...' WHERE entry=2000000836; /* content: Interesting... */
UPDATE db_script_string SET content_loc9='But what is this?' WHERE entry=2000000837; /* content: But what is this? */
UPDATE db_script_string SET content_loc9='Oh, my!' WHERE entry=2000000838; /* content: Oh, my! */
UPDATE db_script_string SET content_loc9='If my blood hadn\'t clotted long ago, it would be boiling with rage right now...' WHERE entry=2000000839; /* content: If my blood hadn\'t clotted long ago, it would be boiling with rage right now... */
UPDATE db_script_string SET content_loc9='How dare you take my eye! I will crush your soul and feed it to my blind and wimpering imps!' WHERE entry=2000000840; /* content: How dare you take my eye! I will crush your soul and feed it to my blind and wimpering imps! */
UPDATE db_script_string SET content_loc9='Come to my lair, whelp! Come to me and pay for your insolecne!' WHERE entry=2000000841; /* content: Come to my lair, whelp! Come to me and pay for your insolecne! */
UPDATE db_script_string SET content_loc9='Hail, Gezhe!  Seventeen more orders have been placed in the Hara\'samid Sector.  The markets of heavy veldarite have all but collapsed, as you predicted.' WHERE entry=2000000842; /* content: Hail, Gezhe!  Seventeen more orders have been placed in the Hara\'samid Sector.  The markets of heavy veldarite have all but collapsed, as you predicted. */
UPDATE db_script_string SET content_loc9='Heraazi exports are on the rise, sir.  We\'ve already bribed the appropriate taxation officers and contraband will begin to arrive within days.' WHERE entry=2000000843; /* content: Heraazi exports are on the rise, sir.  We\'ve already bribed the appropriate taxation officers and contraband will begin to arrive within days. */
UPDATE db_script_string SET content_loc9='Nothing surprising there.  Come back with some real news, will you?' WHERE entry=2000000844; /* content: Nothing surprising there.  Come back with some real news, will you? */
UPDATE db_script_string SET content_loc9='As expected.  Very well, dismissed.' WHERE entry=2000000845; /* content: As expected.  Very well, dismissed. */
UPDATE db_script_string SET content_loc9='By your leave, sir.' WHERE entry=2000000846; /* content: By your leave, sir. */
UPDATE db_script_string SET content_loc9='If you\'re here, then it means you are prepared to begin the study of summoning demonic cohorts to do your bidding. We will start with the lowliest creatures you will be able to call and continue from there. Let us begin.' WHERE entry=2000000847; /* content: If you\'re here, then it means you are prepared to begin the study of summoning demonic cohorts to do your bidding. We will start with the lowliest creatures you will be able to call and continue from there. Let us begin. */
UPDATE db_script_string SET content_loc9='The easiest creature for you to summon is the imp. You should already be able to bring forth this minion but for completeness\' sake I will start with him.' WHERE entry=2000000848; /* content: The easiest creature for you to summon is the imp. You should already be able to bring forth this minion but for completeness\' sake I will start with him. */
UPDATE db_script_string SET content_loc9='This foul little beast is the imp. It is small and weak, making it almost useless as a meatshield, and its damage output is mediocre at best. This creature is best used for support of a larger group.' WHERE entry=2000000849; /* content: This foul little beast is the imp. It is small and weak, making it almost useless as a meatshield, and its damage output is mediocre at best. This creature is best used for support of a larger group. */
UPDATE db_script_string SET content_loc9='Now that you have had a chance to study the imp, let us move on to the next minion you will be able to summon, the voidwalker.' WHERE entry=2000000850; /* content: Now that you have had a chance to study the imp, let us move on to the next minion you will be able to summon, the voidwalker. */
UPDATE db_script_string SET content_loc9='This demonic entity is known as the Voidwalker. Its strength and endurance are significant, making it ideal for defense. Send it to attack your enemy, then use it as a shield while you use your spells and abilities to drain away your opponent\'s life.' WHERE entry=2000000851; /* content: This demonic entity is known as the Voidwalker. Its strength and endurance are significant, making it ideal for defense. Send it to attack your enemy, then use it as a shield while you use your spells and abilities to drain away your opponent\'s life. */
UPDATE db_script_string SET content_loc9='If you\'ve never seen one, it is a sight to behold. A very impressive creature both on and off the field of battle. Next, let us take a look at what I am sure all you male students have been waiting for. The succubus.' WHERE entry=2000000852; /* content: If you\'ve never seen one, it is a sight to behold. A very impressive creature both on and off the field of battle. Next, let us take a look at what I am sure all you male students have been waiting for. The succubus. */
UPDATE db_script_string SET content_loc9='All right now. Aside from the obvious distractions a minion like this will provide against your more masculine foes, she is also capable of dealing out impressive amounts of damage. However, her fragile endurance makes her almost useless as a shield.' WHERE entry=2000000853; /* content: All right now. Aside from the obvious distractions a minion like this will provide against your more masculine foes, she is also capable of dealing out impressive amounts of damage. However, her fragile endurance makes her almost useless as a shield. */
UPDATE db_script_string SET content_loc9='Study hard and you might one day be able to summon one on your own, but for now it\'s time to move on to the felhunter.' WHERE entry=2000000854; /* content: Study hard and you might one day be able to summon one on your own, but for now it\'s time to move on to the felhunter. */
UPDATE db_script_string SET content_loc9='What you see before you is a felhunter. This creature\'s natural talents include spell lock and other abilities which make it unequalled when facing a magically attuned opponent.' WHERE entry=2000000855; /* content: What you see before you is a felhunter. This creature\'s natural talents include spell lock and other abilities which make it unequalled when facing a magically attuned opponent. */
UPDATE db_script_string SET content_loc9='When facing a spellcaster of any kind, this feral beast will be your best friend. Now, let us take a look at something a bit different. This next creature will aid your travels and make your future journeys much easier. Let\'s take a look at a felsteed.' WHERE entry=2000000856; /* content: When facing a spellcaster of any kind, this feral beast will be your best friend. Now, let us take a look at something a bit different. This next creature will aid your travels and make your future journeys much easier. Let\'s take a look at a felsteed. */
UPDATE db_script_string SET content_loc9='I doubt you have had much occasion to see such a creature. These demonic equines will make your travels much faster by acting as your mount as long as you control them. However, they are difficult to control, so be sure you are ready before attempting it.' WHERE entry=2000000857; /* content: I doubt you have had much occasion to see such a creature. These demonic equines will make your travels much faster by acting as your mount as long as you control them. However, they are difficult to control, so be sure you are ready before attempting it. */
UPDATE db_script_string SET content_loc9='There you have it. Our lesson on summoning has come to an end. A new class will begin shortly, so if you wish to brush up, feel free to stay around.' WHERE entry=2000000858; /* content: There you have it. Our lesson on summoning has come to an end. A new class will begin shortly, so if you wish to brush up, feel free to stay around. */
UPDATE db_script_string SET content_loc9='The priest kneels in silent prayer.' WHERE entry=2000000859; /* content: The priest kneels in silent prayer. */
UPDATE db_script_string SET content_loc9='Light, give me strength.' WHERE entry=2000000860; /* content: Light, give me strength. */
UPDATE db_script_string SET content_loc9='Light, lead the fallen from untruth to truth, from darkness to Light and from death to immortality.' WHERE entry=2000000861; /* content: Light, lead the fallen from untruth to truth, from darkness to Light and from death to immortality. */
UPDATE db_script_string SET content_loc9='Where do you think you are going soldier? Those are fatal wounds you\'ve got there. Now, get back in tent and let the nurse treat you!' WHERE entry=2000000862; /* content: Where do you think you are going soldier? Those are fatal wounds you\'ve got there. Now, get back in tent and let the nurse treat you! */
UPDATE db_script_string SET content_loc9='Now get out of my way, it\'s time to send the Burning Legion back to the nether!' WHERE entry=2000000863; /* content: Now get out of my way, it\'s time to send the Burning Legion back to the nether! */
UPDATE db_script_string SET content_loc9='Sniff... That\'s the spirit soldier... I only wish we had more soldiers like you in this army. Now go out there and give \'em hell!' WHERE entry=2000000864; /* content: Sniff... That\'s the spirit soldier... I only wish we had more soldiers like you in this army. Now go out there and give \'em hell! */
UPDATE db_script_string SET content_loc9='NOOOOO!!!' WHERE entry=2000000865; /* content: NOOOOO!!! */
UPDATE db_script_string SET content_loc9='Friends... I\'ve failed you...' WHERE entry=2000000866; /* content: Friends... I\'ve failed you... */
UPDATE db_script_string SET content_loc9='No my friend... we failed you... we failed you.' WHERE entry=2000000867; /* content: No my friend... we failed you... we failed you. */
UPDATE db_script_string SET content_loc9='Thank you! I could have not held out for much longer. A terrible thing has happened here.' WHERE entry=2000000868; /* content: Thank you! I could have not held out for much longer. A terrible thing has happened here. */
UPDATE db_script_string SET content_loc9='We believed that the Sanctum was well fortified, but we were not prepareted for the nature of this assault.' WHERE entry=2000000869; /* content: We believed that the Sanctum was well fortified, but we were not prepareted for the nature of this assault. */
UPDATE db_script_string SET content_loc9='The Black Dragonkin materialized from thin air, and set upon us before we could react.' WHERE entry=2000000870; /* content: The Black Dragonkin materialized from thin air, and set upon us before we could react. */
UPDATE db_script_string SET content_loc9='We did not stand a chance. As my brethren perished around me, I managed to retreat hear and bar the entrance.' WHERE entry=2000000871; /* content: We did not stand a chance. As my brethren perished around me, I managed to retreat hear and bar the entrance. */
UPDATE db_script_string SET content_loc9='They slaughtered us with cold efficiency, but the true focus of their interest seemed to be the eggs kept here in the sanctum.' WHERE entry=2000000872; /* content: They slaughtered us with cold efficiency, but the true focus of their interest seemed to be the eggs kept here in the sanctum. */
UPDATE db_script_string SET content_loc9='The commander of the forces on the ground here is a cruel brute named Zarithrian. But I fear there are greater powers at work.' WHERE entry=2000000873; /* content: The commander of the forces on the ground here is a cruel brute named Zarithrian. But I fear there are greater powers at work. */
UPDATE db_script_string SET content_loc9='In their initial assault I caught a glimpse of their true leader, a fearsome full-grown Twilight Dragon.' WHERE entry=2000000874; /* content: In their initial assault I caught a glimpse of their true leader, a fearsome full-grown Twilight Dragon. */
UPDATE db_script_string SET content_loc9='I know not the extent of their plans heroes, but I know this: they cannot be allowed to succeed!' WHERE entry=2000000875; /* content: I know not the extent of their plans heroes, but I know this: they cannot be allowed to succeed! */
UPDATE db_script_string SET content_loc9='Thank you for freeing us, mortals. Beware, the Blue Flight is alerted to your presence. Even now, Malygos sends Varos Cloudstrider and his ring guardians to defend the Oculus. You will need our help to stand a chance.' WHERE entry=2000000876; /* content: Thank you for freeing us, mortals. Beware, the Blue Flight is alerted to your presence. Even now, Malygos sends Varos Cloudstrider and his ring guardians to defend the Oculus. You will need our help to stand a chance. */
UPDATE db_script_string SET content_loc9='Intruders, your victory will be short-lived. I am Commander Varos Cloudstrider. My drakes control the skies and protect this conduit. I will see to it personally that the Oculus does not fall into your hands!' WHERE entry=2000000877; /* content: Intruders, your victory will be short-lived. I am Commander Varos Cloudstrider. My drakes control the skies and protect this conduit. I will see to it personally that the Oculus does not fall into your hands! */
UPDATE db_script_string SET content_loc9='The trickster Mage-Lord Urom protects the third ring. He will appear alone and defenseless, but do not be fooled by appearances! Urom is a powerful conjurer who commands a menagerie of Phantasmal creatures. Seek him out above.' WHERE entry=2000000878; /* content: The trickster Mage-Lord Urom protects the third ring. He will appear alone and defenseless, but do not be fooled by appearances! Urom is a powerful conjurer who commands a menagerie of Phantasmal creatures. Seek him out above. */
UPDATE db_script_string SET content_loc9='Your greatest challenge lies ahead. Ley-Guardian Eregos is a Blue dragon of immense power. You will find him flying above the uppermost ring.' WHERE entry=2000000879; /* content: Your greatest challenge lies ahead. Ley-Guardian Eregos is a Blue dragon of immense power. You will find him flying above the uppermost ring. */
UPDATE db_script_string SET content_loc9='They who dine on lost souls know only hunger.' WHERE entry=2000000880; /* content: They who dine on lost souls know only hunger. */
UPDATE db_script_string SET content_loc9='Gaze into the void. It is the perpetuity in which they dwell.' WHERE entry=2000000881; /* content: Gaze into the void. It is the perpetuity in which they dwell. */
UPDATE db_script_string SET content_loc9='They drink your fear: it is the blood of life.' WHERE entry=2000000882; /* content: They drink your fear: it is the blood of life. */
UPDATE db_script_string SET content_loc9='They are the whisper on the shivering wind.' WHERE entry=2000000883; /* content: They are the whisper on the shivering wind. */
UPDATE db_script_string SET content_loc9='They do not die; they do not live. They are outside the cycle.' WHERE entry=2000000884; /* content: They do not die; they do not live. They are outside the cycle. */
UPDATE db_script_string SET content_loc9='Where one falls, many shall take its place.' WHERE entry=2000000885; /* content: Where one falls, many shall take its place. */
UPDATE db_script_string SET content_loc9='I thank you, $N. And my brother thanks you.' WHERE entry=2000000886; /* content: I thank you, $N. And my brother thanks you. */
UPDATE db_script_string SET content_loc9='%s is chasing you!' WHERE entry=2000000887; /* content: %s is chasing you! */
UPDATE db_script_string SET content_loc9='We will meet again... Someday.' WHERE entry=2000000888; /* content: We will meet again... Someday. */
UPDATE db_script_string SET content_loc9='Death incarnate has been released upon the world once more! Teron Gorefiend has returned...' WHERE entry=2000000889; /* content: Death incarnate has been released upon the world once more! Teron Gorefiend has returned... */
UPDATE db_script_string SET content_loc9='Release me immediatley or face my wrath!' WHERE entry=2000000890; /* content: Release me immediatley or face my wrath! */
UPDATE db_script_string SET content_loc9='This cell won\'t hold me for long. I will have Illidan\'s head one way or another.' WHERE entry=2000000891; /* content: This cell won\'t hold me for long. I will have Illidan\'s head one way or another. */
UPDATE db_script_string SET content_loc9='Hide! Hide before they see you! Back up!' WHERE entry=2000000892; /* content: Hide! Hide before they see you! Back up! */
UPDATE db_script_string SET content_loc9='The time to strike is at hand. Terminate Icarius.' WHERE entry=2000000893; /* content: The time to strike is at hand. Terminate Icarius. */
UPDATE db_script_string SET content_loc9='Halt!' WHERE entry=2000000894; /* content: Halt! */
UPDATE db_script_string SET content_loc9='What is it, my lord?' WHERE entry=2000000895; /* content: What is it, my lord? */
UPDATE db_script_string SET content_loc9='Zarath you must return to the Black Temple at once! I... I seem to have misplaced Lord Illidan\'s orders. Quickly!' WHERE entry=2000000896; /* content: Zarath you must return to the Black Temple at once! I... I seem to have misplaced Lord Illidan\'s orders. Quickly! */
UPDATE db_script_string SET content_loc9='My lord, surely you do not expect me to leave you unattended. Lord Illidan would have my head if anything were to happen to you.' WHERE entry=2000000897; /* content: My lord, surely you do not expect me to leave you unattended. Lord Illidan would have my head if anything were to happen to you. */
UPDATE db_script_string SET content_loc9='Zarath, I am perfectly capable of making it up this road to Eclipse Point. If we do not deliver the missive, Lord Illidan will have both of our heads! You are dismissed!' WHERE entry=2000000898; /* content: Zarath, I am perfectly capable of making it up this road to Eclipse Point. If we do not deliver the missive, Lord Illidan will have both of our heads! You are dismissed! */
UPDATE db_script_string SET content_loc9='As you wish, my lord.' WHERE entry=2000000899; /* content: As you wish, my lord. */
UPDATE db_script_string SET content_loc9='Ah, sweet, sweet bloodthistle... Probably left behind by one of those filthy addicts at Eclipse Point.' WHERE entry=2000000900; /* content: Ah, sweet, sweet bloodthistle... Probably left behind by one of those filthy addicts at Eclipse Point. */
UPDATE db_script_string SET content_loc9='Their loss is most definitely my gain...' WHERE entry=2000000901; /* content: Their loss is most definitely my gain... */
UPDATE db_script_string SET content_loc9='%s puts the bundle of bloodthistle in his robe.' WHERE entry=2000000902; /* content: %s puts the bundle of bloodthistle in his robe. */
UPDATE db_script_string SET content_loc9='Stand back, $N. The spirits may lash out.' WHERE entry=2000000903; /* content: Stand back, $N. The spirits may lash out. */
UPDATE db_script_string SET content_loc9='Why have you captured us?' WHERE entry=2000000904; /* content: Why have you captured us? */
UPDATE db_script_string SET content_loc9='What is it that you hope to gain from this?' WHERE entry=2000000905; /* content: What is it that you hope to gain from this? */
UPDATE db_script_string SET content_loc9='%s bows.' WHERE entry=2000000906; /* content: %s bows. */
UPDATE db_script_string SET content_loc9='We wish to help you, spirit. We seek your wisdom.' WHERE entry=2000000907; /* content: We wish to help you, spirit. We seek your wisdom. */
UPDATE db_script_string SET content_loc9='Wisdom? It was your people that caused the insufferable wrath of Cyrukh.' WHERE entry=2000000908; /* content: Wisdom? It was your people that caused the insufferable wrath of Cyrukh. */
UPDATE db_script_string SET content_loc9='Why should we help you? The world is ruined. Destroyed.' WHERE entry=2000000909; /* content: Why should we help you? The world is ruined. Destroyed. */
UPDATE db_script_string SET content_loc9='%s nods.' WHERE entry=2000000910; /* content: %s nods. */
UPDATE db_script_string SET content_loc9='You are right, spirit. It was my people that did this to you. Allow us to make it right.' WHERE entry=2000000911; /* content: You are right, spirit. It was my people that did this to you. Allow us to make it right. */
UPDATE db_script_string SET content_loc9='Please tell us this: Who or what is Cyrukh?' WHERE entry=2000000912; /* content: Please tell us this: Who or what is Cyrukh? */
UPDATE db_script_string SET content_loc9='Fool! Cyrukh the fire lord! When the tie was severed, Cyrukh awoke. It is the tears of Cyrukh that fill this land with molten rock.' WHERE entry=2000000913; /* content: Fool! Cyrukh the fire lord! When the tie was severed, Cyrukh awoke. It is the tears of Cyrukh that fill this land with molten rock. */
UPDATE db_script_string SET content_loc9='The... The fire lord? An elemental lord was unearthed? Of course... Gul\'dan you were a damned fool!' WHERE entry=2000000914; /* content: The... The fire lord? An elemental lord was unearthed? Of course... Gul\'dan you were a damned fool! */
UPDATE db_script_string SET content_loc9='He is insane now. Driven mad during the shattering. There is no rest for the elements.' WHERE entry=2000000915; /* content: He is insane now. Driven mad during the shattering. There is no rest for the elements. */
UPDATE db_script_string SET content_loc9='There must be a way.' WHERE entry=2000000916; /* content: There must be a way. */
