UPDATE `locales_quest` SET `offerrewardtext_loc1`='A quick search of the haystack reveals a lockbox from within it!  It would seem that you have found your fortune!' WHERE `entry`=7937;
UPDATE `locales_quest` SET `offerrewardtext_loc1`='A quick search of the chest reveals a lockbox from within it!  It would seem that you have found your fortune!' WHERE `entry`=7938;
