UPDATE `locales_quest` SET `details_loc1`='To the Caverns of Time you go, $G Lord:Lady; $N. Anachronos awaits your return. Give him the eye of C\'Thun as he will undoubtedly wish to place it in his master\'s collection.',`offerrewardtext_loc1`='The Master has left these for you, champion. From the timeless cache: A collection of artifacts spanning thousands of years - the choice is yours.' WHERE `entry`=8802;
