UPDATE `locales_quest` SET `details_loc1`='A hunter by the name of Torwa Pathfinder has requested the help of a druid. He said only we would possess the necessary skills and respect for nature to help him.$B$BThere is a road that connects the Un\'Goro Crater and the desert of Tanaris. As that road enters the crater, you will find Torwa close by.',`objectives_loc1`='Speak with Torwa Pathfinder in Un\'Goro Crater.',`offerrewardtext_loc1`='You have traveled far to meet with me, $C, and not a moment too soon.' WHERE `entry`=9063;
